$t = array();
$t[1] = 'ئه&zwnj;ی ئه&zwnj;و که&zwnj;سانه&zwnj;ی باوه&zwnj;ڕتان هێناوه دوژمنانی من و دوژمنانی خۆتان مه&zwnj;که&zwnj;نه دۆستی گیانی به&zwnj;گیانی، له&zwnj;کاتێکدا خۆشه&zwnj;ویستی و سۆزی خۆتانیان بۆ ده&zwnj;رده&zwnj;برن، به&zwnj;ڕاستی ئه&zwnj;وان بێ باوه&zwnj;ڕیش بوون به&zwnj;وه&zwnj;ی له لایه&zwnj;ن خواوه بۆتان هاتووه&zwnj;، ئه&zwnj;وانه پێغه&zwnj;مبه&zwnj;رو ئێوه&zwnj;ش ده&zwnj;رده&zwnj;که&zwnj;ن له مه&zwnj;ککه&zwnj;، له&zwnj;به&zwnj;ر ئه&zwnj;وه&zwnj;ی که باوه&zwnj;ڕ ده&zwnj;هێنن به&zwnj;خوای په&zwnj;روه&zwnj;ردگارتان، (ئه&zwnj;مه&zwnj;م به&zwnj;گوێ بکه&zwnj;ن) ئه&zwnj;گه&zwnj;ر ئێوه ده&zwnj;رچوون بۆ تێکۆشان له پێناوی من و به&zwnj;ده&zwnj;ستهێنانی ڕه&zwnj;زامه&zwnj;ندی مندا، به&zwnj;نهێنی خۆشه&zwnj;ویستی خۆتانیان پێده&zwnj;گه&zwnj;یه&zwnj;نن، له&zwnj;کاتێکدا من ئاگادارم به&zwnj;وه&zwnj;ی شاردوتانه&zwnj;ته&zwnj;و و به&zwnj;وه&zwnj;ش که ئاشکراتان کردووه&zwnj;، جا هه&zwnj;ر که&zwnj;س له ئێوه کاری وابکات ئه&zwnj;وه به&zwnj;ڕاستی ڕێبازی ڕاستی ون کردووه&zwnj;.';
$t[2] = 'هه&zwnj;ر کاتێك خوانه&zwnj;ناسان ده&zwnj;سه&zwnj;ڵاتیان هه&zwnj;بێت و ئێوه&zwnj;یان ده&zwnj;ست بکه&zwnj;وێت، ئه&zwnj;وه ده&zwnj;بنه دوژمنتان و ده&zwnj;ست و زمانیان به&zwnj;خراپه درێژ ده&zwnj;که&zwnj;ن بۆتان (به&zwnj;هه&zwnj;رچی بۆیان بکرێت) ئه&zwnj;وانه پێیان خۆش بووه و ئاواتیان خواستووه که ئێوه له ئایینه&zwnj;که&zwnj;تان هه&zwnj;ڵگه&zwnj;ڕێنه&zwnj;وه&zwnj;.';
$t[3] = 'خزمان و ڕۆڵه&zwnj;کانتان هه&zwnj;رگیز فریاتان ناکه&zwnj;ون، چونکه له ڕۆژی قیامه&zwnj;تدا له یه&zwnj;کتان جیاده&zwnj;کاته&zwnj;وه&zwnj;، به&zwnj;ڕاستی خوا بینایه به&zwnj;و کارو کرده&zwnj;وه&zwnj;ی که ده&zwnj;یکه&zwnj;ن.';
$t[4] = 'ئه&zwnj;ی ئیمانداران، به&zwnj;ڕاستی له ئیبراهیم و ئه&zwnj;وانه&zwnj;ی که له&zwnj;گه&zwnj;ڵیدا بوون نموونه&zwnj;یه&zwnj;کی چاکتان خراوه&zwnj;ته به&zwnj;رچاو، کاتێك که به قه&zwnj;ومه&zwnj;که&zwnj;ی خۆیانیان وت: دوای ساڵه&zwnj;ها بانگه&zwnj;واز، ئێمه به&zwnj;رین له ئێوه و له&zwnj;و شتانه&zwnj;ش که له&zwnj;جیاتی خوا ده&zwnj;یپه&zwnj;رستن، ئێمه باوه&zwnj;ڕمان به&zwnj;و د&zwnj;ین و ئاینه&zwnj;ی ئێوه نیه&zwnj;، ئیتر دوژمنایه&zwnj;تی و ڕقه&zwnj;به&zwnj;رایه&zwnj;تی له&zwnj;نێوانماندا به&zwnj;رپا بووه تا ئه&zwnj;و کاته&zwnj;ی که باوه&zwnj;ڕ به خوای تاك و ته&zwnj;نها ده&zwnj;هێنن، جگه له&zwnj;و به&zwnj;ڵێنه&zwnj;ی ئیبراهیم دای به باوکی و وتی: بێگومان داوای لێخۆشبوونت بۆ ده&zwnj;که&zwnj;م، ده&zwnj;سه&zwnj;ڵاتی خوایشم نیه ئه&zwnj;گه&zwnj;ر کاره&zwnj;ساتێكت به&zwnj;سه&zwnj;ر بهێنێت، ئینجا وتی: په&zwnj;روه&zwnj;ردگارا، هه&zwnj;ر پشتمان به تۆ به&zwnj;ستووه و هه&zwnj;ر بۆ لای تۆش گه&zwnj;ڕاوینه&zwnj;ته&zwnj;وه&zwnj;، سه&zwnj;ره&zwnj;&zwnj;نجامیش هه&zwnj;ر بۆ لای تۆیه&zwnj;.';
$t[5] = 'په&zwnj;روه&zwnj;ردگارا مه&zwnj;مانکه به&zwnj;مایه&zwnj;ی گومڕایی بۆ ئه&zwnj;وانی بێ باوه&zwnj;ڕ بوون، لێشما خۆش ببه په&zwnj;روه&zwnj;ردگارمان، به&zwnj;ڕاستی هه&zwnj;ر تۆ خوایه&zwnj;کی باڵاده&zwnj;ست و دانایت.';
$t[6] = 'سوێند به خوا به&zwnj;ڕاستی له سه&zwnj;رگوزه&zwnj;شته و ژیانی ئیبراهیم و ئه&zwnj;واندا نموونه و پێشه&zwnj;نگێگی چاک هه&zwnj;یه بۆ ئه&zwnj;وه&zwnj;ی چاویان لێ بکه&zwnj;ن، بۆ ئه&zwnj;و که&zwnj;سه&zwnj;ی که ئاوات و ئومێدی ڕه&zwnj;زامه&zwnj;ندی خوایه&zwnj;و له ڕۆژی دوایی ده&zwnj;ترسێت و حسابی بۆ ده&zwnj;کات، جا ئه&zwnj;وه&zwnj;ش که پشت هه&zwnj;ڵده&zwnj;کات و شوێن ڕێبازی ئه&zwnj;وان ناکه&zwnj;وێت ئه&zwnj;وه بابزانێت خۆی زه&zwnj;ره&zwnj;ر ده&zwnj;کات چونکه خوا هه&zwnj;ر خۆی بێنیازه و شایسته&zwnj;ی هه&zwnj;موو جۆره سوپاسێکه&zwnj;.';
$t[7] = 'نزیکه خوا خۆشه&zwnj;ویستی و سۆز به&zwnj;رپا بکات له نێوان ئێوه و ئه&zwnj;وانه&zwnj;دا که دوژمنایه&zwnj;تیتان له&zwnj;گه&zwnj;ڵ کردن له&zwnj;وان و هه&zwnj;ندێکیان باوه&zwnj;ڕ بهێنن، هه&zwnj;میشه خوا به ده&zwnj;سه&zwnj;ڵاته&zwnj;، هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;ش لێخۆشبوو میهره&zwnj;بانه چاوپۆشی له ڕابوردوو ده&zwnj;کات.';
$t[8] = 'دڵنیاش بن که خوا قه&zwnj;ده&zwnj;غه&zwnj;ی ئه&zwnj;وه&zwnj;تان لێ ناکات که چاك ڕه&zwnj;فتاری و دادپه&zwnj;روه&zwnj;ری بکه&zwnj;ن له&zwnj;گه&zwnj;ڵ ئه&zwnj;و که&zwnj;سانه&zwnj;دا جه&zwnj;نگیان دژ نه&zwnj;کردون و دژایه&zwnj;تی ئاینیان نه&zwnj;کردون و له شوێنه&zwnj;وارتان ده&zwnj;ریان نه&zwnj;کردون، چونکه به&zwnj;ڕاستی خوا دادپه&zwnj;روه&zwnj;رانی خۆش ده&zwnj;وێت.';
$t[9] = 'به&zwnj;ڕاستی خوا ته&zwnj;نها قه&zwnj;ده&zwnj;غه&zwnj;ی ئه&zwnj;وه&zwnj;تان لێ ده&zwnj;کات که پشتیوانیی بکه&zwnj;ن له&zwnj;وانه&zwnj;ی که جه&zwnj;نگیان به&zwnj;رپا کرد دژی ئاین و ده&zwnj;ریان کردن له شوێنه&zwnj;واری خۆتان و پشتیوانی ئه&zwnj;وانیان کرد ڕۆڵیان هه&zwnj;بوو له ده&zwnj;رکردنتاندا، ئه&zwnj;وه&zwnj;ش پشتیوانیان لێ بکات و خۆشی بوێن، جا ئائه&zwnj;وانه هه&zwnj;ر سته&zwnj;مکارن.';
$t[10] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه کاتێک ئافره&zwnj;ته ئیمانداره&zwnj;کان کۆچ ده&zwnj;که&zwnj;ن و دێن بۆ لاتان تاقیان بکه&zwnj;نه&zwnj;وه&zwnj;و به&zwnj;وردیی پرسیاریان لێ بکه&zwnj;ن سه&zwnj;باره&zwnj;ت به&zwnj;کۆچ و هۆی هاتنه&zwnj;که&zwnj;یان، خوا ئاگاداره به ئیمانه&zwnj;که&zwnj;یان، جا ئه&zwnj;گه&zwnj;ر زانیتان باوه&zwnj;ڕدارن، ئه&zwnj;وه مه&zwnj;یانگێڕنه&zwnj;وه بۆ لای مێرده بێ باوه&zwnj;ڕه&zwnj;کانیان، نه ئه&zwnj;م ئافره&zwnj;ته حه&zwnj;ڵاڵی ئه&zwnj;و بێ باوه&zwnj;ڕانه&zwnj;ن، نه&zwnj;ئه&zwnj;و بێ باوه&zwnj;ڕا&zwnj;نه&zwnj;ش حه&zwnj;ڵاڵن بۆ ئه&zwnj;م ئافره&zwnj;ته ئیماندارانه، ئه&zwnj;و ماره&zwnj;ییه&zwnj;ی مێرده کافره&zwnj;کانیان خه&zwnj;رجیان کردووه بۆیان بگێڕنه&zwnj;وه&zwnj;، ئیتر هیچ ڕه&zwnj;خنه&zwnj;یه&zwnj;کیش له&zwnj;سه&zwnj;رتان نیه ئه&zwnj;و ئافرته ماره بکه&zwnj;ن کاتێك ماره&zwnj;ییان بده&zwnj;نێ، ئافره&zwnj;تانی کافرمه&zwnj;هێڵنه&zwnj;وه لای خۆتان، واته ماره&zwnj;یان مه&zwnj;که&zwnj;ن، ئێوه&zwnj;ش ئه&zwnj;ی ئیمانداران ئه&zwnj;گه&zwnj;ر مه&zwnj;سره&zwnj;فێکتان کردووه له ئافره&zwnj;تێکی کافردا داوای بکه&zwnj;نه&zwnj;وه&zwnj;، ئه&zwnj;وانیش ئه&zwnj;گه&zwnj;ر مه&zwnj;سره&zwnj;فێکیان کردووه له ئافره&zwnj;تێکی ئیمانداردا با داوای بکه&zwnj;نه&zwnj;وه&zwnj;، ئائه&zwnj;وه حو&zwnj;کم و فه&zwnj;رمانی خوایه&zwnj;، که خۆی فه&zwnj;رمانڕه&zwnj;وایی ده&zwnj;کات له نێوانتاندا، خوای گه&zwnj;وره&zwnj;ش هه&zwnj;میشه زانا و دانایه&zwnj;.';
$t[11] = 'خۆ ئه&zwnj;گه&zwnj;ر یه&zwnj;کێك یان هه&zwnj;ندێك له هاوسه&zwnj;رانتان گه&zwnj;ڕانه&zwnj;وه بۆ ناو کافران، له&zwnj;وه&zwnj;ودوا جه&zwnj;نگ له نێوان ئێوه و کافراندا به&zwnj;رپابوو و سه&zwnj;رکه&zwnj;وتن به&zwnj;سه&zwnj;ریاندا، ئێوه له ده&zwnj;ستکه&zwnj;وتی ئه&zwnj;و جه&zwnj;نگه زه&zwnj;ره&zwnj;ری ئه&zwnj;و ئیماندارانه ببژێرن که خێزانه&zwnj;کانیان ڕۆشتوون، له&zwnj;و خوایه بترسن که ئێوه هه&zwnj;میشه بڕوادارن پێی.';
$t[12] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) کاتێک ئافره&zwnj;تانی ئیماندار هاتن بۆ لات و په&zwnj;یمانیان پێدایت که&zwnj;: هیچ جۆره هاوه&zwnj;ڵێك بۆ خوا بڕیار نه&zwnj;ده&zwnj;ن، دزی نه&zwnj;که&zwnj;ن، زینا نه&zwnj;که&zwnj;ن، ڕۆڵه&zwnj;کانی خۆیان نه&zwnj;کوژن، هیچ جۆره بوختانێک نه&zwnj;که&zwnj;ن له&zwnj;نێوان ده&zwnj;ست و پێی خۆیاندا (واته دووره په&zwnj;رێزبن له هه&zwnj;موو جۆره خیانه&zwnj;تێک) و فه&zwnj;رمانبه&zwnj;ردارت بن له هه&zwnj;موو چاکه&zwnj;یه&zwnj;کداو یاخی نه&zwnj;بن لێت، جا ئه&zwnj;وه تۆ په&zwnj;یمانیان له&zwnj;گه&zwnj;ڵ ببه&zwnj;ستن و له خوا داوای لێخۆشبوونیان بۆ بکه&zwnj;، چونکه به&zwnj;ڕاستی خوا لێخۆشبوو میهره&zwnj;بانه&zwnj;.';
$t[13] = 'ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه پشت مه&zwnj;به&zwnj;ستن به&zwnj;که&zwnj;سانێك خوا خه&zwnj;شمی لێگرتوون و دۆستایه&zwnj;تیان مه&zwnj;که&zwnj;ن، بێگومان ئه&zwnj;وانه نائومێد و بێ هیوا بوون له پاداشتی قیامه&zwnj;ت، هه&zwnj;روه&zwnj;کو کافران، نا&zwnj;ئومێد بوون له گه&zwnj;ڕانه&zwnj;ی نیشته&zwnj;جێکانی گۆڕستان.';
