$t = array();
$t[1] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر (صلی الله علیه وسلم) (به ئوممه&zwnj;تت ڕابگه&zwnj;یه&zwnj;نه&zwnj;) کاتێك ژنانتان ته&zwnj;ڵاق ده&zwnj;ده&zwnj;ن له&zwnj;تاتی دیاری کراویاندا ته&zwnj;ڵاقیان بده&zwnj;ن (که بریتیه له&zwnj;و کاته&zwnj;ی که ئافره&zwnj;ت له عاده&zwnj;ی مانگانه پاك بۆته&zwnj;وه&zwnj;)، حسابی ئه&zwnj;وکاته&zwnj;ش به وردی ڕابگرن، له په&zwnj;روه&zwnj;ردگاریشتان بترسن و ماوه&zwnj;که&zwnj;یان بۆ درێژ مه&zwnj;که&zwnj;نه&zwnj;وه (ئه&zwnj;گه&zwnj;ر له&zwnj;و کاته دیاریکراوانه&zwnj;دا یه&zwnj;کجار ته&zwnj;ڵاقتان دان، ئه&zwnj;وه با ئه&zwnj;و ئافره&zwnj;ته له ماڵه&zwnj;که&zwnj;ی خۆیدا بمینێته&zwnj;وه و) ده&zwnj;ریان مه&zwnj;که&zwnj;ن له&zwnj;ماڵه&zwnj;کانی خۆیاندا، ئافره&zwnj;ته&zwnj;کانیش با له ماڵ ده&zwnj;رنه&zwnj;چن، مه&zwnj;گه&zwnj;ر کارێکی ناشیرین و ناڕه&zwnj;وای ئاشکرا ئه&zwnj;نجام بده&zwnj;ن، (ئه&zwnj;وکاته با له ماڵ نه&zwnj;مێنن و بچن بۆ ماڵی مه&zwnj;حره&zwnj;مێکیان)، ئا ئه&zwnj;وه&zwnj;یه سنووری خوا له باره&zwnj;ی ته&zwnj;ڵاقه&zwnj;وه&zwnj;، ئه&zwnj;وه&zwnj;ی له سنووری خوا بترازێت ئه&zwnj;وه سته&zwnj;م له خۆی ده&zwnj;کات، ئه&zwnj;ی ئیماندار: تۆ نازانیت له&zwnj;وانه&zwnj;یه دوای ئه&zwnj;و (تێکچوون و ساردییه&zwnj;) خوای گه&zwnj;وره بارودۆخێك پێش بهێنێت (که ئاشت ببنه&zwnj;وه و ڕێك بکه&zwnj;ونه&zwnj;وه و بۆیه نابێت له ماڵ ده&zwnj;رچن).';
$t[2] = 'کاتێك گه&zwnj;یشتنه ماوه&zwnj;ی دیاریکراوی خۆیان (دوای یه&zwnj;ك، یان ئه&zwnj;و دوو ته&zwnj;ڵاقه که خواردو&zwnj;یانه&zwnj;)، ئینجا یان بیانهێڵنه&zwnj;وه لای خۆتان هاوڕێ له&zwnj;گاڵ ڕه&zwnj;فتارو هه&zwnj;ڵسوکه&zwnj;وتی چاکدا، یاخود وازیان لێبهێنن و لێیان جیاببنه&zwnj;وه (له سێهه&zwnj;م جاردا که ته&zwnj;ڵاقتان خوارد) به&zwnj;شێوه&zwnj;یه&zwnj;کی جوان که مافیان پارێزراو بێت، له کاتی جیابوونه&zwnj;وه&zwnj;ی یه&zwnj;کجاریدا با دوو شایه&zwnj;تی دادپه&zwnj;روه&zwnj;رو خواناس له خۆتان له نێواندا هه&zwnj;بێت و ئاماده&zwnj;ی شایه&zwnj;تی ببن و بیپارێزن بۆ خواو له&zwnj;به&zwnj;ر خوا، به&zwnj;وه&zwnj;ی که&zwnj;باسکرا ئامۆژگاری ده&zwnj;کرێت ئه&zwnj;و که&zwnj;سه&zwnj;ی باوه&zwnj;ڕ به خواو ڕۆژی دوایی ده&zwnj;هێنیت، جا ئه&zwnj;وه&zwnj;ی له خوا بترسێت و پارێزکار بێت، ئه&zwnj;وه خوا ده&zwnj;رووی لێ ده&zwnj;کاته&zwnj;وه&zwnj;...';
$t[3] = 'له شوێنێکه&zwnj;وه ڕزق و ڕۆزی پێ ده&zwnj;به&zwnj;خشێت که حسابی بۆ نه&zwnj;کردبێت و خۆی پێی نازانێت، جا ئه&zwnj;وه&zwnj;ی پشت به خوا ببه&zwnj;ستێت، ئه&zwnj;و زاته&zwnj;ی به&zwnj;سه&zwnj;، بۆ ئه&zwnj;وه&zwnj;ی یاریده&zwnj;ده&zwnj;ری بێت، چونکه به&zwnj;ڕاستی خوا کارو فه&zwnj;رمانی خۆی به ئه&zwnj;نجام ده&zwnj;گه&zwnj;یه&zwnj;نێت، بێگومان خوا بۆ هه&zwnj;موو شتێک نه&zwnj;خشه&zwnj;یه&zwnj;کی دیاریکراوی بڕیارداوه (که ده&zwnj;بێت په&zwnj;یڕه&zwnj;وی بکرێت).';
$t[4] = 'ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی که نائومێد بوون له عاده&zwnj;تی مانگانه له هاوسه&zwnj;ره&zwnj;کانتان، ئه&zwnj;گه&zwnj;ر که&zwnj;وتنه گوناهه&zwnj;وه که ئاخۆ عاده&zwnj;تی مانگانه&zwnj;یان مابێت یان نا، ئه&zwnj;وه سێ مانگ چاوه&zwnj;ڕێ بکه&zwnj;ن، هه&zwnj;روه&zwnj;ها ئه&zwnj;وانه&zwnj;ش که به&zwnj;هۆی که&zwnj;م ته&zwnj;مه&zwnj;نیوه هێشتا نه&zwnj;که&zwnj;وتونه&zwnj;ته عاده&zwnj;وه&zwnj;، ئه&zwnj;وانیش سێ مانگ چاوه&zwnj;ڕێ بکه&zwnj;ن، ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ش که سکیان هه&zwnj;یه ماوه&zwnj;که&zwnj;یان ئه&zwnj;وه&zwnj;نده&zwnj;یه تا سکه&zwnj;که&zwnj;یان داده&zwnj;نێن، جا ئه&zwnj;وه&zwnj;ی که له خوا بترسێت و پارێزکار بێت، ئه&zwnj;وه خوا کاری بۆ ئاسان ده&zwnj;کات.';
$t[5] = 'ئا ئه&zwnj;وه فه&zwnj;رمان و سنووری خوایه که ناردوویه&zwnj;تییه خواره&zwnj;وه بۆتان، ئه&zwnj;وه&zwnj;ی له خوا بترسێت و پارێزکار بێت، خوا گوناهه&zwnj;کانی ده&zwnj;سڕێته&zwnj;وه و چاوپۆشی لێ ده&zwnj;کات و پاداشته&zwnj;که&zwnj;ی بۆ گه&zwnj;وره&zwnj;و بێ سنوور ده&zwnj;کات.';
$t[6] = 'ئه&zwnj;و ئافره&zwnj;تانه&zwnj;ی که ته&zwnj;ڵاقێک یان دووانیان ماوه&zwnj;، به&zwnj;گوێره&zwnj;ی تواناتان جێگه و ڕێگه&zwnj;یان بۆ دا&zwnj;بین بکه&zwnj;ن له&zwnj;لای خۆتان، نه&zwnj;که&zwnj;ن ئازاریان بده&zwnj;ن و ته&zwnj;نگه&zwnj;تاویان بکه&zwnj;ن و زۆریان بۆ بهێنن، خۆ ئه&zwnj;گه&zwnj;ر سکیان هه&zwnj;بوو، (ئه&zwnj;گه&zwnj;ر سێ ته&zwnj;ڵاقه&zwnj;ش درابوون) هه&zwnj;ر به&zwnj;خێوان بکه&zwnj;ن و مه&zwnj;سره&zwnj;فیان بکێشن هه&zwnj;تا سکه&zwnj;که&zwnj;یان داده&zwnj;نێن، خۆ ئه&zwnj;گه&zwnj;ر هاتوو منداڵه&zwnj;که&zwnj;یان به&zwnj;شیر گۆشکردو به&zwnj;خێویان کرد ئه&zwnj;وه حه&zwnj;قی خۆیان بده&zwnj;نێ، ده&zwnj;کرێت به&zwnj;شێوه&zwnj;یه&zwnj;کی جوان و چاك ڕاوێژ له&zwnj;ناویه&zwnj;کدا بکه&zwnj;ن بۆ به&zwnj;خێو کردنی منداڵه&zwnj;که&zwnj;، خۆ ئه&zwnj;گه&zwnj;ر پێك نه&zwnj;هاتن و ناکۆك بوون، ئه&zwnj;وه ده&zwnj;کرێت ئافره&zwnj;تێکی تر منداڵه&zwnj;که&zwnj;ی بۆ به&zwnj;خێو بکات و شیری پێ بدات.';
$t[7] = 'جا ئه&zwnj;وه&zwnj;ی ده&zwnj;وڵه&zwnj;مه&zwnj;نده با به&zwnj;گوێره&zwnj;ی ده&zwnj;وڵه&zwnj;مه&zwnj;ندیه&zwnj;که&zwnj;ی به&zwnj;خشنده&zwnj;بێت، ئه&zwnj;وه&zwnj;یش که که&zwnj;م ده&zwnj;سته&zwnj;و هه&zwnj;ژاره&zwnj;، با له&zwnj;وه ببه&zwnj;خشێت که خوا پێی به&zwnj;خشیووه&zwnj;، چونکه خوا به گوێره&zwnj;ی دارایی هه&zwnj;ر که&zwnj;سێک داوی به&zwnj;خشین ده&zwnj;کات، دڵنیاس بن که خوا دوای ته&zwnj;نگانه هه&zwnj;رزانی ده&zwnj;هێنێت، دوای ناخۆشی خۆشی ده&zwnj;هێنێت.';
$t[8] = 'له سه&zwnj;ده&zwnj;کانی پێشوودا چه&zwnj;نداها شارو شارۆچکه یاخی بوون له فه&zwnj;رمانی په&zwnj;روه&zwnj;ردگاریان و فرستاده&zwnj;کانی، ئێمه زۆر به&zwnj;توندیی حسابمان بۆ کردن و سزایه&zwnj;کی توندو پڕئێشمان بۆ پێشهێنان.';
$t[9] = 'ئیتر گیرۆده&zwnj;ی به&zwnj;ڵای خۆیان بوون و ئه&zwnj;و سزایه&zwnj;یان چه&zwnj;شت که خۆیان ده&zwnj;ست پێشکه&zwnj;ریان بۆ کردبوو، سه&zwnj;رئه&zwnj;نجامی کارو کرده&zwnj;وه&zwnj;ی خه&zwnj;ڵکی ئه&zwnj;و شوێنانه&zwnj;ش زیانێکی گه&zwnj;وره بوو. (ئه&zwnj;ویش ئه&zwnj;مه&zwnj;یه که&zwnj;)...';
$t[10] = 'له قیامه&zwnj;تیشدا خوا سزای به&zwnj;ئێش له&zwnj;ناو دۆزه&zwnj;خدا بۆ ئاماده&zwnj;کردوون، ده&zwnj;ی کاواته ئه&zwnj;ی ڕۆشنبیره&zwnj;کان، ئه&zwnj;ی ئه&zwnj;وانه&zwnj;ی باوه&zwnj;ڕتان هێناوه&zwnj;؛ له خوا بترسن پارێزکار بن دڵنیابن خوای گه&zwnj;وره قورئانی بۆ ئێوه دابه&zwnj;زاندووه&zwnj;...';
$t[11] = 'هه&zwnj;روه&zwnj;ها پێغه&zwnj;مبه&zwnj;رێك که ئایه&zwnj;ته ڕوونه&zwnj;کانی خواتان به&zwnj;سه&zwnj;ردا ده&zwnj;خوێنێته&zwnj;وه&zwnj;، تا ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان هێناوه و کرده&zwnj;وه چاکه&zwnj;کانیان ئه&zwnj;نجام داوه له تاریکیه&zwnj;کان ده&zwnj;ریان بهێنێت و ڕزگاریان بکات و بیانخاته ناو نوورو ڕوناکیه&zwnj;وه&zwnj;، جا ئه&zwnj;وه&zwnj;ی باوه&zwnj;ڕ بهێنێت به خواو کرده&zwnj;وه&zwnj;ی چاك ئه&zwnj;نجام بدات، ئه&zwnj;وه ده&zwnj;یخاته باخه&zwnj;کانی به&zwnj;هه&zwnj;شته&zwnj;وه که چه&zwnj;نده&zwnj;ها ڕوبار به ژێر دره&zwnj;خت و به&zwnj;رده&zwnj;م کۆشکه&zwnj;کانیدا ده&zwnj;ڕوات و له&zwnj;وێ ژیانی هه&zwnj;میشه&zwnj;یی و هه&zwnj;تاهه&zwnj;تایی ده&zwnj;بنه سه&zwnj;ر، بێگومان خوا ڕزق و ڕۆزی چاك و پاك و فره&zwnj;و فراوانی بۆ ئاماده کردوون.';
$t[12] = 'خوا ئه&zwnj;و زاته&zwnj;یه که حه&zwnj;وت چین ئاسمانی دروست کردووه و فه&zwnj;رمانی خوا له نێوانیاندا داده&zwnj;به&zwnj;زێت، هه&zwnj;روه&zwnj;ها به&zwnj;وینه&zwnj;ی ئه&zwnj;وان فه&zwnj;رمانی خوا بۆ زه&zwnj;ویش داده&zwnj;به&zwnj;زێت و جێ به جێ ده&zwnj;کرێت، بۆ ئه&zwnj;وه&zwnj;ی بزانن که به&zwnj;ڕاست خوا ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;، بێگومان خوا به زانستی و زانیاری خۆی گه&zwnj;مارۆی هه&zwnj;موو شتێکی داوه&zwnj;.';
