$t = array();
$t[1] = 'گه&zwnj;وره&zwnj;و موباره&zwnj;ك و پیرۆزه&zwnj;، ئه&zwnj;و زاته&zwnj;ی که هه&zwnj;موو بوونه&zwnj;وه&zwnj;رو جیهانی به&zwnj;ده&zwnj;سته&zwnj;، ئه&zwnj;و زاته&zwnj;ی ده&zwnj;سه&zwnj;ڵاتی به&zwnj;سه&zwnj;ر هه&zwnj;موو شتێکدا هه&zwnj;یه&zwnj;.';
$t[2] = 'هه&zwnj;ر ئه&zwnj;و خۆی مردن و ژیانی به&zwnj;دیهێناوه&zwnj;، تا تاقیتان بکاته&zwnj;وه&zwnj;، کێتان کرده&zwnj;وه&zwnj;ی چاکترو په&zwnj;سه&zwnj;ندتره&zwnj;، هه&zwnj;ر ئه&zwnj;ویش زاتێکی باڵاده&zwnj;سته به&zwnj;سه&zwnj;ر بێ باوه&zwnj;ڕاندا، وه لێخۆشبووه له ئیمانداران.';
$t[3] = 'هه&zwnj;روه&zwnj;ها ئه&zwnj;و زاته ئاسمانه&zwnj;کانی به حه&zwnj;وت چین دروست کردووه&zwnj;، له نێو به&zwnj;دیهێنراوه&zwnj;کانی ئه&zwnj;و خوای میهره&zwnj;بانه&zwnj;دا هیچ ناڕێکی و ناته&zwnj;واویه&zwnj;ك به&zwnj;دی ناکه&zwnj;یت، ئه&zwnj;ی ئینسان: هه&zwnj;رچاو بگێڕه&zwnj;و سه&zwnj;رنج بده بزانه هیچ درزو که&zwnj;م و کووڕیه&zwnj;ك به&zwnj;دی ده&zwnj;که&zwnj;یت؟!';
$t[4] = 'پاشان هه&zwnj;ر سه&zwnj;رنج بده&zwnj;و چاو بگێره&zwnj;و ورد به&zwnj;ره&zwnj;وه جار له دوای جارو کۆڵ مه&zwnj;ده&zwnj;، سه&zwnj;رسام و ملکه&zwnj;چ ده&zwnj;بیت، چاوت به نائومێدی ده&zwnj;گه&zwnj;ڕێته&zwnj;وه له&zwnj;کاتێکدا که چاوت ماندووه&zwnj;و په&zwnj;کی که&zwnj;وتووه&zwnj;.';
$t[5] = 'سوێند به&zwnj;خوا بێگومان ئێمه ئاسمانی دنیامان کردووه به چراخان (به&zwnj;هۆی ئه&zwnj;ستێره&zwnj;کانه&zwnj;وه که به شه&zwnj;وگاردا به&zwnj;دی ده&zwnj;کرێن) و به&zwnj;و ئه&zwnj;ستێرانه شه&zwnj;یتانه&zwnj;کان ڕاو ده&zwnj;که&zwnj;ین، له قیامه&zwnj;تیشدا سزاو ئه&zwnj;شکه&zwnj;نجه&zwnj;ی دۆزه&zwnj;خمان بۆ ئاماده کردوون.';
$t[6] = 'هه&zwnj;روه&zwnj;ها بۆ ئه&zwnj;وانه&zwnj;ش که باوه&zwnj;ڕیان به په&zwnj;روه&zwnj;ردگاریان نیه&zwnj;، سزاو ئازاری دۆزه&zwnj;خ ئاماده&zwnj;یه&zwnj;، ئای که چاره&zwnj;نووس و سه&zwnj;رئه&zwnj;نجامێکی زۆر ناسۆرو ناخۆشه&zwnj;...';
$t[7] = 'کاتێك فڕێ ده&zwnj;درێنه ناوی ده&zwnj;نگێکی ناسازو ناخۆش و لووره&zwnj;یه&zwnj;ك له دۆزه&zwnj;خه&zwnj;وه ده&zwnj;بیستن، له&zwnj;کاتێکدا قوڵپ ده&zwnj;دات و ده&zwnj;کوڵێت.';
$t[8] = 'له داخی خوانه&zwnj;ناسان خه&zwnj;ریکه پارچه پارچه ببێت و لێك ببێته&zwnj;وه&zwnj;، هه&zwnj;ر جارێك ده&zwnj;سته&zwnj;و تاقمێك فڕێ ده&zwnj;درێته ناوی پاسه&zwnj;وانی دۆزه&zwnj;خ لێیان ده&zwnj;پرسێت: باشه&zwnj;، ترسێنه&zwnj;رێکتان بۆ نه&zwnj;هات تا له&zwnj;م سه&zwnj;رئه&zwnj;نجامه ئاگادارتان بکات؟!';
$t[9] = 'له وه&zwnj;ڵامدا به&zwnj;که&zwnj;ساسیه&zwnj;وه ده&zwnj;ڵێن: با، به&zwnj;ڵێ، له&zwnj;ڕاستیدا ترسێنه&zwnj;رمان بۆهات، به&zwnj;ڵام ئێمه بڕوامان پێنه&zwnj;کردن و وتمان: خوا هیچی له&zwnj;م باره&zwnj;یه&zwnj;وه نه&zwnj;ناردووه&zwnj;، ئێوه ته&zwnj;نها له گومڕایی گه&zwnj;وره&zwnj;دا گیرتان خواردووه&zwnj;و هیچی تر!!';
$t[10] = 'ئینجا ده&zwnj;ڵێن: ئه&zwnj;گه&zwnj;ر ئێمه گوێمان بگرتایه&zwnj;و بیرو هۆشمان به&zwnj;کار بهێنایه نه&zwnj;ده&zwnj;بووینه نیشته&zwnj;جێی دۆزه&zwnj;خ.';
$t[11] = 'ئه&zwnj;وسا ئیتر به&zwnj;ناچاریی و خه&zwnj;جاڵه&zwnj;تیه&zwnj;وه دان به&zwnj;گوناهو تاوانی خۆیاندا ده&zwnj;نێن، جا با نیشته&zwnj;جێکانی دۆزه&zwnj;خ له&zwnj;وه&zwnj;و لا ترچن.';
$t[12] = 'به&zwnj;ڕاستی ئه&zwnj;وانه&zwnj;ی که له په&zwnj;روه&zwnj;ردگاریان ده&zwnj;ترسن له په&zwnj;نهانیدا، بۆ ئه&zwnj;وانه لێخۆشبوون و پاداشتی گه&zwnj;وره ئاماده&zwnj;یه&zwnj;..';
$t[13] = 'خه&zwnj;ڵکینه&zwnj;؛ چونیه&zwnj;که بۆتان چ به&zwnj;نهێنی قسه بکه&zwnj;ن یان به&zwnj;ئاشکرا، ئه&zwnj;و زاته به&zwnj;هه&zwnj;موو ئه&zwnj;و خه&zwnj;ته&zwnj;ره&zwnj;و خه&zwnj;یاڵانه&zwnj;ی دێن به&zwnj;کرۆکی سینه&zwnj;کاندا زاناو ئاگایه&zwnj;';
$t[14] = 'مه&zwnj;گه&zwnj;ر خوا نازانێت کێی دروست کردووه&zwnj;و چۆنی دروست کردووه&zwnj;، له&zwnj;کاتێکدا ئه&zwnj;و زاته وردکارو به سۆزو ئاگایه&zwnj;.';
$t[15] = 'هه&zwnj;ر ئه&zwnj;و زاته زه&zwnj;وی بۆ زه&zwnj;لیل و ملکه&zwnj;چ کردوون (هه&zwnj;رچیتان بوێت له&zwnj;سه&zwnj;ری دروست ده&zwnj;که&zwnj;ن، چۆنتان بوێت به&zwnj;کاری ده&zwnj;هێنن)، بگه&zwnj;ڕێن به هه&zwnj;موو گۆشه&zwnj;و که&zwnj;ناریدا، له&zwnj;و ڕزق و ڕۆزیه&zwnj;ش بخۆن که پێشکه&zwnj;شتانی ده&zwnj;کات، دڵنیاش بن که سه&zwnj;رئه&zwnj;نجام گه&zwnj;ڕانه&zwnj;وه&zwnj;تان بۆ لای ئه&zwnj;و زاته&zwnj;یه&zwnj;.';
$t[16] = 'ئایا له&zwnj;و زاته ناترسن که له سه&zwnj;رووی ئاسمانه&zwnj;وه&zwnj;یه&zwnj;، که به&zwnj;ناخی زه&zwnj;ویدا بتانباته خواره&zwnj;وه&zwnj;؟! جا ده&zwnj;ستبه&zwnj;جێ ئه&zwnj;و زه&zwnj;وی یه بێته له&zwnj;رزه&zwnj;...';
$t[17] = 'یاخود له&zwnj;و زاته ناترسن که له سه&zwnj;روو ئاسماندایه که بایه&zwnj;کی به&zwnj;هێز هه&zwnj;ڵکاته سه&zwnj;رتان و به&zwnj;رد بارانتان بکات؟! ئه&zwnj;وسا ده&zwnj;زانن هه&zwnj;ڕه&zwnj;شه&zwnj;ی من چۆن بووه&zwnj;.';
$t[18] = 'سوێند به&zwnj;خوا بێگومان خه&zwnj;ڵکانی تری پێش ئه&zwnj;مان بڕوایان به&zwnj;پێغه&zwnj;مبه&zwnj;رانی ئێمه نه&zwnj;کرد ئاشکرایه چ به&zwnj;ڵایه&zwnj;کم به&zwnj;سه&zwnj;رهێناون و چۆن ته&zwnj;مێم کردن.';
$t[19] = 'ئایا سه&zwnj;رنجی باڵنده&zwnj;یان نه&zwnj;داوه به&zwnj;سه&zwnj;ریانه&zwnj;وه پۆل پۆل ده&zwnj;فڕن و باڵه&zwnj;کانیان لێك ده&zwnj;نێن، ئه&zwnj;و باڵندانه جگه خوای میهره&zwnj;بان که&zwnj;س به ئاسمانه&zwnj;وه ڕاگیریان ناکات، به&zwnj;ڕاستی ئه&zwnj;و زاته به هه&zwnj;موو شتێك بینایه&zwnj;و هیچی لێ ون نابێت و چاودێری هه&zwnj;موو دروستکراوانی ده&zwnj;کات (فڕۆکه&zwnj;و که&zwnj;شتی ئاسمانی و.... هتد، هه&zwnj;ر ئه&zwnj;و زاته به هۆکاره&zwnj;کان له ئاسماندا هاتووچۆیان پێده&zwnj;کات و ڕایان ده&zwnj;گرێت).';
$t[20] = 'ئایا کێیه ئه&zwnj;و که&zwnj;سه خۆی به سه&zwnj;ربازو به&zwnj;رگریکه&zwnj;ر له ئێوه ده&zwnj;زانێت و وا ده&zwnj;نوێنێت که ده&zwnj;توانێت به&zwnj;بێ خوای میهره&zwnj;بان سه&zwnj;رتان بخات و پایه&zwnj;دارتان بکات؟! نه&zwnj;خێر، کافران ته&zwnj;نها له خه&zwnj;ڵه&zwnj;تاویدان و هیچی تر؟!';
$t[21] = 'یاخود کێ یه ئه&zwnj;وه&zwnj;ی که ڕزق و ڕۆزیتان پێده&zwnj;به&zwnj;خشێت، ئه&zwnj;گه&zwnj;ر خوا لێتان بگرێته&zwnj;وه&zwnj;؟! نه&zwnj;خێر، به&zwnj;و تاقی کردنه&zwnj;وه&zwnj;یه&zwnj;ش بێ باوه&zwnj;ڕان هه&zwnj;ر له گومڕایی و سه&zwnj;رلێشێواوی و دووره په&zwnj;رێزی له به&zwnj;رنامه&zwnj;ی خوا ڕۆچوون!';
$t[22] = 'جا ئایا ئه&zwnj;و که&zwnj;سه&zwnj;ی که ده&zwnj;مه&zwnj;و ڕوو که&zwnj;وتووه&zwnj;و به&zwnj;و حاڵه&zwnj;وه ده&zwnj;ڕوات، چاکتر ڕێگه ده&zwnj;زانێت؟ یان ئه&zwnj;و که&zwnj;سه&zwnj;ی که به&zwnj;سه&zwnj;ر شه&zwnj;قامێکی ڕاست و ڕه&zwnj;واندا به ڕێك و پێکی ده&zwnj;ڕوات به&zwnj;ڕێوه&zwnj;؟!';
$t[23] = 'ئه&zwnj;ی پێغه&zwnj;مبه&zwnj;ر صلی الله علیه وسلم پێیان بڵێ: هه&zwnj;ر خوایه&zwnj;، که ئێوه&zwnj;ی دروستکردووه&zwnj;و ده&zwnj;زگای بینین و بیستن و تێگه&zwnj;یشتنی پێ به&zwnj;خشیون، که&zwnj;چی که&zwnj;متان شوکرانه بژێرو سوپاسگوزارن.';
$t[24] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: هه&zwnj;ر ئه&zwnj;و خوایه&zwnj;؛ ئێوه&zwnj;ی له خاکی زه&zwnj;وی پێگه&zwnj;یاندووه&zwnj;، گه&zwnj;ڕانه&zwnj;وه&zwnj;و کۆکردنه&zwnj;وه&zwnj;شتان بۆ لای ئه&zwnj;وه&zwnj;.';
$t[25] = 'بێ باوه&zwnj;ڕان ده&zwnj;ڵێن: باشه&zwnj;، که&zwnj;ی ئه&zwnj;م به&zwnj;ڵێنه دێته دی، (زیندووکردنه&zwnj;وه&zwnj;و لێپرسینه&zwnj;وه&zwnj;) ئه&zwnj;گه&zwnj;ر ئێوه ڕاستگۆن؟!';
$t[26] = 'پێیان بڵێ: زانیاری ئه&zwnj;و کاته لای خوایه&zwnj;، من ته&zwnj;نها بێدار که&zwnj;ره&zwnj;وه&zwnj;یه&zwnj;کی ئاشکرام.';
$t[27] = 'کاتێك بینیان ئه&zwnj;و به&zwnj;ڵێنه خواییه یه&zwnj;خه&zwnj;یان پێ ده&zwnj;گرێت، چڕوچاوی ئه&zwnj;وانه&zwnj;ی که باوه&zwnj;ڕیان نه&zwnj;بووه&zwnj;، تاڵ و ترش و ناشرین ده&zwnj;کرێت، ئه&zwnj;وسا پێیان ده&zwnj;وترێت ئا ئه&zwnj;مه ئه&zwnj;و به&zwnj;ڵێنه&zwnj;یه که&zwnj;پێتان ده&zwnj;درا.';
$t[28] = 'هه&zwnj;روه&zwnj;ها پێیان بڵێ: باشه&zwnj;، ئه&zwnj;گه&zwnj;ر خوا، من و هه&zwnj;موو ئه&zwnj;و که&zwnj;سانه&zwnj;ی که شوێنم که&zwnj;وتوون له&zwnj;ناو به&zwnj;رێت، یان به&zwnj;زه&zwnj;یی پیاماندا بێته&zwnj;وه&zwnj;، ئه&zwnj;وسا کێ هه&zwnj;یه بێ باوه&zwnj;ڕان له سزای پڕ ئێشی دۆزه&zwnj;خ ڕزگار بکات؟! (ئه&zwnj;گه&zwnj;ر له&zwnj;سه&zwnj;ر یاخی بوونیان به&zwnj;رده&zwnj;وام بن)';
$t[29] = 'هه&zwnj;روه&zwnj;ها بڵێ: ئێمه خوای میهره&zwnj;بانمان هه&zwnj;یه&zwnj;و باوه&zwnj;ڕی پته&zwnj;ومان پێیه&zwnj;تی و پشت و په&zwnj;نامان هه&zwnj;ر ئه&zwnj;وه&zwnj;، جا ئیتر ڕۆژێك دێت که به ڕوونی بۆتان ده&zwnj;رده&zwnj;که&zwnj;وێت کێ له&zwnj;گومڕایی ئاشکرادا گیری خواردووه&zwnj;و سه&zwnj;ری لێ شێواوه&zwnj;.';
$t[30] = 'پێیان بڵێ: هه&zwnj;واڵم بده&zwnj;نێ باشه ئه&zwnj;گه&zwnj;ر ئاوه&zwnj;که&zwnj;تان ڕۆچوو، کێ ده&zwnj;توانێت ئاوی سازگارو ڕه&zwnj;وانتان بۆ به&zwnj;ده&zwnj;ست بهێنێت؟! (جگه له خوای میهره&zwnj;بان).';
