$t[26] = 'I ułatw moje zadanie!';
$t[27] = 'Rozwiąż węzeł mojego języka,';
$t[28] = 'Aby oni pojęli moją mowę.';
$t[29] = 'Daj mi pomocnika z mojej rodziny';
$t[30] = '- Aarona, mojego brata!';
