$t[36] = 'Oare Dumnezeu nu este de ajuns robului Său, c&acirc;nd oamenii te &icirc;nspăim&acirc;ntă cu cei la care ei se &icirc;nchină &icirc;n afara Lui? Cel pe care Dumnezeu &icirc;l rătăceşte, nu va mai avea altă călăuză.';
$t[37] = 'Cel pe care Dumnezeu &icirc;l călăuzeşte, nimeni nu-l rătăceşte. Oare Dumnezeu nu este Puternic, Stăp&acirc;n al Răzbunării?';
$t[38] = 'Dacă tu &icirc;i &icirc;ntrebi: &ldquo;Cine a creat cerurile şi păm&acirc;ntul?&rdquo; Ei vor spune: &ldquo;Dumnezeu!&rdquo; Spune: &ldquo;Vedeţi? Dacă Dumnezeu &icirc;mi vrea un rău, &icirc;l pot alunga cei pe care &icirc;i chemaţi &icirc;n afara lui Dumnezeu? Dacă &icirc;mi vrea un bine, pot ei opri acest bine?&rdquo; Spune: &ldquo;Dumnezeu &icirc;mi este de ajuns! Cei care au credinţă se &icirc;ncred Lui.&rdquo;';
$t[39] = 'Spune: &ldquo;O, popor al meu! Faceţi voi după putinţa voastră, căci şi eu fac. Cur&acirc;nd veţi afla';
$t[40] = 'cui va veni o os&acirc;ndă ce &icirc;l va face de ruşine şi asupra cui se va abate o os&acirc;ndă necurmată.';
$t[41] = 'Asupra ta Noi am pogor&acirc;t, pentru oameni, Cartea, &icirc;ntru Adevăr. Cel care este bine călăuzit este pentru el &icirc;nsuşi, iar cel care este rătăcit este rătăcit &icirc;mpotriva lui &icirc;nsuşi. Tu nu eşti răspunzător de ei.';
$t[42] = 'Dumnezeu primeşte sufletele &icirc;n clipa morţii, de altfel şi pe cele ale adormiţilor care nu sunt morţi. El le ţine pe cele ale celor răpuşi de moarte şi le trimite &icirc;napoi pe celelalte p&acirc;nă la un anumit soroc. &Icirc;ntru aceasta sunt semne pentru un popor care cugetă!';
$t[43] = 'Ei iau mijlocitor &icirc;n afara lui Dumnezeu? Spune: &ldquo;Şi dacă ei nu au nici putere şi nici minte?&rdquo;';
$t[44] = 'Spune: &ldquo;A lui Dumnezeu este mijlocirea &icirc;ntreagă! A Lui este &icirc;mpărăţia cerurilor şi a păm&acirc;ntului! La El veţi fi &icirc;ntorşi!&rdquo;';
$t[45] = 'C&acirc;nd Dumnezeu, Unul, este amintit, inimile celor care nu cred &icirc;n Viaţa de Apoi se &icirc;ngreţoşează, &icirc;nsă c&acirc;nd sunt amintiţi cei &icirc;n afară de El se &icirc;nveselesc!';
$t[46] = 'Spune: &ldquo;O, Dumnezeule! Creator al cerurilor şi al păm&acirc;ntului, Cunoscător al tăinuitului şi al mărturisitului! Tu &icirc;i vei judeca pe robii Tăi pentru ceea ce se &icirc;nvrăjbeau!&rdquo;';
$t[47] = 'Dacă cei nedrepţi ar avea tot ce se află pe păm&acirc;nt, şi &icirc;ncă pe at&acirc;t, ar da tot să se răscumpere de la os&acirc;nda cea rea din Ziua &Icirc;nvierii, &icirc;nsă le va apărea, de la Dumnezeu, ceea ce ei nici nu socoteau.';
$t[48] = 'Le vor apărea relele pe care le-au agonisit şi vor fi &icirc;nvăluiţi de ceea ce batjocoreau.';
$t[49] = 'C&acirc;nd un rău &icirc;l atinge pe om, Ne cheamă, &icirc;nsă c&acirc;nd &icirc;i dăruim o binefacere spune: &ldquo;Mi s-a dat după ştiinţa mea.&rdquo; Aceasta este o ispită, &icirc;nsă cei mai mulţi nu ştiu.';
$t[50] = 'Aşa spuneau şi cei dinaintea lor, iar ceea ce agoniseau nu le-a slujit la nimic.';
$t[51] = 'Relele pe care le-au agonisit i-au lovit. Şi pe cei nedrepţi dintre aceia &icirc;i vor lovi relele pe care le-au agonisit şi nu-l vor slăbi pe Dumnezeu.';
$t[52] = 'Ei nu ştiu că Dumnezeu &icirc;nzestrează pe cine voieşte, cu prisosinţă, &icirc;nsă şi cu măsură. &Icirc;ntru aceasta sunt semne pentru un popor care crede.';
$t[53] = 'Spune: &ldquo;O, robi ai Mei! Voi ce ce v-aţi &icirc;ntrecut măsura, nu deznădăjduiţi de la milostivenia lui Dumnezeu, căci Dumnezeu iartă păcatele toate, căci El este Iertătorul, Milostivul.';
$t[54] = '&Icirc;ntoarceţi-vă la Domnul vostru! Supuneţi-vă Lui &icirc;nainte să vă vină os&acirc;nda, căci nu veţi mai fi ajutaţi apoi!';
$t[55] = 'Urmaţi prea-binele ce v-a fost pogor&acirc;t de la Domnul vostru &icirc;nainte să vă vină os&acirc;nda deodată de unde nici nu vă aşteptaţi.';
$t[56] = '&Icirc;nainte ca vreun suflet să spună: &ldquo;Vai mie, din nepăsare faţă de Dumnezeu, am fost dintre zeflemitori!&rdquo;';
$t[57] = 'Ori să spună: &ldquo;Dacă Dumnezeu m-ar fi călăuzit aş fi fost dintre cei temători.&rdquo;';
$t[58] = 'Ori să spună c&acirc;nd va vedea os&acirc;nda: &ldquo;Dacă aş fi dus &icirc;napoi, aş fi dintre cei făcători de bine!&rdquo;';
$t[59] = 'Ba nu! Semnele Mele au venit la tine, &icirc;nsă tu le-ai socotit minciună, te-ai &icirc;ng&acirc;mfat şi ai fost dintre tăgăduitori!';
$t[60] = '&Icirc;i vei vedea, &icirc;n Ziua &Icirc;nvierii, pe cei care l-au hulit pe Dumnezeu, cu chipurile &icirc;negrite. Nu este Gheena un sălaş al trufaşilor?';
$t[61] = 'Dumnezeu &icirc;i va m&acirc;ntui spre fericirea lor pe cei care s-au temut. Pe ei nu-i va atinge răul şi nu vor fi m&acirc;hniţi.';
$t[62] = 'Dumnezeu este Creatorul tuturor şi El tuturor le este Proteguitor.';
$t[63] = 'Ale Lui sunt cheile cerurilor şi ale păm&acirc;ntului. Cei care tăgăduiesc semnele lui Dumnezeu, aceştia sunt cei pierduţi.';
$t[64] = 'Spune: &ldquo;O, voi neştiutorilor! Altuia dec&acirc;t lui Dumnezeu &icirc;mi porunciţi să mă &icirc;nchin?';
$t[65] = 'Ţi s-a dezvăluit ţie şi celor dinaintea ta: &ldquo;Dacă eşti &icirc;nchinător la idoli, truda ta va fi deşartă şi tu vei fi dintre cei pierduţi.';
$t[66] = '&Icirc;nchină-te doar lui Dumnezeu şi fii printre cei mulţumitori!&rdquo;';
$t[67] = 'Ei nu i-au dat lui Dumnezeu adevărata Sa măsură. Păm&acirc;ntul &icirc;ntreg, &icirc;n Ziua &Icirc;nvierii, va fi &icirc;n pumnul Său, iar cerurile vor fi &icirc;mpăturite cu dreapta Sa. Preamărire şi &Icirc;nălţare Lui asupra celor care I-i alăturaţi!';
$t[68] = 'C&acirc;nd va sufla &icirc;n tr&acirc;mbiţă, cei din ceruri şi cei de pe păm&acirc;nt vor fi trăsniţi, afară de cei pe care Dumnezeu voieşte. Apoi se va mai sufla &icirc;ncă odată, şi iată-i &icirc;n picioare privind.';
$t[69] = 'Păm&acirc;ntul va străluci de lumina Domnului său. Cartea va fi aşezată. Vor veni profeţii şi martorii. Toţi vor fi judecaţi &icirc;ntru Dreptate şi nimeni nu va fi nedreptăţit.';
$t[70] = 'Fiece suflet va primi după ceea ce a făptuit. El ştie prea bine ce făptuiesc ei.';
$t[71] = 'Cei tăgăduitori vor fi m&acirc;naţi &icirc;n cete către Gheena, iar porţile ei se vor deschide c&acirc;nd ei vor sosi. Păzitorii le vor spune: &ldquo;Oare nu au venit la voi trimişi, dintre voi, care să vă recite versetele Domnului vostru şi ca să vă prevină asupra &icirc;nt&acirc;lnirii cu Ziua aceasta a voastră?&rdquo; Ei vor spune: &ldquo;Ba da!&rdquo; Cuv&acirc;ntul os&acirc;ndei asupra tăgăduitorilor se va &icirc;mplini.';
$t[72] = 'Li se va spune: &ldquo;Intraţi pe porţile Gheenei, unde veţi veşnici. Ce rău este sălaşul celor trufaşi!&rdquo;';
$t[73] = 'Cei care se tem de Domnul lor vor fi m&acirc;naţi &icirc;n cete către Rai, iar porţile lui se vor deschide c&acirc;nd ei vor sosi. Păzitorii vor spune: &ldquo;Pace vouă! Voi aţi fost buni! Intraţi aici şi veşniciţi!&rdquo;';
$t[74] = 'Ei vor spune: &ldquo;Laudă lui Dumnezeu care ne-a adeverit făgăduiala Sa şi ne-a dat păm&acirc;ntul moştenire. Ne vom aşeza &icirc;n Rai unde vrem.&rdquo; C&acirc;t de minunată este răsplata truditorilor!';
$t[75] = 'Vei vedea &icirc;ngerii, adunaţi &icirc;n cerc &icirc;mprejurul Tronului, preamărind cu lauda pe Domnul lor. Vor fi judecaţi &icirc;ntru Adevăr şi se va spune: &ldquo;Laudă lui Dumnezeu, Domnul lumilor!&rdquo;';
