$t[5] = 'Ei spun: &ldquo;Inimile noastre sunt zăvor&acirc;te la ceea ce tu ne chemi, iar urechile noastre sunt lovite de surzenie. Un văl se află &icirc;ntre noi şi tine. Lucrează, căci şi noi lucrăm.&rdquo;';
$t[6] = 'Spune: &ldquo;Eu sunt un om asemena vouă, şi mie mi s-a dezvăluit că Dumnezeul vostru este Dumnezeu, Unul. Mergeţi drept către El şi cereţi-I iertare!&rdquo; Vai celor care &Icirc;i alătură alţi dumnezei,';
$t[7] = 'vai celor care nu dau dania, tăgăduind Viaţa de Apoi!';
$t[8] = 'Cei care cred şi săv&acirc;rşesc fapte bune vor avea o răsplată necurmată.';
$t[9] = 'Spune: &ldquo;Cum &Icirc;l tăgăduiţi pe Cel ce a creat păm&acirc;ntul &icirc;n două zile? Cum &Icirc;i faceţi Lui semeni? Acesta este Domnul lumilor!&rdquo;';
$t[10] = 'El a făcut munţii semeţi deasupra lui. El l-a binecuv&acirc;ntat. El a r&acirc;nduit hrana &icirc;n patru zile pentru cei care cer, deopotrivă.';
$t[11] = 'El s-a &icirc;ntors către cer &mdash; care nu era dec&acirc;t fum &mdash; şi către păm&acirc;nt, spun&acirc;ndu-le: &ldquo;Veniţi am&acirc;ndouă de vreţi ori de nu vreţi!&rdquo; Ele spuseră: &ldquo;Venim, Ţie supun&acirc;ndu-ne!&rdquo;';
$t[12] = 'El a desăv&acirc;rşit şapte ceruri &icirc;n două zile şi a dezvăluit fiecărui cer porunca Sa. Noi am &icirc;mpodobit apoi cerul cel mai apropiat cu lumini şi pază. Aceasta este sortirea Puternicului, Ştiutorului.';
$t[13] = 'Dacă ei se &icirc;ntorc, spune-le: &ldquo;Eu v-am prevenit asupra trăsnetului care va fi asemenea trăsnetului care i-a nimicit pe adiţi şi tamudiţi.&rdquo;';
$t[14] = 'C&acirc;nd trimişii au venit la ei din faţă şi din spate: &ldquo;Nu vă &icirc;nchinaţi dec&acirc;t lui Dumnezeu&rdquo;, ei spuseră: &ldquo;Dacă Domnul nostru ar fi voit, ar fi pogor&acirc;t &icirc;ngeri. Noi nu credem &icirc;n cea cu care voi aţi fost trimişi.&rdquo;';
$t[15] = 'Şi adiţii! Fără drept, ei erau &icirc;ng&acirc;mfaţi pe păm&acirc;nt. Ei spuneau: &ldquo;Cine este mai tare ca putere dec&acirc;t noi?&rdquo; Nu vedeau că Dumnezeu care i-a creat este mai tare ca putere dec&acirc;t ei? Ei s-au lepădat de semnele Noastre!';
$t[16] = 'Noi am dezlănţuit asupra lor un v&acirc;nt ce urla &icirc;n zilele rele ca să-i facem să guste os&acirc;nda ruşinii &icirc;n Viaţa de Acum, &icirc;nsă os&acirc;nda din Viaţa de Apoi va fi mult mai ruşinoasă şi nu vor fi ajutaţi.';
$t[17] = 'Şi tamudiţii pe care i-am călăuzit, au preferat călăuzirii orbecăiala. Trăsnetul os&acirc;ndei umilitoare i-a luat pentru ceea ce agonisiseră.';
$t[18] = 'Noi i-am m&acirc;ntuit pe cei care au crezut şi s-au temut.';
$t[19] = '&Icirc;n Ziua c&acirc;nd vrăjmaşii lui Dumnezeu vor fi str&acirc;nşi către Foc, vor fi &icirc;mpinşi &icirc;nainte';
$t[20] = 'p&acirc;nă vor ajunge la el, şi atunci auzul lor, privirile lor şi pielea lor vor mărturisi ceea ce ei au făptuit.';
$t[21] = 'Ei &icirc;i vor spune pielii lor: &ldquo;De ce ai mărturisit &icirc;mpotriva noastră?&rdquo; Ea va răspunde: &ldquo;Dumnezeu m-a făcut să vorbesc, căci El este Cel ce face fiece lucru să vorbească. El este Cel ce v-a creat &icirc;nt&acirc;ia oară şi la El veţi fi &icirc;ntorşi.&rdquo;';
$t[22] = 'Voi nu vă veţi putea ascunde &icirc;nc&acirc;t auzul vostru, privirile voastre şi pielea voastră să nu mărturisească &icirc;mpotriva voastră. V-aţi &icirc;nchipuit că Dumnezeu nu ştie multe din cele pe care le făptuiaţi?';
$t[23] = 'Aceasta este &icirc;nchipuirea pe care v-aţi făcut-o despre Domnul vostru. Ea v-a prăpădit printre cei pierduţi.';
$t[24] = 'Dacă ei stăruie, Focul va fi sălaşul lor. Dacă vor cere milă, nu vor fi miluiţi.';
$t[25] = 'Noi le-am dat &icirc;nsoţitori care le-au &icirc;mpodobit ceea ce se afla &icirc;n faţa şi &icirc;n spatele lor. Şi s-a &icirc;mplinit asupra lor Cuv&acirc;ntul spus adunărilor de ginni şi oameni ce-au dispărut &icirc;naintea lor! Ei sunt cei pierduţi!';
$t[26] = 'Cei care tăgăduiesc spun: &ldquo;Nu ascultaţi acest Coran, ci luaţi-l &icirc;n zeflemea. Poate veţi &icirc;nvinge!&rdquo;';
$t[27] = 'Celor care au tăgăduit le vom da să guste o aprigă os&acirc;ndă şi le vom răsplăti după cea mai rea faptă pe care au săv&acirc;rşit-o.';
$t[28] = 'Aceasta este răsplata vrăjmaşilor lui Dumnezeu: Focul unde &icirc;şi vor avea Lăcaşul veşnic fiindcă s-au lepădat de semnele Noastre.';
$t[29] = 'Cei care tăgăduiesc vor spune: &ldquo;Domnul nostru! Arată-ne, printre ginni şi printre oameni, pe cei doi care ne-au rătăcit. &Icirc;i vom pune sub picioarele noastre &icirc;nc&acirc;t vor fi dintre cei mai &icirc;njosiţi.&rdquo;';
$t[30] = 'Asupra celor care spun: &ldquo;Domnul nostru este Dumnezeu&rdquo; şi merg drept vor pogor&icirc; &icirc;ngerii: &ldquo;Nu vă fie frică şi nu vă m&acirc;hniţi, ci primiţi bunavestirea Raiului care v-a fost făgăduit.&rdquo;';
$t[31] = 'Noi vă suntem vouă oblăduitori &icirc;n Viaţa de Acum şi &icirc;n Viaţa de Apoi. Voi veţi avea acolo ceea ce sufletele voastre poftesc; voi veţi avea ceea ce cereţi';
$t[32] = 'ca ospeţie de la Iertătorul, Milostivul.';
$t[33] = 'Cine spune un cuv&acirc;nt mai bun dec&acirc;t cel care &Icirc;l cheamă pe Dumnezeu şi săv&acirc;rşeşte o faptă bună spun&acirc;nd: &ldquo;Da, eu sunt dintre cei supuşi!&rdquo;';
$t[34] = 'Un bine nu este asemenea unui rău. Răspunde cu mai bine şi cel care este &icirc;n vrăjmăşie cu tine &icirc;ţi va fi apoi un prieten apropiat.';
$t[35] = 'La acest lucru nu ajung &icirc;nsă dec&acirc;t cei răbdători, nu ajung &icirc;nsă dec&acirc;t cei care au o mare dăruire.';
$t[36] = 'Dacă Diavolul te &icirc;mboldeşte la rău, caută izbăvire la Dumnezeu. El este Auzitorul, Ştiutorul.';
$t[37] = 'Dintre semnele Sale sunt: noaptea, ziua, soarele şi luna. Nu vă prosternaţi nici &icirc;naintea soarelui şi nici &icirc;naintea lunii. Prosternaţi-vă &icirc;naintea lui Dumnezeu care v-a creat, dacă Lui vă &icirc;nchinaţi.';
$t[38] = 'Dacă se arată &icirc;ng&acirc;mfaţi, atunci să ştie că cei care sunt aproape de Domnul tău &icirc;l preamăresc noapte şi zi fără să ostenească!';
$t[39] = 'Tu vezi, printre semnele Sale, păm&acirc;ntul smerit, &icirc;nsă c&acirc;nd trimitem asupra lui apă, se &icirc;nfioară şi prinde viaţă. Cel ce l-a &icirc;nviat pe el, &icirc;nvie şi morţii. El asupra tuturor are putere.';
$t[40] = 'Cei care se leapădă de semnele Noastre nu se vor putea ascunde de Noi. Să fii aruncat &icirc;n Foc este mai bine oare dec&acirc;t să ajungi &icirc;n tihnă la Ziua &Icirc;nvierii? Faceţi cum vreţi, căci El este Văzător a ceea ce făptuiţi.';
$t[41] = 'Pentru cei care au tăgăduit amintirea c&acirc;nd le-a venit, aceasta este o Carte scumpă.';
$t[42] = 'Deşărtăciuni nu vin nici dinaintea şi nici din urma ei, căci este o pogor&acirc;re de la &Icirc;nţeleptul, Lăudatul.';
$t[43] = 'Ţie ţi se spune numai ce li s-a spus şi trimişilor dinaintea ta: &ldquo;Domnul tău este Stăp&acirc;nul iertării, precum şi Stăp&acirc;nul pedepsei dureroase.&rdquo;';
$t[44] = 'Dacă am fi făcut un Coran &icirc;ntr-o limbă străină, ei ar fi spus: &ldquo;Dacă versetele Sale ar fi fost lămurite..., &icirc;nsă aşa sunt &icirc;ntr-o limbă străină, pe c&acirc;nd el este arab.&rdquo; Spune: &ldquo;El este pentru cei care cred &icirc;n călăuzire şi vindecare. Urechile celor care nu cred sunt lovite de surzenie, şi el lor le este orbire. Aceştia vor fi chemaţi dintr-un loc &icirc;ndepărtat.&rdquo;';
$t[45] = 'Noi am dăruit lui Moise Cartea asupra căreia a fost ceartă. Dacă un Cuv&acirc;nt al Domnului tău nu ar fi venit mai &icirc;nainte, s-ar fi judecat &icirc;ntre ei. Ei stau la &icirc;ndoială asupra ei.';
$t[46] = 'Cine face un bine, sieşi şi-l face. Cine face un rău, sieşi şi-l face. Domnul tău nu este nedrept cu robii Săi.';
$t[47] = 'A Lui este ştiinţa Ceasului. Nici un fruct nu iese din coaja sa, nici o femeie nu poartă rod şi nu naşte fără ştirea Sa. &Icirc;n Ziua c&acirc;nd le va striga: &ldquo;Unde sunt cei alăturaţi Mie?&rdquo; Ei vor spune: &ldquo;Te asigurăm că nu este printre noi nici un martor!&rdquo;';
$t[48] = 'Cei pe care-i chemau odinioară se vor lepăda de ei şi nu vor afla cale de scăpare.';
$t[49] = 'Omul nu osteneşte să ceară binele. C&acirc;nd răul &icirc;l atinge &icirc;nsă, este deznădăjduit şi &icirc;ndurerat.';
$t[50] = 'Dacă &icirc;i dăm să guste din milostivenia Noastră, după ce o nenorocire l-a atins, el spune: &ldquo;Aceasta &icirc;mi este mie. Nu cred că Ceasul va veni. Şi dacă voi fi &icirc;ntors la Domnul meu, mie &icirc;mi va fi la El cel mai bine.&rdquo; Noi le dăm de ştire celor care tăgăduiesc asupra a ceea ce au săv&acirc;rşit şi le vom da să guste o grea os&acirc;ndă.';
$t[51] = 'C&acirc;nd ne pogor&acirc;m harul asupra omului, el &icirc;ntoarce spatele şi se &icirc;ndepărtează. C&acirc;nd un rău &icirc;l atinge &icirc;nsă, &icirc;nalţă rugi lungi.';
$t[52] = 'Spune: &ldquo;Aţi văzut? Dacă aceasta este de la Dumnezeu, iar voi o tăgăduiţi? Cine se află &icirc;n mai mare rătăcire dec&acirc;t cel care se desprinde, &icirc;ndepărt&acirc;ndu-se?';
$t[53] = 'Noi le vom arăta semnele Noastre cur&acirc;nd &icirc;n zări şi &icirc;n ei &icirc;nşişi, p&acirc;nă ce li se va desluşi că acesta este Adevărul. Nu este de ajuns că Domnul tău este tuturor Martor?';
$t[54] = 'Mai stau la &icirc;ndoială asupra &icirc;nt&acirc;lnirii cu Domnul lor? Nu este El oare Cel ce cuprinde totul?';
?>