$t[6] = 'E Tij &euml;sht&euml; &ccedil;do gj&euml; q&euml; ekziston n&euml; qiej dhe n&euml; tok&euml; dhe &ccedil;do gj&euml; q&euml; gjendet n&euml; mes tyre, edhe &ccedil;&rsquo;ka n&euml;n dhe.';
$t[7] = 'Andaj, n&euml;se b&euml;n shprehjen (lutjen haptazi), Ai e di t&euml; fsheht&euml;n, madje edhe m&euml; shum&euml; se kjo.';
$t[8] = 'Ai &euml;sht&euml; All-llahu, nuk ka Zot pos Tij, Atij i takojn&euml; emrat m&euml; t&euml; bukur.';
$t[9] = 'A t&euml; ka ardhur ndonj&euml; njohuri p&euml;r ndodhin e Musait?';
$t[10] = 'Kur ai e pa nj&euml; zjarr, e i tha familjes s&euml; vet: &ldquo;Rrini ku jeni, se v&euml;rejta nj&euml; zjarr, e un&euml; ndoshta do t&rsquo;ju sjell&euml; ndonj&euml; ac&euml; prej tij, ose do t&euml; gjej&euml; te zjarri ndonj&euml; udh&euml;rr&euml;fyes&rdquo;.';
$t[11] = 'E kur shkoi te Ai (zjarri), u thirr&euml;: O Musa!';
$t[12] = 'V&euml;rtet Un&euml; jam Zoti yt, hiq at&euml; q&euml; ke mbathur (opingat e nallet), se je n&euml; lugin&euml;n e shenjt&euml; Tuva.';
$t[13] = 'Un&euml; t&euml; zgjodha ty (p&euml;r pejgamber), prandaj d&euml;gjo mir&euml; se &ccedil;&rsquo;po t&euml; shpallet!';
$t[14] = 'V&euml;rtet, vet&euml;m Un&euml; jam All-llahu, nuk ka zot tjet&euml;r pos Meje, pra Mua m&euml; adhuro dhe fal namazin p&euml;r t&euml; m&euml; kujtuar Mua.';
$t[15] = 'S&rsquo;ka dyshim se momenti (kijameti) do t&euml; vij&euml; pa tjet&euml;r, Un&euml; gati e kam fsheh&euml; at&euml;; (do t&euml; vij&euml;) p&euml;r t&rsquo;u shp&euml;rblyer secili njeri p&euml;r at&euml; q&euml; ka b&euml;r&euml;.';
$t[16] = 'Prandaj, t&euml; mos shmang ty nga kjo, ai q&euml; nuk i beson atij dhe q&euml; &euml;sht&euml; dh&euml;n&euml; pas d&euml;shirave, se at&euml;her&euml; shkat&euml;rrohesh.';
$t[17] = 'O Musa, &ccedil;&rsquo;&euml;sht&euml; ajo q&euml; e ke premtuar n&euml; t&euml; djatht&euml;n t&euml;nde?';
$t[18] = 'Ai (Musai) tha: &ldquo;Ai &euml;sht&euml; shkopi im q&euml; t&euml; mbahem n&euml; t&euml;, dhe me t&euml; u shkund (gjethe nga drunjt&euml;) dhenve t&euml; mia, dhe me t&euml; kryej edhe nevoja tjera.';
$t[19] = 'Ai (All-llahu) i tha: &ldquo;Hidh at&euml;, O Musa!&rdquo;';
$t[20] = 'Ai e hodhi at&euml;, kur ja, ai nj&euml; gjarp&euml;r i madh q&euml; l&euml;vizte me shpejt&euml;si.';
$t[21] = '(All-llahu i) Tha: &ldquo;Kape at&euml;, e mos u frik&euml;so, se Ne do ta kthejm&euml; at&euml; p&euml;rs&euml;ri n&euml; gjendjen e m&euml;parshme!&rdquo;';
$t[22] = 'Dhe, fute dor&euml;n t&euml;nd e do ta nxjerr&euml;sh at&euml; t&euml; bardh&euml; pa asnj&euml; t&euml; met&euml;. Kjo &euml;sht&euml; nj&euml; mrekulli tjet&euml;r.';
$t[23] = '(k&euml;t&euml; e b&euml;m&euml;) N&euml; m&euml;nyr&euml; q&euml; t&euml; mund&euml;sojm&euml; t&euml; shoh&euml;sh disa nga argumentet Tona t&euml; m&euml;dha.';
$t[24] = '&ldquo;Shko te faraoni se ai ka ngritur kok&euml; (&euml;sht&euml; b&euml;r&euml; arrogant)&rdquo;.';
$t[25] = 'Ai (Musai) tha: &ldquo;Ma zgj&euml;ro (m&euml; ndihmo) gjoksin tim!';
$t[26] = 'Dhe m&euml; leht&euml;so n&euml; k&euml;t&euml; pun&euml; timen!';
$t[27] = 'M&euml; zgjidh nyjen e gjuh&euml;s sime!';
$t[28] = 'Q&euml; ta kuptojn&euml; fjal&euml;n time!';
$t[29] = 'M&euml; cakto nj&euml; ndihm&euml;tar nga familja ime,';
$t[30] = 'Harunin, v&euml;llain tim,';
$t[31] = 'Q&euml; me t&euml; t&euml; ma forcosh fuqin&euml; time,';
$t[32] = 'B&euml;ma shok at&euml; n&euml; pun&euml;n time,';
$t[33] = 'N&euml; m&euml;nyr&euml; q&euml; t&euml; madh&euml;rojn&euml; Ty m&euml; shum&euml;,';
$t[34] = 'Dhe t&euml; p&euml;rkujtojn&euml; Ty shpesh.';
$t[35] = 'V&euml;rtet, Ti je Ai q&euml; na sheh dhe na i di pun&euml;t&rdquo;.';
$t[36] = 'Ai (All-llahu) tha: &ldquo;O Musa, t&rsquo;u dha ajo q&euml; k&euml;rkove!&rdquo;';
$t[37] = 'Ne edhe nj&euml; her&euml; ta dhuruam ty mir&euml;sin&euml; Tona.';
$t[38] = 'At&euml;her&euml; kur n&euml;n&euml;n e frym&euml;zuam me at&euml; q&euml; nuk kuptohet ndrushe pos me inspirim (me frym&euml;zim - shpallje).';
$t[39] = '(Ne i tham&euml;) &ldquo;V&euml;re at&euml; (f&euml;mij&euml;, Musain) n&euml; ark&euml;, e mandej hidhe n&euml; lum&euml; e lumi e hjedh&euml; n&euml; breg, at&euml; e merre armiku Im dhe i tij. E nga ana Ime mbolla (n&euml; zemra t&euml; njer&euml;zve) dashuri ndaj teje, e q&euml; t&euml; edukoheshe n&euml;n mbik&euml;qyrjen Time&rdquo;.';
$t[40] = 'Kur motra jote ecte (t&euml; p&euml;rcillte) e thoshte: &ldquo;A doni t&rsquo;ju tregoj at&euml; q&euml; do t&euml; kujdeset p&euml;r t&euml;? E Ne t&euml; kthyem te n&euml;na jote, q&euml; t&euml; g&euml;zohej ajo e t&euml; mos mbetej e pik&euml;lluar. Ti pate mbytur nj&euml; njeri, e Ne t&euml; shp&euml;tuam nga m&euml;rzia dhe t&euml; sprovuan me sprova t&euml; m&euml;dha. Ti q&euml;ndrove me vite nd&euml;r banor&euml;t e Medjenit, e pastaj erdhe, o Musa, n&euml; koh&euml;n e caktuar&rdquo;.';
$t[41] = 'Dhe Un&euml; t&euml; zgodha ty p&euml;r shpalljen Time.';
$t[42] = 'Shko me argumentet e Mia ti dhe v&euml;llai yt, e mos e hiqni prej kujdesit t&euml; p&euml;rmendurit ndaj Meje.';
$t[43] = 'Shkoni q&euml; t&euml; dy te faraoni, se ai v&euml;rtet e ka tepruar.';
$t[44] = 'Atij i thuani fjal&euml; t&euml; buta, ndoshta ai mendohet a frik&euml;sohet.';
$t[45] = 'Ata t&euml; dy than&euml;: &ldquo;Zoti yn&euml;, ne kemi frik&euml; se do t&euml; na nd&euml;shkoj&euml; menj&euml;her&euml; ose do t&rsquo;i kaloj&euml; kufijt&euml; kund&euml;r nesh&rdquo;.';
$t[46] = 'Ai (All-llahu) tha: &ldquo;Mos u frik&euml;soni, se Un&euml; (me ndihm&euml;n Time) jam me ju, d&euml;gjoj (&ccedil;&rsquo;do t&rsquo;ju thot&euml;) dhe shoh (&ccedil;&rsquo;do t&euml; b&euml;n me ju)&rdquo;.';
$t[47] = 'I shkoni i thuani atij: &ldquo;Ne jemi t&euml; dy d&euml;rguar t&euml; Zotit t&euml;nd, l&euml;shoi beni israil&euml;t t&euml; vijn&euml; m&euml; ne, e mos i mundo! Ne kemi ardhur me argumentet nga Zoti yt, e shp&euml;timi &euml;sht&euml; p&euml;r at&euml; q&euml; ndjek rrug&euml;n e drejt&euml;.';
$t[48] = 'Ne na &euml;sht&euml; th&euml;n&euml; se d&euml;nimi &euml;sht&euml; p&euml;r at&euml; q&euml; g&euml;njen (t&euml; d&euml;rguarit) dhe ia kthen shpin&euml;n besimit&rdquo;.';
$t[49] = 'Ai (faraoni) tha: &ldquo;E kush &euml;sht&euml; Zoti i ju dyve, o Musa?&rdquo;';
$t[50] = '(Musai) Tha: &ldquo;Zoti yn&euml; &euml;sht&euml; Ai q&euml; &ccedil;do sendi i dha form&euml;n e vet, pastaj e udh&euml;zoi at&euml;?&rdquo;';
$t[51] = 'Ai tha: &ldquo;Si &euml;sht&euml; gjendja e popjve t&euml; m&euml;parsh&euml;m?&rdquo;';
$t[52] = 'Dija p&euml;r ata &euml;sht&euml; te Zoti im, e sh&euml;nuar n&euml; nj&euml; lib&euml;r. Zoti im nuk gabon e as nuk harron&rdquo;.';
$t[53] = '(Zoti im) I cili p&euml;r ju e b&euml;ri tok&euml;n djep, p&euml;r t&euml; mir&euml;n tuaj hapi rrug&euml; n&euml;p&euml;r t&euml;, l&euml;shoi shiun prej s&euml; larti, e kjo mund&euml;soi q&euml; t&euml; mbijn&euml; bim&euml; t&euml; llojeve t&euml; ndryshme.';
$t[54] = 'Hani ju dhe kullotni kafsh&euml;t tuaja (nga ato bim&euml;). V&euml;rtet, p&euml;r t&euml; gjitha k&euml;to (q&euml; u p&euml;rmend&euml;n) ka argumente p&euml;r ata q&euml; kan&euml; mendje t&euml; sh&euml;ndodh&euml;.';
$t[55] = 'Prej asaj (tok&euml;s) Ne u krijuam ju, e ju do t&rsquo;u kthejm&euml; p&euml;rs&euml;ri n&euml; t&euml;, e prej saj do t&rsquo;ju nxjerrim edhe nj&euml; her&euml;.';
$t[56] = 'Ne ia b&euml;m&euml; t&euml; mundshme atij t&rsquo;i shoh&euml; t&euml; gjitha argumentet Ton, por ai i f&euml;njeu ato dhe i refuzoi.';
$t[57] = '(At&euml;her&euml; faraoni) Tha: &ldquo;O Musa, a mos na ke ardhur q&euml; me magjin&euml; t&euml;nde t&euml; na nxjerr&euml;sh prej tok&euml;s ton&euml; (prej Egjiptit)?';
$t[58] = 'Edhe ne do t&euml; kund&euml;rvihemi me nj&euml; aso magjie, pra cakto nj&euml; dhe nj&euml; vend q&euml; do t&euml; na p&euml;rshtatet, e q&euml; nuk do ta thyenim as ne as ti&rdquo;.';
$t[59] = '(Musai) Tha: &ldquo;Koha e caktuar le t&euml; jet&euml; dita e fest&euml;s n&euml; m&euml;nyr&euml; q&euml; njer&euml;zit t&euml; tubohen para dite&rdquo;.';
$t[60] = 'Faraoni u kthye dhe tuboi magjistar&euml;t e vet dhe doli n&euml; koh&euml;n (dhe vendin) e caktuar.';
$t[61] = 'At&euml;her&euml; Musai u tha atyre (magjistar&euml;ve): &ldquo;Mjerimi &euml;sht&euml; juaji, prandaj mos shpifni ndaj All-llaht ndonj&euml; g&euml;njesht&euml;r e t&rsquo;ju shkat&euml;rroj&euml; me nj&euml; d&euml;nim, se pa dyshim ai q&euml; shpif ka d&euml;shtuar keq&rdquo;.';
$t[62] = 'Ata (magjistar&euml;t) polemizuan p&euml;r &ccedil;&euml;shtjen e tyre, por bised&euml;n e b&euml;n&euml; fshehurazi (nga masa).';
$t[63] = 'Ata pastaj (pas bised&euml;s s&euml; fshehur) than&euml;: &ldquo;K&euml;ta t&euml; dy jan&euml; magjistar&euml;, q&euml; me magjin&euml; e tyre duan t&rsquo;ju nxjerrin nga toka juaj dhe ta zhdukin drejtimin (ideologjin&euml;) tuaj q&euml; padyshim &euml;sht&euml; m&euml; i drejti&rdquo;.';
$t[64] = 'Pra, (meq&euml; jan&euml; magjistar&euml;) ju vendosni seriozisht &ccedil;&euml;shtjen tuaj dhe dilni n&euml; shesh tok t&euml; bashkuar, e ai q&euml; trimfon sot, ai ka arritur suksesin,';
$t[65] = 'Ata than&euml;: &ldquo;O Musa, do t&euml; hedh&euml;sh ti apo ne po hedhim t&euml; par&euml;t?&rdquo;';
$t[66] = 'Ai (Musai) tha: Jo, hidhni ju! Kur, ja, atij iu dk se nga ajo magji e tyre litar&euml;t dhe shkopinjt&euml; l&euml;viznin (si gjarp&euml;rinj).';
$t[67] = 'E Musai ndjeu n&euml; vete nj&euml;far&euml; frike.';
$t[68] = 'Ne i tham&euml; atij: &ldquo;Mos ke frik&euml;, &euml;sht&euml; m&euml; se e sigurt se ti do t&euml; jesh triumfues!&rdquo;';
$t[69] = 'Tani hidhe at&euml; q&euml; e ke n&euml; dor&euml;n t&euml;ndet&euml; djatht&euml; (shkopin), q&euml; t&rsquo;i g&euml;lltis&euml; ato q&euml; i b&euml;n&euml; ata, sepse ajo q&euml; b&euml;n&euml; ata nuk &euml;sht&euml; asgj&euml; tjet&euml;r pos mashtrim magjistari, e magistari nuk do t&euml; ket&euml; sukses kudo qoft&euml;.';
$t[70] = 'At&euml;her&euml; (kur shkopi i Musait i &euml;lltiti) magjistar&euml;t u hodh&euml;n n&euml; sexhde e than&euml;: &ldquo;Ne i besuam Zotit t&euml; Harunit dhe t&euml; Musait!&rdquo;';
$t[71] = 'Ai (faraoni) tha: &ldquo;Ai besuat atij para se t&rsquo;u jap&euml; leje? Ai (Musai) &euml;sht&euml; prij&euml;s i juaj, i cili a m&euml;soi magjin&euml;, un&euml; do t&rsquo;ua pres duart e k&euml;mb&euml;t t&euml;rthorazi e do t&rsquo;u var&euml; n&euml; trungujt e hurmave, e at&euml;her&euml; ju do a kuptoni se cili prej nesh ka d&euml;nim m&euml; t&euml; ashp&euml;r e m&euml; t&euml; vazhduesh&euml;m?&rdquo;';
$t[72] = 'Ata than&euml;: &ldquo;Pasha At&euml; q&euml; na krijoi, nuk t&euml; japim p&euml;rpar&euml;si ty ndaj argumenteve q&euml; na erdh&euml;n, e ti b&euml;je at&euml; q&euml; mendon ta b&euml;s, dhe mund ta zbatosh vet&euml;m at&euml; q&euml; i takonjet&euml;s s&euml; k&euml;saj bote!&rdquo;';
$t[73] = 'Ne i besuam Zotit ton&euml; q&euml; Ai t&euml; na fal&euml; gabimet dhe magjin&euml;, me t&euml; cil&euml;n ti na detyrove. All-llahu &euml;sht&euml; m&euml; i miri (n&euml; t&euml; shp&euml;rblyerit) dhe m&euml; i p&euml;rjetshmi (n&euml; t&euml; d&euml;nuar)!';
$t[74] = 'Ai q&euml; i paraqitet Zotit t&euml; vet si kriminel&euml;, e ka Xhehennemin, n&euml; t&euml; cilin as nuk vdes as nuk jeton.';
$t[75] = 'Nd&euml;rsa ai q&euml; i paraqitet Atij besimtar e q&euml; ka b&euml;r&euml; vepra t&euml; mira, t&euml; till&euml;t i presin merita t&euml; larta.';
$t[76] = '(do ta presin) Xhennete t&euml; Adnit, n&euml;p&euml;r t&euml; cil&euml;t do t&euml; rrjedhin lumenj dhe atydo t&euml; jen&euml; p&euml;rjet&euml;. Ky &euml;sht&euml; shp&euml;rblimi i atij q&euml; &euml;sht&euml; pastruar nga mosbesimi.';
$t[77] = 'Ne i kumtuam Musait: &ldquo;Udh&euml;to nat&euml;n me robt&euml; e Mij, hapu atyre me rrug&euml; t&euml; terur n&euml; det, e mos ke frik&euml; se do t&euml; z&euml;n&euml;, a do t&euml; fundos&euml;sh&rdquo;.';
$t[78] = 'Atyre iu vu prapa faraoni me ushtrin&euml; e vet, po ata p&euml;suan n&euml; det ashtu si p&euml;suan.';
$t[79] = 'Faraoni e humbi popullin e vet, e nuk e udh&euml;zoi.';
$t[80] = 'O bijt e israilit, Ne ju shp&euml;tuam prej armikut tuaj, ua p&euml;rcaktuam an&euml;n e djatht&euml; t&euml; Turit (p&euml;r shpalljen e Tevratit), ju furnizuam me rr&euml;shir&euml; (si mjalti) dhe shkurt&euml;za.';
$t[81] = 'Hani nga t&euml; mirat q&euml; u dhuruam, e mos u b&euml;ni p&euml;rbuz&euml;s se do t&rsquo;u godas&euml; hidh&euml;rimi Im, e at&euml; q&euml; e z&euml; hidh&euml;rimi Im, ai ka mbaruar.';
$t[82] = 'Nuk ka dyshim se Un&euml; e fal at&euml; q&euml; &euml;sht&euml; penduar, q&euml; ka besuar, q&euml; ka b&euml;r&euml; vepra t&euml; mira dhe q&euml; p&euml;rqendrohet p&euml;r n&euml; rrug&euml;n e drejt&euml;.';
$t[83] = 'Po ty o Musa: &ldquo;&ccedil;&rsquo;t&euml; nguti para popullit t&euml;nd?&rdquo;';
$t[84] = 'Ai u p&euml;rgjigj: &ldquo;Ja, ata jn&euml; pran&euml; meje, e un&euml; u nguta te ti, o Zoti im, q&euml; t&euml; jesh&euml; i k&euml;naqur ndaj meje!&rdquo;';
$t[85] = 'Ai (All-llahu) Tha: &ldquo;Ne sprovuam popullin t&euml;nd pas teje, dhe ata i hodhi n&euml; humbje Samiriji!&rdquo;';
$t[86] = 'Musai u kthye te populli i vet i hidh&euml;ruar e i pik&euml;lluar dhe tha: &ldquo;O populli im, a nuk u premtoi juve Zoti juaj premtim t&euml; mir&euml;, a mos u b&euml; koh&euml; e gjat&euml; e ju e harruat premtimin, ose d&euml;shiruat t&euml; ju godas&euml; hidh&euml;rimi nga Zoti juaj, andaj e thyet bes&euml;n q&euml; ma keni dh&euml;n&euml;?&rdquo;';
$t[87] = 'Ata than&euml;: &ldquo;Ne nuk e thyem bes&euml;n t&euml;nde me d&euml;shir&euml;n ton&euml;, por ne ishim ngarkuar me barr&euml; t&euml; r&euml;nda nga stolit&euml; e popullit e i hodh&euml;m ato (n&euml; zjarr), e ashtu i hodhi edhe Samirija&rdquo;.';
$t[88] = 'E ai (Samirija) ua mbaroi atyre nj&euml; vi&ccedil; me trup q&euml; p&euml;lliste, e ata than&euml;: &ldquo;Ky &euml;sht&euml; zoti juaj dhe Zoti i Musait, po ai (Musai) e ka harruar&rdquo;.';
$t[89] = 'A nuk e kuptonin ata se ai (vi&ccedil;) as nuk u p&euml;rgjigjej atyre, e as nuk kishte mund&euml;si t&rsquo;ju sillte ndonj&euml; dobi a d&euml;m.';
$t[90] = 'E Haruni atyre u pat th&euml;n&euml; m&euml; par&euml;: &ldquo;O populli im, ju vet&euml;mu sprovuat me t&euml;, pse Zoti juaj &euml;sht&euml; i Gjithm&euml;shir&euml;shmi, andaj ejani pas meje dhe m&euml; d&euml;gjoni p&euml;r k&euml;t&euml; q&euml; po ua th&euml;m&rdquo;.';
$t[91] = 'Ata i than&euml;: &ldquo;Ne nuk dp t&rsquo;i ndahemi k&euml;tij (adhurimit t&euml; vi&ccedil;it) deri q&euml; t&euml; kthehet Musai!&rdquo;';
$t[92] = 'Ai (Musai u kthye e) tha: &ldquo;O Harun &ccedil;&rsquo;t&euml; prngoi ty kur i pe se mor&euml;n rrug&euml;n e gabuar,';
$t[93] = 'E ti mos u vazhdosh rrug&euml;n time? Amos e kund&euml;rshtove edhe ti porosin&euml; time?&rdquo;';
$t[94] = 'Ai (Haruni) tha: &ldquo;O bir i n&euml;n&euml;s sime, mos m&euml; kap as p&euml;r mjekrre as p&euml;r flok&euml;, se un&euml; u frik&euml;sova se do t&euml; thuash: i p&euml;r&ccedil;ave beni israil&euml;t dhe nuk e ke respektuar porosin&euml; time?&rdquo;';
$t[95] = 'Ai (Musai) tha: &ldquo;E &ccedil;ka ishte puna jote, o Samirij?&rdquo;';
$t[96] = 'Tha (Samirija): &ldquo;Un&euml; pash&euml; at&euml; q&euml; ata nuk pan&euml;, e mora nj&euml; grusht nga gjurma e t&euml; d&euml;rguarit (nga f&euml;rkemi i kalit t&euml; Xhibrilit) dhe ia hodha atij (vi&ccedil;it) dhe k&euml;shtu m&euml; mashtroi vetvetja&rdquo;.';
$t[97] = 'Ai (Musai) tha: &ldquo;Prandaj, largohu sepse sa t&euml; jesh&euml; gjall&euml; ke p&euml;r t&euml; th&euml;n&euml;: (k&euml;do q&euml; e sheh) &ldquo;Mos m&rsquo;u afro!&rdquo; E ty t&euml; pret edhe nj&euml; moment (d&euml;nim) i pathyesh&euml;m. E ti tash shikoje zotin t&euml;nd q&euml; e adhurove bindsh&euml;m, e tani at&euml; do ta djegim e do ta b&euml;jm&euml; shkrumb dhe hirin e tij do ta shp&euml;rndajm&euml; n&euml;p&euml;r det&rdquo;.';
$t[98] = 'I adhurari juaj &euml;sht&euml; vet&euml;m All-llahu, q&euml; p&euml;rve&ccedil; Tij nuk ka zot tjet&euml;r, e dituria e Tij ka p&euml;rfshir&euml; &ccedil;do send.';
$t[99] = 'Po k&euml;shtu, Ne t&euml; rr&euml;fejm&euml; ty p&euml;r disa ngandodhit&euml; q&euml; kan&euml; kaluar m&euml; heret dhe nga ana jon&euml; t&euml; dham&euml; ty shpalljen plot p&euml;rkujtime.';
$t[100] = 'Kush i kthen shpin&euml;n k&euml;saj (shpalljes - Kur&rsquo;anit), ai do t&euml; bart&euml; n&euml; dit&euml;n e kijametit barr&euml;n m&euml; t&euml; r&euml;nd&euml;.';
$t[101] = 'N&euml;n at&euml; do t&euml; q&euml;ndrojn&euml; p&euml;rgjithmon&euml;. Sa barr&euml; e keqe &euml;sht&euml; p&euml;r ta n&euml; dit&euml;n e kijametit.';
$t[102] = 'Dit&euml;n kur i fryhet surit (bririt), at&euml; dit&euml; Ne i tubojm&euml; m&euml;katar&euml;t symavijosur (t&euml; verb&euml;r, t&euml; sh&euml;mtuar).';
$t[103] = 'E ata p&euml;shp&euml;risin mes vete: nuk q&euml;ndruat (n&euml; dynja) m&euml; shum&euml; se nja dhjet&euml; dit&euml;.';
$t[104] = 'Ne m&euml; s&euml; miri e dijm&euml; se &ccedil;ka flasin ata, e edhe at&euml; kur m&euml; i men&ccedil;uri prej tyre u tha: &ldquo;Nuk q&euml;ndruat m&euml; shum&euml; se nj&euml; dit&euml;!&rdquo;';
$t[105] = 'Ty t&euml; pyesin edhe p&euml;r kodrat, thuaj: &ldquo;Zoti im mund t&rsquo;i b&euml;j&euml; th&euml;rrime t&euml; shp&euml;rndara&rdquo;.';
$t[106] = 'Dhe vendin e tyre ta b&euml;j&euml; rrafsh pa far&euml; bime e nd&euml;rtese.';
$t[107] = 'E nuk do t&euml; shoh&euml;sh n&euml; t&euml; as ult&euml;sir&euml; e as lart&euml;sir&euml;.';
$t[108] = 'At&euml; dit&euml;, ata do t&euml; shkojn&euml; pas thirr&euml;sit. e nuk do t&rsquo;i largohen atij, e para t&euml; Gjithm&euml;shirshmit ulen z&euml;rat e nuk d&euml;gjohet tjet&euml;r pos nj&euml; z&euml; i ul&euml;t.';
$t[109] = 'At&euml; dit&euml; nuk b&euml;n dobi as nd&euml;rmjet&euml;simi, p&euml;rve&ccedil; atij, t&euml; cilin e ka lejuar i Gjithm&euml;shirshmi t&euml; nd&euml;rmjet&euml;soj&euml; dhe p&euml;r t&euml; cilin e p&euml;lqeu nd&euml;rmjet&euml;simin.';
$t[110] = 'Ai i di ato q&euml; ishin para tyre dhe ato q&euml; vijn&euml; pas, po dija e tyre nuk mund ta p&euml;rfshij ate.';
$t[111] = 'E fytyrat (e m&euml;katar&euml;ve) e turp&euml;ruara, i p&euml;rlen t&euml; P&euml;rjetshmit, t&euml; Gjithfuqishmit, sepse ka d&euml;shtuar ai q&euml; barti padrejt&euml;sin&euml;.';
$t[112] = 'Kush &euml;sht&euml; besimtar dhe b&euml;n vepra t&euml; mira, ai nuk i frik&euml;sohet ndonj&euml; padrejt&euml;sie e as ndonj&euml; mungese (n&euml; shp&euml;rblim).';
$t[113] = 'K&euml;shtu Ne e shpall&euml;m k&euml;t&euml; Kur&rsquo;an arabisht dhe p&euml;rs&euml;rit&euml;m n&euml; t&euml; v&euml;rejtjet, n&euml; m&euml;nyr&euml; q&euml; ata t&euml; ruhen ose ai (Kur&rsquo;ani) t&rsquo;u sjell&euml; atyre p&euml;rvoj&euml; m&euml;simi.';
$t[114] = 'I lart&euml; &euml;sht&euml; All-llahu, Sundue i v&euml;rtet&euml;. Ti mos nxito me Kur&rsquo;anin para se t&euml; p&euml;rfundoj&euml; shpallja e tij te ti, dhe thuaj: &ldquo;Zoti im, m&euml; shto dituri!&rdquo;';
$t[115] = 'Na i pat&euml;m urdh&euml;ruar edhe Ademit (t&euml; mos i afrohej pem&euml;s), po ai harroi, pra re ai nuk gjet&euml;n vendosm&euml;ri.';
$t[116] = 'P&euml;rkujto kur Ne u tham&euml; engj&euml;jve: &ldquo;B&euml;ni sexhde ndaj Ademit, ata i b&euml;n&euml;, pos Iblisit, i cili nuk deshi&rdquo;.';
$t[117] = 'E Ne i tham&euml;: &ldquo;O Adem ky &euml;sht&euml; armik yti dhe i bashk&euml;shortes t&euml;nde, pra mos t&rsquo;ju nxjerr&euml; kurrsesi nga Xhenneti, e t&rsquo;ju v&euml; n&euml; v&euml;shtir&euml;si (p&euml;r t&euml; siguruar mjetet e jetes&euml;s)&rdquo;.';
$t[118] = 'Ti aty (n&euml; Xhennet) nuk ke p&euml;r t&euml; qen&euml; i uritur e as nuk ke p&euml;r t&euml; mbetur i zhveshur.';
$t[119] = 'Aty nuk ke p&euml;r t&euml; pasur etje e as nuk do t&euml; kesh vap&euml;.';
$t[120] = 'Por, at&euml; e ngacmoi djalli duke i th&euml;n&euml;: &ldquo;O Adem, a do t&euml; t&euml; treoj p&euml;r pem&euml;n e pavdekshm&euml;ris&euml; dhe t&euml; sundimit t&euml; pazhduksh&euml;m!&rdquo;';
$t[121] = 'Ata t&euml; dy h&euml;ngr&euml;n nga ajo (pem&euml;) dhe q&euml; t&euml; dy u zhvesh&euml;n, e ia nis&euml;n ta mbulojn&euml; veten e tyre me gjethe q&euml; mbldhnin n&euml;p&euml;r Xhennet, dh k&euml;shtu Ademi theu urdh&euml;rin e Zotit t&euml; ve dhe gaboi.';
$t[122] = 'Mandej Zoti i vet at&euml; t&euml; zgjedhur, ia pranoi pendimin dhe e vuri n&euml; rrug&euml;n e drejt&euml; (t&euml; vendosur).';
$t[123] = 'Ai (All-llahu) u tha: &ldquo;Zbritni prej aty q&euml; t&euml; gjith&euml;, do t&euml; jeni armiq t&euml; nj&euml;ri-tjetrit. N&euml;se u vjen na Un&euml; udh&euml;zim (lib&euml;r e pejgamber) kush i p&euml;rmbahet udh&euml;imit Tim, ai nuk ka p&euml;r t&euml; humbur (n&euml; dunja) e as nuk ka p&euml;r t&euml; d&euml;shtuar (n&euml; et&euml;n tjet&euml;r)&rdquo;.';
$t[124] = 'E kush ia kthen shpin&euml;n udh&euml;zimit Tim, do t&euml; ket&euml; jet&euml; t&euml; v&euml;shtir&euml; dhe n&euml; dit&euml;n e kijametit do ta ringjall t&euml; verb&euml;r.';
$t[125] = 'Ai (q&euml; nuk besoi) do t&euml; thot&euml;: &ldquo;Zoti im, prse m&euml; ngrite t&euml; verb&euml;r, kur un&euml; isha me sy?&rdquo;';
$t[126] = 'Ai (All-llahu) thot&euml;: &ldquo;Ashtu si harrove ti argumentet Tona q&euml; t&rsquo;i ofruam, ashtu je ti i harruar sot&rdquo;.';
$t[127] = 'Po k&euml;shtu Ne e shp&euml;rblejm&euml; edhe at&euml; q&euml; zhytet n&euml; m&euml;kate dhe nk i beson argumentet e Zotit t&euml; vet, po d&euml;nimi n&euml; bot&euml;n tjet&euml;r &euml;sht&euml; edhe m&euml; i ashp&euml;r dhe i p&euml;rjetsh&euml;m.';
$t[128] = 'A nuk e kan&euml; t&euml; qart&euml; ata se sa breza para tyre i kemi zhdukur, e k&euml;ta ecin n&euml;p&euml;r ato vendbanime t&euml; tyre. N&euml; gjurm&euml;t e atyre ka fakte p&euml;r ata q&euml; kan&euml; arsye t&euml; sh&euml;ndosh&euml;.';
$t[129] = 'E, sikur t&euml; mos ishte vendimi dhe afati i caktuar m&euml; heret nga Zoti yt, d&euml;nimi do t&rsquo;i kapte ata.';
$t[130] = 'Prandaj, ti b&euml;hu i duruesh&euml;m ndaj asaj q&euml; thon&euml; ata, dhe duke fal&euml;nderuar Zotin t&euml;nd, falu para lindjes s&euml; diellit dhe para per&euml;ndimit t&euml; tij, falu edhe n&euml; or&euml;t e mat&euml;s dhe n&euml; skajet e dit&euml;s, ashtu q&euml; t&euml; gjejsh k&euml;naq&euml;si (me shp&euml;rblim).';
$t[131] = 'Dhe mos ia ngul syt&euml; bukuris&euml; s&euml; k&euml;saj jete me t&euml; cil&euml;n i b&euml;m&euml; t&euml; k&euml;naqen disa prej tyre (mosbesimtar&euml;), e p&euml;r t&rsquo;i sprovuar me t&euml;, sepse shp&euml;rbimi i Zotit t&euml;nd &euml;sht&euml; m&euml; i mir&euml; dhe &euml;sht&euml; i p&euml;rjetsh&euml;m.';
$t[132] = 'Urdh&euml;ro familjen t&euml;nde t&euml; fal&euml; namaz, e edhe ti vet&euml; zbatoje at&euml;, ngase zne nuk k&euml;rkojm&euml; prej teje ndonj&euml; furnizim (p&euml;r ty as p&euml;r familjen t&euml;nde), Ne t&euml; frnizojm&euml; ty (dhe ata), ardhm&euml;ria e mir&euml; &euml;sht&euml; e atij q&euml; ruhet.';
$t[133] = 'Ata than&euml;: &ldquo;p&euml;rse nuk na solli ai ndonj&euml; argument nga Zoti i vet? A nuk u erdhi atyre sqarim (n&euml; Kur&rsquo;an) p&euml;r at&euml; q&euml; ishte n&euml; broshurat e m&euml;parshme&rdquo;.';
$t[134] = 'Dhe sikur t&rsquo;i nd&euml;shkonim ata me ndonj&euml; d&euml;nim para tij (para se t&euml; shpallim Kur&rsquo;anin) ata do t&euml; thonin: &ldquo;Zoti yn&euml;, p&euml;rse nuk na &ccedil;ove ndonj&euml; t&euml; d&euml;rguar q&euml; t&rsquo;iu p&euml;rmbaheshim fakteve Tua para se t&euml; posht&euml;roheshim e t&euml; mjeroheshim&rdquo;.';
$t[135] = 'Thuaj: &ldquo;Secili &euml;sht&euml; duke pritur, pra pritni edhe ju, se s&euml; shpejti do ta kuptoni se kush ishin ithtar&euml;t e rrug&euml;s s&euml; drejt&euml; dhe e gjeti t&euml; v&euml;rtet&euml;n&rdquo;.';
?>