$t[4] = 'i &auml;ldre tid, till v&auml;gledning f&ouml;r m&auml;nniskorna och det &auml;r Han som har uppenbarat den m&aring;ttstock med vars hj&auml;lp r&auml;tt kan m&auml;tas och skiljas fr&aring;n or&auml;tt. Ett str&auml;ngt straff v&auml;ntar dem som f&ouml;rnekar [sanningen i] Guds budskap - ja, Gud &auml;r allsm&auml;ktig och h&auml;mnden &auml;r Hans.';
$t[5] = 'F&ouml;r Gud &auml;r ingenting dolt vare sig p&aring; jorden eller i himlen.';
$t[6] = 'Det &auml;r Han som formar er i moderlivet enligt Sin vilja. Det finns ingen annan gud &auml;n Han, den Allsm&auml;ktige, den Vise.';
$t[7] = 'Det &auml;r Han som har uppenbarat f&ouml;r dig denna Skrift, d&auml;r det finns fast och klart formulerade budskap - de utg&ouml;r dess k&auml;rna - och andra som &auml;r framst&auml;llda i bilder. Men de vilkas hj&auml;rtan har farit vilse g&aring;r efter s&aring;dant i Skriften som har framst&auml;llts i bild, n&auml;r de f&ouml;rs&ouml;ker s&aring; split och f&ouml;rvirring genom [godtycklig] tolkning av dess innersta mening - dess innersta mening k&auml;nner ingen utom Gud. - De vilkas kunskap &auml;r fast och djupt rotad s&auml;ger: &quot;Vi tror p&aring; denna [Skrift]; allt &auml;r fr&aring;n v&aring;r Herre.&quot; Men ingen &auml;gnar eftertanke &aring;t [och tar varning av] detta utom de som har f&ouml;rst&aring;nd.';
$t[8] = '[De ber:] &quot;Herre! L&aring;t inte v&aring;ra hj&auml;rtan fara vilse sedan Du har v&auml;glett oss. Och f&ouml;rbarma Dig i Din n&aring;d &ouml;ver oss. - Du &auml;r Den som alltid ger!';
$t[9] = 'Herre! Du skall samla alla m&auml;nniskor [inf&ouml;r Dig] en Dag [vars ankomst] st&aring;r utom varje tvivel; Gud sviker helt visst inte Sitt l&ouml;fte.&quot;';
$t[10] = 'DE SOM framh&auml;rdar i att f&ouml;rneka sanningen - ingenting av vad de &auml;ger p&aring; jorden och inte heller deras s&ouml;ner skall kunna skydda dem mot Gud. De skall bli br&auml;nsle f&ouml;r Elden.';
$t[11] = '[Det skall g&aring; f&ouml;r dem] som det gick f&ouml;r Faraos m&auml;n och f&ouml;r deras f&ouml;reg&aring;ngare; de p&aring;stod att V&aring;ra budskap var l&ouml;gn och Gud straffade dem f&ouml;r deras synder; Gud straffar med str&auml;nghet.';
$t[12] = 'S&auml;g till f&ouml;rnekarna av sanningen: &quot;[En dag] skall ni bli besegrade och d&aring; skall ni f&ouml;ras samman i helvetet - en el&auml;ndig rastplats!&quot;';
$t[13] = 'Ni har redan f&aring;tt ett tecken i de tv&aring; h&auml;rarna, som m&ouml;ttes [p&aring; slagf&auml;ltet], varav en stred f&ouml;r Guds sak och den andra [bestod av] dem som f&ouml;rnekade sanningen. De [troende] s&aring;g med egna &ouml;gon att de andra var tv&aring; g&aring;nger s&aring; talrika som de. Men Gud ger Sin hj&auml;lp &aring;t den Han vill. I detta ligger helt visst en l&auml;rdom f&ouml;r de klarsynta.';
$t[14] = 'KVINNORS k&auml;rlek, s&ouml;ner [i m&auml;ngd], skatter av guld och silver, h&auml;star av &auml;del ras, boskap och &aring;kerjordar har utm&aring;lats f&ouml;r m&auml;nniskorna [som det mest &aring;tr&aring;v&auml;rda i livet]. Allt detta h&ouml;r till de gl&auml;dje&auml;mnen som st&aring;r till buds i denna v&auml;rld, men &aring;terkomsten till Gud &auml;r det h&ouml;gsta goda.';
$t[15] = 'S&auml;g: &quot;Skall jag ber&auml;tta f&ouml;r er om det som &auml;r f&ouml;rmer &auml;n dessa [gl&auml;dje&auml;mnen]? Lustg&aring;rdar, vattnade av b&auml;ckar, v&auml;ntar de gudfruktiga hos deras Herre, d&auml;r de skall f&ouml;rbli till evig tid, och hustrur renade fr&aring;n [all jordisk] orenlighet, och Guds v&auml;lbehag.&quot; Gud f&ouml;rlorar ingen av Sina tj&auml;nare ur sikte:';
$t[16] = 'de som s&auml;ger: &quot;Herre, vi tror; f&ouml;rl&aring;t oss v&aring;ra synder och f&ouml;rskona oss fr&aring;n Eldens straff!&quot;,';
$t[17] = 'de t&aring;lmodiga och uth&aring;lliga, de sanningsenliga, de som &ouml;dmjukt b&ouml;jer sig under Hans vilja, de som ger av sitt &aring;t andra och som i den tidiga gryningen ber om Guds f&ouml;rl&aring;telse f&ouml;r sina synder.';
$t[18] = 'GUD [SJ&Auml;LV] vittnar - och [s&aring; g&ouml;r] &auml;nglarna och de som har kunskap - att det inte finns en annan gud &auml;n Han, Uppr&auml;tth&aring;llaren av r&auml;ttvisa och j&auml;mvikt; ingen annan gud finns &auml;n Han, den Allsm&auml;ktige, den Vise.';
$t[19] = 'F&ouml;r Gud &auml;r [den sanna] religionen underkastelse under Hans vilja; de som [tidigare] tog emot uppenbarelsen kom att omfatta skilda meningar f&ouml;rst efter att ha f&aring;tt [all] kunskap och av ovilja och avund mot varandra. Och den som vill f&ouml;rneka sanningen i Guds budskap [b&ouml;r minnas att] Gud &auml;r snar att kalla till r&auml;kenskap.';
$t[20] = 'Om de vill tvista med dig [Muhammad], s&auml;g d&aring;: &quot;Jag har helt och fullt underkastat mig Guds vilja och &auml;ven de som f&ouml;ljer mig [har underkastat sig Hans vilja].&quot; Och fr&aring;ga dem som [i &auml;ldre tid] fick ta emot uppenbarelsen och de okunniga: &quot;Har [ocks&aring;] ni underkastat er Guds vilja?&quot; Om de underkastar sig, d&aring; har de funnit v&auml;gledning, men om de v&auml;nder dig ryggen [skall du l&auml;mna dem i fred]; din plikt &auml;r enbart att framf&ouml;ra budskapet. Gud f&ouml;rlorar ingen av Sina tj&auml;nare ur sikte.';
$t[21] = 'De som f&ouml;rnekar sanningen i Guds budskap och som mot all r&auml;tt d&ouml;dar profeterna och d&ouml;dar dem av folket som talar f&ouml;r r&auml;ttvisa - kung&ouml;r f&ouml;r dem att de har ett pl&aring;gsamt straff [att v&auml;nta].';
$t[22] = 'Dessa m&auml;nniskors str&auml;vanden skall g&aring; om intet [och bli utan frukt] i detta liv och i det kommande, och de skall inte finna n&aring;gon hj&auml;lpare.';
$t[23] = 'Har du inte sett hur n&aring;gra av dem som fick ta emot en del av uppenbarelsen, [n&auml;r] de uppmanas att l&aring;ta den gudomliga Skriften d&ouml;ma mellan dem i deras tvister, v&auml;nder sig bort - ja, de &auml;r [s&aring;dana] som g&aring;r emot [sanning och r&auml;tt].';
$t[24] = 'De har n&auml;mligen sagt att Elden inte skall pl&aring;ga dem mer &auml;n ett begr&auml;nsat antal dagar? Men de l&ouml;gner som de t&auml;nkte ut har gjort dem blinda f&ouml;r vad deras religion [f&ouml;reskriver].';
$t[25] = 'Hur [skall det g&aring; f&ouml;r dem] n&auml;r Vi en Dag samlar dem - [att den Dagen kommer] st&aring;r utom allt tvivel - och var och en till fullo skall f&aring; den l&ouml;n som han f&ouml;rtj&auml;nat och ingen skall lida or&auml;tt';
$t[26] = 'S&Auml;G: &quot;Gud, all makts Herre! Du sk&auml;nker makten &aring;t den Du vill och Du tar makten fr&aring;n den Du vill. Du upph&ouml;jer den Du vill och Du f&ouml;rnedrar den Du vill. I Din hand ligger det goda och Du har allting i Din makt.';
$t[27] = 'Du l&aring;ter natten smyga in i dagen och dagen smyga in i natten. Du l&aring;ter livet spira ur det som &auml;r d&ouml;tt och l&aring;ter d&ouml;den stiga fram ur det levande. Och Du sk&auml;nker Ditt goda i &ouml;verfl&ouml;d &aring;t den Du vill.&quot;';
$t[28] = 'DE TROENDE skall inte ta f&ouml;rnekare av sanningen snarare &auml;n sina trosbr&ouml;der till bundsf&ouml;rvanter - den som g&ouml;r s&aring; sk&auml;r av banden till Gud - om inte ert syfte &auml;r att med detta [skydda er] mot ett hot. Men Gud uppmanar er att inte ta l&auml;tt p&aring; Hans varningar; Gud &auml;r m&aring;let f&ouml;r er f&auml;rd.';
$t[29] = 'S&auml;g: &quot;Vare sig ni d&ouml;ljer det som r&ouml;r sig i ert innersta eller visar det, vet Gud det. Han vet allt det som himlarna rymmer och det som jorden b&auml;r. Och Gud har allt i Sin makt.&quot;';
$t[30] = 'Den Dag d&aring; m&auml;nniskan skall st&auml;llas inf&ouml;r allt det goda som hon har gjort och allt det onda som hon har gjort kommer hon att &ouml;nska att den [Dagen] hade varit o&auml;ndligt avl&auml;gsen. D&auml;rf&ouml;r uppmanar Gud er att inte ta l&auml;tt p&aring; Hans varningar; Gud &ouml;mmar f&ouml;r Sina tj&auml;nare.';
$t[31] = 'S&auml;g [Muhammad]: &quot;Om ni &auml;lskar Gud, f&ouml;lj mig och Gud skall &auml;lska er och f&ouml;rl&aring;ta er era synder. Gud &auml;r st&auml;ndigt f&ouml;rl&aring;tande, barmh&auml;rtig.&quot;';
$t[32] = 'S&auml;g: &quot;Lyd Gud och S&auml;ndebudet.&quot; Men om de v&auml;nder ryggen till [skall de veta att] Gud inte &auml;r v&auml;n till dem som f&ouml;rnekar sanningen.';
$t[33] = 'GUD har utvalt Adam och Noa och Abrahams &auml;tt och `Imrans &auml;tt [och upph&ouml;jt dem] &ouml;ver v&auml;rldens alla m&auml;nniskor;';
$t[34] = 'de stammar [alla] fr&aring;n varandra. Gud h&ouml;r allt, vet allt.';
$t[35] = 'Och `Imrans hustru bad till Gud och sade: &quot;Herre! Jag viger vad jag b&auml;r i mitt sk&ouml;te &aring;t Din tj&auml;nst. Tag emot mitt [offer], Du som h&ouml;r allt, vet allt.';
$t[36] = 'Och d&aring; hon f&ouml;tt barnet sade hon: &quot;Herre, jag har f&ouml;tt en dotter - fast&auml;n Gud noga visste vad hon hade framf&ouml;tt; ett gossebarn &auml;r inte som ett flickebarn - och jag har gett henne namnet Maria och jag ber Dig skydda och bevara henne och hennes efterkommande mot Dj&auml;vulen, den utst&ouml;tte.&quot;';
$t[37] = 'Och hennes Herre tog n&aring;digt emot henne och l&auml;t henne v&auml;xa upp och arta sig v&auml;l och uppdrog &aring;t Sakarias att vaka &ouml;ver henne. Var g&aring;ng Sakarias bes&ouml;kte henne i helgedomen, fann han henne f&ouml;rsedd med f&ouml;da. Och n&auml;r han fr&aring;gade henne: &quot;Maria, varifr&aring;n har du f&aring;tt detta?&quot; svarade hon: &quot;Det &auml;r fr&aring;n Gud; Gud sk&auml;nker Sitt goda i &ouml;verfl&ouml;d till den Han vill.&quot;';
$t[38] = 'D&auml;r [i helgedomen] bad Sakarias till Gud: &quot;Herre! L&aring;t mig, som en g&aring;va fr&aring;n Dig, f&aring; en [son], sund till kropp och sj&auml;l! Du b&ouml;nh&ouml;r den som ber.&quot;';
$t[39] = 'Och &auml;nglarna ropade till honom medan han stod i helgedomen, f&ouml;rs&auml;nkt i b&ouml;n, och sade: &quot;Gud kung&ouml;r f&ouml;r dig det glada budskapet om [en son] Johannes, som skall bekr&auml;fta ett ord fr&aring;n Gud och [bli] en ledare [f&ouml;r sitt folk], en renlevnadsman och en profet, en av de r&auml;ttf&auml;rdiga.&quot;';
$t[40] = '[Sakarias] sade: &quot;Herre, hur skulle jag kunna f&aring; en son? Jag har ju uppn&aring;tt h&ouml;g &aring;lder och min hustru &auml;r ofruktsam.&quot; [&Auml;ngeln] sade: &quot;S&aring; [skall det bli]; Gud g&ouml;r vad Han vill.&quot;';
$t[41] = '[Sakarias] sade: &quot;Herre! Ge mig ett tecken.&quot; [&Auml;ngeln] svarade: &quot;Det tecken [du f&aring;r &auml;r] att du under tre dagar inte skall tala till m&auml;nniskor annat &auml;n med gester. Och &aring;kalla din Herre utan att f&ouml;rtr&ouml;ttas och prisa Honom morgon och afton.&quot;';
$t[42] = '[MINNS] &auml;nglarnas ord till Maria: &quot;Gud har utvalt dig och renat dig [och upph&ouml;jt] dig &ouml;ver alla v&auml;rldens kvinnor!';
$t[43] = 'Maria! F&ouml;rr&auml;tta din andakt inf&ouml;r din Herre med &ouml;dmjukhet; fall ned p&aring; ditt ansikte [inf&ouml;r Honom] och b&ouml;j ditt huvud tillsammans med dem som b&ouml;jer sina huvuden [i b&ouml;n].&quot;';
$t[44] = 'Detta h&ouml;r till de [f&ouml;r dig, Muhammad] ok&auml;nda ting, som Vi [nu] uppenbarar f&ouml;r dig; du var inte med dem n&auml;r de kastade lott om vem som skulle ha ansvar f&ouml;r Maria; och du var inte med dem n&auml;r de tvistade [om detta] med varandra.';
$t[45] = 'Och &auml;nglarna sade: &quot;Maria! Gud tillk&auml;nnager f&ouml;r dig det glada budskapet om ett ord fr&aring;n Honom [varigenom du skall f&ouml;da den] vars namn skall vara Kristus Jesus, Marias son, &auml;rad och lovprisad i denna v&auml;rld och i det kommande livet och en av dem som skall vara i [Guds] n&auml;rhet.';
$t[46] = 'Han skall tala till m&auml;nniskorna i vaggan och i mogen &aring;lder och han skall vara en av de r&auml;ttf&auml;rdiga.&quot;';
$t[47] = 'Maria sade: &rdquo;Herre! Hur skulle jag kunna f&aring; en son, d&aring; ingen man har r&ouml;rt mig?&rdquo; [&Auml;ngeln] sade: &rdquo;S&aring; [skall det bli]; Gud skapar vad Han vill. Om Han vill att n&aring;got skall vara, s&auml;ger Han endast till det: \'Var!\' - och det &auml;r.';
$t[48] = 'Han skall undervisa din son i Skriften, [sk&auml;nka honom] visdom och [ge honom] Tora och Evangelium';
$t[49] = 'och [s&auml;nda honom] till israeliterna som Sitt s&auml;ndebud.&quot; [Och Jesus sade:] &quot;Jag har kommit till er med ett tecken fr&aring;n er Herre. Jag skall forma &aring;t er i lera n&aring;got som liknar en f&aring;gel och jag skall andas p&aring; den och med Guds till&aring;telse skall den bli en f&aring;gel. Och jag skall bota de blinda och de spet&auml;lska och med Guds vilja skall jag v&auml;cka de d&ouml;da till liv. Och jag skall s&auml;ga er vad ni &auml;ter och vad ni l&auml;gger upp som f&ouml;rr&aring;d i era hus. I detta ligger ett tecken f&ouml;r er - om ni &auml;r [sanna] troende.';
$t[50] = 'Och [jag har kommit] f&ouml;r att bekr&auml;fta vad som &auml;nnu best&aring;r av Tora och f&ouml;r att f&ouml;rklara till&aring;tet n&aring;got av det som var f&ouml;rbjudet f&ouml;r er. Och jag har kommit till er med ett tecken fr&aring;n er Herre. Frukta d&auml;rf&ouml;r Gud och f&ouml;lj mig.';
$t[51] = 'Gud &auml;r min Herre och er Herre; det &auml;r d&auml;rf&ouml;r Honom ni skall dyrka! Detta &auml;r en rak v&auml;g.&quot;';
$t[52] = 'N&auml;r Jesus m&auml;rkte deras ovillighet att tro [p&aring; honom], sade han: &quot;Vem vill hj&auml;lpa mig [att kalla m&auml;nniskorna] till Guds v&auml;g?&quot; De vitkl&auml;dda sade: &quot;Vi &auml;r Guds medhj&auml;lpare! Vi tror p&aring; Gud; vittna du att vi har underkastat oss Hans vilja!';
$t[53] = 'Herre! Vi tror p&aring; det som Du har uppenbarat och vi vill f&ouml;lja S&auml;ndebudet; r&auml;kna oss d&auml;rf&ouml;r bland [sanningens] vittnen.&quot;';
$t[54] = 'Och de [som inte ville tro] smidde onda planer [mot Jesus], men [&auml;ven] Gud smed planer - ingen smider b&auml;ttre planer &auml;n Gud.';
$t[55] = 'Och Gud sade: &quot;Jesus! Jag skall kalla dig till Mig och Jag skall upph&ouml;ja dig till Min [h&auml;rlighet] och Jag skall rena dig fr&aring;n [de beskyllningar som] f&ouml;rnekarna av sanningen [riktade mot dig]. Och Jag skall s&auml;tta dem som f&ouml;ljer dig h&ouml;gt &ouml;ver dem som f&ouml;rnekar [dig] &auml;nda till Uppst&aring;ndelsens dag; d&aring; skall ni alla v&auml;nda tillbaka till Mig och Jag skall d&ouml;ma mellan er i allt det som ni var oense om.';
$t[56] = 'Vad betr&auml;ffar f&ouml;rnekarna av sanningen, skall Jag ge dem ett str&auml;ngt straff i denna v&auml;rld och i det kommande livet; och de kommer inte att finna n&aring;gon hj&auml;lpare.&quot;';
$t[57] = 'Och Han skall ge dem som tror och lever ett r&auml;ttskaffens liv deras fulla bel&ouml;ning. Gud &auml;r sannerligen inte de or&auml;ttf&auml;rdigas v&auml;n.';
$t[58] = 'DETTA SOM Vi framf&ouml;r till dig [Muhammad] &auml;r en del av [V&aring;ra] budskap och den visa P&aring;minnelsen.';
$t[59] = 'Inf&ouml;r Gud &auml;r Jesus till sin natur att likna vid Adam. Han skapade honom av jord och sade till honom: &quot;Var!&quot; och han &auml;r.';
$t[60] = '[Det &auml;r] sanningen fr&aring;n din Herre; hys inte minsta tvivel om detta!';
$t[61] = 'Om n&aring;gon vill tvista med dig om detta, sedan du tagit emot den kunskap som har kommit dig till del, s&auml;g d&aring;: &quot;L&aring;t oss kalla v&aring;ra s&ouml;ner och era s&ouml;ner, v&aring;ra kvinnor och era kvinnor, ja, l&aring;t oss alla samlas och &ouml;dmjukt be Gud att Han avkunnar Sin dom, och l&aring;t oss nedkalla Guds f&ouml;rd&ouml;melse &ouml;ver alla som ljuger.&quot;';
$t[62] = 'Ja, detta &auml;r sanningen - det finns ingen gud utom Gud, och Gud &auml;r f&ouml;rvisso den Allsm&auml;ktige, den Vise.';
$t[63] = 'Och om de v&auml;nder er ryggen [b&ouml;r de veta att] Gud har full kunskap om dem som vill st&ouml;ra ordningen p&aring; jorden och sprida sedef&ouml;rd&auml;rv.';
$t[64] = 'S&auml;g: &quot;Efterf&ouml;ljare av &auml;ldre tiders uppenbarelser! L&aring;t oss samlas kring en regel, gemensam f&ouml;r oss och f&ouml;r er - att vi inte skall dyrka n&aring;gon utom Gud och inte s&auml;tta n&aring;got vid Guds sida och inte erk&auml;nna andra m&auml;nniskor som v&aring;ra herrar och beskyddare i Guds st&auml;lle.&quot; Och s&auml;g, om de v&auml;nder er ryggen: &quot;Vittna d&aring;, att det &auml;r vi som har underkastat oss Hans vilja.&quot;';
$t[65] = 'VARF&Ouml;R tvistar ni efterf&ouml;ljare av &auml;ldre tiders uppenbarelser om Abraham? Tora uppenbarades ju inte - och inte heller Evangeliet - f&ouml;rr&auml;n efter hans tid. Vill ni inte anv&auml;nda ert f&ouml;rst&aring;nd?';
$t[66] = 'H&auml;r tvistar ni [med varandra] om det som ni k&auml;nner till; varf&ouml;r vill ni tvista om det som ni &auml;r helt okunniga om? Gud vet, men ni vet inte.';
$t[67] = 'Abraham var [till sin tro] varken jude eller kristen - han s&ouml;kte och fann den rena, ursprungliga tron och underkastade sig Guds vilja; och han v&auml;grade att ge avgudarna sin dyrkan.';
$t[68] = 'De m&auml;nniskor som st&aring;r Abraham n&auml;rmast &auml;r helt visst de som f&ouml;ljer hans exempel, s&aring;som denne Profet och [hans skaror av] troende. Och Gud &auml;r de troendes Beskyddare.';
$t[69] = 'Bland efterf&ouml;ljarna av &auml;ldre tiders uppenbarelser finns de som vill leda er p&aring; villov&auml;gar. De kan bara vilseleda sig sj&auml;lva - men det inser de inte.';
$t[70] = 'Ni, efterf&ouml;ljare av &auml;ldre tiders uppenbarelser! Varf&ouml;r avvisar ni Guds budskap fast&auml;n ert eget vittnesb&ouml;rd [bekr&auml;ftar deras sanning]?';
$t[71] = 'Efterf&ouml;ljare av &auml;ldre tiders uppenbarelser! Varf&ouml;r kl&auml;r ni &ouml;ver det sanna med l&ouml;gn och g&ouml;mmer mot b&auml;ttre vetande undan sanningen?';
$t[72] = 'N&aring;gra av efterf&ouml;ljarna av &auml;ldre tiders uppenbarelser s&auml;ger [till de andra]: &quot;[F&ouml;rklara er] tro p&aring; det som har uppenbarats f&ouml;r de troende vid dagens b&ouml;rjan och avvisa det &ouml;vriga; kanske skall de d&aring; v&auml;nda om.';
$t[73] = 'Tro inte p&aring; n&aring;gon som inte bek&auml;nner sig till er religion!&quot; S&auml;g: &quot;Den [sanna] v&auml;gledningen &auml;r Guds v&auml;gledning; [&auml;r ni r&auml;dda] att andra skall f&aring; del av s&aring;dana [uppenbarelser] som ni har tagit emot? Eller att de skall tvista med er inf&ouml;r er Herre?&quot; S&auml;g: &quot;Guds n&aring;deg&aring;vor &auml;r i Hans hand; Han sk&auml;nker dem till den Han vill. Gud n&aring;r &ouml;verallt och Han vet allt,';
$t[74] = 'och Han f&ouml;rbarmar Sig &ouml;ver den Han vill. Guds n&aring;d &auml;r en outsinlig k&auml;lla.&quot;';
$t[75] = 'OCH BLAND efterf&ouml;ljarna av &auml;ldre tiders uppenbarelser finns de som, om du anf&ouml;rtror dem en skatt av guld, [villigt] &aring;terl&auml;mnar den; och det finns de som, om du anf&ouml;rtror dem en enda dinar, v&auml;grar &aring;terl&auml;mna den om du inte ans&auml;tter dem med ideliga p&aring;minnelser. De p&aring;st&aring;r n&auml;mligen: &quot;Vi har inte blivit &aring;lagda [att h&aring;lla v&aring;ra l&ouml;ften], n&auml;r det g&auml;ller dessa okunniga m&auml;nniskor&quot;; s&aring; s&auml;ger de om Gud, trots att de vet b&auml;ttre, det som inte &auml;r sant.';
$t[76] = 'Nej, det f&ouml;rh&aring;ller sig tv&auml;rtom! [Gud k&auml;nner] dem som &auml;r trogna f&ouml;rbundet med Honom och fruktar Honom; Gud &auml;lskar de gudfruktiga.';
$t[77] = 'De som f&ouml;r en ynklig vinst f&ouml;rnekar f&ouml;rbundet med Gud och de eder som de har svurit, skall inte f&aring; del av det eviga livets goda och p&aring; Uppst&aring;ndelsens dag skall Gud varken tala till dem eller se p&aring; dem, och Han skall inte rena dem [fr&aring;n synd]; ett sv&aring;rt lidande v&auml;ntar dem.';
$t[78] = 'Bland dem finns helt visst de som lindar sina tungor om Skriften f&ouml;r att ni skall tro att [deras f&ouml;rvr&auml;ngda ord] &auml;r h&auml;mtade ur Skriften, fast&auml;n de inte &auml;r h&auml;mtade ur Skriften; och de s&auml;ger: &quot;Detta &auml;r fr&aring;n Gud&quot;, fast&auml;n det inte &auml;r fr&aring;n Gud. Och s&aring; s&auml;ger de om Gud mot b&auml;ttre vetande det som inte &auml;r sant.';
$t[79] = 'Det &auml;r ot&auml;nkbart att en m&auml;nniska, som Gud har gett uppenbarelsens g&aring;va och visdom och kallat till profet, d&auml;refter skulle s&auml;ga till m&auml;nniskorna: &quot;Tillbe mig vid sidan av Gud.&quot; Nej, [vad han skulle s&auml;ga &auml;r:] &quot;Bli Guds h&auml;ngivna tj&auml;nare som undervisar [andra] och f&ouml;rdjupar sin egen kunskap i Skriften.&quot;';
$t[80] = 'Inte heller [skulle han] befalla er att betrakta &auml;nglar och profeter som [himmelska] beskyddare. [Eller tror ni att han skulle] uppmana er att &aring;terfalla i otro sedan ni har underkastat er Guds vilja?';
$t[81] = '[EFTERF&Ouml;LJARE av &auml;ldre tiders uppenbarelser! Minns att] Gud erbj&ouml;d er Sitt f&ouml;rbund genom profeterna. [Han sade:] &quot;Jag har l&aring;tit er f&aring; del av uppenbarelse och visdom. [En g&aring;ng] skall ett s&auml;ndebud komma till er med bekr&auml;ftelse av den sanning som [redan] &auml;r i er hand; d&aring; skall ni tro p&aring; honom och st&ouml;dja honom.&quot; [Och Han] sade: &quot;&Auml;r det ert beslut att ing&aring; f&ouml;rbund med Mig p&aring; denna grund?&quot; De svarade: &quot;Ja, det &auml;r v&aring;rt beslut.&quot; [Och Gud] sade: &quot;Vittna d&aring; om detta; Jag skall vittna med er.';
$t[82] = 'De som efter detta drar sig ifr&aring;n [sin plikt] &auml;r de som har f&ouml;rh&auml;rdat sig i trots och olydnad.&quot;';
$t[83] = '&Ouml;nskar de en annan [religion] &auml;n den som Gud har gett dem? [Gud] som alla i himlarna och p&aring; jorden underkastar sig av fri vilja eller n&ouml;dda och tvungna, och till vilken de skall &aring;terb&ouml;rdas.';
$t[84] = 'S&auml;g: &quot;Vi tror p&aring; Gud och p&aring; det som har uppenbarats f&ouml;r oss och det som uppenbarades f&ouml;r Abraham och Ismael och Isak och Jakob och deras efterkommande och det som uppenbarades f&ouml;r Moses och Jesus och f&ouml;r [alla de &ouml;vriga] profeterna av deras Herre; vi g&ouml;r ingen &aring;tskillnad mellan dem. Och vi har underkastat oss Hans vilja.&quot;';
$t[85] = 'Om n&aring;gon vill anta n&aring;gon annan religion &auml;n Islam kommer detta att v&auml;gras honom, och i n&auml;sta liv skall han st&aring; bland f&ouml;rlorarna.';
$t[86] = 'Skulle Gud v&auml;gleda dem som f&ouml;rnekar tron efter att ha antagit den och vittnat att S&auml;ndebudet i sanning [&auml;r Guds uts&auml;nde] och som f&aring;tt se klara bevis? Nej, Gud v&auml;gleder inte de or&auml;ttf&auml;rdiga.';
$t[87] = 'Guds och &auml;nglarnas och alla m&auml;nniskors f&ouml;rd&ouml;melse skall bli deras l&ouml;n.';
$t[88] = 'Under denna [dom] skall de f&ouml;rbli; deras straff skall inte lindras och de skall inte beviljas uppskov.';
$t[89] = 'Men de som i &aring;nger v&auml;nder &aring;ter [till Gud] och g&ouml;r bot och b&auml;ttring [skall inte straffas] - Gud &auml;r st&auml;ndigt f&ouml;rl&aring;tande, barmh&auml;rtig.';
$t[90] = 'De som f&ouml;rnekar sanningen efter att ha antagit tron och som d&auml;refter f&ouml;rh&auml;rdas i sin f&ouml;rnekelse - deras &aring;nger skall inte godtas; nej, de har &ouml;vergett den raka v&auml;gen [utan &aring;terv&auml;ndo].';
$t[91] = 'De som f&ouml;rnekar sanningen och d&ouml;r som f&ouml;rnekare skall inte f&aring; k&ouml;pa sig fria, om de &auml;n erbj&ouml;d allt guld i v&auml;rlden. Ett sv&aring;rt lidande v&auml;ntar dem och de skall inte finna n&aring;gon hj&auml;lpare.';
$t[92] = '[TROENDE!] Ni kommer inte att uppn&aring; sann fromhet, f&ouml;rr&auml;n ni ger &aring;t andra av det som ni [sj&auml;lva] v&auml;rdes&auml;tter; Gud har full k&auml;nnedom om vad ni ger.';
$t[93] = 'ALL F&Ouml;DA var [i &auml;ldre tid] till&aring;ten f&ouml;r Israels barn utom de f&ouml;rbud som Israel sj&auml;lv p&aring;lade sig f&ouml;re uppenbarelsen av Tora. S&auml;g: &quot;Tag fram Tora och l&auml;s h&ouml;gt ur den, om ni h&aring;ller p&aring; sanningen.&quot;';
$t[94] = 'Och de som h&auml;danefter s&auml;tter ihop l&ouml;gner om Gud drivs av ont upps&aring;t.';
$t[95] = 'S&auml;g: &quot;Gud har [i uppenbarelsen] sagt sanningen. Tro d&auml;rf&ouml;r med Abrahams rena, ursprungliga tro, han som v&auml;grade avgudarna sin dyrkan.&quot;';
$t[96] = 'Det f&ouml;rsta tempel som restes f&ouml;r m&auml;nniskornas bruk var helt visst Helgedomen i Bakkah, en v&auml;lsignelse och en ledstj&auml;rna f&ouml;r hela v&auml;rlden.';
$t[97] = 'Den f&ouml;rmedlar klara budskap [och d&auml;r finns] den plats d&auml;r Abraham en g&aring;ng stod; den som stiger in d&auml;r &auml;r i trygghet. Vallf&auml;rden till denna Helgedom &auml;r en plikt gentemot Gud f&ouml;r var och en som har m&ouml;jlighet att genomf&ouml;ra den. Och den som f&ouml;rnekar sanningen [skall veta att] Gud &auml;r Sig sj&auml;lv nog och inte beh&ouml;ver [n&aring;gon eller n&aring;got i] Sin skapelse.';
$t[98] = 'S&Auml;G: &quot;Efterf&ouml;ljare av &auml;ldre tiders uppenbarelser! Varf&ouml;r f&ouml;rnekar ni sanningen i Guds budskap, fast&auml;n [ni vet att] Gud &auml;r vittne till allt vad ni g&ouml;r?&quot;';
$t[99] = 'S&auml;g: &quot;Efterf&ouml;ljare av &auml;ldre tiders uppenbarelser! Varf&ouml;r vill ni hindra dem som antar tron fr&aring;n att f&ouml;lja Guds v&auml;g och f&aring; den att framst&aring; som krokig, fast&auml;n ni sj&auml;lva vittnar [att den &auml;r rak]? Ingenting av vad ni g&ouml;r g&aring;r Gud f&ouml;rbi.&quot;';
$t[100] = 'Troende! [Det finns de] bland dem som [f&ouml;rr] fick ta emot uppenbarelsen som, om ni g&aring;r dem till m&ouml;tes, kommer att g&ouml;ra allt f&ouml;r att ni skall f&ouml;rneka den tro som ni har antagit.';
$t[101] = 'Hur skulle ni kunna f&ouml;rneka er tro, som h&ouml;r Guds budskap l&auml;sas och har Hans S&auml;ndebud mitt ibland er? Den som vill h&aring;lla sig till Gud leds till en rak [och s&auml;ker] v&auml;g.';
$t[102] = 'Troende! Frukta Gud s&aring; som det &aring;ligger er att frukta Honom och underkasta er Hans vilja, innan d&ouml;den &ouml;verraskar er!';
$t[103] = 'Och grip alla med ett fast grepp om Guds r&auml;ddningslina och l&aring;t er inte splittras! Minns Guds v&auml;lg&auml;rningar mot er: fiendskap hade r&aring;tt mellan er, men Han l&auml;t [viljan till] f&ouml;rsoning tr&auml;nga in i era hj&auml;rtan och med Hans v&auml;lsignelse blev ni br&ouml;der. Ja, Han r&auml;ddade er fr&aring;n randen av en flammande avgrund! S&aring; klarg&ouml;r Gud Sina budskap f&ouml;r er f&ouml;r att ni skall f&aring; v&auml;gledning';
$t[104] = 'och bli till en gemenskap av m&auml;nniskor som uppmanar varandra att g&ouml;ra gott och anbefaller det som &auml;r r&auml;tt och f&ouml;rbjuder det som &auml;r or&auml;tt. Dem skall det g&aring; v&auml;l i h&auml;nder.';
$t[105] = 'F&ouml;lj inte dem som splittrades [i sekter] efter att ha n&aring;tts av de klara vittnesb&ouml;rden [om sanningen]. De har ett h&aring;rt straff att v&auml;nta';
$t[106] = 'den Dag, d&aring; [n&aring;gras] ansikten skall str&aring;la av ljus och [andras] skall m&ouml;rkna. Och till dem vilkas ansikten har m&ouml;rknat [skall s&auml;gas:] &quot;Ni f&ouml;rnekade ju tron efter att ha antagit den? Smaka d&aring; straffet f&ouml;r er f&ouml;rnekelse!&quot;';
$t[107] = 'Men de vilkas ansikten str&aring;lar av ljus skall inneslutas i Guds n&aring;d och skall s&aring; f&ouml;rbli till evig tid.';
$t[108] = 'Detta &auml;r Guds budskap, som Vi l&aring;ter framf&ouml;ra till dig [Muhammad] med sanningen - Gud vill ingen i Sin skapelse n&aring;got ont.';
$t[109] = 'Gud tillh&ouml;r allt det som himlarna rymmer och det som jorden b&auml;r. Och allt g&aring;r [sist och slutligen] tillbaka till Gud.';
$t[110] = 'NI UTG&Ouml;R det b&auml;sta samfund som f&aring;tt uppst&aring; [bland och] f&ouml;r m&auml;nniskorna: ni anbefaller det som &auml;r r&auml;tt och f&ouml;rbjuder det som &auml;r or&auml;tt och ni tror p&aring; Gud. Om efterf&ouml;ljarna av &auml;ldre tiders uppenbarelser hade trott [p&aring; denna], skulle det helt visst ha varit b&auml;st f&ouml;r dem. Fast&auml;n det finns bland dem de som tror, har de flesta av dem f&ouml;rh&auml;rdat sig i trots och olydnad.';
$t[111] = 'De kan inte v&aring;lla er mer &auml;n obetydlig skada och om de ger sig i strid med er, kommer de att v&auml;nda ryggen till [och fly] och till sist skall de st&aring; helt utan hj&auml;lp.';
$t[112] = 'Var de &auml;n p&aring;tr&auml;ffas skall de utst&aring; skam och van&auml;ra, om de inte [s&ouml;ker skydd i] f&ouml;rbundet med Gud och [&aring;teruppr&auml;ttar] banden till m&auml;nniskorna; de har &aring;dragit sig Guds vrede och st&ouml;rtats i djupt el&auml;nde, eftersom de framh&auml;rdade i att f&ouml;rneka Guds budskap och d&ouml;dade profeterna mot all r&auml;tt, trotsade st&auml;ndigt Gud och &ouml;vertr&auml;dde [Hans bud].';
$t[113] = 'Alla &auml;r inte av samma sort. Bland efterf&ouml;ljarna av &auml;ldre tiders uppenbarelser finns [&auml;ven] redbara m&auml;nniskor, som under nattens timmar l&auml;ser h&ouml;gt ur Guds budskap och faller ned p&aring; sina ansikten [i tillbedjan].';
$t[114] = 'De tror p&aring; Gud och p&aring; den Yttersta dagen och de anbefaller det som &auml;r r&auml;tt och f&ouml;rbjuder det som &auml;r or&auml;tt och de t&auml;vlar om att g&ouml;ra gott. De h&ouml;r till de r&auml;ttf&auml;rdiga';
$t[115] = 'och vad gott de g&ouml;r skall inte fr&aring;nk&auml;nnas dem; Gud vet vilka de &auml;r som fruktar Honom.';
$t[116] = 'De som framh&auml;rdar i att f&ouml;rneka sanningen - ingenting av vad de &auml;ger p&aring; jorden skall ge dem skydd mot Gud och deras s&ouml;ner skall inte [kunna f&ouml;rsvara dem]. Deras arvedel &auml;r Elden och d&auml;r skall de f&ouml;rbli till evig tid.';
$t[117] = 'Vad [m&auml;nniskor] ger ut f&ouml;r [ting] som h&ouml;r till detta liv kan j&auml;mf&ouml;ras med de or&auml;ttf&auml;rdigas gr&ouml;da som f&ouml;rh&auml;rjas av en frostig vind. Gud har inte gjort dem or&auml;tt - nej, [med sina handlingar] g&ouml;r de or&auml;tt mot sig sj&auml;lva.';
$t[118] = 'TROENDE! S&ouml;k inte f&ouml;rtrogna v&auml;nner bland dem som st&aring;r utanf&ouml;r er [gemenskap]! De sparar ingen m&ouml;da f&ouml;r att s&aring; f&ouml;rvirring i era led och &ouml;nskar att ni skall pl&aring;gas av olyckor och el&auml;nde. Hatet fl&ouml;dar redan &ouml;ver deras l&auml;ppar, men vad de b&auml;r i sitt inre &auml;r l&aring;ngt v&auml;rre. Vi har l&aring;tit er se tecknen - [drag slutsatsen] om ni har f&ouml;rst&aring;nd.';
$t[119] = 'Det &auml;r ni som visar dem tillgivenhet; de hyser d&auml;remot inga varma k&auml;nslor f&ouml;r er, fast&auml;n ni tror p&aring; uppenbarelsen hel och h&aring;llen. N&auml;r de m&ouml;ter er s&auml;ger de: &quot;Vi tror.&quot; Men n&auml;r de &auml;r ensamma, biter de sig i fingertopparna i ursinne. S&auml;g: &quot;M&aring;tte ni g&aring; under i ert ursinne! Gud vet vad som r&ouml;r sig i m&auml;nniskans innersta.&quot;';
$t[120] = 'Om det g&aring;r er v&auml;l blir de besvikna, och de gl&auml;der sig n&auml;r ni drabbas av en motg&aring;ng. Men om ni b&auml;r [allt detta] med j&auml;mnmod och fruktar Gud, kan deras onda anslag inte i n&aring;got avseende skada er. Gud har full uppsikt &ouml;ver [dem och] deras f&ouml;retag.';
$t[121] = 'MINNS [Muhammad] att du en tidig morgon l&auml;mnade ditt hem f&ouml;r att gruppera de troende till strid; Gud h&ouml;r allt, vet allt.';
$t[122] = 'Tv&aring; grupper bland dina m&auml;n var n&auml;ra att f&ouml;rlora modet, trots att de stod under Guds beskydd - det &auml;r till Gud som de troende m&aring;ste lita.';
$t[123] = 'Och Gud sk&auml;nkte er segern vid Badr, d&aring; ni var en oansenlig skara. Frukta d&auml;rf&ouml;r Gud - kanske skall ni tacka [Honom].';
$t[124] = '[Och minns] dina ord till de troende: &quot;&Auml;r det inte nog f&ouml;r er [att veta] att Gud skall l&aring;ta tretusen &auml;nglar stiga ned och f&ouml;rst&auml;rka era led?';
$t[125] = 'Ja, om ni h&aring;ller st&aring;nd och fruktar Gud och om [fienden] i denna stund g&aring;r till angrepp skall er Herre s&auml;nda femtusen &auml;nglar som stormar fram till er hj&auml;lp.&quot;';
$t[126] = 'Gud l&auml;t detta [s&auml;gas] enbart f&ouml;r att inge er nytt hopp och stilla oron i era hj&auml;rtan - segern kan inte komma fr&aring;n n&aring;gon annan &auml;n Gud, den Allsm&auml;ktige, den Vise -';
$t[127] = '[och] f&ouml;r att l&aring;ta n&aring;gra av sanningens f&ouml;rnekare smaka d&ouml;den eller nederlagets f&ouml;r&ouml;dmjukelse och se sina f&ouml;rhoppningar grusas.';
$t[128] = 'Det ankommer inte p&aring; dig [Muhammad] att best&auml;mma om [Gud] skall ta emot deras &aring;nger eller straffa dem - de &auml;r or&auml;ttf&auml;rdiga m&auml;nniskor.';
$t[129] = 'Allt det som himlarna rymmer och det som jorden b&auml;r tillh&ouml;r Gud. Han f&ouml;rl&aring;ter den Han vill och Han straffar den Han vill. Gud &auml;r st&auml;ndigt f&ouml;rl&aring;tande, barmh&auml;rtig.';
$t[130] = 'TROENDE! Livn&auml;r er inte p&aring; ocker som f&ouml;rdubblar och m&aring;ngdubblar [vinsten]. Frukta Gud - kanske skall det g&aring; er v&auml;l i h&auml;nder.';
$t[131] = 'Och frukta Elden, som &auml;r beredd att ta emot dem som f&ouml;rnekar sanningen.';
$t[132] = 'Och lyd Gud och S&auml;ndebudet - kanske erfar ni [Hans] barmh&auml;rtighet.';
$t[133] = 'Och t&auml;vla med varandra om er Herres f&ouml;rl&aring;telse och ett paradis, lika vidstr&auml;ckt som himlarna och jorden, som st&aring;r berett att ta emot de gudfruktiga,';
$t[134] = 'de som ger &aring;t andra s&aring;v&auml;l i v&auml;lf&auml;rdens dagar som i tider av brist och som l&auml;gger band p&aring; sin vrede och f&ouml;rl&aring;ter sina medm&auml;nniskor - Gud &auml;lskar dem som g&ouml;r det goda och det r&auml;tta -';
$t[135] = 'de som, om de beg&aring;r en skaml&ouml;s handling eller [p&aring; annat s&auml;tt] tillfogat sig sj&auml;lva or&auml;tt, minns Gud och ber Honom om f&ouml;rl&aring;telse f&ouml;r sina synder - vem kan f&ouml;rl&aring;ta synderna utom Gud? - och som inte forts&auml;tter att beg&aring; s&aring;dana handlingar mot b&auml;ttre vetande.';
$t[136] = 'Den l&ouml;n [som v&auml;ntar] dem skall vara deras Herres f&ouml;rl&aring;telse och lustg&aring;rdar, vattnade av b&auml;ckar, d&auml;r de skall f&ouml;rbli till evig tid - en h&auml;rlig bel&ouml;ning f&ouml;r dem som arbetat och str&auml;vat!';
$t[137] = 'F&Ouml;RE ER tid har [andra] seder och levnadsm&ouml;nster varit f&ouml;rh&auml;rskande [och i sin tur avl&ouml;st dem som tidigare r&aring;dde]. G&aring; d&auml;rf&ouml;r ut i v&auml;rlden och se [sp&aring;ren som visar] hur slutet blev f&ouml;r dem som p&aring;stod att sanningen &auml;r l&ouml;gn.';
$t[138] = 'Detta &auml;r en klar fingervisning till alla m&auml;nniskor och en v&auml;gledning och en varning f&ouml;r de gudfruktiga.';
$t[139] = 'Tappa inte modet och gl&ouml;m er sorg! St&aring;r ni fasta i tron kommer ni att vinna &ouml;verhanden.';
$t[140] = 'Om ett h&aring;rt slag har drabbat er, har [era fiender avgudadyrkarna] ocks&aring; drabbats av ett h&aring;rt slag. Vi l&aring;ter m&auml;nniskorna, de ena efter de andra, uppleva s&aring;dana [svarta] dagar f&ouml;r att de troende skall f&aring; ge Gud bevis [p&aring; sin tro] och f&ouml;r att Han skall utse dem bland er som skall f&aring; vittna som martyrer [f&ouml;r sin tro] - Gud &auml;r ingalunda de or&auml;ttf&auml;rdigas v&auml;n -';
$t[141] = 'och f&ouml;r att s&auml;tta de troende p&aring; prov och l&aring;ta f&ouml;rnekarna av sanningen g&aring; under.';
$t[142] = 'Tror ni att ni f&aring;r stiga in i paradiset utan att [ha visat] Gud vilka av er som vill k&auml;mpa [f&ouml;r Hans sak] och som kan h&aring;lla ut [i farans stund]?';
$t[143] = 'Innan ni hade m&ouml;tt d&ouml;den ville ni d&ouml; [f&ouml;r er tro]; nu har ni m&ouml;tt den ansikte mot ansikte.';
$t[144] = 'MUHAMMAD &auml;r ingenting mer &auml;n ett s&auml;ndebud och [andra] s&auml;ndebud har kommit och g&aring;tt f&ouml;re honom. Om han d&ouml;r eller stupar i strid, kommer ni d&aring; att g&ouml;ra en helomv&auml;ndning? Den som v&auml;nder helt om v&aring;llar inte Gud n&aring;gon skada. Men Gud skall bel&ouml;na dem som visar tacksamhet.';
$t[145] = 'Ingen m&auml;nniska f&aring;r d&ouml; annat &auml;n i enlighet med Guds vilja och vid en fastst&auml;lld tidpunkt. Den som vill l&ouml;nas med detta livets goda, honom skall Vi ge n&aring;got av detta; och den som &ouml;nskar l&ouml;nas med det eviga livets goda, honom skall Vi ge n&aring;got av detta. Och Vi skall bel&ouml;na dem som visar tacksamhet.';
$t[146] = 'Hur m&aring;nga profeter har inte m&aring;st k&auml;mpa [f&ouml;r tron], f&ouml;ljda av skaror av m&auml;n som vigt sina liv &aring;t Gud. De f&auml;llde inte modet inf&ouml;r de [pr&ouml;vningar] de m&aring;ste utst&aring; f&ouml;r Guds sak; de sviktade inte och gav inte vika [f&ouml;r fienden] - Gud &auml;lskar dem som h&aring;ller st&aring;nd.';
$t[147] = 'Och [n&auml;r de bad] sade de bara: &quot;Herre, f&ouml;rl&aring;t oss v&aring;ra synder och om vi har g&aring;tt till &ouml;verdrift [eller brustit] i uppfyllandet av v&aring;r plikt! G&ouml;r oss stadiga p&aring; foten och l&aring;t oss segra &ouml;ver dessa f&ouml;rnekare av sanningen!&quot;';
$t[148] = 'Och Gud gav dem som l&ouml;n [b&aring;de] denna v&auml;rldens goda och det h&ouml;gsta goda, ett evigt liv - Gud &auml;lskar dem som g&ouml;r det goda och det r&auml;tta.';
$t[149] = 'TROENDE! Om ni g&aring;r sanningens f&ouml;rnekare till m&ouml;tes kommer de att f&ouml;rm&aring; er att v&auml;nda om [och &ouml;verge tron] och d&aring; st&aring;r ni som f&ouml;rlorare.';
$t[150] = 'Men Gud &auml;r er Beskyddare och Han &auml;r den b&auml;ste Hj&auml;lparen.';
$t[151] = 'Vi skall g&ouml;ra s&aring; att [blodet i] f&ouml;rnekarnas &aring;dror isas av skr&auml;ck [n&auml;r de t&auml;nker p&aring;] att de [v&aring;gade] s&auml;tta medhj&auml;lpare vid Guds sida utan att Han n&aring;gonsin gett [dem n&aring;got] bemyndigande. Elden skall bli deras tillflykt - denna syndarnas fruktansv&auml;rda boning!';
$t[152] = 'GUD h&ouml;ll sannerligen Sitt l&ouml;fte till er, d&aring; ni med Hans vilja var n&auml;ra att i grund sl&aring; [era fiender], till dess modet svek er och ni blev oense om inneb&ouml;rden av den order [ni hade f&aring;tt] och br&ouml;t mot den, d&aring; Han redan hade l&aring;tit [segern] h&auml;gra f&ouml;r er syn. Det fanns de bland er som inte hade annat &auml;n denna v&auml;rldens [goda] f&ouml;r &ouml;gonen och det fanns de som s&aring;g mot evigheten. D&auml;refter l&auml;t Han er dra er undan [fienden] f&ouml;r att s&auml;tta er p&aring; prov. Men Han har nu utpl&aring;nat er synd - ja, Guds n&aring;d fl&ouml;dar &ouml;ver de troende.';
$t[153] = '[Minns] hur ni flydde uppf&ouml;r [bergets sida] utan att se er om, fast&auml;n S&auml;ndebudet l&aring;ngt bakom er ropade er tillbaka. Och Gud l&auml;t er k&auml;nna sorg och gr&auml;melse [i geng&auml;ld] f&ouml;r den sorg och gr&auml;melse [ni v&aring;llade Profeten] och f&ouml;r att [l&auml;ra er att] inte s&ouml;rja &ouml;ver det [byte] som ni gick miste om, inte [ens] &ouml;ver det [nederlag] som ni led. Gud &auml;r v&auml;l underr&auml;ttad om vad ni g&ouml;r.';
$t[154] = 'Och efter denna bittra erfarenhet l&auml;t Han ett stort lugn s&auml;nka sig &ouml;ver er, s&aring; att n&aring;gra bland er fick vila, medan andra som fr&auml;mst bekymrade sig om sitt eget v&auml;l t&auml;nkte oriktiga [och h&auml;diska] tankar om Gud, [tankar som hade sin rot] i avgudadyrkans okunnighet, och de fr&aring;gade [sig]: &quot;Hade vi &ouml;ver huvud taget n&aring;gon del i beslutet?&quot; S&auml;g [Muhammad]: &quot;Det var till alla delar Guds beslut.&quot; De g&ouml;mmer n&aring;got i sitt inre som de inte vill visa dig [n&auml;r] de s&auml;ger: &quot;Om vi &ouml;ver huvud taget hade haft n&aring;gon del i beslutet skulle vi inte h&auml;r ha lidit dessa f&ouml;rluster.&quot; S&auml;g: &quot;&Auml;ven om ni hade stannat hemma, skulle de vilkas d&ouml;d stod skriven ha begett sig till den plats d&auml;r de skulle falla.&quot; [Allt detta skedde] d&auml;rf&ouml;r att Gud ville pr&ouml;va vad ni bar inom er och rensa ut orenheterna ur era hj&auml;rtan. - Gud vet vad som r&ouml;r sig i m&auml;nniskans innersta.';
$t[155] = 'De bland er som gjorde helt om den dag de tv&aring; h&auml;rarna m&ouml;ttes, kunde f&ouml;rledas till detta av Dj&auml;vulen enbart p&aring; grund av [ett fel] som de hade beg&aring;tt. Men Gud har nu utpl&aring;nat deras synd - Gud &auml;r mild och &ouml;verseende.';
$t[156] = 'Troende! S&auml;g inte som f&ouml;rnekarna av sanningen s&auml;ger om sina br&ouml;der som [d&ouml;tt] under resa i fj&auml;rran land eller [fallit] i strid: &quot;Om de hade blivit kvar hos oss, skulle de inte ha d&ouml;tt eller d&ouml;dats.&quot; - Gud [l&aring;ter dem t&auml;nka dessa tankar], s&aring; att deras hj&auml;rtan skulle k&auml;nna &ouml;kad sorg och gr&auml;melse, eftersom det &auml;r Gud som sk&auml;nker liv och sk&auml;nker d&ouml;d. Gud ser vad ni g&ouml;r.';
$t[157] = 'Och om ni stupar [i kampen] f&ouml;r Guds sak eller d&ouml;r, &auml;r Guds f&ouml;rl&aring;telse och Hans barmh&auml;rtighet helt visst b&auml;ttre &auml;n allt vad m&auml;nniskorna kan l&auml;gga p&aring; h&ouml;g;';
$t[158] = 'vare sig ni d&ouml;r eller d&ouml;das, &auml;r det till Gud ni skall samlas &aring;ter.';
$t[159] = 'I Sin barmh&auml;rtighet l&auml;t Gud dig visa dem mildhet; om du hade varit str&auml;ng och h&aring;rd och tillslutit ditt hj&auml;rta [f&ouml;r dem], skulle de helt s&auml;kert ha dragit sig ifr&aring;n dig. Gl&ouml;m d&auml;rf&ouml;r nu [deras fel] och be Gud f&ouml;rl&aring;ta dem. Inh&auml;mta deras r&aring;d i de angel&auml;genheter [som &auml;r av vikt] och n&auml;r du v&auml;l har fattat ditt beslut, s&auml;tt d&aring; din lit till Gud; Gud &auml;lskar dem som s&auml;tter sin lit till Honom.';
$t[160] = 'Om Gud hj&auml;lper er kan ingen besegra er; men om Gud &ouml;verger er, vem kan sedan hj&auml;lpa er? F&ouml;rmana d&auml;rf&ouml;r de troende att s&auml;tta all sin lit till Gud!';
$t[161] = 'DET &Auml;R en orimlig tanke att en [Guds] profet skulle bedra [sina medm&auml;nniskor]; bedragaren skall p&aring; Uppst&aring;ndelsens dag stiga fram med sitt bedr&auml;geri i handen och d&aring; skall var och en fullt ut erh&aring;lla den l&ouml;n han har f&ouml;rtj&auml;nat och ingen lida or&auml;tt.';
$t[162] = 'Kan den vars str&auml;van det &auml;r att vinna Guds v&auml;lbehag j&auml;mf&ouml;ras med den som &aring;drar sig Guds vrede och vars tillflykt skall bli helvetet - detta j&auml;mmerliga m&aring;l';
$t[163] = '[Nej,] hos Gud har de var och en sin [givna] plats i en rangordning [av salighet och f&ouml;rd&ouml;melse]. Och Gud ser vad de g&ouml;r.';
$t[164] = 'Gud visade sannerligen de troende [en stor n&aring;d], d&aring; Han l&auml;t ett s&auml;ndebud komma till dem, [en man] ur deras egna led, f&ouml;r att framf&ouml;ra Hans budskap till dem och rena dem [fr&aring;n synd] och undervisa dem i Skriften och [profeternas] visdom; innan dess var de helt uppenbart p&aring; or&auml;tt v&auml;g.';
$t[165] = 'OCH N&Auml;R ni nu har drabbats av ett h&aring;rt slag, efter att ha tillfogat [era fiender] ett tv&aring; g&aring;nger h&aring;rdare [slag], fr&aring;gar ni kanske: &quot;Hur har detta kunnat ske?&quot; S&auml;g: &quot;Det som har skett har sin rot inom er sj&auml;lva.&quot; Gud har allt i sin makt.';
$t[166] = 'Det som ni m&aring;ste utst&aring; den dag d&aring; de tv&aring; h&auml;rarna m&ouml;ttes, skedde enligt Guds vilja och f&ouml;r att det skulle st&aring; klart vilka de sanna troende var,';
$t[167] = 'och st&aring; klart vilka de var som hycklade tro. [N&auml;r] de sistn&auml;mnda fick uppmaningen: &quot;Kom och k&auml;mpa f&ouml;r Guds sak!&quot; eller &quot;F&ouml;rsvara er!&quot;, svarade de: &quot;Bara vi s&auml;kert visste [att det skall bli] strid, skulle vi g&aring; med er.&quot; Den dagen var de otron n&auml;rmare &auml;n tron, n&auml;r de yttrade ord som inte kom fr&aring;n hj&auml;rtat, fast&auml;n Gud v&auml;l visste vad de ville d&ouml;lja -';
$t[168] = 'de som sade om sina br&ouml;der, d&auml;r de satt [i trygghet]: &quot;Om de hade lyssnat till oss, skulle de inte ha d&ouml;dats.&quot; S&auml;g: &quot;H&aring;ll d&aring; d&ouml;den borta fr&aring;n er sj&auml;lva, om det ni s&auml;ger &auml;r sant.&quot;';
$t[169] = 'Och betrakta inte dem som fallit i kampen f&ouml;r Guds sak som d&ouml;da. Nej, de lever [och] Gud s&ouml;rjer f&ouml;r dem.';
$t[170] = 'De gl&auml;ds &aring;t vad Gud i Sin n&aring;d har sk&auml;nkt dem och de gl&auml;ds &ouml;ver [budskapet] att de som [&auml;nnu] inte har f&ouml;ljt dem utan blivit kvar inte skall k&auml;nna fruktan och att ingen sorg skall tynga dem.';
$t[171] = 'De gl&auml;ds &ouml;ver Guds v&auml;lg&auml;rningar och Hans n&aring;d och [l&ouml;ftet] att Guds bel&ouml;ning till de troende inte skall utebli,';
$t[172] = '[bel&ouml;ningen] till dem som besvarade Guds och S&auml;ndebudets kallelse efter att ha drabbats av det sv&aring;ra slaget. De bland dem som gjorde det goda och det r&auml;tta och fruktade Gud har en rik bel&ouml;ning [att v&auml;nta].';
$t[173] = 'De som k&auml;nde sin tro v&auml;xa, n&auml;r de varnades att [styrkor] hade dragits samman mot dem och att de borde vara p&aring; sin vakt, och svarade: &quot;Vi beh&ouml;ver ingen annan hj&auml;lpare &auml;n Gud. Var finns en m&auml;ktigare beskyddare?&quot;';
$t[174] = 'Och de &aring;terv&auml;nde [styrkta av] Guds v&auml;lsignelse och Hans n&aring;d; det onda hade inte r&ouml;rt vid dem. Deras m&aring;l var att vinna Guds v&auml;lbehag och Guds n&aring;d &auml;r en outsinlig k&auml;lla.';
$t[175] = 'Dj&auml;vulen [ingav dem att varna er]; han vill injaga [i er] fruktan f&ouml;r sina f&ouml;rbundna. Ni skall inte frukta dem - frukta Mig, om ni &auml;r troende!';
$t[176] = 'S&ouml;rj inte &ouml;ver dem som t&auml;vlar om att f&ouml;rneka sanningen; de kan p&aring; intet s&auml;tt skada Gud. Det &auml;r Guds vilja att inte ge dem del av det eviga livets goda, ja, ett pl&aring;gsamt straff v&auml;ntar dem.';
$t[177] = 'De som har bytt bort tron mot f&ouml;rnekelse kan p&aring; intet s&auml;tt skada Gud och ett sv&aring;rt lidande v&auml;ntar dem.';
$t[178] = 'L&aring;t inte f&ouml;rnekarna av sanningen tro att den frist Vi beviljar dem &auml;r till deras f&ouml;rdel. Vi ger dem denna frist s&aring; att de kan hopa synd p&aring; synd; ett f&ouml;rnedrande straff v&auml;ntar dem.';
$t[179] = 'Det &auml;r ot&auml;nkbart att Gud skulle l&aring;ta de troende [leva] i de former som ni [f&ouml;rnekare] lever, l&auml;ngre &auml;n till dess Han skilt mellan de onda och de goda. Men Gud l&aring;ter er inte f&aring; insikt i det som &auml;r dolt f&ouml;r m&auml;nniskor; [f&ouml;r detta] utser Gud den Han vill bland Sina s&auml;ndebud. Tro d&auml;rf&ouml;r p&aring; Gud och Hans s&auml;ndebud; om ni tror [p&aring; Gud] och fruktar Honom, v&auml;ntar er en rik bel&ouml;ning.';
$t[180] = 'DE SOM girigt beh&aring;ller f&ouml;r sig sj&auml;lva det som Gud i Sin n&aring;d har sk&auml;nkt dem skall inte tro att det blir till deras b&auml;sta. Nej, det kommer att vara ett ont f&ouml;r dem. P&aring; Uppst&aring;ndelsens dag skall de b&auml;ra det som de s&aring; girigt h&ouml;ll fast vid h&auml;ngande om halsen. - Arvet efter himlarna och jorden skall tillfalla Gud. Och Gud &auml;r v&auml;l underr&auml;ttad om vad ni g&ouml;r.';
$t[181] = 'Gud har h&ouml;rt dem som s&auml;ger: &quot;Gud &auml;r fattig - vi &auml;r rika.&quot; Vi skall teckna ned vad de s&auml;ger, liksom [Vi tecknade ned] deras r&auml;ttsstridiga dr&aring;p p&aring; profeterna, och [p&aring; Uppst&aring;ndelsens dag] skall Vi s&auml;ga till dem: &quot;Pr&ouml;va eldens straff -';
$t[182] = 'f&ouml;ljden av det som era h&auml;nder har s&auml;nt framf&ouml;r er [till domen] - Gud l&aring;ter inte Sina tj&auml;nare lida or&auml;tt!&quot;';
$t[183] = 'Och de som p&aring;st&aring;r: &quot;Gud har &aring;lagt oss att inte tro p&aring; en profet som inte [f&ouml;reskriver] br&auml;nnoffer&quot; - s&auml;g [till dem]: &quot;F&ouml;re mig har profeter kommit till er med klara vittnesb&ouml;rd och med det ni s&auml;ger. Varf&ouml;r d&ouml;dade ni dem d&aring;, om det som ni [nu] s&auml;ger &auml;r sanning?&quot;';
$t[184] = 'Och om de kallar dig l&ouml;gnare, har profeter f&ouml;re dig kallats l&ouml;gnare n&auml;r de kom med klara vittnesb&ouml;rd och upptecknad visdom och uppenbarelser fyllda av [Guds] ljus.';
$t[185] = 'Varje m&auml;nniska skall smaka d&ouml;den. Men f&ouml;rst p&aring; Uppst&aring;ndelsens dag skall ni f&aring; er fulla l&ouml;n. Den som d&aring; rycks bort fr&aring;n Elden och f&ouml;rs till paradiset har sannerligen vunnit en stor seger. - Livet i denna v&auml;rld [sk&auml;nker] bara bedr&auml;glig gl&auml;dje.';
$t[186] = 'Ni skall helt visst f&aring; utst&aring; pr&ouml;vningar i vad avser er egendom och er sj&auml;lva, och ni skall f&aring; h&ouml;ra m&aring;nga skymford fr&aring;n dem som f&aring;tt ta emot uppenbarelser f&ouml;re er och fr&aring;n dem som s&auml;tter medhj&auml;lpare vid Guds sida. Men om ni b&auml;r allt med j&auml;mnmod och fruktar Gud [har ni best&aring;tt provet]; detta [kr&auml;ver] fasthet i f&ouml;resatsen!';
$t[187] = 'N&Auml;R GUD sl&ouml;t f&ouml;rbund med dem som fick ta emot uppenbarelsen tog Han l&ouml;fte av dem [med orden]: &quot;D&ouml;lj inte detta utan kung&ouml;r det f&ouml;r m&auml;nniskorna!&quot; Men de kastade l&ouml;ftet bakom sig och s&aring;lde det f&ouml;r en usel slant - ja, ynklig &auml;r deras vinst av denna byteshandel!';
$t[188] = 'Tro inte att de som gl&auml;der sig &aring;t det som de [anser sig ha] utr&auml;ttat men ocks&aring; g&auml;rna vill f&aring; ber&ouml;m f&ouml;r vad de inte har gjort - tro inte att straffet f&ouml;r dem skall utebli; nej, ett sv&aring;rt lidande v&auml;ntar dem.';
$t[189] = 'GUDS &Auml;R herrav&auml;ldet &ouml;ver himlarna och jorden och Gud har allt i Sin makt.';
$t[190] = 'I skapelsen av himlarna och jorden och i v&auml;xlingen mellan natt och dag ligger helt visst budskap till dem som vill anv&auml;nda sitt f&ouml;rst&aring;nd;';
$t[191] = 'de som minns Gud n&auml;r de st&aring;r, n&auml;r de sitter och n&auml;r de l&auml;gger sig till vila, och som, n&auml;r de begrundar himlarnas och jordens skapelse [ber med dessa ord]: &quot;Herre! Du har inte skapat [allt] detta i blindo. Stor &auml;r Du i Din h&auml;rlighet! F&ouml;rskona oss fr&aring;n Eldens straff!';
$t[192] = 'Herre, &ouml;ver den som Du utl&auml;mnar &aring;t Elden har Du helt visst [f&ouml;rut] hopat skam och van&auml;ra. Och de or&auml;ttf&auml;rdiga skall inte finna n&aring;gon hj&auml;lpare.';
$t[193] = 'Herre! Vi har h&ouml;rt r&ouml;sten som kallar [oss] till tron: - \'Tro p&aring; er Herre!\' - och vi har blivit troende. Herre! F&ouml;rl&aring;t oss v&aring;ra synder och utpl&aring;na v&aring;ra d&aring;liga handlingar och l&aring;t oss d&ouml; med de fromma, de som g&ouml;r det goda och det r&auml;tta!';
$t[194] = 'Sk&auml;nk oss, Herre, det som Du har lovat oss genom Dina s&auml;ndebud och l&aring;t oss inte st&aring; d&auml;r med skammen p&aring; Uppst&aring;ndelsens dag! Aldrig sviker Du Ditt l&ouml;fte!&quot;';
$t[195] = 'Och Gud svarar dem: &quot;Jag skall inte l&aring;ta n&aring;gon av er som arbetar och str&auml;var, vare sig man eller kvinna, se sin str&auml;van g&aring; f&ouml;rlorad - ni &auml;r [alla] av samma rot [och skall l&ouml;nas p&aring; samma s&auml;tt]. De som &ouml;vergav sina hem f&ouml;r att fly undan det onda, eller drevs bort d&auml;rifr&aring;n och led annan of&ouml;rr&auml;tt f&ouml;r Min skull och de som k&auml;mpade [f&ouml;r Min sak] och stupade, f&ouml;r dem alla skall Jag utpl&aring;na deras d&aring;liga handlingar och f&ouml;ra dem till lustg&aring;rdar, vattnade av b&auml;ckar - en bel&ouml;ning fr&aring;n Gud sj&auml;lv; ja, den b&auml;sta bel&ouml;ningen v&auml;ntar hos Gud.&quot;';
$t[196] = 'L&Aring;T DIG inte vilseledas av att de som f&ouml;rnekar sanningen kan p&aring; jorden v&auml;ltra sig i n&ouml;jen och njutningar.';
$t[197] = 'Deras gl&auml;dje &auml;r kortvarig; d&auml;refter skall helvetet bli deras tillflykt - ett el&auml;ndigt vilorum!';
$t[198] = 'Men p&aring; de gudfruktiga v&auml;ntar lustg&aring;rdar, vattnade av b&auml;ckar, d&auml;r de skall f&ouml;rbli till evig tid som Guds &auml;rade g&auml;ster. Och det b&auml;sta f&ouml;r de fromma &auml;r det [som v&auml;ntar] hos Gud.';
$t[199] = 'Bland mottagarna av &auml;ldre tiders uppenbarelser finns helt visst de som tror p&aring; Gud och p&aring; det som har uppenbarats f&ouml;r er och det som uppenbarades f&ouml;r dem; de &ouml;dmjukar sig inf&ouml;r Gud och de s&auml;ljer inte Guds budskap f&ouml;r en ynklig slant. De skall f&aring; sin bel&ouml;ning av sin Herre - Gud &auml;r snar att kalla till r&auml;kenskap.';
$t[200] = 'Troende! Uth&auml;rda allt - ja, t&auml;vla om att ge prov p&aring; t&aring;lamod och uth&aring;llighet och var alltid beredda att k&auml;mpa [f&ouml;r tron] och frukta Gud! Kanske skall det g&aring; er v&auml;l i h&auml;nder.';
?>