$t[9] = 'Om du fr&aring;gar [m&auml;nniskorna]: &quot;Vem har skapat himlarna och jorden?&quot; - svarar de helt s&auml;kert: &quot;Den Allsm&auml;ktige, den Allvetande har skapat dem.&quot;';
$t[10] = '[Ja, det &auml;r] Han som har gjort jorden till er vagga och som p&aring; den har stakat ut v&auml;gar &aring;t er, s&aring; att ni kan ta er fram [p&aring; era f&auml;rder].';
$t[11] = '[Och det &auml;r Han] som l&aring;ter regn falla fr&aring;n skyn i r&auml;tta m&auml;ngder, och d&auml;rmed v&auml;cker Vi till liv d&ouml;tt land. S&aring; skall ni ocks&aring; uppv&auml;ckas [fr&aring;n de d&ouml;da till nytt liv].';
$t[12] = '[Och det &auml;r Han] som har skapat alla motsatspar, och [Han] som har gett er de skepp och de riddjur som ni f&auml;rdas med,';
$t[13] = 'f&ouml;r att ni, efter att ha tagit dem i er tj&auml;nst och stigit ombord eller suttit upp p&aring; dem, skall minnas er Herres godhet och s&auml;ga: &quot;Stor &auml;r Han i Sin h&auml;rlighet, som l&aring;ter oss f&ouml;rfoga &ouml;ver dem; sj&auml;lva skulle vi inte ha kunnat ta dem i v&aring;r tj&auml;nst!';
$t[14] = '[L&aring;t oss aldrig gl&ouml;mma att] vi skall v&auml;nda tillbaka till v&aring;r Herre!&quot;';
$t[15] = 'OCH [&Auml;ND&Aring;] vill de g&ouml;ra n&aring;gra av de varelser som Han har skapat till en del av Honom sj&auml;lv! Hur otacksam &auml;r m&auml;nniskan inte!';
$t[16] = 'Skulle Han bland allt det som Han har skapat v&auml;lja d&ouml;ttrar och &aring;t er sk&auml;nka [idel] s&ouml;ner?';
$t[17] = 'Om n&aring;gon av dem f&aring;r h&ouml;ra att ett [barn av det k&ouml;n] som han s&auml;tter i samband med den N&aring;derike [f&ouml;tts &aring;t honom], m&ouml;rknar hans uppsyn och den undertryckta vreden sjuder inom honom.';
$t[18] = '[Och han klagar:] &quot;&Auml;nnu en av dem som v&auml;xer upp omgiven av bj&auml;fs och grannl&aring;t och som har sv&aring;rt att reda sig i en diskussion!';
$t[19] = 'Och de tror att &auml;nglarna, som &auml;r den N&aring;derikes tj&auml;nare, &auml;r kvinnliga v&auml;sen! Har de kanske bevittnat deras skapelse? Nej, deras p&aring;st&aring;ende skall antecknas och de skall st&auml;llas till svars [f&ouml;r det].';
$t[20] = 'De s&auml;ger ocks&aring;: &quot;Hade det varit den N&aring;derikes vilja, skulle vi inte ha &auml;gnat dem v&aring;r dyrkan.&quot; Om detta vet de ingenting - de f&ouml;ljer bara l&ouml;sa antaganden och f&ouml;rmodar [&auml;n det ena, &auml;n det andra].';
$t[21] = 'Eller har Vi f&ouml;re denna [Koran] sk&auml;nkt dem en Skrift [av annat inneh&aring;ll] som de st&ouml;der sig p&aring;?';
$t[22] = 'Nej, de s&auml;ger tv&auml;rtom: &quot;Vi har f&ouml;rst&aring;tt att detta var v&aring;ra f&auml;ders tro, och vi f&ouml;ljer i deras sp&aring;r.&quot;';
$t[23] = 'Och s&aring; har det varit f&ouml;rr; Vi har aldrig s&auml;nt en varnare till ett folk f&ouml;re dig utan att de n&ouml;jeslystna veklingarna bland dem sade: &quot;Vi har f&ouml;rst&aring;tt att detta var v&aring;ra f&auml;ders tro och vi f&ouml;ljer deras exempel.&quot;';
$t[24] = '[Och n&auml;r profeten] sade: &quot;Men om jag ger er b&auml;ttre v&auml;gledning &auml;n den som ni har f&ouml;rst&aring;tt att era f&auml;der f&ouml;ljde?&quot; blev deras svar [alltid]: &quot;Vi avvisar det [budskap] som [ni profeter p&aring;st&aring;r att] ni har anf&ouml;rtrotts!&quot;';
$t[25] = 'Och Vi l&auml;t dem k&auml;nna p&aring; V&aring;r vederg&auml;llning. Se d&auml;r vilket slut de fick som p&aring;stod att sanningen &auml;r l&ouml;gn!';
$t[26] = 'ABRAHAM talade till sin fader och sitt folk och sade: &quot;Jag avsv&auml;r mig allt ansvar f&ouml;r er dyrkan [av falska gudar]!';
$t[27] = '[Jag dyrkar] ingen annan &auml;n Honom som har skapat mig, och Han skall leda mig p&aring; r&auml;tt v&auml;g!&quot;';
$t[28] = 'Och dessa ord yttrade han f&ouml;r att de skulle bevaras bland hans efterkommande, s&aring; att de [som avviker fr&aring;n den rena tron] skall finna v&auml;gen tillbaka.';
$t[29] = 'Ja, dessa [dina samtida, Muhammad,] och deras f&ouml;rf&auml;der har f&aring;tt njuta av det goda i denna v&auml;rld tills sanningen och ett s&auml;ndebud som f&ouml;rklarade den kom till dem.';
$t[30] = 'N&auml;r sanningen nu har n&aring;tt dem, s&auml;ger de: &quot;Detta &auml;r bl&auml;ndande och f&ouml;rh&auml;xande v&auml;ltalighet och vi tror inte p&aring; det!&quot;';
$t[31] = 'Och de s&auml;ger: &quot;Varf&ouml;r har inte denna Koran uppenbarats f&ouml;r en framst&aring;ende man fr&aring;n [en av] de tv&aring; st&auml;derna?&quot; -';
$t[32] = '&Auml;r det de som skall f&ouml;rdela din Herres n&aring;d? Nej, det &auml;r Vi som sk&auml;nker var och en vad han beh&ouml;ver f&ouml;r sitt uppeh&auml;lle i detta liv, och Vi ger f&ouml;rm&aring;ner &aring;t n&aring;gra som h&ouml;jer dem [flera] steg &ouml;ver de andra, s&aring; att den ene kan ta den andre i sin tj&auml;nst - [p&aring; liknande s&auml;tt f&ouml;rdelar Vi V&aring;ra g&aring;vor av andligt slag]. - Din Herres n&aring;d &auml;r mer v&auml;rd &auml;n de [rikedomar] som m&auml;nniskor kan samla.';
$t[33] = 'Och om inte m&auml;nniskorna - [eggade av att se s&aring;dan rikedom] - hade l&ouml;pt fara att urarta till ett samfund [av girighet och ondska], kunde Vi ha gett dem som f&ouml;rnekar den N&aring;derike, bost&auml;der med tak av silver och trappor att stiga uppf&ouml;r [av silver]';
$t[34] = 'och portar [av silver] och divaner att vila p&aring; [av silver];';
$t[35] = 'ja, [Vi kunde till och med ha gett dem allt detta] av guld! Men [om Vi ocks&aring; gav f&ouml;rnekarna detta] skulle det inte inneb&auml;ra mer &auml;n &ouml;gonf&auml;gnad f&ouml;r dem i detta f&ouml;rg&auml;ngliga liv, medan det eviga livet [v&auml;ntar] de gudfruktiga hos din Herre.';
$t[36] = 'Den som &auml;r blind f&ouml;r den N&aring;derikes p&aring;minnelser l&aring;ter Vi f&aring; ett ont v&auml;sen som st&auml;ndig f&ouml;ljeslagare;';
$t[37] = 'och detta v&auml;sen hindrar honom att sl&aring; in p&aring; den v&auml;g [som leder till Gud], fast&auml;n han sj&auml;lv tror sig vara p&aring; r&auml;tt v&auml;g.';
$t[38] = '[Men] d&aring; han [p&aring; Domens dag] tr&auml;der inf&ouml;r Oss, s&auml;ger han [till f&ouml;ljeslagaren]: &quot;Om &auml;nd&aring; avst&aring;ndet mellan mig och dig varit lika l&aring;ngt som mellan platsen d&auml;r solen g&aring;r upp och platsen d&auml;r den g&aring;r ned!&quot; - F&ouml;ljeslagaren var allts&aring; alltigenom ond!';
$t[39] = 'Men det kommer inte att vara er till nytta [och inte heller till tr&ouml;st] att ni, liksom ni har gjort or&auml;tt tillsammans, skall lida straffet tillsammans.';
$t[40] = 'KAN DU f&ouml;rm&aring; de d&ouml;va att h&ouml;ra eller visa de blinda och dem som har g&aring;tt ohj&auml;lpligt vilse, den r&auml;tta v&auml;gen?';
$t[41] = 'Antingen Vi l&aring;ter dig d&ouml; [innan straffet n&aring;r dem - d&aring; skall du veta att] Vi kommer att straffa dem -';
$t[42] = 'eller Vi l&aring;ter dig se det som Vi har varnat dem f&ouml;r, [kan du vara f&ouml;rvissad om att] Vi har dem i V&aring;r makt.';
$t[43] = 'H&aring;ll d&auml;rf&ouml;r fast vid det som Vi har uppenbarat f&ouml;r dig; din v&auml;g &auml;r den raka v&auml;gen!';
$t[44] = 'Genom denna [Skrift] skall ditt och ditt folks namn h&aring;llas i [evig] &auml;ra och [en Dag] skall ni f&aring; redog&ouml;ra [f&ouml;r det bruk ni gjorde av denna g&aring;va].';
$t[45] = 'Och fr&aring;ga V&aring;ra s&auml;ndebud som Vi s&auml;nde f&ouml;re dig, om Vi vid sidan av den N&aring;derike satte upp gudomligheter som de skulle dyrka.';
$t[46] = 'OCH VI s&auml;nde Moses med V&aring;ra budskap till Farao och hans storm&auml;n. Och Moses sade: &quot;Jag &auml;r ett s&auml;ndebud [fr&aring;n] v&auml;rldarnas Herre&quot;.';
$t[47] = 'Men n&auml;r han steg fram inf&ouml;r dem med V&aring;ra tecken och under, gjorde de narr av dem;';
$t[48] = 'likv&auml;l var varje nytt tecken som de fick se m&auml;ktigare &auml;n det f&ouml;reg&aring;ende. Och Vi l&auml;t V&aring;rt straff drabba dem, f&ouml;r att de skulle v&auml;nda tillbaka [till Oss].';
$t[49] = 'Och [varje g&aring;ng straffet drabbade dem] sade de: &quot;Be f&ouml;r oss till din Herre, du trollkarl, enligt det f&ouml;rbund som Han har slutit med dig [att Han befriar oss fr&aring;n detta straff]; d&aring; skall vi l&aring;ta oss ledas p&aring; r&auml;tt v&auml;g.';
$t[50] = 'Men n&auml;r Vi befriade dem fr&aring;n straffet, br&ouml;t de sitt ord.';
$t[51] = 'Och Farao l&auml;t utf&auml;rda ett upprop till sitt folk [och sade]: &quot;Mitt folk! Har jag inte herrav&auml;ldet &ouml;ver Egyptens [land]? Se allt detta flodvatten som flyter fram under min fot! Inser ni inte [att det &auml;r jag som &auml;r er herre]';
$t[52] = 'Jag &auml;r sannerligen f&ouml;rmer &auml;n han, som [bara] &auml;r v&auml;rd f&ouml;rakt och som inte ens kan uttrycka sig klart!';
$t[53] = 'Varf&ouml;r har inte [hans Herre] sk&auml;nkt honom armringar av guld eller l&aring;tit honom &aring;tf&ouml;ljas av &auml;nglar?&quot;';
$t[54] = 'S&aring; visade Farao hur han f&ouml;raktade sitt folk, men de f&ouml;ljde honom - de var ett folk av h&aring;rdnackade syndare.';
$t[55] = 'Och n&auml;r deras trots t&auml;nt V&aring;r vrede l&auml;t Vi V&aring;r h&auml;mnd drabba dem och Vi dr&auml;nkte dem alla';
$t[56] = 'och gjorde dem till ett exempel och [deras &ouml;de till] en l&auml;rdom f&ouml;r efterf&ouml;ljande sl&auml;kten.';
$t[57] = 'N&Auml;R MARIAS son anf&ouml;rs som exempel [p&aring; en gudom vid sidan av Gud], larmar ditt folk [Muhammad]';
$t[58] = 'och s&auml;ger: &quot;&Auml;r inte v&aring;ra gudomligheter f&ouml;rmer &auml;n han?&quot; Men de n&auml;mner honom bara f&ouml;r att f&aring; till st&aring;nd en diskussion. Ja, de &auml;r m&auml;nniskor som finner n&ouml;je i diskussioner [f&ouml;r deras egen skull]!';
$t[59] = 'Han var ingenting annat &auml;n [V&aring;r] tj&auml;nare, som Vi i V&aring;r n&aring;d [kallade till profet] och gjorde till en f&ouml;rebild f&ouml;r Israels barn.';
$t[60] = 'Och om Vi ville - [inv&aring;nare i Mekka!] - kunde Vi l&aring;ta &auml;nglar befolka jorden som era eftertr&auml;dare.';
$t[61] = 'Hans [&aring;terkomst till jorden] skall vara ett tecken att den Yttersta stunden [n&auml;rmar sig]; tvivla d&auml;rf&ouml;r inte p&aring; den. Och [s&auml;g, Muhammad]: &quot;F&ouml;lj mig, detta &auml;r en rak v&auml;g!';
$t[62] = 'Och l&aring;t inte Dj&auml;vulen hejda er; han &auml;r verkligen er svurne fiende!&quot;';
$t[63] = 'OCH N&Auml;R Jesus kom med [tecken och] klara bevis, sade han: &quot;Jag har kommit till er med [uppenbarad] visdom och f&ouml;r att klarg&ouml;ra f&ouml;r er n&aring;gra av de ting som ni tvistar om. Frukta Gud och f&ouml;lj mig!';
$t[64] = 'Gud &auml;r min Herre och er Herre; det &auml;r Honom ni skall dyrka! Detta &auml;r en rak v&auml;g.&quot;';
$t[65] = 'Men de sekter [som s&ouml;ndringen] bland dem [gav upphov till] hade olika meningar [om Jesus]. Olyckliga de syndare som m&aring;ste frukta straffet [som skall drabba dem] en sorgens och pl&aring;gans Dag!';
$t[66] = 'V&Auml;NTAR de [som f&ouml;rnekar sanningen] att den Yttersta stunden skall komma &ouml;ver dem pl&ouml;tsligt n&auml;r de minst anar det [- och &aring;ngerns tid &auml;r ute]';
$t[67] = 'Den Dagen skall de som f&ouml;rut var n&auml;ra v&auml;nner bli varandras fiender, [alla] utom de som fruktade Gud.';
$t[68] = '[Och Gud skall s&auml;ga:] &quot;I dag skall ni, Mina tj&auml;nare, inte k&auml;nna n&aring;gon fruktan och ingen sorg skall tynga er,';
$t[69] = 'ni, som trodde p&aring; V&aring;ra budskap och som underkastade er V&aring;r vilja!';
$t[70] = 'Stig in, ni och era livsledsagare, i paradiset och njut dess gl&auml;dje [i fulla drag]!&quot;';
$t[71] = 'De skall betj&auml;nas med fat och dryckesk&auml;rl av guld, och d&auml;r skall finnas allt det som sj&auml;larna l&auml;ngtar efter och som &auml;r en fr&ouml;jd f&ouml;r &ouml;gat. Och d&auml;r skall ni f&ouml;rbli till evig tid.';
$t[72] = 'Detta &auml;r paradiset, er arvedel [som &auml;r l&ouml;nen] f&ouml;r det ni gjorde [i livet].';
$t[73] = 'D&auml;r skall ni f&aring; smaka frukter i m&auml;ngd [av l&auml;ckraste slag].';
$t[74] = 'Men de obotf&auml;rdiga, trotsiga syndarna skall f&ouml;rbli under helvetetsstraffets [f&ouml;rbannelse]';
$t[75] = 'utan n&aring;gon lindring i pl&aring;gorna, och de skall sjunka ned i djup f&ouml;rtvivlan.';
$t[76] = 'Vi gjorde dem ingen or&auml;tt; de begick or&auml;tt [mot sig sj&auml;lva].';
$t[77] = 'De kommer att ropa till [helvetets v&auml;ktare]: &quot;Du som h&auml;rskar [&ouml;ver oss]! Be till din Herre att Han d&ouml;mer oss till f&ouml;rintelse!&quot; Men hans svar blir: &quot;[Nej,] h&auml;r skall ni f&ouml;rbli!&quot;';
$t[78] = 'VI HAR gett er sanningen, men f&ouml;r de flesta av er &auml;r sanningen f&ouml;rhatlig.';
$t[79] = 'Har de gjort upp en plan [hur de skall fastst&auml;lla vad som &auml;r sanning]? D&aring; s&auml;tter Vi V&aring;r plan i verket!';
$t[80] = 'Tror de kanske att Vi inte k&auml;nner deras hemligheter och [inte vet om] deras &ouml;verl&auml;ggningar [bakom lyckta d&ouml;rrar]? Helt visst [&auml;r det s&aring;] och V&aring;ra uts&auml;nda &auml;r hos dem och nedtecknar [allt].';
$t[81] = '[Muhammad!] S&auml;g [till dem som s&auml;tter andra vid Guds sida]: &quot;Jag skulle vara den f&ouml;rste som tillbad den N&aring;derikes son, om Han hade n&aring;gon.&quot;';
$t[82] = 'Stor &auml;r Han i Sin h&auml;rlighet, himlarnas och jordens Herre, Herren till allmaktens tron - och fj&auml;rran fr&aring;n [m&auml;nniskornas alla f&ouml;rs&ouml;k] att beskriva [Hans V&auml;sen].';
$t[83] = 'L&auml;mna dem nu att f&ouml;rstr&ouml; sig med sina ordlekar, till dess de st&aring;r inf&ouml;r den Dag som de har f&aring;tt l&ouml;fte om.';
$t[84] = 'Det &auml;r Han som &auml;r Gud i himlen och Gud p&aring; jorden och Han &auml;r den Allvise, den Allvetande.';
$t[85] = 'V&auml;lsignad &auml;r Han som &auml;ger herrav&auml;ldet &ouml;ver himlarna och jorden och allt det som &auml;r d&auml;remellan! Hos Honom [ensam] finns kunskapen om den Yttersta stunden och till Honom skall ni f&ouml;ras &aring;ter!';
$t[86] = 'De som de &aring;kallar i st&auml;llet f&ouml;r Gud har inte makt att tala till f&ouml;rm&aring;n [f&ouml;r sina tillbedjare]; ingen [kan g&ouml;ra detta] utom den som med [full] kunskap vittnar om sanningen.';
$t[87] = 'Och om du fr&aring;gar dem [som dyrkar andra v&auml;sen vid sidan av Gud], vem som har skapat dem, svarar de helt visst: &quot;Det &auml;r Gud.&quot; Hur f&ouml;rvirrade &auml;r inte deras begrepp!';
$t[88] = 'Och [Gud har h&ouml;rt Sin Profets] klagan: &quot;Herre, de &auml;r m&auml;nniskor som inte vill tro!&quot;';
$t[89] = 'F&ouml;rl&aring;t dem och h&auml;lsa dem med fredsh&auml;lsningen - de kommer att f&aring; veta [sanningen].';
