$t[86] = 'Na tukawachunga wakhalifu kuwapeleka Jahannamu hali ya kuwa wana kiu.';
$t[87] = 'Hawatakuwa na mamlaka ya uombezi ila wale walio chukua ahadi kwa Arrahmani Mwingi wa Rehema..';
$t[88] = 'Na wao ati husema kuwa Arrahmani Mwingi wa Rehema ana mwana!';
$t[89] = 'Hakika mmeleta jambo la kuchusha mno!';
$t[90] = 'Zinakaribia mbingu kutatuka kwa hilo, na ardhi kupasuka, na milima kuanguka vipande vipande.';
$t[91] = 'Kwa kudai kwao kuwa Arrahmani Mwingi wa Rehema ana mwana.';
$t[92] = 'Wala hahitajii Arrahmani Mwingi wa Rehema kuwa na mwana.';
$t[93] = 'Hapana yeyote aliomo mbinguni na ardhini ila atafika kwa Arrahmani Mwingi wa Rehema kuwa ni mtumwa wake.';
$t[94] = 'Yeye hakika amewadhibiti na amewahisabu sawa sawa.';
