$t[39] = 'بۇلار پەرۋەردىگارىڭ ساڭا ۋەھيى قىلغان ھېكمەتلەرنىڭ بىر قىسمىدۇر، اﷲ قا باشقا مەبۇدنى شېرىك قىلمىغىن، بولمىسا مالامەت قىلىنغان، (اﷲ نىڭ رەھمىتىدىن) قوغلانغان ھالدا جەھەننەمگە تاشلىنىسەن';
$t[40] = 'ئەجەبا اﷲ سىلەرگە ئوغۇللارنى خاس قىلىپ، پەرىشتىلەرنى ئۆزىنىڭ قىزلىرى قىلىۋالغانمۇ؟ ھەقىقەتەن سىلەر چوڭ سۆزنى قىلىۋاتىسىلەر';
$t[41] = 'ئۇلارنى ئىبرەت ئالسۇن دەپ (ۋەز - نەسىھەتنى) قۇرئاندا تۈرلۈك شەكىلدە بايان قىلدۇق، (بۇنىڭ بىلەن) ئۇلار (ھەقىقەتەن) تېخىمۇ يىراقلاشماقتا';
$t[42] = 'مۇشرىكلار ئېيتقاندەك، اﷲ بىلەن بىرگە يەنە باشقا ئىلاھلار بولغان تەقدىردە ئەرش ئىگىسى (اﷲ ئۈستىدىن) غەلىبە قىلىش ئۈچۈن ئەلۋەتتە يول ئىزدىگەن بولاتتى';
$t[43] = 'اﷲ پاكتۇر، ئۇلارنىڭ بوھتان سۆزلىرىدىن يۈكسەك دەرىجىدە ئالىيدۇر';
$t[44] = 'يەتتە ئاسمان - زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار اﷲ نى پاك دەپ بىلىدۇ، (كائىناتتىكى) قانداقلىكى نەرسە بولمىسۇن، اﷲ نى پاك دەپ مەدھىيىلەيدۇ (يەنى اﷲ نىڭ ئۇلۇغلۇقىنى سۆزلەيدۇ)، لېكىن سىلەر (تىلىڭلار ئوخشاش بولمىغانلىقى ئۈچۈن) ئۇلارنىڭ مەدھىيىسىنى سەزمەيسىلەر، اﷲ ھەقىقەتەن (بەندىلىرىگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر';
$t[45] = '(ئى مۇھەممەد!) سەن ئاخىرەتكە ئىشەنمەيدىغان (مۇشرىكلارغا) قۇرئاننى ئوقۇغان چېغىڭدا ئۇلار بىلەن سېنىڭ ئاراڭدا يوشۇرۇن پەردە پەيدا قىلدۇق';
$t[46] = 'قۇرئاننى چۈشەنمىسۇن دەپ ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، ئۇلارنىڭ قۇلاقلىرىنى ئېغىر قىلدۇق، قۇرئاندا يالغۇز پەرۋەردىگارىڭنى تىلغا ئالساڭ، ئۇلار ئۈركۈپ قېچىپ كېتىدۇ';
$t[47] = 'تىلاۋىتىڭگە ئۇلارنىڭ نېمە ئۈچۈن (يەنى مەسخىرە ئۈچۈن) قۇلاق سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار قىرائىتىڭنى تىڭشىغان چاغلىرىدا (سېنىڭ توغراڭدا) پىچىرلىشىدۇ، ئەينى ۋاقىتتا زالىملار (مۆمىنلەرگە): &laquo;سىلەر پەقەت سېھىرلەنگەن ئادەمگىلا ئەگىشىۋاتىسىلەر&raquo; دەيدۇ';
$t[48] = 'ئۇلارنىڭ سېنىڭ توغراڭدا (بىردەم سېھرىگەر، بىردەم شائىر ۋە بىردەم مەجنۇن دېگەندەك) نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار (مۇشۇنداق بوھتانلارنى چاپلاش بىلەن) ئازدى، ئۇلار (ھىدايەتكە) يول تاپالمايدۇ';
$t[49] = 'مۇشرىكلار: &lsaquo;&lsaquo;بىز قۇرۇق سۆڭەك ۋە چىرىپ توپا بولۇپ كەتكەندىن كېيىن چوقۇم قايتا تىرىلىپ يېڭىدىن ئادەم بولامدۇق؟ &rsaquo;&rsaquo; دېيىشىدۇ';
$t[50] = 'سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (اﷲ سىلەرنى تىرىلدۈرىدۇ) ئۇلار: &lsaquo;&lsaquo;بىزنى كىم تىرىلدۈرىدۇ؟&raquo; دېگىن، ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: &lsaquo;&lsaquo;ئۇ قاچان بولىدۇ؟ &rsaquo;&rsaquo; دېيىشىدۇ، (سەن) &lsaquo;&lsaquo;ئۈمىدكى، ئۇ يېقىندا بولىدۇ&rsaquo;&rsaquo; دېگىن';
$t[51] = 'سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (اﷲ سىلەرنى تىرىلدۈرىدۇ) ئۇلار: &lsaquo;&lsaquo;بىزنى كىم تىرىلدۈرىدۇ؟&raquo; دېگىن، ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: &lsaquo;&lsaquo;ئۇ قاچان بولىدۇ؟ &rsaquo;&rsaquo; دېيىشىدۇ، (سەن) &lsaquo;&lsaquo;ئۈمىدكى، ئۇ يېقىندا بولىدۇ&rsaquo;&rsaquo; دېگىن';
$t[52] = 'شۇ كۈندە اﷲ سىلەرنى چاقىرىدۇ، سىلەر ئۇنىڭغا ھەمدى ئېيتىپ ئىجابەت قىلىسىلەر ۋە (دۇنيادا) پەقەت ئازغىنا تۇرغاندەك ئويلايسىلەر';
$t[53] = 'بەندىلىرىمگە ئېيتقىنكى، ئۇلار ياخشى سۆزلەرنى قىلسۇن، شۈبھىسىزكى، شەيتان ئۇلارنىڭ ئارىسىدا پىتنە قوزغايدۇ، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر';
$t[54] = 'اﷲ سىلەرنى ئوبدان بىلىدۇ، اﷲ سىلەرگە رەھىم قىلىدۇ، خالىسا سىلەرنى ئازابلايدۇ، سېنى ئۇلار (يەنى كاپىرلار) غا ھامىي قىلىپ ئەۋەتكىنىمىز يوق';
$t[55] = 'پەرۋەردىگارىڭ ئاسمانلاردىكى، زېمىندىكى بەندىلىرىنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، بىز ھەقىقەتەن پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق';
$t[56] = 'داۋۇتقا زەبۇرنى ئاتا قىلدۇق، سىلەر اﷲ نى قويۇپ خۇدا دەپ ئويلىغانلىرىڭلارنى چاقىرىڭلار. ئۇلارنىڭ قولىدىن سىلەردىن بالانى كۆتۈرۈۋېتىشمۇ ۋە ئۇنى (باشقىلارغا) يۆتكەپ قويۇشمۇ كەلمەيدۇ';
$t[57] = 'ئۇلار (اﷲ نى قويۇپ ئىلاھ دەپ) ئىبادەت قىلىدىغانلارنىڭ (ئۆزلىرى ئىبادەت ئارقىلىق) پەرۋەردىگارىغا يېقىن بولۇشنى تىلەيدۇ، ئۇلارنىڭ ئارىسىدا (پەرۋەردىگارىغا) يېقىنراق بولغىنى (ھەم ئىبادەت ئارقىلىق اﷲ قا يېقىن بولۇشنى تىلەيدۇ)، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ (ئۇنىڭ) ئازابىدىن قورقىدۇ، پەرۋەردىگارىنىڭ ئازابى ھەقىقەتەن ساقلىنىشقا تېگىشلىكتۇر';
$t[58] = '(اﷲ قا ئاسىيلىق قىلغان) ھەرقانداق بىر شەھەر بولمىسۇن، ئۇنى بىز قىيامەت كۈنىدىن بۇرۇن ھالاك قىلىمىز، ياكى ئۇنى قاتتىق ئازابلايمىز، بۇ لەۋھۇلمەھپۇزدا پۈتۈلۈپ كەتكەن (ھۆكۈم) دۇر';
$t[59] = '(قۇرەيش مۇشرىكلىرى تەلەپ قىلغان) مۆجىزىلەرنى مەيدانغا كەلتۈرمەسلىكىمىز پەقەت بۇرۇنقىلار (يەنى ئىلگىرىكى ئۈممەتلەرنىڭ) مۆجىزىلەرنى يالغانغا چىقارغانلىقى ئۈچۈندۇر، بىز سەمۇدقا چىشى تۆگىنى روشەن (مۆجىزە) قىلىپ بەردۇق، ئۇلار ئۇنى ئىنكار قىلدى. بىز مۆجىزىلەرنى پەقەت (بەندىلەرنى) قورقۇتۇش ئۈچۈنلا ئەۋەتىمىز';
$t[60] = 'ئۆز ۋاقتىدا بىز ساڭا پەرۋەردىگارىڭنىڭ ھەقىقەتەن كىشىلەرنىڭ (ئەھۋالى) نى تولۇق بىلىدىغانلىقىنى ئېيتتۇق، بىز ساڭا (مىراج كېچىسى ئاسمان - زېمىندىكى ئاجايىپلاردىن) كۆرسەتكەن كۆرۈنۈشنى، قۇرئاندىكى لەنەت قىلىنغان دەرەخنى (يەنى قۇرئاندا زىكرى قىلىنغان زەققۇم دەرىخىنى) پەقەت كىشىلەر ئۈچۈن سىناق قىلدۇق، بىز ئۇلارنى (يەنى مۇشرىكلارنى) قورقۇتىمىز. بىزنىڭ قورقۇتىشىمىز ئۇلارنىڭ يولسىزلىقىنى تېخىمۇ كۈچەيتىدۇ';
$t[61] = 'ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: &laquo;ئادەمگە سەجدە قىلىڭلار&zwj;&zwnj;&zwj;!&raquo; دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى. ئىبلىس: &laquo;سەن لايدىن ياراتقانغا سەجدە قىلامدىمەن؟&raquo; دېدى';
$t[62] = 'ئىبلىس: &laquo;ماڭا ئېيتىپ بەرگىنە، سەن مەندىن ئۈستۈن قىلغان ئادەم مۇشۇمۇ؟ ئەگەر ماڭا قىيامەتكىچە مۆھلەت بېرىدىغان بولساڭ، ئۇنىڭ ئەۋلادىنىڭ ئازغىنىسىدىن باشقىسىنى (ئازدۇرۇپ) تۈپ يىلتىزىدىن قۇرۇتۇۋېتىمەن (يەنى ئۇلارنى خالىغانچە يېتىلەيمەن)&raquo; دېدى';
$t[63] = 'اﷲ (ئىبلىسقا) ئېيتتى؛ &laquo;بارغىن (ساڭا مۆھلەت بەردىم)، ئۇلاردىن (يەنى ئادەم بالىلىرىدىن) كىمكى ساڭا ئەگىشىدىكەن، (جەھەننەم ساڭا ۋە ئۇلارغا) بېرىلگەن تولۇق جازادۇر';
$t[64] = 'ئۇلارنىڭ ئىچىدىن قوزغىتالايدىغانلىكى ئادىمىڭنى ئاۋازىڭ بىلەن (يەنى گۇناھقا چاقىرىشىڭ بىلەن) قوزغاتقىن، ئاتلىق ۋە پىيادە قوشۇنىڭ بىلەن ئۇلارغا ھۇجۇم قىلغىن، ئۇلارنىڭ ماللىرىغا ۋە بالىلىرىغا شېرىك بولغىن، ئۇلارغا (يالغان) ۋەدىلەرنى بەرگىن - شەيتاننىڭ ئۇلارغا بەرگەن ۋەدىسى پەقەت ئالدامچىلىقتۇر';
$t[65] = 'شۈبھىسىزكى، مېنىڭ (ئىخلاسمەن) بەندىلىرىم ئۈستىدىن سەن ھۆكۈمرانلىق قىلالمايسەن. پەرۋەردىگارىڭ ھەقىقەتەن ھامىي بولۇشقا يېتەرلىكتۇر&raquo;';
$t[66] = 'پەرۋەردىگارىڭلار، سىلەرنى پەزلىنى (يەنى پەزلىدىن بولغان رىزقىڭلارنى) تەلەپ قىلسۇن دەپ، كېمىلەرنى سىلەر ئۈچۈن دېڭىزدا ماڭدۇرۇپ بېرىدۇ، ئۇلار ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر';
$t[67] = 'سىلەر دېڭىزدا بىرەر ئاپەتكە يولۇققان چېغىڭلاردا، ئېسىڭلارغا سىلەر چوقۇنۇۋاتقان مەبۇدلار كەلمەي، پەقەت اﷲ لا كېلىدۇ (يەنى ئىنسان مۇنداق چاغدا اﷲ تىن باشقىغا ئىلتىجا قىلمايدۇ)، اﷲ سىلەرنى (غەرق بولۇشتىن) قۇتقۇزۇپ، (ئامان - ئېسەن) قۇرۇقلۇققا چىقارغاندا يۈز ئۆرۈيسىلەر (يەنى ئىخلاسىڭلار تۈگەپ ئاۋۋالقى ھالىتىڭلارغا قايتىۋالىسىلەر). ئىنسان كۇفرىلىق قىلغۇچىدۇر (يەنى اﷲ نىڭ نېمەتلىرىنى ئىنكار قىلىش ئىنساننىڭ تەبىئىتىدۇر)';
$t[68] = '(دېڭىزدا غەرق بولۇپ كېتىشتىن قۇتۇلغىنىڭلار بىلەن) اﷲ نىڭ سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن، ياكى سىلەرگە ئاسماندىن تاش ياغدۇرۇپ ھالاك قىلىشىدىن، ئاندىن سىلەرنى (اﷲ نىڭ ئازابىدىن ساقلايدىغان) ھېچ ھامىي تاپالماسلىقىڭلاردىن قورقمامسىلەر؟';
$t[69] = 'ياكى اﷲ نىڭ سىلەرنى دېڭىزدا قايتا قاتناتقۇزۇپ (دېڭىزدىكى چېغىڭلاردا) قارا بوران چىقىرىپ، كۈفرىلىق قىلغانلىقىڭلار تۈپەيلىدىن سىلەرنى غەرق قىلىۋېتىشىدىن، ئاندىن سىلەرگە بىزدىن ئۆچ ئېلىپ بېرىدىغان ھېچ ئەھدى تاپالماسلىقىڭلاردىن قورقمامسىلەر؟';
$t[70] = 'شەك - شۈبھىسىزكى، بىز ئادەم بالىلىرىنى ھۆرمەتلىك قىلدۇق، ئۇلارنى قۇرۇقلۇقتا (ئۇلۇغلارغا) مىندۇردۇق، دېڭىزدا (كېمىلەرگە) چىقاردۇق، ئۇلارنى شېرىن يېمەكلىكلەر بىلەن رىزىقلاندۇردۇق، ئۇلارنى مەخلۇقاتلىرىمىزنىڭ نۇرغۇنىدىن ئۈستۈن قىلدۇق';
$t[71] = 'بىز ھەممە ئادەمنى ئۇلارنىڭ نامە - ئەمالى بىلەن چاقىرىدىغان كۈننى (ئېسىڭدا تۇتقىن)، نامە - ئەمالى ئوڭ قولىدىن بېرىلگەنلەر ئۆزلىرىنىڭ نامە - ئەمالىنى ئوقۇيدۇ، ئۇلارغا قىلچە زورلۇق قىلىنمايدۇ';
$t[72] = 'بۇ دۇنيادا (دىلى) كۆر بولغان ئادەم ئاخىرەتتىمۇ كۆر ۋە تېخىمۇ گۇمراھ بولىدۇ';
$t[73] = 'ئۇلار سېنى بىز ساڭا ۋەھىي قىلغان قۇرئانغا خىلاپلىق قىلدۇرۇپ، بىزگە قۇرئاندىن غەيرىنى ئىپتىرا قىلدۇرۇشقا تاس قالدى، (ئۇلارنىڭ كۆڭلىدىكىدەك قىلغىنىڭدا) ئەلۋەتتە ئۇلار سېنى (ئۆزلىرىگە) دوست قىلىۋالاتتى';
$t[74] = 'سېنى بىز (ھەقتە تۇرۇشتا) مۇستەھكەم قىلمىغان بولساق، ئۇلارغا مايىل بولۇپ كەتكىلى ھەقىقەتەن تاس قالغان ئىدىڭ';
$t[75] = 'ئەگەر شۇنداق قىلىدىغان بولساڭ، ئەلۋەتتە ساڭا دۇنيا ۋە ئاخىرەتتە ھەسسىلەپ ئازاب بەرگەن بولاتتۇق، ئاندىن سەن بىزگە قارشى ياردەم بېرىدىغان ھېچ ئادەم تاپالمايتتىڭ';
$t[76] = '(يەھۇدىيلار) سېنى (مەدىنە) زېمىنىدىن چىقىرىۋېتىش ئۈچۈن، ئۇ يەردىن سېنى خاتىرجەم تۇرغۇزماسلىققا تاس قالدى، شۇنداق بولغاندا، سەن چىقىپ كەتكەندىن كېيىن، ئۇلار ئازغىنا ۋاقىت تۇرالايتتى (يەنى ئازغىنا ۋاقىتتىن كېيىن ھالاك بولاتتى)';
$t[77] = '(بۇ) سەندىن ئىلگىرى بىز ئەۋەتكەن پەيغەمبەرلىرىمىزنىڭ تۇتقان يولىدۇر (يەنى پەيغەمبەرلىرىنى ئارىسىدىن ھەيدەپ چىقارغان ھەرقانداق ئۈممەتنى ھالاك قىلىش اﷲ نىڭ پەيغەمبەرلىرى ھەققىدە تۇتقان يولىدۇر)، تۇتقان يولىمىزدا ئۆزگىرىش تاپالمايسەن';
$t[78] = '(ئى مۇھەممەد!) كۈن قايرىلغان ۋاقىتتىن تارتىپ قاراڭغۇ چۈشكەن ۋاقىتقا قەدەر ناماز ئوقۇغىن. بامدات نامىزىنىمۇ ئوقۇغىن، (كۈندۈزنىڭ ۋە كېچىنىڭ پەرىشتىلىرى) چوقۇم بامدات نامىزىدا ھازىر بولىدۇ';
$t[79] = 'ساڭا نەپلە (ئىبادەت) بولۇشى ئۈچۈن، كېچىنىڭ بىر قىسىمىدا (ناماز ئوقۇشقا، قۇرئان ئوقۇشقا) ئويغانغىن، (ئى مۇھممەد&zwj;! (پەرۋەردىگارىڭنىڭ سېنى مەدھىيىلىنىدىغان ئورۇنغا (يەنى بۈيۈك شاپائەت ماقامىغا) تۇرغۇزۇشى ھەقىقەتتۇر';
$t[80] = '&laquo;پەرۋەردىگارىم! مېنى (قەبرەمگە) ئوڭۇشلۇق كىرگۈزگىن، (قەبرەمدىن) ئوڭۇشلۇق چىقارغىن، ماڭا دەرگاھىڭدىن ھەققە ياردەم بېرىدىغان قۇۋۋەت ئاتا قىلغىن&raquo; دېگىن';
$t[81] = '&laquo;ھەقىقەت (يەنى ئىسلام) كەلدى، باتىل (يەنى كۈفرى) يوقالدى. باتىل ھەقىقەتەن ئوڭاي يوقىلىدۇ&raquo; دېگىن';
$t[82] = 'بىز مۆمىنلەرگە (يەنى ئۇلارنىڭ دىللىرىغا) شىپا ۋە رەھمەت بولىدىغان قۇرئان ئايەتلىرىنى نازىل قىلىمىز، قۇرئان كاپىرلارغا زىياندىن باشقىنى زىيادە قىلمايدۇ (يەنى ئۇلار قۇرئاننى تەستىق قىلمىغانلىقتىن، ئۇلارنىڭ كۇفرى تېخىمۇ ئاشىدۇ)';
$t[83] = 'ئىنسانغا (تۈرلۈك نېمەتلەرنى) بەرسەك (پەرۋەردىگارىنىڭ ئىبادىتىدىن) يۈز ئۆرۈيدۇ، (چوڭچىلىق قىلىپ) پەرۋەردىگارىدىن يىراقلىشىدۇ، ئۇنىڭغا بىرەر كۆڭۈلسىزلىك يەتسە، (اﷲ نىڭ رەھمىتىدىن) مەيۈسلىنىپ كېتىدۇ';
$t[84] = 'ھەر ئادەم (ھىدايەت ۋە گۇمراھلىقتا) ئۆز يولى بويىچە ئىش قىلىدۇ، پەرۋەردىگارىڭلار كىمنىڭ توغرا يولدا ئىكەنلىكىنى ئوبدان بىلىدۇ';
$t[85] = 'ئۇلار سەندىن روھنىڭ ماھىيىتى ھەققىدە سورىشىدۇ. &lsaquo;&lsaquo;روھنىڭ ماھىيىتىنى پەرۋەردىگارىم بىلىدۇ&rsaquo;&rsaquo; دېگىن، سىلەرگە پەقەت ئازغىنا ئىلىم بېرىلگەن';
$t[86] = 'ئەگەر بىز خالىساق ساڭا ۋەھيى قىلغان قۇرئاننى ئەلۋەتتە (دىللاردىن ۋە مۇسھەپلەردىن) كۆتۈرۈۋەتتۇق، ئاندىن ئۇنى ئەسلىگە كەلتۈرۈشكە بىزگە قارشى ھېچقانداق ھامىي تاپالمايتتىڭ';
$t[87] = 'پەقەت پەرۋەردىگارىڭ رەھمەت قىلىش يۈزىسىدىن ئۇنى كۆتۈرۈۋەتمىدى. اﷲ نىڭ رەھمىتى ساڭا ھەقىقەتەن چوڭ بولدى';
$t[88] = 'ئېيتقىنكى، &laquo;ئەگەر ئىنسانلار، جىنلار بۇ قۇرئاننىڭ ئوخشىشىنى مەيدانغا كەلتۈرەلمەيدۇ&raquo;';
$t[89] = 'بۇ قۇرئاندا ئىنسانلار ئۈچۈن تۈرلۈك مەسىللەرنى خىلمۇ - خىل شەكىلدە بايان قىلدۇق، (روشەن پاكىتلارنىڭ بولىشىغا قارىماي) ئىنسانلارنىڭ تولىسى پەقەت (ھەقىقەتنى) ئىنكار قىلىدۇ';
$t[90] = 'ئۇلار ئېيتتى: &laquo;تاكى سەن بىزگە يەر ئاستىدىن بىر بۇلاقنى ئېتىلدۇرۇپ چىقارمىغىچە ساڭا ھەرگىز ئىشەنمەيمىز';
$t[91] = 'ياكى سېنىڭ خورمىلىق، تاللىق ۋە ئوتتۇرىسىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان بىر بېغىڭ بولسۇن';
$t[92] = 'ياكى سەن ئېيتقاندەك ئۈستىمىزگە ئاسماننى پارچە - پارچە قىلىپ چۈشۈرگىن، ياكى (پەيغەمبەرلىكىڭگە) گۇۋاھچى قىلىپ اﷲ نى ۋە پەرىشتىلەرنى ئالدىمىزغا كەلتۈرگىن';
$t[93] = 'ياكى سېنىڭ ئالتۇندىن ئۆيۈڭ بولسۇن، ياكى سەن (شوتا بىلەن) ئاسمانغا چىققىن، تاكى (اﷲ تەرىپىدىن سېنىڭ ئۇنىڭ بەندىسى ۋە پەيغەمبەرلىكى ئىكەنلىكىڭ يېزىلغان) بىز ئوقۇيالايدىغان بىر كىتابنى ئېلىپ چۈشمىگىچە سېنىڭ ئاسمانغا چىققانلىقىڭغا ھەرگىز ئىشەنمەيمىز&raquo;. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;اﷲ پاكتۇر، مەن پەقەت بىر پەيغەمبەر ئىنسانمەن (اﷲ نىڭ ئىزنى بولمىسا ھېچ ئادەم بىرەر مۆجىزە كەلتۈرەلمەيدۇ)&raquo;';
$t[94] = 'ئۇلار (يەنى مۇشرىكلار) نىڭ ئۆزلىرىگە ھىدايەت (يەنى پەيغەمبەر بىلەن قۇرئان) كەلگەن چاغدا ئىمان ئېيتماسلىقلىرى &laquo;اﷲ ئىنساننى پەيغەمبەر قىلىپ ئەۋەتەمدۇ؟&raquo; دېگەنلىكلىرى ئۈچۈندۇر';
$t[95] = '(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;مۇبادا زېمىندا (يەنى زېمىن ئەھلىدەك) مېڭىپ يۈرۈيدىغان، ئولتۇراقلاشقان پەرىشتىلەر بولسا ئىدى، ئەلۋەتتە ئۇلارغا ئاسماندىن پەرىشتىنى پەيغەمبەر قىلىپ چۈشۈرەتتۇق&raquo;';
$t[96] = '(ئى مۇھەممەد&zwj;! (ئېيتقىنكى، &laquo;اﷲ مەن بىلەن سىلەرنىڭ ئاراڭلاردا مېنىڭ راستلىقىمغا گۇۋاھ بولۇشقا يېتەرلىكتۇر. اﷲ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر. كۆرۈپ تۇرغۇچىدۇر&raquo;';
$t[97] = 'كىمكى اﷲ ھىدايەت قىلغان ئىكەن ئۇ ھىدايەت تاپقۇچىدۇر، اﷲ گۇمراھ قىلغان ئادەمگە اﷲ تىن باشقا (ئازابتىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچىلەرنى تاپالمايسەن، قىيامەت كۈنى ئۇلارنى دۈم ياتقۇزۇپ (سۆرەلگەن) كۆر، گاچا، گاس ھالدا يىغىمىز، ئۇلارنىڭ جايى جەھەننەم بولىدۇ، (جەھەننەمنىڭ) ئوتى پەسلەپ قالسا، ئۇلارغا (ئوتنى) تېخىمۇ يالقۇنجىتىمىز';
$t[98] = 'ئۇلارنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە &laquo;چىرىگەن سۆڭەك ۋە توپىغا ئايلانغىنىمىزدىن كېيىن راستلا قايتىدىن تېرىلىپ يېڭىدىن ئادەم بولامدۇق؟&raquo; دېگەنلىكلىرى ئۈچۈن، ئۇلارغا بېرىلگەن جازا ئەنە شۇدۇر';
$t[99] = 'ئاسمانلارنى، زېمىننى ياراتقات اﷲ نىڭ ئۇلارغا ئوخشاش ئىنسانلارنى يارىتالايدىغانلىقىنى ئۇلار بىلمەمدۇ؟ اﷲ ئۇلارنىڭ (ئۆلۈشى ۋە تىرىلىشى ئۈچۈن) شەك - شۈبھىسىز مۇئەييەن ۋاقىت بەلگىلىدى، كاپىرلار پەقەت كۇفرىغىلا ئۇنايدۇ';
$t[100] = '&laquo;ئەگەر پەرۋەردىگارىمنىڭ رەھمەت خەزىنىلىرى سىلەرنىڭ قولۇڭلاردا بولسا، چىقىمدىن (يەنى تۈگەپ كېتىشىدىن) قورقۇپ، چوقۇم بېخىللىق قىلاتتىڭلار، ئىنسان بېخىل كېلىدۇ&raquo; دېگىن';
$t[101] = 'بىز مۇساغا ھەقىقەتەن توققۇز روشەن مۆجىزە ئاتا قىلدۇق، مۇسا پىرئەۋنگە ۋە ئۇنىڭ قەۋمىگە كەلگەن چاغدا بىز مۇساغا: &laquo;(ئۇلاردىن) ئىسرائىل ئەۋلادىنى (قويۇۋېتىشنى) سۆرىغىن&raquo; (دېدۇق). پىرئەۋن ئۇنىڭغا: &laquo;ئى مۇسا! سېنى مەن چوقۇم جادۇ قىلىنغان دەپ ئويلايمەن&raquo; دېدى';
$t[102] = 'مۇسا: &laquo;(ئى پىرئەۋن!) بۇلار (يەنى توققۇز مۆجىزە) نى پەقەت ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) دەلىل قىلىپ نازىل قىغانلىقىنى سەن (گەرچە تېلىڭدا ئىقرار قىلمىساڭمۇ دىلىڭدا ئەلۋەتتە) ئوبدان بىلىسەن، مەن سېنى، ئى پىرئەۋن، ھەقىقەتەن، ھالاك بولىدۇ دەپ ئويلايمەن&raquo; دېدى';
$t[103] = 'پىرئەۋن ئۇلارنى (مىسىر) زېمىنىدىن چىقىرىۋەتمەكچى بولدى، بىز پىرئەۋننى ۋە ئۇنىڭ بىلەن بىللە بولغانلارنى - ھەممىسىنى (دېڭىز) غا غەرق قىلىۋەتتۇق';
$t[104] = 'پىرئەۋننى غەرق قىلىۋەتكەندىن كېيىن، بىز ئىسرائىل ئەۋلادىغا: &laquo;(مىسىر) زېمىنىدا تۇرۇڭلار، ئاخىرەت ۋەدىسى (يەنى قىيامەت) كەلگەن چاغدا سىلەرنى (قەبرەڭلاردىن مەھشەرگاھقا) (كاپىرلار بىلەن مۆمىنلەرنى) ئارىلاش ئېلىپ كېلىمىز&raquo; دېدۇق';
$t[105] = 'قۇرئاننى ھەقىقەت ئاساسىدا نازىل قىلدۇق، قۇرئانمۇ ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا نازىل بولدى، سېنى بىز پەقەت خوش خەۋەر بەرگۈچى، ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق';
$t[106] = 'قۇرئاننى كىشىلەرگە دانە - دانە ئوقۇپ بېرىشىڭ ئۈچۈن ئۇنى بۆلۈپ - بۆلۈپ نازىل قىلدۇق، ئۇنى تەدرىجىي نازىل قىلدۇق';
$t[107] = '&laquo;قۇرئانغا ئىشىنىڭلار ياكى ئىشەنمەڭلار (مەيلى، چۈنكى ئىشىنىشىڭلار ئۇنىڭغا كامالەت، ئىشەنمەسلىكىڭلار ئۇنىڭغا نۇقسان ئېلىپ كەلمەيدۇ)&raquo; دېگىن. شۈبھىسىزكى، قۇرئان نازىل بولۇشتىن ئىلگىرى كىتاب بېرىلگەنلەر قۇرئاننى ئاڭلىغان چاغلىرىدا (تەسىرلىنىپ) دەرھال سەجدىگە بارىدۇ';
$t[108] = 'ئۇلار: &laquo;پەرۋەردىگارىمىز پاكتۇر، پەرۋەردىگارىمىزنىڭ ۋەدىسى چوقۇم ئورۇنلىنىدۇ&raquo; دەيدۇ';
$t[109] = 'ئۇلار يىغلىغان ھالدا سەجدە قىلىشقا يىقىلىدۇ (قۇرئاننى ئاڭلاش بىلەن ئۇلار اﷲ قا) تېخىمۇ تەزەررۇ قىلىدۇ';
$t[110] = '&laquo;اﷲ دەپ ئاتىساڭلارمۇ بولىدۇ، رەھمان دەپ ئاتىساڭلارمۇ بولىدۇ، قايسىنى ئاتىساڭلار (ھەممىسى ياخشىدۇر). چۈنكى اﷲ نىڭ نۇرغۇن گۈزەل ئىسىملىرى بار&raquo; دېگىن، نامىزىڭدا (قىرائەتنى) (مۇشرىكلار ئاڭلاپ ساڭا ئەزىيەت يەتكۈزمەسلىگى ئۈچۈن) يۇقىرى ئاۋاز بىلەنمۇ ئوقۇمىغىن، (مۆمىنلەر ئاڭلىماي قالماسلىقى ئۈچۈن) پەس ئاۋاز بىلەنمۇ ئوقۇمىغىن. ئۇلارنىڭ ئارىسىدا ئوتتۇرا يول تۇتقىن';
$t[111] = 'ئېيتقىنكى، &laquo;جىمى ھەمدۇسانا بالىسى بولۇشتىن پاك بولغان، سەلتەنىتىدە شېرىكى بولمىغان، خارلىقتىن قۇتۇلۇش ئۈچۈن دوستقا موھتاج بولمىغان (يەنى خار بولمىغان، ياردەمچىگە موھتاج بولمىغان) اﷲ قا خاستۇر! اﷲ نى مۇكەممەل رەۋىشتە ئۇلۇغلىغىن&raquo;';
?>