$t[60] = 'تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا، ئەنە شۇلارلا جەننەتكە كىرىدۇ، ئۇلارنىڭ (ئەمەللىرىنىڭ ساۋابىدىن) ھېچ نەرسە كېمەيتىلمەيدۇ';
$t[61] = 'ئۇ دائىمىي تۇرالغۇ بولغان، مەرھەمەتلىك اﷲ بەندىلىرىگە ۋەدە قىلغان، ئۇلار كۆرمەي تۇرۇپ (ئىشەنگەن) جەننەتلەردۇر، اﷲ نىڭ (جەننەت بىلەن قىلغان) ۋەدىسى چوقۇم ئىشقا ئاشىدۇ';
$t[62] = 'ئۇلار جەننەتتە بىھۇدە سۆز ئاڭلىمايدۇ، پەقەت (پەرىشتىلەرنىڭ ئۇلارغا بەرگەن) سالامىنىلا ئاڭلايدۇ، ئۇلار جەننەتتە ئەتىگەن - ئاخشامدا (كۆڭلى تارتقان يىمەك - ئىچمەكلەردىن) رىزىققا ئىگە بولۇپ تۇرىدۇ';
$t[63] = 'بەندىلىرىمىز ئىچىدىكى تەقۋادارلارغا بىز مىراس قىلىپ بېرىدىغان جەننەت ئەنە شۇ';
$t[64] = 'بىز (دۇنياغا) پەرۋەردىگارىڭنىڭ ئەمرى بىلەن چۈشىمىز، بىزنىڭ ئالدىمىزدىكى، ئارقىمىزدىكى ۋە ئالدى - كەينىمىز ئارىسىدىكى ئىشلارنىڭ ھەممىسى اﷲ نىڭ ئىلكىدىدۇر، پەرۋەردىگارىڭ (بەندىلىرىنىڭ ئەمەللىرىدىن ھېچ نەرسىنى) ئۇنتۇمايدۇ';
$t[65] = 'ئۇ ئاسمانلارنىڭ، زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، ئۇنىڭغا ئىبادەت قىلغىن، اﷲ نىڭ ئىبادىتىگە چىداملىق بولغىن، سەن ئۇنىڭ شېرىكى بارلىقىنى بىلەمسەن؟';
$t[66] = 'ئىنسان (يەنى ئۆلگەندىن كېيىن تىرىلىشىگە ئېتىقاد قىلمايدىغان كاپىر): &laquo;مەن چوقۇم تىرىلەمدىم؟&raquo; دەيدۇ';
$t[67] = 'ئىنسان بىز ئۇنى ئىلگىرى يوقتىن بار قىلغانلىقىمىزنى ئەسلىمەمدۇ؟';
$t[68] = 'پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلارنى ئەلۋەتتە (ئۇلارنى ئازدۇرغان) شەيتانلار بىلەن قوشۇپ يىغىمىز، ئاندىن ئۇلارنى جەھەننەمنىڭ چۆرىسىدە تىزلاندۇرۇپ ئولتۇرغۇزغان ھالدا ئەلۋەتتە ھازىر قىلىمىز';
$t[69] = 'ئاندىن ھەربىر گۇرۇھنىڭ ئىچىدىن مەرھەمەتلىك اﷲ قا ئەڭ ئاسىي بولغانلىرىنى تارتىپ چىقىرىمىز';
$t[70] = 'ئاندىن بىز دوزاخ ئازابىغا ئەڭ لايىق بولغانلارنى ئەلۋەتتە ئوبدان بىلىمىز';
$t[71] = 'سىلەرنىڭ ئىچىڭلاردىن دوزاخقا بارمايدىغان بىرەر كىشىمۇ قالمايدۇ، بۇ پەرۋەردىگارىڭنىڭ ئۆزگەرمەس ھۆكمىدۇر';
$t[72] = 'ئاندىن ئەڭ تەقۋادارلارنى (جەھەننەمدىن) قۇتقۇزىمىز، زالىملارنى جەھەننەمدە تىزلىنىپ ئولتۇرغان ھالدا قويىمىز';
$t[73] = 'ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنغان چاغدا، كاپىرلار مۆمىنلەرگە: &laquo;ئىككى گۇرۇھتىن قايسىنىڭ ئورنى ياخشى، سورۇنى گۈزەل؟&raquo; دېدى';
$t[74] = 'ئۇلاردىن بۇرۇن (بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، بۇلار مەيلى مال - مۈلۈك جەھەتتىن بولسۇن، مەيلى تاشقى كۆرۈنۈش جەھەتتىن بولسۇن، ئۇلاردىن ئارتۇق ئىدى';
$t[75] = '(ئى مۇھەممەد!) ئېيتقىنكى، &laquo;كىمكى گۇمراھلىقتا بولىدىكەن، مەرھەمەتلىك اﷲ ئۇنىڭغا مۆھلەت بىرىدۇ، تاكى ئۇلار ئازاب ۋە قىيامەتتىن ئىبارەت اﷲ ئۇلارغا ۋەدە قىلغان نەرسىلەرنى كۆرگەندە ئاندىن كىمنىڭ ئورنى يامان ۋە كىمنىڭ لەشكىرى ئاجىز ئىكەنلىكىنى بىلىدۇ&raquo;';
$t[76] = 'اﷲ ھىدايەت تاپقۇچىلارنىڭ ھىدايىتىنى زىيادە قىلىدۇ، باقى قالىدىغان ياخشى ئەمەللەر ساۋاب جەھەتتىن ۋە ئاقىۋەت جەھەتتىن پەرۋەردىگارىڭنىڭ دەرگاھىدا ئارتۇقتۇر';
$t[77] = 'بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ماڭا (ئاخىرەتتە) ئەلۋەتتە مال - مۈلۈك، ئەۋلاد بېرىلىدۇ دېگەن ئادەمنى كۆردۈڭمۇ؟';
$t[78] = 'ئۇ غەيبنى بىلگەنمۇ؟ ياكى مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنمۇ؟';
$t[79] = 'ھەرگىز ئۇنداق بولغان ئەمەس، ئۇنىڭ دېگەنلىرىنى خاتىرىلەپ قويىمىز، ئۇنىڭغا بېرىدىغان ئازابنى كۈچەيتىمىز';
$t[80] = 'ئۇنىڭ دېگەنلىرىنى (يەنى مېلىنى ۋە ئەۋلادىنى) باشقىلارغا مىراس قىلىپ قالدۇرىمىز، ئۇ ئالدىمىزغا تەنھا كېلىدۇ';
$t[81] = 'ئۇلار (يەنى مۇشرىكلار) ئىززەتكە ئېرىشىش ئۈچۈن (يەنى بۇتلىرىنىڭ شاپائىتى بىلەن اﷲ نىڭ ئالدىدا ئىززەتلىك بولۇش ئۈچۈن)، اﷲ نى قويۇپ ئىلاھلار (يەنى بۇتلار) غا چوقۇندى';
$t[82] = 'ئىش ئۇلارنىڭ گۇمان قىلغىنىدەك ئەمەس (يەنى بۇتلىرى ئۇلارنىڭ اﷲ ئالدىدا ئىززەت تېپىشلىرىنىڭ سەۋەبى بولالمايدۇ)، ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) چوقۇنۇشىنى ئىنكار قىلىدۇ، ئۇلار (يەنى مۇشرىكلار) نىڭ دۈشمىنىگە ئايلىنىدۇ';
$t[83] = 'بىزنىڭ شەيتانلارنى كاپىرلارغا ئەۋەتكەنلىكىمىزنى (يەنى مۇسەللەت قىلغانلىقىمىزنى) بىلمەمسەن؟ شەيتانلار ئۇلارنى (گۇناھلارغا) قىزىقتۇرىدۇ';
$t[84] = 'ئۇلارنىڭ ھالاك بولۇشىغا ئالدىراپ كەتمىگىن، (ئۇلارنىڭ كۈنى ئاز قالغان بولۇپ) ئۇنى بىز ساناپ تۇرىمىز';
$t[85] = 'شۇ كۈندە (يەنى قىيامەت كۈنىدە) تەقۋادارلارنى مەرھەمەتلىك پەرۋەردىگارنىڭ دەرگاھىغا ئىززەت - ئىكرام بىلەن يىغىمىز';
$t[86] = 'گۇناھكارلارنى ئۇلار چاڭقىغان ھالدا جەھەننەمگە ھەيدەيمىز';
$t[87] = 'مەرھەمەتلىك اﷲ نىڭ ئەھدىگە ئېرىشكەنلەردىن باشقىلار شاپائەت قىلىشقا قادىر بولالمايدۇ';
$t[88] = 'ئۇلار: &laquo;اﷲ نىڭ بالىسى بار&raquo; دېيىشتى';
$t[89] = 'سىلەر شەك - شۈبھىسىز قەبىھ بىر سۆزنى قىلدىڭلار';
$t[90] = 'بۇ سۆزنىڭ (يامانلىقىدىن) ئاسمانلار پارچىلىنىپ كەتكىلى، يەر يېرىلىپ كەتكىلى، تاغلار گۇمران بولۇپ كەتكىلى تاس قالدى';
$t[91] = 'بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ نىڭ بالىسى بار دەپ دەۋا قىلدى';
$t[92] = 'بالىسى بولۇش اﷲ قا لايىق ئەمەستۇر';
$t[93] = 'ئاسمانلاردا ۋە زېمىندا اﷲ قا بەندە بولۇپ بويسۇنمايدىغان ھېچ مەخلۇق يوقتۇر';
$t[94] = 'شەك - شۈبھىسىزكى، اﷲ ئۇلارنى تولۇق ئىگەللىدى ۋە سانىنى مۇكەممەل بىلدى';
$t[95] = 'قىيامەت كۈنى ئۇلارنىڭ ھەممىسى اﷲ نىڭ دەرگاھىغا تەنھا كېلىدۇ';
$t[96] = 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئۈچۈن اﷲ ھەقىقەتەن (ئۇلارنىڭ دىللىرىدا) مۇھەببەت پەيدا قىلىدۇ';
$t[97] = '(ئى مۇھەممەد!) تەقۋادارلارغا خۇش - خەۋەر بېرىشىڭ ۋە سەركەش قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، بىز قۇرئاننى سېنىڭ تىلىڭ بىلەن چۈشىنىشلىك قىلىپ نازىل قىلدۇق';
$t[98] = 'ئۇلاردىن ئىلگىرى نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلاردىن بىرەر ئەھەدىنى كۆرەلەمسەن؟ ياكى ئۇلاردىن بىرەر پەس ئاۋاز ئاڭلىيالامسەن؟ (يەنى ئۇلار شۇنداق تەلتۆكۈس ھالاك قىلىندىكى، ئۇلاردىن كۆرگىلى ۋە ئاۋازىنى ئاڭلىغىلى ھېچ ئەھەدى قالمىدى)';
?>