$t[39] = 'كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ';
$t[40] = 'ئى ئسرائىل ئەۋلادى! مېنىڭ سىلەرگە بەرگەن نېمىتىمنى ياد ئېتىڭلار، ماڭا بەرگەن (ئىمان ئېيتىش ۋە تائەت - ئىبادەت قىلىش ھەققىدىكى) ۋەدەڭلارغا ۋاپا قىلىڭلار، مەنمۇ سىلەرگە قىلغان (ساۋاب بېرىش ھەققىدىكى) ۋەدەمگە ۋاپا قىلىمەن، مەندىنلا قورقۇڭلار';
$t[41] = 'مەن نازىل قىلغان تەۋراتنى تەستىق قىلغۇچى قۇرئانغا ئىمان كەلتۈرۈڭلار، قۇرئاننى بىرىنچى بولۇپ ئىنكار قىلغۇچى بولماڭلار، ئايەتلىرىمنى ئەرزان باھادا سېتىۋەتمەڭلار، ماڭىلا تەقۋادارلىق قىلىڭلار';
$t[42] = 'ھەقنى باتىلغا (يەنى اﷲ نازىل قىلغان ھەقىقەتنى ئۆزۈڭلار ئويدۇرۇپ چىققان نەرسىگە) ئارىلاشتۇرماڭلار، ھەقنى بىلىپ تۇرۇپ يوشۇرماڭلار';
$t[43] = 'نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىلىڭلار';
$t[44] = 'كىشىلەرنى ياخشى ئىشقا بۇيرۇپ ئۆزۈڭلارنى ئۇنتۇمسىلەر؟ ھالبۇكى، سىلەر كىتاب (يەنى تەۋرات) نى ئوقۇپ تۇرىسىلەر، چۈشەنمەمسىلەر';
$t[45] = 'سەۋر قىلىش، ناماز ئوقۇش ئارقىلىق (اﷲ تىن) ياردەم تىلەڭلار. ناماز اﷲ تىن قورققۇچىلار (يەنى كەمتەرلىك بىلەن اﷲ قا بويسۇنغۇچىلار) دىن باشقىلارغا ھەقىقەتەن مۇشەققەتلىك ئىشتۇر';
$t[46] = 'اﷲ تىن قورققۇچىلار پەرۋەردىگارىغا چوقۇم مۇلاقات بولىدىغانلىقىغا ۋە اﷲ نىڭ دەرگاھىغا چوقۇم قايتىپ بارىدىغانلىقىغا جەزمەن ئىشىنىدۇ';
$t[47] = 'ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) تامام جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار';
$t[48] = 'كىشى كىشىگە ئەسقاتمايدىغان، ھېچ كىشىنىڭ شاپائىتى قوبۇل قىلىنمايدىغان، ھېچ كىشىدىن فىدىيە ئېلىنمايدىغان، ئۇلار (يەنى كاپىرلار ۋە گۇناھكار بەندىلەر) ياردەمگە ئېرىشەلمەيدىغان كۈندىن قورقۇڭلار';
$t[49] = 'ئۆز ۋاقتىدا سىلەرنى قاتتىق قىينىغان، ئوغۇللىرىڭلارنى بوغۇزلاپ، قىزلىرىڭلارنى (ئىشقا سېلىش ئۈچۈن) تىرىك قالدۇرغان پىرئەۋن گۇرۇھىدىن سىلەرنى قۇتقۇزدۇق. بۇ، پەرۋەردىگارىڭلارنىڭ (ياخشى ئادەم بىلەن يامان ئادەمنى ئاجرىتىدىغان) چوڭ سىنىقى ئىدى';
$t[50] = 'ئۆز ۋاقتىدا دېڭىزنى يېرىپ سىلەرنى قۇتقۇزدۇق (يەنى دېڭىزنىڭ سۈيىنى ئوتتۇرىدىن بۆلۈپ، قۇرۇق يول ھاسىل قىلىپ، شۇ يەردىن سىلەرنى سالامەت ئۆتكۈزدۇق) ۋە پىرئەۋن گۇرۇھىنى كۆز ئالدىڭلاردا غەرق قىلدۇق';
$t[51] = 'ئۆز ۋاقتىدا مۇساغا 40 كېچە (كۈتكەندىن كېيىن تەۋراتنى نازىل قىلىشنى) ۋەدە قىلدۇق. ئۇ سىلەردىن ئايرىلغاندىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ، موزاينى مەبۇد قىلىۋالدىڭلار';
$t[52] = 'شۇنىڭدىن كېيىن، شۈكۈر قىلسۇن دەپ، سىلەرنى ئەپۇ قىلدۇق';
$t[53] = 'ئۆز ۋاقتىدا سىلەرنى ھىدايەت تاپسۇن دەپ، مۇساغا ھەقنى باتىلدىن ئايرىغۇچى كىتابنى (يەنى تەۋراتنى) بەردۇق';
$t[54] = 'ئۆز ۋاقتىدا مۇسا ئۆز قەۋمىگە: &laquo;ئى قەۋمىم! سىلەر موزاينى مەبۇد قىلىپ، ھەقىقەتەن ئۆزۈڭلارغا زۇلۇم قىلدىڭلار، ياراتقۇچىڭلارغا تەۋبە قىلىڭلار، موزايغا چوقۇنمىغانلار چوقۇنغانلارنى ئۆلتۈرسۇن&raquo; دېدى. مۇنداق قىلىش ياراتقۇچىڭلارنىڭ دەرگاھىدا سىلەر ئۈچۈن ياخشىدۇر. اﷲ تەۋبەڭلارنى قوبۇل قىلدى. اﷲ تەۋبىنى ھەقىقەتەن بەكمۇ قوبۇل قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[55] = 'ئۆز ۋاقتىدا: &laquo;ئى مۇسا! اﷲ نى ئوپئوچۇق كۆرمىگىچە ساڭا ھەرگىز ئىشەنمەيمىز&raquo; دېدىڭلار - دە، (سىلەرگە نازىل بولغان ئازابنى) كۆرۈپ تۇرغىنىڭلار ھالدا سىلەرنى چاقماق سوقتى';
$t[56] = 'ئاندىن كېيىن، سىلەرنى شۈكۈر قىلسۇن، دەپ ئۆلگىنىڭلاردىن كېيىن قايتا تىرىلدۈردۇق';
$t[57] = 'سىلەرگە بۇلۇتنى سايىۋەن قىلىپ بەردۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (بىز سىلەرگە) &laquo;رىزىق قىلىپ بەرگەن پاك، شېرىن نەرسىلەردىن يەپ - ئىچىڭلار&raquo; (دېدۇق). ئۇلار (نېمەتلىرىمىزگە نانكورلۇق قىلىش بىلەن) بىزگە ئەمەس، پەقەت ئۆزلىرىگىلا زۇلۇم قىلدى';
$t[58] = 'ئۆز ۋاقتىدا (مۇسا ئارقىلىق ئەجدادلىرىڭلارغا): &laquo;بۇ شەھەرگە (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئۇ يەردە خالىغىنىڭلارچە كەڭتاشا يەپ - ئىچىڭلار، (بەيتۇلمۇقەددەس) دەرۋازىسىدىن (شۈكۈر قىلىش يۈزىسىدىن) سەجدە قىلغان ھالدا كىرىڭلار، (پەرۋەردىگارىمىز) گۇناھلىرىمىزنى كەچۈرگىن دەڭلار، گۇناھىڭلارنى مەغپىرەت قىلىمىز، ياخشىلارغا تېخىمۇ ئارتۇق ساۋاب بېرىمىز&raquo; دېدۇق';
$t[59] = 'زۇلۇم قىلغۇچىلار ئۆزلىرىگە سۆزلەنگەن سۆزنى باشقا سۆزگە ئۆزگەرتىۋەتتى، زۇلۇم قىلغۇچىلار يولدىن چىققانلىقلىرى (يەنى اﷲ قا ئاسىيلىق قىلغانلىقلىرى) ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب چۈشۈردۇق';
$t[60] = 'ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمى ئۈچۈن سۇ تەلەپ قىلغان ئىدى، بىز ئۇنىڭغا: &laquo;ھاساڭ بىلەن تاشنى ئۇرغىن&raquo; دېدۇق. تاشتىن 12 بۇلاق ئېتىلىپ چىقتى، ھەممە ئادەم (يەنى قەبىلە) ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى، (ئۇلارغا) &laquo;اﷲ بەرگەن رىزىقتىن يەڭلار ۋە ئىچىڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار&raquo; (دېدۇق)';
$t[61] = 'ئۆز ۋاقتىدا (سىنا چۆلىدە تەرەنجىبىن ۋە بۆدۈنە بىلەنلا ئوزۇقلىنىۋاتقىنىڭلاردا) سىلەر: &laquo;ئى مۇسا! بىز بىر خىل يېمەكلىككە چىداپ تۇرالمايمىز، بىز ئۈچۈن پەرۋەردىگارىڭغا دۇئا قىلغىن، بىزگە زېمىننىڭ كۆكتاتلىرىدىن تەرخەمەك، سامساق، يېسىمۇق ۋە پىيازلارنى ئۆستۈرۈپ بەرسۇن&raquo; دېدىڭلار، مۇسا: &laquo;سىلەر ياخشىنى ناچارغا تېگىشەمسىلەر؟ (يەنى پىياز، سامساق، كۆكتاتنى تەرەنجىبىن بىلەن بۆدۈنىدىن ئارتۇق كۆرەمسىلەر؟) بىرەر شەھەرگە كىرىڭلار، (شۇ يەردە) تىلىگىنىڭلار بار&raquo; دېدى. ئۇلار خارلىقتا، موھتاجلىقتا قالدى. اﷲ نىڭ غەزىپىگە تېگىشلىك بولدى. بۇ ئۇلارنىڭ اﷲ نىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى تۈپەيلىدىن بولدى. بۇ اﷲ قا ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئاشقانلىقلىرى تۈپەيلىدىن بولدى';
$t[62] = 'شۈبھىسىزكى، مۆمىنلەر، يەھۇدىيلار، ناسارالار (ئارىسىدىن) ۋە پەرىشتىلەرگە، يۇلتۇزلارغا چوقۇنغۇچىلار ئارىسىدىن اﷲ قا، ئاخىرەت كۈنىگە ئىمان كەلتۈرگەن ۋە ياخشى ئەمەللەرنى قىلغانلار پەرۋەردىگارىنىڭ قېشىدا ئەجرىدىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ يوقتۇر';
$t[63] = 'ئۆز ۋاقتىدا، بىز سىلەردىن چىن ۋەدە ئالغان ۋە تۇر تېغىنى ئۈستۈڭلارغا تىكلەپ قويغان، سىلەرگە: &laquo;(دۇنيادا ھالاك بولۇشتىن، ئاخىرەتتە ئازابقا قېلىشتىن) ساقلىنىشىڭلار ئۈچۈن بىز بەرگەن كىتابنى مەھكەم تۇتۇڭلار (يەنى تەۋراتقا ئەمەل قىلىڭلار)، ئۇنىڭدىكى ئەھكاملارنى ئېسىڭلاردا ساقلاڭلار&raquo; (دېگەن ئىدۇق)';
$t[64] = 'شۇنىڭدىن كېيىن (بەرگەن ۋەدەڭلاردىن) يۈز ئۆرۈدۈڭلار، سىلەرگە اﷲ نىڭ پەزلى - مەرھەمىتى بولمىسا ئىدى، چوقۇم زىيان تارتقۇچىلاردىن بولاتتىڭلار';
$t[65] = 'ئاراڭلاردىكى شەنبە كۈنى (بېلىق تۇتماسلىق) توغرىسىدىكى شەرىئەت چەكلىمىسىدىن چىقىپ كەتكۈچىلەرنى (قانداق قىلغانلىقىمىزنى)، ئەلۋەتتە بىلىسىلەر، بىز ئۇلارغا: &laquo;خار مايمۇن بولۇپ كېتىڭلار&raquo; دېدۇق';
$t[66] = 'ئۇنى (يەنى ئۇلارغا بېرىلگەن بۇ جازانى) زامانداشلىرى ۋە كېيىنكىلەر ئۈچۈن ئىبرەت، تەقۋادارلار ئۈچۈن ۋەز - نەسىھەت قىلدۇق';
$t[67] = 'ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمىگە: &laquo;اﷲ ھەقىقەتەن سىلەرنى بىر كالا بوغۇزلاشقا بۇيرۇيدۇ&raquo; دېگەن ئىدى. ئۇلار: &laquo;بىزنى مەسخىرە قىلىۋاتامسەن؟&raquo; دېدى. مۇسا: &laquo;جاھىللاردىن (يەنى مەسخىرە قىلغۇچىلاردىن) بولۇپ قېلىشتىن اﷲ قا سېغىنىپ پاناھ تىلەيمەن&raquo; دېدى';
$t[68] = 'ئۇلار: &laquo;بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە قانداق كالا ئىكەنلىكىنى بايان قىلسۇن&raquo; دېدى. مۇسا: &laquo;اﷲ ئۇنى قېرىمۇ ئەمەس، ياشمۇ ئەمەس، ئوتتۇرا ياش بولسۇن دەپ ئېيتتى، سىلەر بۇيرۇلغاننى قىلىڭلار&raquo; دېدى';
$t[69] = 'ئۇلار: &laquo;بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە ئۇنىڭ رەڭگىنى بايان قىلسۇن&raquo; دېدى. مۇسا ئېيتتىكى، &laquo;اﷲ ھەقىقەتەن ئۇنىڭ رەڭگى قارىغۇچىلارنى زوقلاندۇرىدىغان ساپسېرىق بولسۇن دېدى&raquo;';
$t[70] = 'ئۇلار: &laquo;پەرۋەردىگارىڭغا ئىلتىجا قىلغىن، ئۇنىڭ قانداق ئىكەنلىكىنى بىزگە بايان قىلسۇن، (يەنى دېھقانچىلىققا ئىشلىتىلگەن كالىمۇ، ياكى سەمرىتىش ئۈچۈن بېقىلغان كالىمۇ؟) بۇ بىزگە مۈجمەل بولۇپ قالدى (بۇ ئېنىق بولىدىغان بولسا) خۇدا خالىسا (ئۇنى) بىز چوقۇم تاپىمىز&raquo; دېدى';
$t[71] = 'مۇسا: &laquo;اﷲ ئۇنىڭ يەر ھەيدەپ، ئېكىن سۇغۇرۇپ كۆندۈرۈلگەن بولماسلىقىنى، بېجىرىم، ئالىسى يوق (يەنى رەڭگىدە سېرىقلىقتىن باشقا رەڭ يوق) بولۇشىنى ئېيتتى&raquo; دېدى. ئۇلار: &laquo;ئەمدى تولۇق بايان قىلدىڭ&raquo; دېدى ئۇلار (كالىنى) تېپىپ كېلىپ بوغۇزلىدى، (ئۇلار سوئال سوراۋەرگەنلىكتىن) بۇنى ئورۇنلىيالمىغىلى تاس قالدى';
$t[72] = 'ئۆز ۋاقتىدا سىلەر بىر كىشىنى ئۆلتۈرگەن ۋە (قاتىلنىڭ كىملىكى) توغرىسىدا دەتالاش قىلىشقان ئىدىڭلار. سىلەر يوشۇرماقچى بولغان نەرسىنى (يەنى قاتىلنىڭ ئىشىنى) اﷲ ئاشكارىلىغۇچىدۇر';
$t[73] = '(سىلەرگە مۇسا ئارقىلىق) دېدۇقكى، كالىنىڭ بىر پارچىسى بىلەن ئۆلۈكنى ئۇرۇڭلار (ئۇ تىرىلىپ قاتىلنى ئېيتىپ بېرىدۇ)، اﷲ ئۆلۈكلەرنى شۇنداق (يەنى كۆز ئالدىڭلاردا بۇ ئۆلۈكنى تىرىلدۈرگەندەك) تىرىلدۈرىدۇ، سىلەرنى چۈشەنسۇن دپ قۇدرىتىنىڭ ئالامەتلىرىنى سىلەرگە كۆرسىتىدۇ';
$t[74] = '(ئى يەھۇدىيلار جامائەسى!) شۇنىڭدىن كېيىن (يەنى شانلىق مۆجىزىلەرنى كۆرگەندىن كېيىن) دىلىڭلار قېتىپ كەتتى، تاشتەك ياكى تاشتىنمۇ قاتتىق قېتىپ كەتتى. تاشلار ئارىسىدا ئىچىدىن (سۇ ئېتىلىپ چىقىپ) ئېرىقلار ھاسىل بولىدىغانلىرى بار، يېرىلىپ ئارىسىدىن سۇ چىقىدىغانلىرىمۇ بار؛ اﷲ تىن قورقۇپ (تاغ چوققىلىرىدىن) دومىلاپ چۈشىدىغانلىرىمۇ بار (دېمەك، تاش يۇمشايدۇ، سىلەرنىڭ دىلىڭلار بولسا يۇمشىمايدۇ ۋە تەسىرلەنمەيدۇ)، اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر';
$t[75] = '(ئى مۆمىنلەر جامائەسى! دەۋىتىڭلار ئارقىلىق) يەھۇدىيلارنىڭ ئىمان ئېيتىشىنى ئۈمىد قىلامسىلەر؟ ھالبۇكى، ئۇلاردىن بىر گۇرۇھ ئادەم، اﷲ نىڭ كالامىنى ئاڭلاپ چۈشەنگەندىن كېيىن، ئۇنى بىلىپ تۇرۇپ (قەستەن) ئۆزگەرتىۋەتتى';
$t[76] = 'ئۇلار مۆمىنلەر بىلەن ئۇچراشقانلىرىدا: &laquo;ئىمان ئېيتتۇق&raquo; دەيتتى. بىر - بىرى بىلەن يالغۇز جايدا تېپىشقانلىرىدا: &laquo;پەرۋەردىگارىڭلار قېشىدا مۆمىنلەر سىلەرگە قارشى پاكىت كۆرسەتسۇن دەپ اﷲ سىلەرگە بىلدۈرگەن ۋەھيىنى (يەنى مۇھەممەد ئەلەيھسسالامنىڭ تەۋراتتىكى سۈپەتلىرىنى) ئۇلارغا سۆزلەپ بېرەمسىلەر؟ (بۇنى) چۈشەنمەمسىلەر!&raquo; دەيتتى';
$t[77] = 'يەھۇدىيلار ئۆزلىرىنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى اﷲ نىڭ بىلىپ تۇرىدىغانلىقىنى ئۇقمامدۇ؟';
$t[78] = 'ئۇلارنىڭ ساۋاتسىزلىرى كىتابنى (يەنى تەۋراتنى) ئۇقمايدۇ، (ئەھبارلىرى ئويدۇرۇپ چىققان) ئويدۇرمىلارنىلا بىلىدۇ، گۇمان بىلەنلا ئىش قىلىدۇ';
$t[79] = 'كىتابنى ئۆز قوللىرى بىلەن يېزىپ ئۇنى ئاز پۇلغا سېتىش (يەنى دۇنيانىڭ ئازغىنا مەنپەئىتى) ئۈچۈن، بۇ اﷲ تەرىپىدىن نازىل بولغان دېگۈچىلەرگە ۋاي! قولى بىلەن يازغانلىرى (يەنى تەۋراتنى ئۆزگەرتكەنلىكلىرى) ئۈچۈن ئۇلارغا ۋاي! (بۇنىڭ بىلەن) ئېرىشكەن (ھارام) نەرسىلىرى ئۈچۈن ئۇلارغا ۋاي';
$t[80] = 'ئۇلار: &laquo;بىزنى دوزاخ ئوتى ساناقلىق كۈنلەرلا كۆيدۈرىدۇ&raquo; دەيدۇ. (ئۇلارغا) &laquo;سىلەرگە اﷲ شۇنداق قىلىشقا ۋەدە بەرگەنمۇ، (ۋەدە بەرگەن بولسا) اﷲ ۋەدىسىگە ھەرگىزمۇ خىلاپلىق قىلمايدۇ، ياكى ئۆزۈڭلار بىلمەيدىغاننى اﷲ نامىدىن دەۋاتامسىلەر؟&raquo; دېگىن';
$t[81] = 'ئۇنداق ئەمەس (سىلەرنى دوزاخ ئوتى كۆيدۈرىدۇ)، گۇناھ قىلغان ۋە گۇناھقا چۆمۈپ كەتكەنلەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ';
$t[82] = 'ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئەھلى جەننەتتۇر. ئۇلار جەننەتتە مەڭگۈ قالىدۇ';
$t[83] = 'ئۆز ۋاقتىدا بىز ئىسرائىل ئەۋلادىدىن اﷲ تىن باشقىغا ئىبادەت قىلماسلىققا، ئاتا - ئانىغا، خىش - ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە ياخشىلىق قىلىشقا، كىشىلەرگە ياخشى سۆز قىلىشقا، ناماز ئۆتەشكە، زاكات بېرىشكە ئەھدە ئالدۇق. ئاراڭلاردىكى ئازغىنا كىشىدىن باشقىڭلار ئەھدىدىن يېنىۋالدىڭلار، سىلەر ھەمىشە (ئەھدەڭلاردىن) يېنىۋالىسىلەر';
$t[84] = 'ئۆز ۋاقتىدا بىر - بىرىڭلارنىڭ قېنىنى تۆكمەسلىككە، بىر - بىرىڭلارنى يۇرتلىرىڭلاردىن ھەيدەپ چىقارماسلىققا، سىلەردىن چىن ئەھدە ئالغان ئىدۇق. كېيىن بۇنى (يەنى ئەھدىنى) ئېتىراپ قىلغان ئىدىڭلار، بۇنىڭغا ئۆزۈڭلار گۇۋاھسىلەر';
$t[85] = 'كېيىن سىلەر (ئەھدىنى بۇزۇپ) بىر - بىرىڭلارنى ئۆلتۈردۈڭلار، ئىچىڭلاردىن بىر تۈركۈم كىشىلەرنى يۇرتىدىن ھەيدەپ چىقاردىڭلار، گۇناھ ۋە دۈشمەنلىك قىلىش بىلەن، ئۇلارنىڭ قارشى تەرىپىدىكىلەرگە ياردەم بەردىڭلار، ئۇلار ئەسىر چۈشۈپ كەلسە، فىدىيە بېرىپ قۇتقۇزۇۋالىسىلەر، ھالبۇكى، ئۇلارنى يۇرتلىرىدىن ھەيدەپ چىقىرىش سىلەرگە ھارام قىلىنغان ئىدى (يەنى بىر - بىرىڭلارنى ئۆلتۈرۈش ۋە يۇرتىدىن ھەيدەپ چىقىرىشنى راۋا كۆرۈسىلەرۇ، ئۇلارنىڭ ئەسىرگە چۈشۈپ دۈشمەنلىرىڭلارنىڭ قولىدا بولۇپ قېلىشىنى راۋا كۆرمەيسىلەر). سىلەر كىتابنىڭ (يەنى تەۋراتنىڭ) بىر قىسىم ئەھكاملىرىغا ئىشىنىپ، بىر قىسىم ئەھكاملىرىنى ئىنكار قىلامسىلەر؟ سىلەردىن شۇنداق قىلغانلارنىڭ جازاسى پەقەت ھاياتىي دۇنيادا خارلىققا قېلىش، قىيامەت كۈنى قاتتىق ئازابقا دۇچار بولۇشتۇر. اﷲ قىلمىشىڭلاردىن غاپىل ئەمەستۇر';
$t[86] = 'ئەنە شۇلار ئاخىرەتنى بېرىپ، دۇنيا تىرىكچىلىكىنى سېتىۋالغان كىشىلەردۇر. شۇنىڭ ئۈچۈن، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ';
$t[87] = 'شەك - شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋرات) بەردۇق. ئۇنىڭدىن كېيىن ئارقىمۇئارقا پەيغەمبەرلەر ئەۋەتتۇق، مەريەم ئوغلى ئىساغا مۆجىزىلەر بەردۇق، ھەمدە ئۇنى روھۇلقۇددۇس (يەنى جىبرىئىل) بىلەن يۆلىدۇق. ھەرقاچان بىرەر پەيغەمبەر كۆڭلۈڭلارغا ياقمايدىغان بىرنەرسە ئېلىپ كەلسە، تەكەببۇرلۇق قىلىۋېرەمسىلەر؟ بىر قىسىم پەيغەمبەرلەرنى ئىنكار قىلدىڭلار، يەنە بىر قىسىم پەيغەمبەرلەرنى ئۆلتۈردۈڭلار';
$t[88] = 'ئۇلار: &laquo;دىللىرىمىز پەردىلەنگەن&raquo; دېدى. ئۇنداق ئەمەس، ئۇلارنىڭ كۇفرى سەۋەبلىك اﷲ ئۇلارغا لەنەت قىلدى، ئۇلاردىن ئىمان ئېيتىدىغانلار ناھايىتى ئاز';
$t[89] = 'ئۇلارغا اﷲ تەرىپىدىن ئۇلاردىكى كىتاب (تەۋرات) نى تەستىقلايدىغان كىتاب (قۇرئان) نازىل بولغان چاغدا (ئۇنىڭغا ئىشەنمىدى). ئىلگىرى ئۇلار (مۇھەممەد ئەلەيھىسسالامنى ۋاسىتە قىلىپ) كاپىرلارغا قارشى ئۆزلىرىگە ياردەم كېلىشىنى تىلەيتتى، ئۇلار بىلىدىغان (يەنى تەۋراتتا سۈپىتى بايان قىلىنغان) پەيغەمبەر كەلگەن چاغدا ئۇنى ئىنكار قىلدى. اﷲ كاپىرلارغا (يەنى پەيغەمبەرلەرنىڭ تۈگەنچىسىنى ئىنكار قىلغان يەھۇدىيلارغا) لەنەت قىلدى';
$t[90] = 'يەھۇدىيلارنىڭ اﷲ نازىل قىلغان قۇرئاننى ئىنكار قىلىشى اﷲ نىڭ ئۆز پەزلىنى (يەنى پەيغەمبەرلىكنى) خالىغان بەندىسىگە چۈشۈرگەنلىكىگە ھەسەت قىلىش يۈزىسىدىندۇر. ئۇلارنىڭ ئۆزلىرىنى (يەنى ئىمانىنى) كۇفرىغا سېتىشى ئەجەب يامان ئىشتۇر. ئۇلار ئۈستى - ئۈستىگە غەزەپكە تېگىشلىك بولدى. كاپىرلار خارلىغۇچى ئازابقا دۇچار بولىدۇ';
$t[91] = 'ئۇلارغا: &laquo;اﷲ نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار&raquo; دېيىلسە، ئۇلار: &laquo;ئۆزىمىزگە نازىل قىلىنغان كىتابقا (يەنى تەۋراتقا) ئىمان كەلتۈرىمىز&raquo; دەيدۇ. ئۇنىڭدىن كېيىنكى كىتابقا (يەنى قۇرئانغا) ئىشەنمەيدۇ، ھالبۇكى، ئۇ (يەنى قۇرئان) ھەقتۇر، ئۇلارنىڭ قولىدىكى كىتابنى تەستىق قىلغۇچىدۇر. (ئۇلارغا) &laquo;سىلەر (تەۋراتقا) ئىشىنىدىغان بولساڭلار، ئىلگىرى نېمە ئۈچۈن اﷲ نىڭ پەيغەمبەرلىرىنى ئۆلتۈردۈڭلار؟&raquo; دېگىن';
$t[92] = 'مۇسا سىلەرگە ھەقىقەتەن ئېنىق مۆجىزىلەرنى كەلتۈردى، مۇسا (تۇر تېغىغا كەتكەن) دىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ موزاينى مەبۇد قىلىۋالدىڭلار';
$t[93] = 'ئۆز ۋاقتىدا بىز سىلەردىن (تەۋراتقا ئەمەل قىلىشقا) چىن ۋەدە ئالغان ئىدۇق، سىلەرگە: &laquo;مەن نازىل قىلغان كىتابنى مەھكەم تۇتۇڭلار (بولمىسا ئۈستۈڭلارغا تاغنى تاشلايمىز)&raquo; دەپ تۇر تېغىنى ئۈستۈڭلارغا تىكلىدۇق ۋە (تەۋراتقا) قۇلاق سېلىڭلار (دېدۇق). ئۇلار: &laquo;سۆزۈڭگە قۇلاق سالدۇق ئەمرىڭگە ئاسىيلىق قىلدۇق&raquo; دېيىشتى. ئۇلارنىڭ كۇفرى تۈپەيلىدىن دىللىرىغا موزاينىڭ مۇھەببىتى سىڭىپ كەتتى. ئۇلارغا: &laquo;ئەگەر سىلەر مۆمىن بولساڭلار، ئىمانىڭلار سىلەرنى ئەجەب يامان ئىشقا بۇيرۇيدىكىنا؟ (سىلەر مۆمىن ئەمەس، چۈنكى ئىمان موزايغا چوقۇنۇشقا بۇيرۇمايدۇ)&raquo; دېگىن';
$t[94] = 'ئۇلارغا: &laquo;ئەگەر ئاخىرەت يۇرتى (يەنى جەننەت) اﷲ نىڭ قېشىدا (سىلەر گۇمان قىلغاندەك) باشقىلارغا ئەمەس، يالغۇز سىلەرگىلا خاس بولسا، (سىلەرنى جەننەتكە ئۇلاشتۇرىدىغان) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار! (جەننەت بىزگىلا خاس دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار&raquo; دېگىن';
$t[95] = 'ئۇلار قىلغان يامان ئەمەللىرى سەۋەبلىك، ئۆلۈمنى ھەرگىزمۇ ئارزۇ قىلمايدۇ. اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر';
$t[96] = 'شۈبھىسىزكى، ئۇلارنى ھاياتقا ھەممە كىشىدىن، ھەتتا مۇشرىكلاردىنمۇ ھېرىس كۆرۈسەن؛ ھەر بىرى مىڭ يىل ئۆمۈر كۆرۈشنى ئارزۇ قىلىدۇ، ئۇزۇن ئۆمۈر كۆرۈش ئۇلارنى ئازابتىن قۇتۇلدۇرۇپ قالالمايدۇ. اﷲ ئۇلار نىڭ قىلمىشلىرىنى ئېنىق كۆرۈپ تۇرغۇچىدۇر';
$t[97] = 'ئېيتقىنكى، &laquo;جىبرىئىلغا دۈشمەن بولغان ئادەم (اﷲ قا دۈشمەندۇر)، چۈنكى ئۇ (يەنى جىبرىئىل) اﷲ نىڭ ئەمرى بىلەن ئۆزىدىن ئىلگىرىكىنى (يەنى ساماۋى كىتابلارنى) تەستىق قىلغۇچى، توغرا يول كۆرسەتكۈچى، مۆمىنلەرگە بېشارەت بەرگۈچى قۇرئاننى سېنىڭ قەلبىڭگە نازىل قىلدى';
$t[98] = 'كىمكى اﷲ نى، اﷲ نىڭ پەرىشتىلىرىنى، پەيغەمبەرلىرىنى، جىبرىئىلنى، مىكائىلنى دۈشمەن تۇتىدىكەن (اﷲ نى دۈشمەن تۇتقان بولىدۇ)، شۈبھىسىزكى، اﷲ كاپىرلارنى دۈشمەن تۇتىدۇ&raquo;';
$t[99] = 'شەك - شۈبھىسىزكى، ساڭا بىز روشەن ئايەتلەرنى نازىل قىلدۇق، ئۇلارنى پەقەت توغرا يولدىن چىققانلارلا ئىنكار قىلىدۇ';
$t[100] = 'ھەر قاچان ئۇلار بىرەر ئەھدە تۈزۈشسە، بىر تۈركۈمى ئۇنى بۇزىدۇ، بەلكى ئۇلارنىڭ (يەنى يەھۇدىيلارنىڭ) تولىسى (تەۋراتقا) ئىمان ئېيتمايدۇ';
$t[101] = 'ئۇلارغا اﷲ تەرىپىدىن قوللىرىدىكى كىتابنى تەستىقلايدىغان بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە، (ئۇنىڭ پەيغەمبەرلىك دەلىللىرىنى) بىلمەيدىغاندەك، اﷲ نىڭ كىتابىنى (يەنى تەۋراتنى) ئارقىسىغا چۆرۈۋېتىدۇ (يەنى تەۋراتنىڭ سۆزلىرىگە ئەمەل قىلمايدۇ)';
$t[102] = 'ئۇلار سۇلايماننىڭ پادىشاھلىق زامانىدىكى شەيتانلارنىڭ (سېھرىگەرلىكىگە ئائىت) سۆزلىرىگە ئەگەشتى. سۇلايمان (سېھرىگەر بولغىنى ۋە سېھىر ئۆگىنىش بىلەن) كاپىر بولغىنى يوق، لېكىن شەيتانلار كىشىلەرگە سېھىر ئۆگىتىپ كاپىر بولدى. ئۇلار بابىلدىكى ھارۇت - مارۇت دېيىلىدىغان ئىككى پەرىشتىگە نازىل قىلىنغان سېھىرنى ئۈگىتەتتى. (بۇ ئىككى پەرىشتە) بىز كىشىلەرنى سىنايمىز كاپىر بولمىغىن، دېمىگىچە ھېچ كىشىگە سېھىر ئۆگەتمەيتتى. ئۇلار بۇ ئىككىسىدىن ئەر - خوتۇننى بىر - بىرىدىن ئايرىۋېتىدىغان نەرسىلەرنى (يەنى سېھىرگەرلىكنى) ئۆگىنەتتى، ئۇلار (سېھىر ئارقىلىق) اﷲ نىڭ رۇخسىتىسىز ھېچ كىشىگە زىيان يەتكۈزەلمەيتتى. ھالبۇكى، ئۇلار ئۆزلىرىگە زىيىنى بار، پايدىسى يوق نەرسىنى ئۆگىنەتتى. يەھۇدىيلار (تەۋراتنى تاشلاپ) سېھىرنى سېتىۋالغۇچىغا ئاخىرەتتە (اﷲ نىڭ رەھمىتىدىن ۋە جەننىتىدىن) ھېچ نېسىۋە يوق ئىكەنلىكىنى ئوبدان بېلەتتى، ئۇلار جانلىرىنىڭ بەدىلىگە سېتىۋالغان نەرسىسىنىڭ (يەنى ئۆزلىرى ئۈچۈن سېھىر ئۆگىنىشنى تاللىشىنىڭ) ئەجەبمۇ يامان ئىش ئىكەنلىكىنى كاشكى بىلسە ئىدى';
$t[103] = 'ئەگەر ئۇلار ئىمان كەلتۈرسە (سېھىرنى تاشلاپ) اﷲ تىن قورقسا، (ئۇلار ئۈچۈن) اﷲ نىڭ دەرگاھىدىكى ساۋاب ئەلۋەتتە ياخشى ئىدى، كاشكى ئۇلار بۇنى بىلسە ئىدى';
$t[104] = 'ئى مۆمىنلەر! (پەيغەمبەرگە) رائىنا دەپ خىتاب قىلماي، ئۇنزۇرنا دەپ خىتاب قىلىڭلار، (سۆزىگە) قۇلاق سېلىڭلار، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ';
$t[105] = 'ئەھلى كىتاب ۋە مۇشرىكلاردىن بولغان كاپىرلار پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە بىرەر ياخشىلىقنىڭ چۈشۈشىنى ياقتۇرمايدۇ، اﷲ ئۆزىنىڭ خالىغان بەندىسىگە پەيغەمبەرلىكنى خاس قىلىدۇ، اﷲ چوڭ پەزل ئىگىسىدۇر';
$t[106] = 'قانداقلا بىر ئايەتنى ئەمەلدىن قالدۇرساق ياكى ئۇنتۇلدۇرساق، (ئورنىغا) ئۇنىڭدىن ئارتۇق ياكى شۇنىڭغا ئوخشاش بىر ئايەتنى كەلتۈرىمىز. اﷲ نىڭ ھەر نەرسىگە قادىر ئىكەنلىكىنى بىلمەمسەن؟';
$t[107] = 'ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى اﷲ قا مەنسۇپ ئىكەنلىكىنى بىلمەمسەن؟ سىلەرگە اﷲ تىن باشقا ھېچقانداق دوست ۋە مەدەتكار يوقتۇر';
$t[108] = 'ئۇلار (يەنى يەھۇدىيلار) ئىلگىرى مۇسادىن سوئال سورىغاندەك، سىلەرمۇ پەيغەمبىرىڭلاردىن سوئال سورىماقچى بولامسىلەر؟ كىمكى ئىمانىنى كۇفرىغا تېگىشسە، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ';
$t[109] = 'ئەھلى كىتاب ئىچىدىكى نۇرغۇن كىشىلەر ئۆزلىرىگە ھەقىقەت (يەنى دىنىڭلارنىڭ ھەقلىقى) ئاشكارا بولغاندىن كېيىن، ھەسەت قىلىش يۈزىسىدىن سىلەرنى ئىمانىڭلاردىن قايتۇرۇپ كاپىر قىلىشنى ئارزۇ قىلىدۇ. اﷲ نىڭ ئەمرى كەلگەنگە قەدەر (يەنى اﷲ سىلەرگە ئۇلار بىلەن ئۇرۇش قىلىشقا رۇخسەت قىلغۇچە) ئۇلارنى ئەپۇ قىلىڭلار ۋە كەچۈرۈڭلار. اﷲ ھەقىقەتەن ھەر نەرسىگە قادىردۇر';
$t[110] = 'نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، ئۆزۈڭلار ئۈچۈن ئىشلىگەن ھەرقانداق ياخشى ئەمەل بولسا، اﷲ نىڭ دەرگاھىدا ئۇنىڭ ساۋابىنى تاپىسىلەر، اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر';
$t[111] = 'ئۇلار: &laquo;يەھۇدىي ياكى ناسارا بولمىغان ئادەم (يەنى يەھۇدىيلار يەھۇدىي بولمىغان ئادەم، ناسارالار ناسارا بولمىغان ئادەم) ھەرگىزمۇ جەننەتكە كىرمەيدۇ&raquo; دېيىشتى. بۇ، ئۇلارنىڭ قۇرۇق ئارزۇسىدۇر. &laquo;ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، دەلىلىڭلارنى كەلتۈرۈڭلار&raquo; دېگىن';
$t[112] = 'ئۇنداق ئەمەس، كىمكى ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى اﷲ قا تاپشۇرىدىكەن (يەنى اﷲ نىڭ ئەمرىگە بويسۇنۇپ، ئەمەلىنى خالىس اﷲ ئۈچۈن قىلىدىكەن)، ئۇ پەرۋەردىگارىنىڭ دەرگاھىدا ساۋابتىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) ھېچ قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ';
$t[113] = 'يەھۇدىيلار: &laquo;ناسارالارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)&raquo; دېدى. ناسارالارمۇ: &laquo;يەھۇدىيلارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)&raquo; دېدى. ھالبۇكى، ئۇلار كىتابنى (يەنى يەھۇدىيلار تەۋراتنى، ناسارالار ئىنجىلنى) ئوقۇيدۇ، بىلىمسىز ئادەملەر (يەنى ئەرەب مۇشرىكلىرى) مۇ ئۇلار دېگەندەك دېدى، اﷲ قىيامەت كۈنى ئۇلارنىڭ ئۆزئارا ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ';
$t[114] = 'اﷲ نىڭ مەسجىدلىرىدە اﷲ نىڭ نامىنىڭ ياد قىلىنىشىنى توسقان ۋە ئۇلارنى ۋەيران قىلىشقا ئورۇنغانلاردىنمۇ زالىم كىشى بارمۇ؟ مەسجىدلەرگە ئۇلارنىڭ پەقەت قورققان ھالدىلا كىرىشى لايىق ئىدى، ئۇلار دۇنيادا رەسۋا بولىدۇ. ئاخىرەتتە چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) دۇچار بولىدۇ';
$t[115] = 'مەشرىقمۇ، مەغرىبمۇ (يەنى پۈتۈن يەر يۈزى) اﷲ نىڭدۇر، قايسى تەرەپكە يۈزلەنسەڭلەرمۇ، ئۇ اﷲ نىڭ تەرىپى (يەنى سىلەرگە ئىختىيار قىلغان قىبلىسى) دۇر. اﷲ (نىڭ مەغپىرىتى) ھەقىقەتەن كەڭدۇر، (اﷲ) ھەممىنى بىلگۈچىدۇر';
$t[116] = 'ئۇلار (يەنى يەھۇدىيلار، ناسارالار، مۇشرىكلار): &laquo;اﷲ نىڭ بالىسى بار&raquo; دەيدۇ. اﷲ (ئۇلارنىڭ گۇمان قىلغان نەرسىسىدىن) پاكتۇر، ئۇنداق ئەمەس، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى اﷲ نىڭدۇر، ھەممىسى ئۇنىڭغا بويسۇنغۇچىدۇر';
$t[117] = 'اﷲ ئاسمانلار ۋە زېمىننى يوقتىن بار قىلغۇچىدۇر. بىر ئىشنى ۋۇجۇدقا كەلتۈرۈشنى ئىرادە قىلسا، &laquo;ۋۇجۇدقا كەل&raquo; دەيدۇ - دە، ئۇ ۋۇجۇدقا كېلىدۇ';
$t[118] = 'بىلمەيدىغانلار (يەنى قۇرەيش كاپىرلىرى): &laquo;اﷲ نېمە ئۈچۈن بىز بىلەن سۆزلەشمەيدۇ؟ ياكى نېمە ئۈچۈن بىزگە (راست پەيغەمبەرلىكنىڭ دەلىلى سۈپتىدە) بىرەر مۆجىزە كەلمەيدۇ؟&raquo; دەيدۇ. ئۇلاردىن بۇرۇن ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) شۇنداق سۆزنى قىلغان ئىدى؛ ئۇلارنىڭ دىللىرى بىر - بىرىگە ئوخشايدۇ. ئايەتلىرىمىزنى جەزمەن ئىشىنىدىغان (يەنى ھەقىقەت ئىزدەيدىغان) قەۋم ئۈچۈن ھەقىقەتەن بايان قىلدۇق';
$t[119] = 'بىز سېنى ھەقىقەتەن (مۆمىنلەرگە جەننەت بىلەن) خوش خەۋەر بەرگۈچى، (كاپىرلارنى دوزاخ ئازابىدىن) ئاگاھلاندۇرغۇچى قىلىپ ھەق (دىن) بىلەن ئەۋەتتۇق. سەن ئەھلى دوزاخ (يەنى كاپىرلاردىن ئىمان ئېيتمىغانلار) توغرىسىدا جاۋابكار ئەمەسسەن';
$t[120] = 'سەن يەھۇدىيلار ۋە ناسارالارنىڭ دىنىغا كىرمىگىچە ئۇلار سەندىن ھەرگىزمۇ رازى بولمايدۇ. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، &laquo;توغرا يول پەقەت اﷲ نىڭ يولىدۇر&raquo;. ساڭا ئىلىم كەلگەندىن كېيىن (يەنى پاكىتلىق ھەقىقەت ساڭا ئاشكارا بولغاندىن كېيىن)، ئەگەر سەن ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگىشىدىغان بولساڭ، (سېنى اﷲ نىڭ ئازابىدىن قۇتقۇزىدىغان) ھېچقانداق دوستمۇ بولمايدۇ، مەدەتكارمۇ بولمايدۇ';
$t[121] = 'بىز ئاتا قىلغان كىتابنى تېگىشلىك رەۋىشتە ئوقۇيدىغانلار بار، ئەنە شۇلار ئۇنىڭغا ئىشىنىدۇ. كىملەركى كىتابنى (يەنى قۇرئاننى) ئىنكار قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر';
$t[122] = 'ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) ھەممە جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار';
$t[123] = 'بىراۋغا بىراۋ قىلچىلىك ياردەم قىلالمايدىغان، ھېچ كىشىنىڭ فىدىيىسى قوبۇل قىلىنمايدىغان، ھېچ كىشىنىڭ شاپائىتى پايدا قىلمايدىغان، ئۇلارغا (يەنى كاپىرلار ۋە گۇناھكارلارغا) ياردەم قىلىنمايدىغان كۈندىن قورقۇڭلار';
$t[124] = 'ئۆز ۋاقتىدا ئىبراھىمنى پەرۋەردىگارى بىر قانچە ئەمرلەر (يەنى شەرىئەت تەكلىپلىرى) بىلەن سىنىدى، ئىبراھىم ئۇلارنى بەجا كەلتۈردى. اﷲ ئۇنىڭغا: &laquo;سېنى چوقۇم كىشىلەرگە (دىندا) پېشۋا قىلىمەن&raquo; دېدى، ئىبراھىم: &laquo;مېنىڭ بىر قىسىم ئەۋلادىمنىمۇ پېشۋا قىلساڭ&raquo; دېدى. اﷲ: &laquo;مېنىڭ (پېشۋا قىلىش) ئەھدەمگە زالىملار (يەنى كاپىرلار) ئېرىشەلمەيدۇ&raquo; دېدى';
$t[125] = 'ئۆز ۋاقتىدا بىز كەبىنى كىشىلەر ئۈچۈن جەم بولىدىغان جاي ۋە ئامان جاي قىلىپ بەردۇق. (كىشىلەرگە) ماقامى ئىبراھىمنى نامازگاھ قىلىڭلار (يەنى شۇ يەردە ناماز ئوقۇڭلار دېدۇق). ئىبراھىم بىلەن ئىسمائىلغا ئۆيۈمنى (يەنى كەبەمنى) تاۋاپ قىلغۇچىلار، ئېتىكاپتا ئولتۇرغۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار ئۈچۈن پاك تۇتۇشنى بۇيرۇدۇق';
$t[126] = 'ئۆز ۋاقتىدا ئىبراھىم: &laquo;پەرۋەردىگارىم! بۇ يەرنى (يەنى مەككىنى) تېنىچ شەھەر قىلىپ بەرگىن، ئاھالىسىدىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقانلارنى تۈرلۈك مېۋىلەر بىلەن رىزىقلاندۇرغىن&raquo; دېدى. اﷲ: &laquo;كاپىر بولغان ئادەمنىمۇ (رىزىقتىن) ئازغىنا مۇددەت (يەنى ھاياتلىقىدا) بەھرىمەن قىلىمەن، ئاندىن كېيىن ئۇنى (ئاخىرەتتە) دوزاخقا ھەيدەيمەن&raquo; دېدى. (بۇ) نېمىدېگەن يامان ئاقىۋەت!';
$t[127] = 'ئۆز ۋاقتىدا ئىبراھىم بىلەن ئىسمائىل كەبىنىڭ ئۇلىنى قوپۇرۇۋېتىپ: &laquo;پەرۋەردىگارىمىز بىزنىڭ (خىزمىتىمىزنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمىزنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمىزنى) بىلىپ تۇرغۇچىسەن';
$t[128] = 'پەرۋەردىگارىمىز! ئىككىمىزنى ئۆزۈڭگە ئىتائەتمەن قىلغىن، بىزنىڭ ئەۋلادلىرىمىزدىنمۇ ئۆزۈڭگە ئىتائەتمەن ئۈممەت چىقارغىن، بىزگە ھەجىمىزنىڭ قائىدىلىرىنى بىلدۈرگىن، تەۋبىمىزنى قوبۇل قىلغىن، چۈنكى سەن تەۋبىنى ناھايىتى قوبۇل قىلغۇچىسەن، ناھايىتى مېھرىبانسەن';
$t[129] = 'پەرۋەردىگارىمىز! ئۇلارنىڭ ئىچىدىن ئايەتلىرىڭنى ئۇلارغا تىلاۋەت قىلىپ بېرىدىغان، كىتابىڭنى (يەنى قۇرئاننى)، ھېكمەتنى (يەنى پاك سۈننەتنى) ئۇلارغا ئۆگىتىدىغان، ئۇلارنى (مۇشرىكلىك ۋە گۇناھلاردىن) پاك قىلىدىغان بىر پەيغەمبەر ئەۋەتكىن، ھەقىقەتەن سەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن&raquo; دېدى';
$t[130] = 'ئۆزىنى ئەخمەق قىلغانلاردىن باشقا كىم ئىبراھىمنىڭ دىنىدىن يۈز ئۆرۈيدۇ، ھەقىقەتەن بىز ئۇنى بۇ دۇنيادا (پەيغەمبەرلىككە) تاللىدۇق. شەك - شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ';
$t[131] = 'ئۆز ۋاقتىدا پەرۋەردىگارى ئۇنىڭغا: &laquo;(پەرۋەردىگارىڭنىڭ ئەمرىگە) ئىتائەت قىلغىن&raquo; دېدى. ئۇ: &laquo;ئالەملەرنىڭ پەرۋەردىگارىغا ئىتائەت قىلدىم&raquo; دېدى';
$t[132] = 'ئىبراھىم ۋە يەئقۇب ئۆز ئوغۇللىرىغا ۋەسىيەت قىلىپ: &laquo;ئى ئوغۇللىرىم! اﷲ سىلەرگە مۇشۇ دىنىنى (يەنى ئىسلام دىنىنى) تاللىدى، پەقەت مۇسۇلمان پىتىڭلارچە ئۆلۈڭلار (يەنى ئىمانىڭلاردا مەھكەم تۇرۇڭلار، تاكى ئىمان بىلەن كېتىڭلار)&raquo; دېدى';
$t[133] = 'يەئقۇب جان ئۈزۈۋاتقان ۋاقتىدا سىلەر يېنىدا بارمىدىڭلار؟ شۇ چاغدا يەئقۇب ئوغۇللىرىدىن: &laquo;مەن ئۆلگەندىن كېيىن سىلەر كىمگە ئىبادەت قىلىسىلەر؟&raquo; دەپ سورىدى. ئۇلار: &laquo;سېنىڭ ئىلاھىڭ ۋە ئاتا - بوۋىلىرىڭ ئىبراھىم، ئىسمائىل، ئىسھاقلارنىڭ ئىلاھى بولغان بىر اﷲ قا ئىبادەت قىلىمىز، بىز ئۇنىڭغا ئىتائەت قىلغۇچىمىز&raquo; دېدى';
$t[134] = 'ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى (نىڭ ساۋابى) ئۆزلىرى ئۈچۈندۇر. سىلەرنىڭ ئەمەلىڭلار (نىڭمۇ ساۋابى) ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس';
$t[135] = 'ئۇلار: &laquo;يەھۇدىي ياكى ناسارا بولۇڭلار، توغرا يول تاپىسىلەر&raquo; دەيدۇ. &laquo;ئۇنداق ئەمەس، بىز ئىبراھىمنىڭ ئىسلامغا ئۇيغۇن دىنىغا ئەگىشىمىز، ئۇ مۇشرىكلاردىن ئەمەس ئىدى&raquo; دېگىن';
$t[136] = 'ئېيتىڭلاركى، &laquo;اﷲ قا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغان ۋەھيىگە، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇلارنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھيىگە، مۇساغا بېرىلگەن (تەۋراتقا)، ئىساغا بېرىلگەن (ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەن (كىتابلارغا) ئىمان ئېيتتۇق، ئۇلاردىن ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى يەھۇدىيلار ۋە ناسارالارغا ئوخشاش، ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ بەزىسىنى ئىنكار قىلمايمىز)، بىز اﷲ قا بويسۇنغۇچىلارمىز&raquo;';
$t[137] = 'ئەگەر ئۇلار (يەنى ئەھلى كىتاب) سىلەر ئىمان ئېيتقاندەك ئىمان ئېيتسا، ئەلۋەتتە، توغرا يول تاپقان بولىدۇ. ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (سەن بىلەن) ئاداۋەتتە بولغان بولىدۇ، ئۇلارغا قارشى اﷲ ساڭا كۇپايىدۇر. اﷲ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ھىيلە - مىكرىنى) بىلىپ تۇرغۇچىدۇر';
$t[138] = 'اﷲ نىڭ دىنىدا چىڭ تۇرۇڭلار، اﷲ نىڭ دىنىدىن ياخشى كىمنىڭ دىنى بار؟ بىز اﷲ قا ئىبادەت قىلغۇچىلارمىز';
$t[139] = '&laquo;بىز بىلەن اﷲ توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ ھالبۇكى، ئۇ ھەم بىزنىڭ پەرۋەردىگارىمىز، ھەم سىلەرنىڭ پەرۋەردىگارىڭلاردۇر. بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، بىز اﷲ قا سادىقمىز&raquo; دېگىن';
$t[140] = '&laquo;ياكى ئىبراھىم، ئىسمائىل، ئىسھاق، يەئقۇب ۋە ئۇنىڭ ئەۋلادى يەھۇدىي ئىدى ياكى ناسارا ئىدى&raquo; دەمسىلەر؟ ئېيتقىنكى، &laquo;(ئۇلارنىڭ قايسى دىندا ئىكەنلىكىنى) سىلەر ئوبدان بىلەمسىلەر؟ ياكى اﷲ ئوبدان بىلەمدۇ؟ قولىدا اﷲ نازىل قىلغان دەلىل - ئىسپات تۇرۇپ ئۇنى يوشۇرغان ئادەمدىن زالىم كىم بار؟ اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر&raquo;';
$t[141] = 'ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى ئۆزلىرى ئۈچۈندۇر. سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس';
$t[142] = 'بەزى ئەخمەق كىشىلەر: &laquo;ئۇلارنى (يەنى پەيغەمبەر ئەلەيھىسسالام بىلەن مۆمىنلەرنى) قاراپ كېلىۋاتقان قىبلىسىدىن (يەنى بەيتۇلمۇقەددەستىن) نېمە يۈز ئۆرۈگۈزگەندۇ؟&raquo; دەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، &laquo;مەشرىق ۋە مەغرىب (يەنى ھەممە تەرەپ) اﷲ نىڭدۇر، اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ&raquo;';
$t[143] = 'شۇنىڭدەك (يەنى سىلەرنى ئىسلامغا ھىدايەت قىلغاندەك) كىشىلەرگە (يەنى ئۆتكەنكى ئۈممەتلەرگە) شاھىت بولۇشۇڭلار ئۈچۈن ۋە پەيغەمبەرنىڭ سىلەرگە شاھىت بولۇشى ئۈچۈن، بىز سىلەرنى ياخشى ئۈممەت قىلدۇق. سەن يۈز كەلتۈرۈپ كېلىۋاتقان تەرەپنى (يەنى بەيتۇلمۇقەددەسنى) قىبلە قىلغانلىقىمىز پەيغەمبەرگە ئەگەشكەنلەرنى ئاسىيلىق قىلغانلاردىن (يەنى مۇرتەد بولۇپ كەتكەنلەردىن) ئايرىۋېلىشىمىز ئۈچۈنلا ئىدى. اﷲ ھىدايەت قىلغانلاردىن باشقىلارغا بۇ (يەنى قىبلىنىڭ ئۆزگەرتىلىشى) ھەقىقەتەن ئېغىردۇر. اﷲ سىلەرنىڭ ئىمانىڭلارنى (يەنى ئىلگىرى بەيتۇلمۇقەددەسكە قاراپ ئوقۇغان نامىزىڭلارنى) بىكار قىلىۋەتمەيدۇ. اﷲ كىشىلەرگە ھەقىقەتەن مېھرىباندۇر، ناھايىتى كۆيۈمچاندۇر';
$t[144] = 'بىز سېنىڭ (كەبە قىبلەڭ بولۇشىنى تىلەپ) قايتا - قايتا ئاسمانغا قارىغانلىقىڭنى كۆرۈپ تۇرۇۋاتىمىز. سېنى چوقۇم سەن ياقتۇرىدىغان قىبلىگە يۈزلەندۈرىمىز. (نامازدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) قەيەردە بولماڭلار (نامازدا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. ھەقىقەتەن كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) بۇنىڭ (يەنى قىبلىنىڭ كەبىگە ئۆزگەرتىلىشىنىڭ) پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى، چوقۇم بىلىدۇ، اﷲ ئۇلارنىڭ قىلمىشىدىن غاپىل ئەمەس';
$t[145] = 'كىتاب بېرىلگەنلەرگە بارچە دەلىلنى كەلتۈرسەڭمۇ، ئۇلار سېنىڭ قىبلەڭگە بويسۇنمايدۇ، سەنمۇ ئۇلارنىڭ قىبلىسىگە بويسۇنمايسەن؛ ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) ھەم ئۆز ئارا بىر - بىرىنىڭ قىبلىسىگە بويسۇنمايدۇ. ساڭا ئىلىم (يەنى ۋەھيى) كەلگەندىن كېيىن، مۇبادا ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنساڭ، ئۇ چاغدا سەن چوقۇم زۇلۇم قىلغانلاردىن بولىسەن';
$t[146] = 'بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار بىلەن ناسارالار) ئۇنى (يەنى پەيغەمبەرنى) ئۆز ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ. ئۇلارنىڭ بىر پىرقىسى، شۈبھىسىزكى، ھەقىقەتنى بىلىپ تۇرۇپ يوشۇرىدۇ';
$t[147] = 'ھەقىقەت اﷲ تەرىپىدىن كەلگەندۇر، ھەرگىز شەك كەلتۈرگۈچىلەردىن بولمىغىن';
$t[148] = 'ھەر (ئۈممەت) نىڭ يۈز كەلتۈرىدىغان تەرىپى (يەنى قىبلىسى) بار، (ئى مۆمىنلەر!) ياخشى ئەمەللەرنى قىلىشقا ئالدىراڭلار، قەيەردە بولماڭلار، (قىيامەت كۈنى) اﷲ ھەممەڭلارنى (ھېساب ئۈچۈن) يىغىدۇ، اﷲ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر';
$t[149] = '(قەيەردىن سەپەرگە چىقمىغىن، نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم (يەنى كەبە) تەرەپكە قىلغىن؛ بۇ، ھەقىقەتەن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر، اﷲ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر';
$t[150] = 'كىشىلەر (يەنى يەھۇدىيلار ۋە مۇشرىكلار) گە سىلەرگە قارشى دەلىل - ئىسپات بولماسلىقى ئۈچۈن، (ئى مۇھەممەد! سەپەر قىلىشقا) قەيەردىن چىقمىغىن، (نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) سىلەرمۇ قەيەردە بولماڭلار، (نامىزىڭلاردا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. پەقەت ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلارلا (قىبلىنىڭ ئۆزگەرگەنلىكىنى سىلەرگە قارشى دەلىل قىلىۋالىدۇ)، ئۇلار (يەنى زالىملار) دىن قورقماڭلار، (مېنىڭ ئەمرىمنى تۇتۇش بىلەن) مەندىن قورقۇڭلار. (قىبلىنى ئۆزگەرتىشكە ئەمر قىلىشىم) سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزۈشۈم ئۈچۈن ۋە توغرا يولدا بولۇشۇڭلار ئۈچۈندۇر';
$t[151] = 'شۇنىڭدەك (يەنى سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزگىنىمدەك) ئۆز ئىچىڭلاردىن سىلەرگە بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ بېرىدىغان، سىلەرنى (مۇشرىكلىك ۋە گۇناھدىن) پاك قىلىدىغان، سىلەرگە كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى دىنىي ئەھكاملارنى) ئۆگىتىدىغان، سىلەرگە بىلمىگەنلىرىڭلارنى بىلدۈرىدىغان بىر پەيغەمبەر ئەۋەتتۇق';
$t[152] = 'مېنى (تائەت - ئىبادەت بىلەن) ياد ئېتىڭلار، (مېنى ياد ئەتسەڭلار) مەنمۇ سىلەرنى (ساۋاب بېرىش بىلەن، مەغپىرەت قىلىش بىلەن) ياد ئېتىمەن. ماڭا شۈكۈر قىلىڭلار، ناشۈكۈرلۈك قىلماڭلار';
$t[153] = 'ئى مۆمىنلەر! سەۋر ئارقىلىق ۋە ناماز ئارقىلىق ياردەم تىلەڭلار. اﷲ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر';
$t[154] = 'اﷲ نىڭ يولىدا ئۆلتۈرۈلگەنلەرنى (يەنى شېھىتلەرنى) ئۆلۈك دېمەڭلار، بەلكى ئۇلار تىرىكتۇر، لېكىن سىلەر بۇنى سەزمەيسىلەر';
$t[155] = 'بىز سىلەرنى بىرئاز قورقۇنچ بىلەن، بىرئاز قەھەتچىلىك بىلەن ۋە ماللىرىڭلارغا، جانلىرىڭلارغا، بالىلىرىڭلارغا، زىرائەتلىرىڭلارغا يېتىدىغان زىيان بىلەن چوقۇم سىنايمىز. (بېشىغا كەلگەن مۇسىبەت، زىيان - زەخمەتلەرگە) سەۋر قىلغۇچىلارغا (جەننەت بىلەن) خۇش خەۋەر بەرگىن';
$t[156] = 'ئۇلارغا بىرەر مۇسىبەت كەلگەن چاغدا، ئۇلار: &laquo;بىز ئەلۋەتتە اﷲ نىڭ ئىگىدارچىلىقىدىمىز (يەنى اﷲ نىڭ بەندىلىرىمىز)، چوقۇم اﷲ نىڭ دەرگاھىغا قايتىمىز&raquo; دەيدۇ';
$t[157] = 'ئەنە شۇلار پەرۋەردىگارىنىڭ مەغپىرىتى ۋە رەھمىتىگە ئېرىشكۈچىلەردۇر، ئەنە شۇلار ھىدايەت تاپقۇچىلاردۇر';
$t[158] = 'سەفا بىلەن مەرۋە ھەقىقەتەن اﷲ نىڭ (دىنىنىڭ) ئالامەتلىرىدۇر. ھەج قىلغان ياكى ئۆمرە قىلغان ئادەم ئۇلارنىڭ ئارىسىدا سەئيى قىلسا ھېچ باك يوق. كىمكى ئۆز ئىختىيارى بىلەن بىرەر ياخشىلىق قىلىدىكەن (اﷲ ئۇنىڭ مۇكاپاتىنى بېرىدۇ)، اﷲ ئەلۋەتتە شۈكۈرنىڭ مۇكاپاتىنى بەرگۈچىدۇر، اﷲ ھەممىنى بىلگۈچىدۇر';
$t[159] = 'بۇ كىتابتا (يەنى تەۋراتتا) كىشىلەرگە توغرا يولنى ئېنىق بايان قىلغىنىمىزدىن كېيىن، بىز نازىل قىلغان (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان) روشەن دەلىللەرنى ۋە توغرا يولنى يوشۇرىدىغانلارغا اﷲ لەنەت قىلىدۇ (يەنى رەھمىتىدىن يىراق قىلىدۇ)؛ لەنەت قىلغۇچىلار (يەنى پەرىشتىلەر ۋە مۆمىنلەر) مۇ ئۇلارغا لەنەت قىلىدۇ';
$t[160] = 'پەقەت تەۋبە قىلغانلار، (ئەمەلىنى) تۈزەتكەنلەر، يەھۇدىيلارنىڭ تەۋرات ئەھكاملىرىدىن يوشۇرغانلىرىنى) بايان قىلغانلارلا بۇنىڭدىن مۇستەسنا. ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىمەن، مەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇرمەن. ناھايىتى مېھرىباندۇرمەن';
$t[161] = 'شۈبھىسىزكى، كاپىر بولۇپ، كاپىر پېتى ئۆلگەنلەر بار، ئەنە شۇلار چوقۇم اﷲ نىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچرايدۇ';
$t[162] = 'ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا بېرىلىدىغان ئازاب كېچىكتۈرۈلمەيدۇ';
$t[163] = 'سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر؛ ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر';
$t[164] = 'ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىشىدە، كىشىلەرگە پايدىلىق نەرسىلەرنى ئېلىپ دېڭىزدا ئۈزۈپ يۈرگەن كېمىلەردە، اﷲ بۇلۇتتىن ياغدۇرۇپ بەرگەن، ئۆلگەن زېمىننى تىرىلدۈرىدىغان يامغۇردا، يەر يۈزىگە تارقىتىۋەتكەن ھايۋاناتلاردا، شامال يۆنۈلۈشلىرىنىڭ ئۆزگىرىپ تۇرۇشىدا، ئاسمان - زېمىن ئارىسىدا بويسۇندۇرۇلغان بۇلۇتلاردا، چۈشىنىدىغان كىشىلەر ئۈچۈن، ئەلۋەتتە (اﷲ نىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىللەر باردۇر';
$t[165] = 'بەزى ئادەملەر اﷲ تىن غەيرىيلەرنى (يەنى بۇتلارنى) اﷲ قا شېرىك قىلىۋالىدۇ، ئۇلارنى (مۆمىنلەرنىڭ) اﷲ نى دوست تۇتقىنىدەك دوست تۇتىدۇ، (يەنى ئۇلۇغلايدۇ ۋە ئۇلارغا بويسۇنىدۇ). مۆمىنلەر اﷲ نى ھەممىدىن بەك دوست تۇتقۇچىلاردۇر، زالىملار (قىيامەت كۈنى ئۆزلىرىگە تەييارلانغان) ئازابنى كۆرگەن چاغدا پۈتۈن كۈچ - قۇۋۋەتنىڭ اﷲ قا مەنسۈپ ئىكەنلىكىنى ۋە اﷲ نىڭ ئازابىنىڭ قاتتىق ئىكەنلىكىنى كاشكى بىلسە ئىدى (دۇنيادا اﷲ قا شېرىك كەلتۈرگەنلىكلىرىگە ھەددىدىن زىيادە پۇشايمان قىلاتتى)';
$t[166] = 'ئەينى ۋاقىتتا ئەگەشتۈرگۈچىلەر ئازابنى كۆرۈپ، ئەگەشكۈچىلەردىن ئادا - جۇدا بولىدۇ. ئۇلارنىڭ ئۆزئارا مۇناسىۋىتى (يەنى دۇنيادىكى دوستلۇقى) ئۈزۈلىدۇ';
$t[167] = 'ئەگەشكۈچىلەر: &laquo;كاشكى بىزلەرگە (دۇنياغا) قايتىشقا بولسا ئىدى، ئۇلار بىزدىن ئادا - جۇدا بولغاندەك بىزمۇ ئۇلاردىن ئادا - جۇدا بولاتتۇق&raquo; دەيدۇ. اﷲ ئۇلارغا (يامان) ئىشلىرىنىڭ ھەسرەت بولغانلىقىنى مۇشۇنداق كۆرسىتىدۇ، ئۇلار دوزاختىن چىقمايدۇ';
$t[168] = 'ئى ئىنسانلار! يەر يۈزىدىكى ھالال - پاكىز نەرسىلەردىن يەڭلار، شەيتاننىڭ يوللىرىغا ئەگەشمەڭلار، چۈنكى شەيتان سىلەرگە ئوچۇق دۈشمەندۇر';
$t[169] = 'شەيتان سىلەرنى ھەقىقەتەن يامانلىقلارغا، قەبىھ گۇناھلارغا ۋە اﷲ نامىدىن، يالغاندىن (اﷲ سىلەرگە ھالال قىلغان نەرسىلەرنى ھارام دەپ ۋە ھارام قىلغان نەرسىلەرنى ھالال دەپ)، سىلەر بىلمەيدىغان نەرسىلەرنى ئېيتىشقا بۇيرۇيدۇ';
$t[170] = 'ھەرقاچان ئۇلارغا (يەنى مۇشرىكلارغا): &laquo;اﷲ نازىل قىلغان نەرسىگە (يەنى اﷲ پەيغەمبىرىگە نازىل قىلغان ۋەھيىگە، قۇرئانغا) ئەگىشىڭلار&raquo; دېيىلسە، ئۇلار: &laquo;ياق، ئاتا - بوۋىلىرىمىزدىن قالغان دىنغا ئەگىشىمىز&raquo; دېيىشىدۇ. ئاتا - بوۋىلىرى ھېچ نەرسىنى چۈشەنمىگەن ۋە توغرا يولدا بولمىغان تۇرسا، يەنە ئۇلارغا ئەگىشەمدۇ؟';
$t[171] = 'كاپىرلەرنى ھىدايەتكە دەۋەت قىلغۇچى خۇددى مەنىسىنى چۈشەنمەي تاۋۇشىنى ۋە نىدانىلا ئاڭلايدىغان چاھارپايلارنى چاقىرغان ئادەمگە ئوخشايدۇ (كاپىرلار ھەقنى ئاڭلاشتىن) گاستۇر، (ھەقنى سۆزلەشتىن) گاچىدۇر، (ھەقنى كۆرۈشتىن) كوردۇر، ئۇلار (ۋەز - نەسىھەتنى چۈشەنمەيدۇ';
$t[172] = 'ئى مۆمىنلەر! بىز سىلەرنى رىزىقلاندۇرغان ھالال نەرسىلەردىن يەڭلار، ئەگەر اﷲ غىلا ئىبادەت قىلىدىغان بولساڭلار، اﷲ قا (يەنى اﷲ نىڭ ھەددى - ھېسابسىز نېمەتلىرىگە) شۈكۈر قىلىڭلار';
$t[173] = 'اﷲ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، اﷲ تىن غەيرىينىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى، كىمكى ئۆز ئىختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك يېسە، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ. ھەقىقەتەن اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[174] = 'اﷲ كىتابتا (يەنى تەۋراتتا) نازىل قىلغان نەرسىلەرنى يوشۇرىدىغان ۋە ئۇنى ئازغىنا پۇلغا ساتىدىغانلارنىڭ قارنىغا يېگىنى پەقەت ئوتتۇر (چۈنكى ئۇلارنىڭ يېگەن شۇ ھارام مېلى ئۇلارنى دوزاخقا ئېلىپ بارىدۇ)، قىيامەت كۈنى اﷲ ئۇلارغا (ئۇلارنى خۇشال قىلىدىغان) سۆز قىلمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ';
$t[175] = 'ئەنە شۇلار ھىدايەتنى گۇمراھلىققا، مەغپىرەتنى ئازابقا (يەنى جەننەتنى دوزاخقا) تېگىشتى، ئۇلار دوزاخ ئازابىغا نېمىدېگەن چىداملىق!';
$t[176] = 'بۇ (قاتتىق ئازاب) شۇنىڭ ئۈچۈنكى، اﷲ ھەقىقەتەن ھەق كىتابنى (تەۋراتنى) نازىل قىلدى (ئۇلار تەۋراتتىكى نەرسىلەرنى يوشۇردى ۋە ئۆزگەرتىۋەتتى)، كىتاب توغرىسىدا ئىختىلاپ قىلغۇچىلار چۇڭقۇر نىزادىدۇر';
$t[177] = 'سىلەرنىڭ كۈن چىققان ۋە كۈن پاتقان تەرەپكە يۈزكەلتۈرۈشۈڭلارنىڭ ئۆزىلا ياخشى ئەمەلگە ياتمايدۇ. بەلكى اﷲ قا، ئاخىرەت كۈنىگە، پەرىشتىلەرگە، كىتابقا (يەنى اﷲ نازىل قىلغان كىتابلارغا)، پەبغەمبەرلەرگە ئىمان كەلتۈرۈش، اﷲ نى سۆيۈش يۈزىسىدىن خىش - ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە (يەنى پۇل - مېلىدىن ئالاقىسى ئۈزۈلۈپ قالغان مۇساپىرلارغا)، سائىللارغا ۋە قۇللارنىڭ ئازادلىققا ئېرىشىشىگە پۇل - مال ياردەم بېرىش، ناماز ئوقۇش، زاكات بېرىش، ئەھدىگە ۋاپا قىلىش، يوقسۇزلۇققا، كېسەللىككە ۋە (اﷲ نىڭ يولىدا قىلىنغان) ئۇرۇشقا بەرداشلىق بېرىش ياخشى ئەمەلگە كىرىدۇ. ئەنە شۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) (ئىمانىدا) راستچىل ئادەملەردۇر، ئەنە شۇلار تەقۋادار ئادەملەردۇر';
$t[178] = 'ئى مۆمىنلەر! ئۆلتۈرۈلگەنلەر ئۈچۈن قىساس ئېلىش سىلەرگە پەرز قىلىندى، ھۆر ئادەم ئۈچۈن ھۆر ئادەمدىن، قۇل ئۈچۈن قۇلدىن، ئايال ئۈچۈن ئايالدىن قىساس ئېلىنىدۇ. قاتىل ئۈچۈن (دىنىي) قېرىندىشى تەرىپىدىن بىرنەرسە كەچۈرۈم قىلىنسا (يەنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى قاتىلدىن دىيەت ئېلىشقا رازى بولۇپ قىساس ئېلىشتىن ۋاز كەچسە، ئۇ، جىنايەتچىدىن دىيەتنى) چىرايلىقچە تەلەپ قىلىشى لازىم، (جىنايەتچىمۇ دىيەتنى) ياخشىلىقچە (يەنى كېچىكتۈرمەستىن، كېمەيتىۋەتمەستىن تولۇق) بېرىشى لازىم. بۇ (ھۆكۈم) پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە بېرىلگەن) يېنىكلىتىشتۇر ۋە رەھمەتتۇر. شۇنىڭدىن كېيىن (يەنى دىيەتنى قوبۇل قىلغاندىن كېيىن)، چېكىدىن ئېشىپ كەتكەن (يەنى قاتىلنى ئۆلتۈرگەن) ئادەم قاتتىق ئازابقا دۇچار بولىدۇ';
$t[179] = 'ئى ئەقىل ئىگىلىرى! سىلەرگە قىساستا ھاياتلىق بار، (يەنى كىشى بىراۋنى ئۆلتۈرگەن تەقدىردە ئۆزىنىڭمۇ ئۆلتۈرۈلىدىغانلىقىنى بىلسە، ئۇ، ئادەم ئۆلتۈرۈشتىن يانىدۇ، شۇنىڭ بىلەن، ئۇنىڭ ئۆزىمۇ، ئۇ ئۆلتۈرمەكچى بولغان ئادەممۇ ئۆلۈشتىن ساقلىنىپ قالىدۇ). (ناھەق قان تۆكۈشتىن) ساقلىنىشىڭلار ئۈچۈن (قىساس يولغا قويۇلدى)';
$t[180] = 'ئەگەر سىلەردىن بىرەيلەن ئۆلۈش ئالدىدا كۆپ مال قالدۇرسا، ئۇنىڭ ئاتا - ئانىسىغا ۋە خىش - ئەقرىبالىرىغا ئادىللىق بىلەن ۋەسىيەت قىلىشى تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك بۇرچى سۈپىتىدە پەرز قىلىندى';
$t[181] = 'ۋەسىيەتنى ئاڭلىغاندىن كىيىن، كىمكى ئۇنى ئۆزگەرتىدىكەن، ئۇنىڭ گۇناھى ئۆزگەرتكەنلەرگە يۈكلىنىدۇ. اﷲ ھەقىقەتەن ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر';
$t[182] = 'كىمكى ۋەسىيەت قىلغۇچىنىڭ سەۋەنلىكى ياكى قەستەن ناتوغرا قىلغانلىقىنى بىلىپ، ئۇلارنىڭ (يەنى ۋەسىيەت قىلغۇچىلار بىلەن ۋەسىيەت قىلىنغۇچىلارنىڭ) ئارىسىنى تۈزەپ قويسا، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ، اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[183] = 'ئى مۆمىنلەر! (گۇناھلاردىن) ساقلىنىشىڭلار ئۈچۈن، سىلەردىن ئىلگىرىكىلەرگە (يەنى ئىلگىرىكى ئۈممەتلەرگە) روزا پەرز قىلىنغاندەك، سىلەرگىمۇ (رامىزان روزىسى) پەرز قىلىندى';
$t[184] = '(بۇ پەرز قىلىنغان روزا) ساناقلىق كۈنلەردۇر، سىلەردىن كىمكى كېسەل ياكى سەپەر ئۈستىدە بولۇپ (روزا تۇتمىغان بولسا)، تۇتمىغان كۈنلەرنى (يەنى قازاسىنى) باشقا كۈنلەردە تۇتسۇن، روزىنى (قېرىلىق ياكى ئاجىزلىق تۈپەيلىدىن مۇشەققەت بىلەن) ئاران تۇتىدىغان كىشىلەر تۇتمىسا، (كۈنلۈكى ئۈچۈن) بىر مىسكىن تويغۇدەك تاماق فىدىيە بېرىشى لازىم. كىمكى فىدىيىنى (بەلگىلەنگەن مىقداردىن) ئارتۇق بەرسە، بۇ ئۆزى ئۈچۈن ياخشىدۇر، ئەگەر بىلسەڭلار، روزا تۇتۇش سىلەر ئۈچۈن (ئېغىز ئوچۇق يۈرۈشتىن ۋە فىدىيە بېرىشتىن) ياخشىدۇر';
$t[185] = 'رامىزان ئېيىدا قۇرئان نازىل بولۇشقا باشلىدى، قۇرئان ئىنسانلارغا يېتەكچىدۇر، ھىدايەت قىلغۇچى ۋە ھەق بىلەن ناھەقنى ئايرىغۇچى روشەن ئايەتلەردۇر، سىلەردىن كىمكى رامىزان ئېيىدا ھازىر بولسا رامىزان روزىسىنى تۇتسۇن؛ كىمكى كېسەل ياكى سەپەر ئۈستىدە (يەنى مۇساپىر) بولۇپ (تۇتمىغان بولسا، تۇتمىغان كۈنلەر ئۈچۈن) باشقا كۈنلەردە تۇتسۇن. اﷲ سىلەرگە ئاسانلىقنى خالايدۇ، تەسلىكنى خالىمايدۇ، (ئاغزىڭلار ئوچۇق يۈرگەن كۈنلەرنىڭ قازاسىنى قىلىش بىلەن رامىزان روزىسىنىڭ) سانىنى تولدۇرشۇڭلارنى، سىلەرنى ھىدايەت قىلغانلىقىغا اﷲ نى ئۇلۇغلىشىڭلارنى، (ئۇنىڭ ئىنئاملىرىغا) شۈكۈر قىلىشىڭلارنى خالايدۇ';
$t[186] = 'مېنىڭ بەندىلىرىم سەندىن مەن توغرۇلۇق سورىسا (ئۇلارغا ئېيتقىنكى)، مەن ھەقىقەتەن ئۇلارغا يېقىنمەن (يەنى ئۇلارنىڭ ئەھۋالىنى، سۆزلىرىنى بىلىپ تۇرىمەن)، ماڭا دۇئا قىلسا، مەن دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلىمەن، ئۇلار توغرا يول تېپىش ئۈچۈن مېنىڭ دەۋىتىمنى قوبۇل قىلسۇن ۋە ماڭا ئىمان ئېيتسۇن (يەنى ھەمىشە ئىمان بىلەن بولسۇن)';
$t[187] = 'روزا كېچىلىرىدە ئاياللىرىڭلارغا يېقىنچىلىق قىلىش سىلەرگە ھالال قىلىندى، ئۇلار سىلەر ئۈچۈن كىيىمدۇر (يەنى ئۇلار سىلەرگە ئارىلىشىپ ياشايدۇ ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىدۇ)، سىلەرمۇ ئۇلار ئۈچۈن كىيىمسىلەر (يەنى سىلەرمۇ ئۇلارغا ئارىلىشىپ ياشايسىلەر ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىسىلەر). اﷲ ئۆزۈڭلارنىڭ ئۆزۈڭلارنى ئالدىغانلىقىڭلارنى (يەنى روزا كېچىسى يېقىنچىلىق قىلغىنىڭلارنى) بىلدى. اﷲ تەۋبەڭلارنى قوبۇل قىلدى، سىلەرنى ئەپۇ قىلدى. ئەمدى ئۇلارغا (يەنى ئاياللىرىڭلارغا) يېقىنچىلىق قىلىڭلار، اﷲ سىلەرگە تەقدىر قىلغان نەرسىنى (يەنى پەرزەنتنى) تەلەپ قىلىڭلار، تاكى تاڭنىڭ ئاق يىپى قارا يىپىدىن ئايرىلغانغا (يەنى تاڭ يورۇغانغا) قەدەر يەڭلار، ئىچىڭلار، ئاندىن كەچ كىرگىچە روزا تۇتۇڭلار، سىلەر مەسجىدتە ئېتىكاپتا ئولتۇرغان چېغىڭلاردا (كۈندۈز بولسۇن كېچە بولسۇن)، ئۇلارغا يېقىنچىلىق قىلماڭلار، ئەنە شۇلار اﷲ بەلگىلىگەن چېگرىلاردۇر (يەنى مەنئى قىلغان ئىشلاردۇر)، ئۇنىڭغا يېقىنلاشماڭلار. كىشىلەرنىڭ (ھارام قىلىنغان ئىشلاردىن) ساقلىنىشلىرى ئۈچۈن، اﷲ ئايەتلىرىنى ئۇلارغا مۇشۇنداق بايان قىلىدۇ';
$t[188] = 'بىر - بىرىڭلارنىڭ ماللىرىنى ناھەق يەۋالماڭلار، بىلىپ تۇرۇپ كىشىلەرنىڭ بىر قىسىم ماللىرىنى زۇلۇم بىلەن يەۋېلىش ئۈچۈن ھاكىملارغا پارا بەرمەڭلار';
$t[189] = '(ئى مۇھەممەد!) سەندىن يېڭى چىققان ئاي (يەنى ئاينىڭ كىچىك چىقىپ چوڭىيىدىغانلىقى، ئاندىن كىچىكلەيدىغانلىقى) توغرۇلۇق سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، &laquo;ئۇ كىشىلەرنىڭ (تىرىكچىلىك، سودا - سېتىق، مۇئامىلە، روزا تۇتۇش، ئېغىز ئېچىش قاتارلىق) ئىشلارنىڭ ۋە ھەجنىڭ ۋاقىتلىرىنى ھېسابلىشى ئۈچۈندۇر&raquo;. (جاھىلىيەت دەۋرىدە قىلغىنىڭلاردەك ئېھرامدىكى چېغىڭلاردا) ئۆيلەرنىڭ ئارقىسىدىن (تۆشۈك ئېچىپ) كىرىش ياخشىلىققا ياتمايدۇ. (اﷲ نىڭ ئەمرىگە مۇخالىپ ئىش قىلىشتىن) قورققان ئادەم ياخشى ئەمەل (ئىگىسى) دۇر. (ئېھرامدىكى چېغىڭلاردا ئادەتتىكىدەك) ئۆيلەرنىڭ دەرۋازىلىرىدىن كىرىڭلار، بەختكە ئېرىشىشڭلار ئۈچۈن اﷲ قا تەقۋادارلىق قىلىڭلار';
$t[190] = 'سىلەرگە ئۇرۇش ئاچقان ئادەملەرگە قارشى اﷲ يولىدا جىھاد قىلىڭلار، تاجاۋۇز قىلماڭلار، تاجاۋۇز قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتمايدۇ';
$t[191] = 'ئۇلارنى (يەنى مۇشرىكلارنى) (ئۇلار ھەرەمدە بولسۇن، باشقا يەردە بولسۇن، ئۇرۇش ھارام قىلىنغان ئايلاردا بولسۇن) ئۇچراتقان يەردە ئۆلتۈرۈڭلار، سىلەرنى يۇرتۇڭلاردىن (يەنى مەككىدىن) چىقىرىۋەتكەندەك، سىلەرمۇ ئۇلارنى يۇرتىدىن چىقىرىۋېتىڭلار، زىيانكەشلىك قىلىش ئۇرۇشتىنمۇ ياماندۇر. تاكى ئۇلار ئۆزلىرى ئۇرۇش ئاچمىغىچە مەسجىدى ھەرەم ئەتراپىدا ئۇلار بىلەن ئۇرۇش قىلماڭلار، ئەگەر ئۇلار (مەسجىدى ھەرەم ئەتراپىدا) ئۆزلىرى ئۇرۇش ئاچسا، ئۇلارنى ئۆلتۈرۈۋېرىڭلار. كاپىرلارنىڭ جازاسى شۇنداق بولىدۇ';
$t[192] = 'ئەگەر ئۇلار ئۇرۇشنى توختاتسا (سىلەرمۇ ئۇرۇشتىن قول يىغىڭلار)، اﷲ (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[193] = 'تاكى زىيانكەشلىك تۈگىگەن ۋە اﷲ نىڭ دىنى يولغا قويۇلغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار ئۇرۇشنى توختاتسا، زۇلۇم قىلغۇچىلاردىن باشقىلارغا دۈشمەنلىك قىلىشقا بولمايدۇ';
$t[194] = '(ئۇرۇش) ھارام قىلىنغان ئاي (ئۇرۇش) ھارام قىلىنغان ئايغا تەڭدۇر (يەنى ئۇرۇش ھارام قىلىنغان. ئايلاردا دۈشمىنىڭلار سىلەرگە چېقىلسا، شۇ ئايلاردا سىلەرمۇ ئۇلارغا چېقىلساڭلار بولىدۇ). ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەر تەڭ - باراۋەردۇر (يەنى ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەرنى باشقىلار دەپسەندە قىلسا، ئىنتىقام ئېلىش يۈزسىدىن، سىلەرمۇ ئۇلارنى دەپسەندە قىلساڭلار ھېچ يامىنى يوق. (بىراۋ سىلەرگە قانچىلىك چېقىلغان بولسا، سىلەرمۇ ئۇنىڭغا شۇنچىلىك چېقىلىڭلار؛ اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ تەقۋادارلار بىلەن بىللىدۇر';
$t[195] = 'اﷲ نىڭ يولىدا (پۇل - مال) سەرپ قىلىڭلار، ئۆزۈڭلارنى ھالاكەتكە تاشلىماڭلار، ئېھسان قىلىڭلار، ئېھسان قىلغۇچىلارنى اﷲ ھەقىقەتەن دوست تۇتىدۇ';
$t[196] = 'ھەج بىلەن ئۆمرەنى اﷲ ئۈچۈن تولۇق ئورۇنلاڭلار، ئەگەر (دۈشمەن ياكى كېسەللىك سەۋەبىدىن) ھەجنى ياكى ئۆمرەنى ئادا قىلىشتا توسقۇنلۇققا ئۇچرىساڭلار، ئۇ ھالدا نېمە ئوڭاي بولسا (يەنى تۆگە، كالا، قويدىن نېمىنى تاپالىساڭلار)، شۇنى قۇربانلىق قىلىڭلار، قۇربانلىق قىلىنىدىغان مال جايىغا (يەنى بەلگىلەنگەن جايغا) يەتمىگىچە بېشىڭلارنى چۈشۈرمەڭلار، سىلەردىن كىمكى كېسەل (يەنى چېچىنى چۈشۈرسە زىيان قىلىدىغان كېسەل) ياكى بېشىدا ئىللەت (يەنى باش ئاغرىقى، پىتقا ئوخشاشلار) بولۇپ (بېشىنى چۈشۈرمىسە)، فىدىيە قىلىش يۈزىسىدىن (ئۈچ كۈن) روزا تۇتسۇن، ياكى (ئالتە مىسكىنگە ئۈچ سا) سەدىقە بەرسۇن، ياكى (بىرەر مال) قۇربانلىق قىلسۇن، ئەمىنلىكتە بولغان چېغىڭلاردا (ئاراڭلاردىن) ئۆمرە قىلغاندىن كېيىن ھەج قىلىشتىن بەھرىمەن بولغان ئادەم (يەنى ھەجدىن بۇرۇن ئۆمرە قىلىپ تاماملىغان ئادەم) نېمە ئوڭاي بولسا، شۇنى قۇربانلىق قىلسۇن، (پۇلى يوقلۇقتىن ياكى قۇربانلىق مال يوقلۇقىدىن قۇربانلىق مال) تاپالمىغان ئادەم ھەج جەريانىدا ئۈچ كۈن روزا تۇتسۇن، (ھەجدىن) قايتقاندىن كېيىن يەتتە كۈن روزا تۇتسۇن، ئۇنىڭ ھەممىسى ئون كۈن بولىدۇ. مانا بۇ (يەنى ئۆمرە قىلغان ئادەمگە قۇربانلىق قىلىش لازىملىقى ياكى روزا تۇتۇش ھەققىدىكى ھۆكۈم) مەسجىدى ھەرەم دائىرىسىدە (يەنى ھەرەمدە) ئولتۇرۇشلۇق بولمىغان كىشىلەر ئۈچۈندۇر. اﷲ قا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، اﷲ نىڭ ئازابى قاتتىقتۇر';
$t[197] = 'ھەج ۋاقتى مەلۇم بىرقانچە ئايدۇر (يەنى شەۋۋال، زۇلقەئدە ئايلىرى ۋە زۇلھەججە ئېيىنىڭ ئون كۈنىدۇر). بۇ ئايلاردا ھەج قىلىشنى نىيەت قىلغان (يەنى ئېھرام باغلىغان) ئادەمنىڭ جىنسىي ئالاقە قىلىشى، گۇناھ قىلىشى ۋە جاڭجال قىلىشى مەنئى قىلىنىدۇ، سىلەر قانداقلىكى ياخشى ئەمەلنى قىلساڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ (ئاخىرەتلىكىڭلار ئۈچۈن) زاد - راھىلە ئېلىۋېلىڭلار، ئەڭ ياخشى زاد - راھىلە تەقۋادارلىقتۇر. ئى ئەقىل ئىگىلىرى! ماڭا تەقۋادارلىق قىلىڭلار';
$t[198] = 'پەرۋەردىگارىڭلاردىن (ھەج مەۋسۈمىدە تىجارەت ۋە باشقا ئوقەت ئارقىلىق) رىزىق تەلەپ قىلساڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ. ئەرەفاتتىن قايتقان چېغىڭلاردا مەشئەرى ھەرەمدا (يەنى مۇزدەلىفەدە) اﷲ نى ياد ئېتىڭلار، اﷲ سىلەرنى ھىدايەت قىلغانلىقى ئۈچۈن، ئۇنى ياد ئېتىڭلار، ئىلگىرى سىلەر ھەقىقەتەن گۇمراھ ئىدىڭلار (يەنى اﷲ سىلەرنى ھىدايەت قىلىشتىن ئىلگىرى گۇمراھلار قاتارىدا ئىدىڭلار)';
$t[199] = 'ئاندىن كېيىن، سىلەر (ئەرەفاتتىن) كىشىلەر قايتقان جاي بىلەن قايتىڭلار، اﷲ تىن مەغپىرەت تەلەپ قىلىڭلار. اﷲ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[200] = 'سىلەر ھەجگە ئائىت ئىبادەتلەرنى ئادا قىلغاندىن كېيىن، اﷲ نى بولسا ئاتا - بوۋاڭلارنى (پەخىرلىنىپ) ياد ئەتكەندەك ياكى ئۇنىڭدىنمۇ زىيادە ياد ئېتىڭلار، بەزى كىشىلەر: &laquo;ئى پەرۋەردىگارىمىز! بىزگە (نېسىۋىمىزنى) بۇ دۇنيادىلا بەرگىن&raquo; دەيدۇ. ھالبۇكى، ئۇنىڭغا ئاخىرەتتە (ياخشىلىقتىن) ھېچ نېسىۋە يوقتۇر';
$t[201] = 'بەزى كىشىلەر: &laquo;پەرۋەردىگارىمىز! بىزگە دۇنيادا ياخشىلىق ئاتا قىلغىن، ئاخىرەتتىمۇ ياخشىلىق ئاتا قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن&raquo; دەيدۇ';
$t[202] = 'ئەنە شۇلار ئۆزلىرى قىلغان ئەمەللىرىنىڭ مول مېۋىسىنى كۆرىدۇ. اﷲ نىڭ ھېساب ئېلىشى تېزدۇر (يەنى شۇنچە كۆپ خالايىقتىن قىسقا ۋاقىت ئىچىدە ھېساب ئېلىپ بولىدۇ)';
$t[203] = 'ساناقلىق كۈنلەردە (يەنى تەشرىق كۈنلىرىدە) اﷲ نى ياد ئېتىڭلار (يەنى نامازلىرىڭلارنىڭ ئاخىرىدا تەكبىر ئېيتىڭلار)، (مىنادىن) ئالدىراپ ئىككى كۈندە قايتقان ئادەمگە ھېچ گۇناھ بولمايدۇ، تەخىر قىلىپ قايتقان ئادەمگىمۇ ھېچ گۇناھ بولمايدۇ. (يۇقىرىقى ئەھكاملار) تەقۋادارلىق قىلىپ (ھەجنى تولۇق ئادا قىلماقچى بولغان) ئادەم ئۈچۈندۇر. اﷲ تىن قورقۇڭلار؛ بىلىڭلاركى، سىلەر ھېساب ئۈچۈن اﷲ نىڭ دەرگاھىغا يىغىلىسىلەر';
$t[204] = '(ئى مۇھەممەد!) كىشىلەر ئارىسىدا شۇنداق ئادەم باركى، ئۇنىڭ دۇنيا تىرىكچىلىكى توغرىسىدىكى سۆزى سېنى قىزىقتۇرىدۇ (لېكىن ئۇ يالغانچى مۇناپىقتۇر)، ئۇ دىلىدىكى نەرسىگە (يەنى دىلى باشقا تىلى باشقا ئەمەسلىكىگە) اﷲ نى گۇۋاھ قىلىدۇ. ھالبۇكى، ئۇ (ساڭا ۋە ساڭا ئەگەشكۈچىلەرگە) ئەشەددىي دۈشمەندۇر (كۆرۈنۈشتە ئۇ شېرىن سۆزى ئارقىلىق دىندار قىياپەتكە كىرىۋالىدۇ)';
$t[205] = 'ئۇ (ھۇزۇرۇڭدىن) قايتقاندىن كېيىن، زېمىندا بۇزۇقچىلىق قىلىش ئۈچۈن ۋە زىرائەتلەرنى، ھايۋاناتلارنى ھالاك قىلىش ئۈچۈن تىرىشىدۇ (ئۇنىڭ بۇزغۇنچىلىقى ئەمەلدە ئىنسانلارنى ھالاك قىلىش ئۈچۈندۇر، چۈنكى زىرائەتلەرسىز ۋە ھايۋاناتلارسىز ئىنسانلارنىڭ ياشىيالىشى مۇمكىن ئەمەس). اﷲ بۇزۇقچىلىقنى ياقتۇرمايدۇ';
$t[206] = 'ئۇنىڭغا (ۋەز - نەسىھەت قىلىنىپ يامان سۆز - ھەرىكەتلىرىڭدىن قايتىپ) اﷲ تىن قورققىن دېيىلسە، غۇرۇرى ئۇنىڭغا گۇناھ يۈكلەيدۇ (يەنى ھەقتىن تەكەببۇرلۇق بىلەن باش تارتىپ، پىتنە - پاساتتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا جەھەننەم يېتەرلىكتۇر، جەھەننەم ناھايىتى يامان جايدۇر';
$t[207] = 'بەزى كىشىلەر باركى، ئۇلار اﷲ نىڭ رەزاسى ئۈچۈن جېنىنى پىدا قىلىدۇ (قىلغان ئەمەلى بىلەن پەقەت اﷲ نىڭ رازىلىقىنىلا تىلەيدۇ). اﷲ بەندىلىرىگە تولىمۇ مەرھەمەتلىكتۇر';
$t[208] = 'ئى مۆمىنلەر! ئىسلام دىنىغا پۈتۈنلەي كىرىڭلار (يەنى ئسلام دىنىنىڭ پۈتۈن ئەھكاملىرىغا بويسۇنۇڭلار، بەزى ئەھكاملىرىغا ئەمەل قىلىپ، بەزى ئەھكاملىرىنى تەرك ئەتمەڭلار)، شەيتاننىڭ يوللىرىغا (ۋە ئىغۋاسىغا) ئەگەشمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر';
$t[209] = 'سىلەرگە (ئىسلام دىنىنىڭ ھەقلىقىنى كۆرسىتىدىغان) روشەن دەلىللەر كەلگەندىن كېيىن (توغرا يولدىن) تېيىپ كەتسەڭلار، بىلىڭلاركى، (سىلەر جازالىنىسىلەر، چۈنكى) اﷲ غالىبتۇر (يەنى سىلەردىن ئىنتىقام ئېلىشتىن ئاجىز ئەمەستۇر)، ھېكمەت بىلەن ئش قىلغۇچىدۇر';
$t[210] = 'ئۇلار (يەنى ئسلامغا كىرىشنى تەرك ئەتكەنلەر) پەقەت اﷲ نىڭ (قىيامەت كۈنى خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن) بۇلۇت پارچىلىرى ئىچىدە كېلىشىنى، پەرىشتلەرنىڭ كېلىشىنى ۋە (خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىپ، بىر گۇرۇھ جەننەتى، بىر گۇرۇھ دوزىخى بولۇپ ئايرىلىش بىلەن خالايىقنىڭ) ئىشىنىڭ پۈتۈشىنى كۈتەمدۇ؟ (بەندىلەرنىڭ) ھەممە ئىشى اﷲ قا قايتۇرۇلىدۇ';
$t[211] = '(ئى مۇھەممەد!) ئىسرائىل ئەۋلادىدىن سورىغىنكى، بىز ئۇلارغا نەقەدەر كۆپ روشەن دەلىللەرنى ئاتا قىلدۇق. كىمكى اﷲ نىڭ نېمىتىگە ئېرىشكەندىن كېيىن، ئۇنى (كۇفرىلىق قىلىپ) ئۆزگەرتىۋەتسە (اﷲ ئۇنىڭغا ئازاب قىلىدۇ)، اﷲ نىڭ ئازابى ھەقىقەتەن قاتتىقتۇر';
$t[212] = 'كاپىرلارغا دۇنيا تىرىكچىلىكى چىرايلىق كۆرسىتىلدى (يەنى ئۇلار دۇنيانىڭ شەھۋەتلىرىگە، نېمەتلىرىگە مەپتۇن بولىدۇ)، مۆمىنلەرنى مەسخىرە قىلىدۇ، ھالبۇكى قىيامەت كۈنى تەقۋادارلار ئۇلاردىن (دەرىجە جەھەتتە) ئۈستۈن تۇرىدۇ؛ اﷲ خالىغان كىشىگە ھېسابسىز رىزىق بېرىدۇ';
$t[213] = 'ئىنسانلار (دەسلەپتە) بىر ئۈممەت (يەنى ھەق دىندا) ئىدى (كېيىن ئۇلارنىڭ بەزىسى ئىمان ئېيتىپ، بەزىسى ئىمان ئېيتماي ئىختىلاپ قىلىشتى)، اﷲ (مۆمىنلەرگە جەننەت بىلەن) خۇش خەۋەر بەرگۈچى، (كۇففارلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتى؛ (اﷲ) كىشىلەرنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىش ئۈچۈن، ئۇلارغا ھەق كىتابنى نازىل قىلدى، پەقەت كىتاب بېرىلگەن كىشىلەرلا ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ھەسەت قىلىشىپ (يەنى كاپىرلار مۆمىنلەرگە ھەسەت قىلىپ)، كىتاب توغرىسىدا ئىختىلاپ قىلىشتى، اﷲ ئۆز ئىرادىسى بويىچە مۆمىنلەرنى ئۇلار (يەنى گۇمراھلار) ئىختىلاپ قىلىشقان ھەقىقەتكە ھىدايەت قىلدى. اﷲ خالىغان كىشىنى توغرا يولغا باشلايدۇ';
$t[214] = 'سىلەر تېخى ئىلگىرىكىلەر ئۇچرىغان كۈلپەتلەرگە ئۇچرىماي تۇرۇپ جەننەتكە كىرىشنى ئويلامسىلەر؟ سىلەردىن ئىلگىرى ئۆتكەن (مۆمىن) لەر ئېغىرچىلىقلارغا، كۈلپەتلەرگە ئۇچرىغان ۋە چۆچۈتۈۋېتىلگەن ئىدى، ھەتتا پەيغەمبەر ۋە مۆمىنلەر: &laquo;اﷲ نىڭ (بىزگە ۋەدە قىلغان) ياردىمى قاچان كېلىدۇ؟&raquo; دېگەن ئىدى. بىلىڭلاركى، اﷲ نىڭ ياردىمى ھەقىقەتەن يېقىندۇر';
$t[215] = '(ئى مۇھەممەد!) سەندىن (ماللىرىنى) قانداق سەرپ قىلسا بولدىغانلىقىنى سورايدۇ، ئېيتقىنكى، &laquo;مال - مۈلكىڭلاردىن نېمىنى سەرپ قىلماڭلار، ئۇنى ئاتا ئاناڭلارغا، خىش - ئەقرىبالىرىڭلارغا، مىسكىنلەرگە ۋە ئىبن سەبىللەرگە سەرپ قىلساڭلار بولىدۇ. ياخشىلىقتىن نېمىنى قىلماڭلار، اﷲ ئۇنى بىلىپ تۇرىدۇ&raquo;';
$t[216] = 'سىلەرگە جىھاد پەرز قىلىندى، ھالبۇكى، سىلەر ئۇنى ياقتۇرمايسىلەر، سىلەر بىرەر نەرسىنى ياقتۇرماسلىقىڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن پايدىلىقتۇر؛ سىلەر بىرەر نەرسىنى ياقتۇرۇشۇڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن زىيانلىقتۇر. (سىلەرگە نېمىنىڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر، (شۇنىڭ ئۈچۈن) اﷲ بۇيرۇغانغا ئالدىراڭلار';
$t[217] = '(ئى مۇھەممەد!) ئۇلار سەندىن: &laquo;ھارام قىلىنغان ئايدا ئۇرۇش قىلىشقا بولامدۇ؟&raquo; دەپ سورايدۇ، ئېيتقىنكى، &laquo;بۇ ئايدا ئۇرۇش قىلىش چوڭ گۇناھتۇر؛ اﷲ نىڭ يولىدىن توسۇش، اﷲ قا كۇفرىلىق قىلىش، مەسجىدى ھەرەمدا (ئىبادەت قىلىشتىن) توسۇش ۋە مەسجىدى ھەرەمدىن ئاھالىنى ھەيدەپ چىقىرىش اﷲ نىڭ دەرگاھىدا تېخىمۇ چوڭ گۇناھتۇر، پىتنە (يەنى مۆمىنلەرگە زىيانداشلىق قىلىش) ئادەم ئۆلتۈرۈشتىنمۇ قاتتىق گۇناھتۇر&raquo;. ئۇلار (يەنى كۇففارلار) قولىدىن كەلسىلا سىلەرنى دىنىڭلاردىن قايتۇرۇۋەتكەنگە قەدەر سىلەر بىلەن داۋاملىق ئۇرۇشىدۇ. سىلەردىن كىمكى ئۆز دىنىدىن قايتىپ كاپىر پېتى ئۆلسە، ئۇنىڭ ئەمەللىرى دۇنيا ۋە ئاخىرەتتە بىكار بولۇپ كېتىدۇ. بۇنداق ئادەملەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر';
$t[218] = 'شۈبھىسىزكى، ئمان ئېيتقانلار، ھىجرەت قىلغانلار ۋە اﷲ نىڭ يولىدا جىھاد قىلغانلار ئەنە شۇنداق كىشىلەر اﷲ نىڭ رەھمىتىنى ئۈمىد قىلىدۇ، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر';
$t[219] = '(ئى مۇھەممەد!) سەندىن ھاراق ۋە قىمار توغرىسىدا سورىشىدۇ، سەن ئۇلارغا: &laquo;بۇنىڭ ھەر ئىككىسىدە چوڭ گۇناھ ۋە كىشىلەرگە (ئازغىنا ماددى) پايدىمۇ بار، لېكىن ئۇلاردىكى گۇناھ پايدىغا قارىغاندا تېخىمۇ چوڭ&raquo; دېگىن، ئۇلار سەندىن نېمىنى سەدىقە قىلىش توغرۇلۇق سورايدۇ، &laquo;(ئېھتىياجدىن) ئارتۇقىنى (سەدىقە قىلىڭلار)&raquo; دېگىن. (دۇنيا ۋە ئاخىرەتنىڭ ئىشلىرىنى) تەپەككۇر قىلىشىڭلار ئۈچۈن اﷲ ئۆز ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ';
$t[220] = '(ئى مۇھەممەد!) ئۇلار سەندىن يېتىملەر توغرۇلۇق سورىشىدۇ، ئېيتقىنكى، &laquo;(تەربىيىلەش، ماللىرىنى ئۆستۈرۈپ) ئۇلارنى تۈزەش ياخشىدۇر، ئۇلار بىلەن (ئۇلارغا پايدىلىق بولغان رەۋىشتە) ئارىلىشىپ ياشىساڭلار، ئۇ چاغدا (يەنى ئۇلارغا قېرىنداشلارچە مۇئامىلە قىلىڭلار)، ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر. اﷲ (يېتىملەرنىڭ ئىشلىرىنى) بۇزغۇچى ۋە تۈزىگۈچىنى بىلىپ تۇرىدۇ. ئەگەر اﷲ خالىسا، سىلەرنى (قىيىن ئىشقا تەكلىپ قىلىش بىلەن) چوقۇم جاپاغا سالاتتى&raquo;. اﷲ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[221] = '(ئى مۇسۇلمانلار!) مۇشرىك ئاياللار ئىمان ئېيتمىغىچە ئۇلارنى نىكاھىڭلارغا ئالماڭلار. (ھۆر) مۇشرىك ئايال (ھۆسن - جامالى ۋە مېلى بىلەن) سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ، مۆمىن دېدەك، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر. مۇشرىك ئەرلەر ئىمان ئېيتمىغىچە مۆمىن ئاياللارنى ئۇلارغا ياتلىق قىلماڭلار، (ھۆر) مۇشرىك ئەر سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ مۆمىن قۇل، ئەلۋەتتە، ئۇنىڭدىن ئارتۇقتۇر. ئەنە شۇلار (يەنى مۇشرىك ئەر ۋە ئاياللار سىلەرنى) دوزاخقا چاقىرىدۇ، اﷲ ئۆز ئىرادىسى بويىچە (سىلەرنى) جەننەتكە، مەغپىرەتكە چاقىرىدۇ، كىشىلەر پەند - نەسىھەت ئالسۇن دەپ اﷲ ئۇلارغا ئۆز ئايەتلىرىنى بايان قىلىدۇ';
$t[222] = '(ئى مۇھەممەد!) ئۇلار سەندىن ھەيز توغرۇلۇق (يەنى ھەيزدار ئايال بىلەن جىنسىي ئالاقە قىلىشنىڭ دۇرۇسلۇقى ياكى دۇرۇس ئەمەسلىكى توغرۇلۇق) سورايدۇ. ئېيتقىنكى، &laquo;ھەيز زىيانلىقتۇر (يەنى ھەيز مەزگىلىدە جىنسىي ئالاقە قىلىش ئەر - خوتۇن ھەر ئىككىسىگە زىيانلىقتۇر)، ھەيز مەزگىلىدە ئايالىڭلاردىن نېرى تۇرۇڭلار، (ھەيزدىن) پاك بولغۇچە ئۇلارغا يېقىنچىلىق قىلماڭلار، پاك بولغاندا، ئۇلارغا اﷲ بۇيرۇغان جايدىن يېقىنچىلىق قىلىڭلار&raquo;. اﷲ ھەقىقەتەن تەۋبە قىلغۇچىلارنى دوست تۇتىدۇ. (ھارامدىن ۋە نىجاسەتتىن) پاك بولغۇچىلارنى ھەقىقەتەن دوست تۇتىدۇ';
$t[223] = 'ئاياللىرىڭلار سىلەر ئۈچۈن (خۇددى) ئېكىنزارلىقتۇر (يەنى نەسىل تېرىيدىغان جايدۇر). ئېكىنزارلىقىڭلارغا خالىغان رەۋىشتە كېلىڭلار، ئۆزۈڭلار ئۈچۈن ئالدىنئالا ياخشى ئەمەل تەييارلاڭلار، (گۇناھتىن چەكلىنىش بىلەن) اﷲ تىن قورقۇڭلار، بىلىڭلاركى، سىلەر اﷲ قا مۇلاقات بولۇسىلەر، مۆمىنلەرگە (جەننەت بىلەن) خۇش خەۋەر بەرگىن';
$t[224] = 'ياخشىلىق قىلماسلىققا، تەقۋادار بولماسلىققا ۋە كىشىلەرنى ئەپلەشتۈرمەسلىككە قەسەم قىلىش شەكلى بىلەن (ئۇلارنى قىلىشقا) اﷲ نى توسالغۇ قىلماڭلار (يەنى اﷲ بىلەن قەسەم قىلىشنى ياخشى ئىشلارنى قىلماسلىقنىڭ سەۋەبى قىلماڭلار)، اﷲ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر';
$t[225] = 'مەقسەتسىز ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتمايدۇ، قەستەن ئىچكەن قەسىمىڭلار ئۈچۈن اﷲ سىلەرنى جازاغا تارتىدۇ. اﷲ مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)';
$t[226] = 'ئاياللىرىغا يېقىنچىلىق قىلماسلىققا قەسەم ئىچكەن كىشىلەر تۆت ئاي كۈتىدۇ؛ ئەگەر ئۇلار (بۇ مۇددەت ئىچىدە ئاياللىرىغا يېقىنچىلىق قىلىشقا) قايتسا، (ئاياللىرىغا يامانلىق قىلغان گۇناھىنى) اﷲ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر. اﷲ ناھايىتى مېھرىباندۇر';
$t[227] = 'ئەگەر ئۇلار تالاق قىلىش نىيىتىگە كەلسە، اﷲ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر';
$t[228] = 'تالاق قىلىنغان ئاياللار ئۈچ ھەيز ئۆتكۈچە ئىددەت تۇتىدۇ. ئەگەر ئۇلار اﷲ قا، ئاخىرەت كۈنىگە ئىشىنىدىكەن، بەچچىدانلىرىدىكى اﷲ ياراتقان نەرسىنى (يەنى بالىنى ياكى ھەيزنى) يوشۇرۇشى دۇرۇس ئەمەس، ئۇلارنىڭ ئەرلىرى ئەگەر ئەپلىشىپ قېلىشنى خالىسا، شۇ مۇددەت ئىچىدە ئۇلارنى قايتۇرۇپ كېلىشكە (باشقىلارغا قارىغاندا) ئەڭ ھەقلىقتۇر. ئاياللىرى ئۈستىدە ئەرلەرنىڭ ھەقلىرى بولغىنىدەك، ئەرلىرى ئۈستىدە ئاياللارنىڭمۇ ھەقلىرى بار (يەنى ئاياللار مۇۋاپىق دەرىجىدە ھوقۇقتىن بەھرىمەن بولۇشى كېرەك، مۇۋاپىق دەرىجىدە مەجبۇرىيەتمۇ ئۆتىشى كېرەك). (ئاياللارغا مەھرى بېرىش ۋە ئۇلارنىڭ تۇرمۇشىنى قامداش مەجبۇرىيەتلىرى ئەرلەرگە يۈكلەنگەنلىكتىن) ئەرلەر ئاياللاردىن بىر دەرىجە ئارتۇقلۇققا ئىگە. اﷲ غالىبتۇر. ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[229] = '(قايتا يارىشىشقا بولىدىغان) تالاق ئىككى قېتىمدۇر، ئۇنىڭدىن كېيىن (خوتۇنىنى) چىرايلىقچە تۇتۇش، ياكى ياخشىلىق بىلەن (يەنى زۇلۇم قىلماي، يامان گېپىنى قىلماي، كىشىلەرنى ئۇنىڭدىن يىراقلاشتۇرماي) قويۇۋېتىش لازىم، اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىق خەۋپى (يەنى ياخشى ئۆتەلمەسلىك، اﷲ بەلگىلىگەن ئەر - خوتۇنلۇق ھوقۇقلىرىغا رىئايە قىلالماسلىق خەۋپى) بولمىسىلا، ئۆزۈڭلارنىڭ ئاياللىرىڭلارغا (مەھرى ئۈچۈن) بەرگەن مال - مۈلۈكتىن ھېچقانداق نەرسىنى قايتۇرۇۋېلىش دۇرۇس بولمايدۇ. ئەگەر سىلەر ئۇلار (يەنى ئەر - ئايال) نىڭ اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىقىدىن قورقساڭلار، خوتۇننىڭ (تالاق خېتىنى ئېلىش مەقسىتىدە) مال بەرگەنلىكى ئۈچۈن ئۇ ئىككىسىگە (يەنى ئەر - خوتۇنغا) گۇناھ بولمايدۇ. ئەنە شۇلار اﷲ نىڭ بەلگىلىمىلىرىدۇر، ئۇنىڭغا خىلاپلىق قىلماڭلار اﷲ نىڭ بەلگىلىمىلىرىگە خىلاپلىق قىلغۇچىلار زالىملاردۇر';
$t[230] = 'ئەگەر (ئۇ) ئۇنى (يەنى خوتۇنىنى) (ئۈچ قېتىم) تالاق قىلسا، (خوتۇن) باشقا ئەرگە تەگمىگىچە ئۇنىڭغا دۇرۇس بولمايدۇ، كېيىنكى ئەر ئۇنى تالاق قىلغاندىن كېيىن (ئىددىتىنى توشقۇزۇپ)، بۇرۇنقى ئەر بىلەن قايتا ياراشسا، ئۈلارغا ھېچ گۇناھ بولمايدۇ. ئەگەر ئۇلار اﷲ نىڭ بەلگىلىمىلىرىگە رىئايە قىلالايدىغانلىقىنى (يەنى قايتا ياراشقاندىن كېيىن ياخشى ئۆتەلەيدىغانلىقىنى) تەسەۋۋۇر قىلسا. بۇ اﷲ نىڭ بەلگىلىمىلىرىدۇركى، اﷲ ئۇنى بىلىدىغان (يەنى ئىشنىڭ ئاقىۋىتىگە پەمى يېتىدىغان) قەۋم ئۈچۈن بايان قىلىدۇ';
$t[231] = 'سىلەر ئاياللارنى تالاق قىلغان بولساڭلار، ئۇلارنىڭ ئىددىتى توشاي دەپ قالغان بولسا، ئۇلار بىلەن چىرايلىقچە يارىشىپ، (ئۇلارنى نىكاھىڭلاردا) تۇتۇڭلار، ياكى ئۇلارنى ياخشىلىق بىلەن قويۇۋېتىڭلار، زۇلۇم قىلىش مەقسىتىدە زىيان يەتكۈزۈپ ئۇلارنى تۇتۇۋالماڭلار. كىمكى شۇنداق قىلىدىكەن (يەنى كىمكى چىرايلىقچە ئۆي تۇتۇش نىيىتى بىلەن ئەمەس، بەلكى ئىددىتىنى ئۇزارتىپ زۇلۇم قىلىش نىيىتىدە خوتۇنى بىلەن يارىشىدىكەن)، ئۆزىگە ئۆزى زۇلۇم قىلغان بولىدۇ. اﷲ نىڭ ئايەتلىرىنى كۈلكە قىلىۋالماڭلار (يەنى اﷲ نىڭ ئەھكاملىرىنى خىلاپلىق قىلىش يولى بىلەن مەسخىرە قىلماڭلار)، اﷲ نىڭ سىلەرگە بەرگەن نېمىتىنى ۋە سىلەرگە ۋەز - نەسىھەت قىلىش ئۈچۈن نازىل قىلغان كىتابنى، ھېكمىتىنى (يەنى كىتابدىكى ئەھكاملارنى) ياد ئېتىڭلار، اﷲ قا تەقۋادارلىق قىلىڭلار. بىلىڭلاركى، اﷲ ھەر نەرسىنى بىلگۈچىدۇر';
$t[232] = 'ئەگەر سىلەر ئاياللارنى تالاق قىلغان بولۇپ، ئۇلارنىڭ ئىددىتى توشقان ۋە قائىدە بويىچە (تەگمەكچى بولغان) ئېرى بىلەن پۈتۈشكەن بولسا، ئۇلارنى نىكاھلىنىشتىن توسماڭلار. بۇنىڭ بىلەن، ئىچىڭلاردىن اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرگەنلەرگە نەسىھەت قىلىنىدۇ، بۇ (يەنى ۋەز - نەسىھەت ئېلىش، اﷲ نىڭ ئەمرىلىرىنى تۇتۇش)، سىلەر ئۈچۈن ئەڭ پايدىلىقتۇر ۋە ئەڭ پاكتۇر، (شەرىئەت ئەھكاملىرىدىن سىلەرگە نېمىلەر ئەڭ پايدىلىق ئىكەنلىكىنى) اﷲ بىلىدۇ، سىلەر بىلمەيسىلەر';
$t[233] = 'ئانىلار (بالىلارنى ئېمىتىش مۇددىتىدە) تولۇق ئېمىتمەكچى بولسا، تولۇق ئىككى يىل ئېمىتىشى لازىم. ئاتىلار ئانىلارنى قائىدە بويىچە يېمەك - ئىچمەك ۋە كىيىم - كېچەك بىلەن تەمىنلەپ تۇرۇشى كېرەك، كىشى پەقەت قولىدىن كېلىدىغان ئىشقىلا تەكلىپ قىلىنىدۇ. ئانىنى بالىسى سەۋەبلىك زىيان تارتقۇزماسلىق لازىم. (ئەگەر ئاتا ئۆلۈپ كەتسە) ئۇنىڭ ۋارىسى ئاتىغا ئوخشاش مەسئۇلىيەتنى (يەنى ئانا بولغۇچىغا نەپىقە بېرىش ۋە ئۇنىڭ ھەقلىرىگە رىئايە قىلىش قاتارلىقلارنى) ئۆز ئۈستىگە ئېلىشى كېرەك. ئەگەر ئاتا - ئانا كېڭىشىپ (بالىنى ئىككى يىل توشماستىنلا) ئايرىۋەتمەكچى بولسا، ئۇلارغا ھېچبىر گۇناھ بولمايدۇ. ئەگەر بالىلىرىڭلارنى ئىنىكئانىلارغا ئېمىتمەكچى بولساڭلار، قائىدە بويىچە ئۇلارنىڭ ھەققىنى بەرسەڭلارلا سىلەرگە ھېچ گۇناھ بولمايدۇ. اﷲ تىن (يەنى اﷲ نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىشتىن) قورقۇڭلار، بىلىڭلاركى، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر';
$t[234] = 'ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئاياللىرى قېلىپ قالغان كىشىلەر بولسا، ئاياللىرى تۆت ئاي ئون كۈن ئىددەت تۇتۇشى لازىم، ئىددىتى توشقاندىن كېيىن ئۆزلىرى توغرۇلۇق قائىدە بويىچە ئىش قىلسا، سىلەرگە (يەنى شۇ ئاياللارغا ئىگىدارچىلىق قىلغۇچىلارغا) ھېچ گۇناھ بولمايدۇ. اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر';
$t[235] = 'ئىددىتى توشمىغان ئاياللارغا ئۇلارنى ئالىدىغانلىقىڭلارنى بۇرىتىپ ئۆتسەڭلار ياكى بۇنى دىلىڭلاردا يوشۇرۇن تۇتساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. ئۇلارغا ئېغىز ئاچىدىغانلىقىڭلار اﷲ قا مەلۇمدۇر، لېكىن ئۇلار بىلەن يوشۇرۇن ۋەدىلىشىپ قويماڭلار، پەقەت (ئاشكارا ئېيتىلسا كىشى خىجىل بولمايدىغان) مۇۋاپىق سۆزنى قىلساڭلار بولىدۇ، ئىددىتى توشمىغىچە ئۇلارنى نىكاھىڭلارغا ئېلىشقا بەل باغلىماڭلار، بىلىڭلاركى، اﷲ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، ئۇنىڭدىن (يەنى اﷲ نىڭ ئەمرىگە خىلاپلىق قىلىپ جازالىنىشتىن) ھەزەر قىلىڭلار، بىلىڭلاركى، اﷲ ناھايىتى مەغپىرەت قىلغۇچىدۇر، (اﷲ تىن قورقۇپ گۇناھتىن چەكلەنگەنلەرگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)';
$t[236] = 'ئەگەر سىلەر ئاياللىرىڭلارغا يېقىنچىلىق قىلماي ياكى مەھرى تەيىنلىمەي تۇرۇپ ئۇلارنى تالاق قىلساڭلار (يەنى نىكاھىڭلارغا ئالغان ئايال بىلەن بىر يەردە بولمىغان ياكى مەھرىنى مۇئەييەنلەشتۈرمىگەن شارائىتتا) ئۇنى قويۇۋەتسەڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن ئۇنىڭغا مۇتئە (يەنى بەلگىلىك مىقداردا پۇل - مال) بېرىشىڭلار كېرەك، ئۇلارغا نەرسە بېرىڭلار، باي ئۆز ھالىغا يارىشا، كەمبەغەلمۇ ئۆز ھالىغا يارىشا قائىدە بويىچە بەرسۇن، (بۇ) ياخشى ئادەملەرنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر';
$t[237] = 'ئەگەر سىلەر ئۇلارغا يېقىنچىلىق قىلىشتىن ئىلگىرى ۋە مەھرى تەيىنلىنىپ بولغاندىن كېيىن تالاق قىلساڭلار، تەيىنلەنگەن مەھرىنىڭ يېرىمىنى بېرىڭلار، ئەمما ئاياللار ئۆتۈنسە (يەنى مەھرىدىن ۋازكېچىپ پۈتۈنلەي ئالمىسا) ياكى نىكاھنىڭ تۈگۈنى ئۆز ئىلكىدە بولغان ئەرلەر ئۆتۈنسە (يەنى تەيىنلەنگەن مەھرىنىڭ ھەممىسىنى بەرسە)، شۇ بويىچە بىر تەرەپ قىلىش لازىم. سىلەر ئۆتۈنسەڭلار، (ئۆتۈنگەن ئادەم) تەقۋادارلىققا ئەڭ يېقىندۇر. ئۆزئارا ئېھسان قىلىشنى ئۇنتۇماڭلار. اﷲ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر';
$t[238] = '(بەش) نامازنى بولۇپمۇ نامازدىگەرنى داۋاملاشتۇرۇڭلار (يەنى پۈتۈن شەرتلىرى بىلەن ۋاقتىدا تولۇق ئادا قىلىڭلار). اﷲ نىڭ ھۇزۇرىدا (يەنى نامازدا) ئىتائەتمەنلىك بىلەن تۇرۇڭلار';
$t[239] = 'قورقۇنچتا قالغىنىڭلاردا، پىيادە ياكى ئۇلاغلىق كېتىپ بېرىپ (ناماز ئوقۇڭلار)، ئامانلىق تاپقىنىڭلاردا اﷲ نىڭ سىلەرگە ئۆگەتكىنى بويىچە اﷲ نى ياد ئېتىڭلار (يەنى قورقۇنچ تۈگىگەندە، اﷲ نامازنى قانداق ئۆتەشكە ئەمر قىلغان بولسا، تولۇق ئەركانلىرى بىلەن شۇنداق ئۆتەڭلار';
$t[240] = 'سىلەرنىڭ ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئايالى قېلىپ قالىدىغانلار ئۇلارنىڭ (يەنى ئاياللىرىنىڭ) بىر يىلغىچە يېمەك - ئىچمەك، كىيىم - كېچەك بىلەن تەمىنلىنىشىنى، ئۆيىدىن ھەيدەپ چىقىرىلماسلىقىنى ۋەسىيەت قىلسۇن. ئەگەر (ئاياللىرى) ئۆزلىرى چىقىپ كەتسە، ئۇلارنىڭ ئۆزلىرى توغرىسىدا قىلغان قائىدىلىك ئشلىرى ئۈچۈن سىلەرگە (يەنى مېيىتنىڭ ئىگىلىرىگە) ھېچ گۇناھ بولمايدۇ. اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[241] = 'تالاق قىلىنغان ئاياللارغا قائىدە بويىچە نەپىقە بېرىش لازىم. (ئۇ) تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر';
$t[242] = 'سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن اﷲ (ئۆز ئەھكاملىرىنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىنى سىلەرگە مۇشۇنداق بايان قىلىدۇ';
$t[243] = 'ئۆزلىرى مىڭلارچە تۇرۇقلۇق، ئۆلۈمدىن قورقۇپ، يۇرتلىرىدىن قېچىپ چىققان كىشىلەردىن خەۋىرىڭ يوقمۇ؟ اﷲ ئۇلارنى &laquo;ئۆلۈڭلار&raquo; دېدى (ئۇلار ئۆلدى). ئاندىن كېيىن اﷲ ئۇلارنى تىرىلدۈردى، اﷲ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋە لېكىن ئىنسانلارنىڭ كۆپچىلىكى (اﷲ نىڭ نېمىتىگە) شۈكۈر قىلمايدۇ';
$t[244] = 'اﷲ نىڭ يولىدا جىھاد قىلىڭلار، بىلىڭلاركى، اﷲ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى، ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر';
$t[245] = 'كىمكى اﷲ قا قەرزىي ھەسەنە بېرىدىكەن (يەنى اﷲ نىڭ يولىدا پۇل - مېلىنى خۇشاللىق بىلەن سەرپ قىلىدىكەن)، اﷲ ئۇنىڭغا نەچچە ھەسسە (يەنى ئون ھەسسىدىن يەتتە يۈز ھەسسىگىچە) كۆپ قايتۇرىدۇ. اﷲ (سىناش ئۈچۈن، بەزى كىشىلەرنىڭ رىزقىنى) تار قىلىدۇ، (بەزى كىشىلەرنىڭ رىزقىنى) كەڭ قىلىدۇ، (قىيامەت كۈنى) اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر';
$t[246] = 'ساڭا مۇسا (ۋاپاتى) دىن كېيىن ئىسرائىل ئەۋلادىدىدىن بولغان بىر جامائەنىڭ خەۋىرى يەتمىدىمۇ؟ ئۇلار ئۆز ۋاقتىدا ئۆزلىرىنىڭ پەيغەمبىرىگە؛ &laquo;بىزگە پادىشاھ تىكلەپ بەرگىن، (ئۇنىڭ بىلەن دۈشمەنلەرگە قارشى) اﷲ نىڭ يولىدا جىھاد قىلايلى&raquo; دېيىشتى. پەيغەمبەر: &laquo;سىلەرگە جىھاد پەرز قىلىنسا جىھاد قىلماي قالارسىلەرمۇ؟&raquo; دېدى. ئۇلار: &laquo;يۇرتلىرىمىزدىن ھەيدەپ چىقىرىلغان ۋە ئوغۇللىرىمىزدىن جۇدا قىلىنغان تۇرساق قانداقمۇ اﷲ نىڭ يولىدا جىھاد قىلمايلى؟&raquo; دېدى. ئۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئازغىنىسىدىن باشقا ھەممىسى جىھادتىن باش تارتتى. اﷲ زالىملارنى ئوبدان بىلگۈچىدۇر';
$t[247] = 'پەيغەمبىرى ئۇلارغا: &laquo;اﷲ ھەقىقەتەن سىلەرگە تالۇتنى پادىشاھ قىلىپ ئەۋەتتى&raquo; دېدى. ئۇلار (پەيغەمبىرىگە ئېتىراز بىلدۈرۈپ): &laquo;ئۇ قانداقمۇ بىزگە پادىشاھ بولىدۇ. (ئارىمىزدا پادىشاھ ئەۋلاتلىرى بولغانلىقتىن) پادىشاھلىققا بىز ئۇنىڭدىن ھەقلىقمىز، ئۇنىڭ ئۈستىگە ئۇنىڭ مال - مۈلكى كۆپ بولمىغان تۇرسا&raquo; دېدى. پەيغەمبەر: &laquo;اﷲ ھەقىقەتەن سىلەرگە (پادىشاھ قىلىشقا) ئۇنى ئىختىيار قىلدى، ئۇنىڭ ئىلمىنى زىيادە ۋە بەدىنىنى قابىل قىلدى، اﷲ سەلتەنەتنى خالىغان ئادەمگە بېرىدۇ. اﷲ (نىڭ پەزلى) كەڭدۇر، (ئۇنىڭغا كىمنىڭ لايىق بولىدىغانلىقىنى) ئوبدان بىلىدۇ&raquo; دېدى';
$t[248] = 'ئۇلارغا پەيغەمبىرى ئېيتتىكى: &laquo;ئۇنىڭ پادىشاھلىقىنىڭ ئالامىتى شۇكى، سىلەرگە بىر ساندۇق كېلىدۇ، ئۇنىڭدا رەببىڭلار تەرىپىدىن سىلەرنى تەسكىن تاپقۇزىدىغان نەرسە ۋە مۇسانىڭ ھارۇننىڭ تەۋەلىرى قالدۇرۇپ كەتكەن نەرسىلەر (يەنى مۇسانىڭ ھاسىسى، كىيىمى ۋە تەۋرات يېزىلغان بەزى تاختىلار) قاچىلانغان بولىدۇ، ئۇنى پەرىشتىلەر كۆتۈرۈپ كېلىدۇ. ئەگەر (اﷲ قا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان) بولساڭلار، بۇنىڭدا (يەنى تابۇتنىڭ نازىل بولۇشىدا اﷲ نىڭ تالۇتنى سىلەرگە پادىشاھلىققا تاللىغانلىقىغا) ئەلۋەتتە سىلەر ئۈچۈن (روشەن) ئالامەت بار&raquo;';
$t[249] = 'تالۇت ئەسكەرلىرى بىلەن (بەيتۇلمۇقەددەستىن) ئايرىلغان چاغدا: &laquo;اﷲ سىلەرنى بىر دەريا بىلەن سىنايدۇ، كىمكى ئۇنىڭدىن ئىچىدىكەن، ئۇ ماڭا تەۋە ئەمەس، كىمكى دەريا سۈيىنى تېتىمايدىكەن، ئۇ ھەقىقەتەن ماڭا تەۋەدۇر، ئۇنىڭدىن (ئۇسسۇزلۇقىنى بىرئاز بېسىش ئۈچۈن) ئىچكەن ئادەم (بۇيرۇققا خىلاپلىق قىلغان بولمايدۇ)&raquo; دېدى. ئۇلارنىڭ ئىچىدىكى ئازغىنا كىشىدىن باشقا ھەممىسى ئۇنىڭدىن ئىچتى. تالۇت ۋە ئۇنىڭغا ئىمان ئېيتقان كىشىلەر دەريادىن ئۆتكەن چاغدا (دۈشمەننىڭ كۆپلۈكىنى كۆرۈپ قورقۇنچقا چۈشۈپ، ئۇلارنىڭ بىر پىرقىسى): &laquo;بۈگۈن بىز جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇرۇشۇشقا تاقەت كەلتۈرەلمەيمىز&raquo; دېدى. (تالۇتنىڭ تەۋەلىرىدىن) اﷲ قا مۇلاقات بولۇشقا ئېتىقاد قىلىدىغانلار: &laquo;اﷲ نىڭ ئىرادىسى بىلەن ئاز جامائە كۆپ جامائە ئۈستىدىن غەلىبە قىلىدۇ&raquo; دېدى. اﷲ چىداملىق كۆرسەتكۈچىلەر بىلەن بىللىدۇر';
$t[250] = 'ئۇلار جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇچراشقان چاغدا: &laquo;پەرۋەردىگارىمىز! قەلبىمىزنى چىداملىقلىق بىلەن تولدۇرغىن، قەدەملىرىمىزنى (ئۇرۇش مەيدانىدا) ساباتلىق قىلغىن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن&raquo; دېدى';
$t[251] = 'مۆمىنلەر اﷲ نىڭ ئىرادىسى بىلەن كاپىرلارنى مەغلۇپ قىلدى، داۋۇد جالۇتنى ئۆلتۈردى. اﷲ داۋۇدقا سەلتەنەتنى، ھېكمەتنى (يەنى پەيغەمبەرلىكنى) بەردى. ئۇنىڭغا خالىغان نەرسىلىرىنى (يەنى پايدىلىق ئىلىملەرنى) بىلدۈردى. اﷲ ئىنسانلارنىڭ بەزىسىنى بەزىسى بىلەن مۇداپىئە قىلىپ تۇرمىسا (يەنى كۈچلۈك تاجاۋۇزچىغا ئۇنىڭدىنمۇ كۈچلۈك بولغان بىرسىنى ئاپىرىدە قىلمىسا) ئىدى، يەر يۈزى ئەلۋەتتە پاساتقا ئايلىناتتى (يەنى خارابلىققا يۈزلىنەتتى)، لېكىن اﷲ (يامانلىقنى ئۈستۈنلۈككە ئىگە قىلماسلىق بىلەن) پۈتۈن جاھان ئەھلىگە مەرھەمەت قىلغۇچىدۇر';
$t[252] = 'بۇ اﷲ نىڭ ئايەتلىرىدۇركى، ئۇنى بىز ساڭا توغرا رەۋىشتە ئوقۇپ بېرىمىز. (ئى مۇھەممەد!) سەن ئەلۋەتتە (اﷲ نىڭ دەۋىتىنى يەتكۈزۈش ئۈچۈن ئەۋەتىلگەن) پەيغەمبەرلەرنىڭ بىرىسەن';
$t[253] = 'ئەنە شۇ پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق؛ اﷲ ئۇلارنىڭ بەزىسى (يەنى مۇسا ئەلەيھىسسالام) بىلەن سۆزلەشتى ۋە بەزىسىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) دەرىجىسىنى يۇقىرى كۆتۈردى. مەريەم ئوغلى ئىساغا (ئۆلۈكلەرنى تىرىلدۈرۈش، گاچىلارنى، بەرەس كېسىلىنى ساقايتىشقا ئوخشاش) روشەن مۆجىزىلەرنى ئاتا قىلدۇق. ئۇنى روھۇلقۇدۇس (يەنى جىبرىئىل ئەلەيھىسسالام) بىلەن كۈچلەندۈردۇق، ئەگەر اﷲ خالىغان بولسا ئىدى، ئۇلاردىن كېيىن كەلگەن ئۈممەتلەر ئۇلارغا روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ئۇرۇشماس ئىدى، لېكىن ئۇلار ئىختىلاپ قىلىشتى، بەزىسى ئىمان ئېيتتى، بەزىسى كاپىر بولدى. ئەگەر اﷲ خالىغا بولسا ئىدى، ئۇلار ئۆزئارا ئۇرۇشماس ئىدى، لېكىن اﷲ خالىغىنىنى قىلىدۇ';
$t[254] = 'ئى مۆمىنلەر! سودا - سېتىق، دوستلۇق ۋە (اﷲ نىڭ ئىزنىسىز) شاپائەت بولمايدىغان كۈنى (يەنى قىيامەت كۈنى) يىتىپ كېلىشتىن بۇرۇن، سىلەرگە بىز رىزىق قىلىپ بەرگەن مال - مۈلۈكلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىڭلار. كۇفرانى نېمەت قىلغۇچىلار (ئۆزلىرىگە) زۇلۇم قىلغۇچىلاردۇر';
$t[255] = 'بىر اﷲ تىن باشقا ھېچ ئىلاھ يوقتۇر؛ اﷲ ھەمىشە تىرىكتۇر، ھەممىنى ئىدارە قىلىپ تۇرغۇچىدۇر؛ ئۇ مۈگدەپ قالمايدۇ، ئۇنى ئۇيقۇ باسمايدۇ؛ ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە اﷲ نىڭ (مۈلكى) دۇر؛ اﷲ نىڭ رۇخسىتىسىز كىممۇ اﷲ نىڭ ئالدىدا شاپائەت قىلالىسۇن؛ اﷲ ئۇلارنىڭ ئالدىدىكى (يەنى دۇنيادا قىلغان)، كەينىدىكى (يەنى ئۇلار ئۈچۈن ئاخىرەتتە تەييارلىغان) ئىشلارنىڭ ھەممىسىنى بىلىپ تۇرىدۇ؛ ئۇلار اﷲ نىڭ مەلۇماتىدىن (اﷲ) ئۇلارغا بىلدۈرۈشنى خالىغان نەرسىلەردىن (يەنى پەيغەمبەرلەرنىڭ تىلى ئارقىلىق بىلدۈرگەن نەرسىلەردىن) باشقا ھېچ نەرسىنى بىلمەيدۇ، اﷲ نىڭ كۇرسى (مەلۇماتى) ئاسمانلارنى ۋە زېمىننى ئۆز ئىچىگە ئالىدۇ. ئاسمان - زېمىننى ساقلاش ئۇنىڭغا ئېغىر كەلمەيدۇ. ئۇ يۇقىرى مەرتىۋىلىكتۇر، ھەممىدىن ئۇلۇغدۇر';
$t[256] = 'دىندا (ئۇنىڭغا كىرىشكە) زورلاش يوقتۇر، ھىدايەت گۇمراھلىقتىن ئېنىق ئايرىلدى، كىمكى تاغۇت (يەنى اﷲ تىن باشقا بارچە مەبۇد) نى ئىنكار قىلىپ، اﷲ قا ئىمان ئېيتىدىكەن، ئۇ سۇنماس، مەھكەم تۇتقىنى تۇتقان بولىدۇ، اﷲ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر';
$t[257] = 'اﷲ مۆمىنلەرنىڭ ئىگىسىدۇ، ئۇلارنى (كۇفرىنىڭ) زۇلمىتىدىن (ئىماننىڭ) يورۇقلۇقىغا چىقىرىدۇ؛ كاپىرلارنىڭ ئىگىسى شەيتاندۇر. ئۇلارنى (ئىماننىڭ) يورۇقلۇقىدىن (گۇمراھلىقنىڭ) قاراڭغۇلۇقىغا چىقىرىدۇ. ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر';
$t[258] = 'پەرۋەردىگارى توغرىسىدا ئىبراھىم بىلەن مۇنازىرىلەشكەن، اﷲ نىڭ ئاتا قىلغان پادىشاھلىقى ئۇنى ئىبراھىم بىلەن مۇنازىرىلىشىشكە ئېلىپ كەلگەن ئادەم (يەنى نەمرۇد) نى كۆرمىدىڭمۇ؟ ئەينى زاماندا ئىبراھىم (اﷲ نىڭ بارلىقىغا دەلىل كۆرسىتىپ): &laquo;مېنىڭ پەرۋەردىگارىم (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنى) ئۆلتۈرەلەيدۇ&raquo; دېدى. ئۇ: &laquo;مەنمۇ (ئۆلۈكنى) تىرىلدۈرەلەيمەن، (تىرىكنى) ئۆلتۈرەلەيمەن&raquo; دېدى (نەمرۇد ئۆلۈمگە ھۆكۈم قىلىنغان ئىككى ئادەمنى چاقىرتىپ كىلىپ بىرىنى ئۆلتۈردى، بىرىنى قويۇپ بەردى). ئىبراھىم (نەمرۇدنىڭ بۇنداق ھاماقەتلىكىنى كۆرۈپ): &laquo;اﷲ ھەقىقەتەن كۈننى شەرقتىن چىقىرالايدۇ، (خۇدالىق دەۋاسى قىلىدىغان بولساڭ) سەن ئۇنى غەربتىن چىقىرىپ باققىن&raquo; دېدى. (بۇنداق پاكىت ئالدىدا) كاپىر ئېغىز ئاچالماي قالدى. اﷲ زالىم قەۋمنى ھىدايەت قىلمايدۇ';
$t[259] = 'ياكى سەن تام، ئۆگزىلىرى يىقىلىپ ۋەيران بولغان (خارابە) شەھەر (يەنى بەيتۇلمۇقەددەس) دىن ئۆتكەن كىشىنى (يەنى ئۇزەيرنى) كۆرمىدىڭمۇ؟ ئۇ (ئەجەبلىنىپ): &laquo;اﷲ بۇ شەھەرنىڭ ئۆلگەن ئاھالىسىنى قانداق تىرىلدۈرەر؟&raquo; دېدى. اﷲ ئۇنى يۈز يىل ئۆلۈك ھالىتىدە تۇرغۇزۇپ ئاندىن (كامالى قۇدرىتىنى كۆرسىتىش ئۈچۈن) تىرىلدۈردى. اﷲ (پەرىشتە ئارقىلىق) ئۇنىڭدىن: &laquo;(بۇ ھالەتتە) قانچىلىك تۇردۇڭ؟&raquo; دەپ سورىدى. ئۇ: &laquo;بىر كۈن ياكى بىر كۈنگە يەتمەيدىغان ۋاقىت تۇردۇم&raquo; دېدى. اﷲ ئېيتتى: &laquo;ئۇنداق ئەمەس، (تولۇق) 100 يىل تۇردۇڭ! يېمەك ئىچمىكىڭگە قارىغىنكى، بۇزۇلمىغان؛ ئېشىكىڭگە قارىغىنكى (ئۇنىڭ سۆڭەكلىرى چىرىپ كەتكەن). (ساڭا يۇقىرىقىدەك ئادەتتىن تاشقىرى ئىشلارنى قىلغانلىقىمىز) سېنى بىز كىشىلەرگە (قۇدرىتىمىزنىڭ) دەلىلى قىلىش ئۈچۈندۇر. بۇ سۆڭەكلەرگە قارىغىنكى، ئۇنى قانداق قۇراشتۇرىمىز، ئاندىن ئۇنىڭغا گۆش قوندۇرىمىز. (يۇقىرىقى ئىشلار) ئۇنىڭغا ئېنىق بولغان چاغدا (يەنى ئۇ روشەن مۆجىزىلەرنى كۆرگەن چاغدا)، ئۇ: &laquo;مەن اﷲ نىڭ ھەممە نەرسىگە قادىر ئىكەنلىكىنى بىلدىم&raquo; دېدى';
$t[260] = 'ئۆز ۋاقتىدا ئىبراھىم: &laquo;پەرۋەردىگارىم، ئۆلۈكلەرنى قانداق تىرىلدۈرىدىغانلىقىڭنى ماڭا كۆرسەتكىن&raquo; دېدى. اﷲ: &laquo;(ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىمغا) ئىشەنمىدىڭمۇ؟&raquo; دېدى. ئىبراھىم: &laquo;ئىشەندىم، لېكىن كۆڭلۈم (تېخىمۇ) قارار تاپسۇن ئۈچۈن (كۆرۈشنى تىلەيمەن)&raquo; دېدى. اﷲ ئېيتتى: &laquo;قۇشتىن تۆتنى ئالغىن، ئۇلارنى ئۆزۈڭگە توپلىغىن (يەنى ئۇلارنى پارچىلاپ گۆشلىرى بىلەن پەيلىرىنى ئارىلاشتۇرغىن)، ئاندىن ھەربىر تاغقا ئۇلاردىن بىر بۆلۈكىنى قويغىن، ئاندىن ئۇلارنى چاقىرغىن، سېنىڭ ئالدىڭغا ئۇلار چاپسان كېلىدۇ&raquo;. بىلگىنكى، اﷲ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر';
$t[261] = 'اﷲ نىڭ يولىدا پۇل - مېلىنى سەرپ قىلغانلارنىڭ (سەرپ قىلغان نەرسىسى يەرگە تېرىلىپ) يەتتە باشاق چىقارغان، ھەر باشىقىدا 100 دان تۇتقان بىر دانغا ئوخشايدۇ. اﷲ خالىغان بەندىسىگە ھەسسىلەپ ساۋاب بېرىدۇ. اﷲ نىڭ مەرھەمىتى (چەكلىك ئەمەس) كەڭدۇر (اﷲ مال سەرپ قىلغۇچىنىڭ نىيىتىنى بىلگۈچىدۇر)';
$t[262] = 'پۇل - مېلىنى اﷲ يولىدا سەرپ قىلىدىغان، ئارقىسىدىن ئۇنىڭغا مىننەت قىلمايدىغان، ۋە (خەير - ساخاۋىتىگە ئېرىشكەنلەرنى) رەنجىتمەيدىغان كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (قىيامەت كۈنى) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ';
$t[263] = 'ياخشى سۆز ۋە كەچۈرۈش كېيىن ئەزىيەت يەتكۈزىدىغان سەدىقىدىن ئەۋزەلدۇر. اﷲ (بەندىلەرنىڭ سەدىقىسىدىن) بىھاجەتتۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ';
$t[264] = 'ئى مۆمىنلەر! پۇل - مېلىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان، اﷲ قا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرمەيدىغان كىشى (نىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتكىنىگە) ئوخشاش، بەرگەن سەدىقەڭلارنى مىننەت قىلىش ۋە ئەزىيەت يەتكۈزۈش بىلەن بىكار قىلىۋەتمەڭلار. بۇنداق (پۇل - مېلىنى باشقىلارغا كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان) ئادەم خۇددى ئۈستىگە توپا - چاڭ قونۇپ قالغان، قاتتىق يامغۇردىن كېيىن (يۇيۇلۇپ) بۇرۇنقىدەك بولۇپ قالغان سىلىق تاشقا ئوخشايدۇ. ئۇلار قىلغان ئەمەللىرى ئۈچۈن (ئاخىرەتتە) ھېچقانداق ساۋابقا ئىگە بولالمايدۇ. اﷲ كاپىر قەۋمىنى ھىدايەت قىلمايدۇ';
$t[265] = 'ماللىرىنى اﷲ نىڭ رازىلىقىنى تىلەش ۋە نەپسىلىرىنى (سەدىقە - ئېھسانغا) ئادەتلەندۈرۈش يۈزىسىدىن سەرپ قىلىدىغانلار ئېگىزلىككە جايلاشقان، قاتتىق يامغۇردىن بەھرىمەن بولسا مېۋىسى ئىككى ھەسسە كۆپ بولىدىغان، قاتتىق يامغۇردىن بەھرىمەن بولمىسا شەبنەم بىلەنمۇ قانىدىغان (يەنى قايسى ھالدا بولسۇن مېۋە بېرىدىغان) بىر باغقا ئوخشايدۇ (دېمەك، بۇنداق خەير - ساخاۋەتلىك ئادەم مېلى كۆپ بولسا كۆپكە يارىشا، ئاز بولسا ئازغا يارىشا بېرىدۇ). اﷲ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر';
$t[266] = 'ئاراڭلاردا كىممۇ ياقتۇرسۇنكى، ئۇنىڭ خورمىلىق، ئۈزۈملۈك، ئاستىدىن ئېرىقلار ئېقىپ تۇرىدىغان، مېۋىلەرنىڭ ھەممە خىللىرى بولغان بىر بېغى بولسۇن، ئۇنىڭ ئۆزى قېرىپ قالغان، بالىلىرى ئۇششاق بولۇپ، (بۇ باغ) ئوت ئېلىپ كەلگەن بىر قارا بوراندا كۆيۈپ كەتسۇن. سىلەرنىڭ ئويلىنىشىڭلار ئۈچۈن، اﷲ سىلەرگە ئايەتلىرىنى شۇنداق (گۈزەل مىسال بىلەن) بايان قىلىدۇ (يەنى پۇل - مېلىنى رىيا ئۈچۈن سەرپ قىلغان ياكى باشقىلارغا رەنجىتىش، مىننەت قىلىش بىلەن سەرپ قىلغان ئادەم ساۋابىنى ئەڭ موھتاج تۇرۇقلۇق يوقىتىپ قويغان بولىدۇ)';
$t[267] = 'ئى مۆمىنلەر! سىلەر ئېرىشكەن نەرسىلەرنىڭ ۋە بىز سىلەرگە زېمىندىن چىقىرىپ بەرگەن نەرسىلەر (يەنى ئاشلىقلار، مېۋىلەر) نىڭ ياخشىلىرىدىن سەدىقە قىلىڭلار؛ ئۆزۈڭلارمۇ پەقەت كۆزۈڭلارنى يۇمۇپ تۇرۇپ ئالىدىغان ناچارلىرىنى ئىلغاپ سەدىقە قىلماڭلار. بىلىڭلاركى، اﷲ (بۇنداق سەدىقەڭلاردىن) بىھاجەتتۇر، ھەمدىگە لايىقتۇر';
$t[268] = 'شەيتان سىلەرنى پېقىرلىقتىن قورقىتىدۇ، يامان ئىشلارغا (يەنى بېخىللىققا، زاكات بەرمەسلىككە) بۇيرۇيدۇ؛ اﷲ سىلەرگە ئۆز مەغپىرىتىنى ۋە پەزلىنى ۋەدە قىلىدۇ. اﷲ نىڭ مەرھەمىتى كەڭدۇر، ئۇ ھەممىنى بىلىپ تۇرغۇچىدۇر';
$t[269] = 'ھېكمەتنى (يەنى ياخشى ئەمەلگە يېتەكلەيدىغان پايدىلىق ئىلىمنى) خالىغان بەندىسىگە ئاتا قىلىدۇ؛ ھېكمەت ئاتا قىلىنغان ئادەمگە كۆپ ياخشىلىق ئاتا قىلىنغان بولىدۇ. پەقەت ئەقىللىق ئادەملەرلا بۇنىڭدىن پەند - نەسىھەت ئالىدۇ';
$t[270] = 'نېمىنى خەير - ئېھسان قىلماڭلار ياكى (اﷲ نىڭ يولىدا) نېمىنى ۋەدە قىلماڭلار، اﷲ ئۇنى ھەقىقەتەن بىلىپ تۇرىدۇ. زالىملار (يەنى زاكاتنى مەنئى قىلغۇچىلار ياكى پۇل - مېلىنى گۇناھ ئىشلارغا سەرپ قىلغۇچىلار) غا ھېچقانداق مەدەتكار چىقمايدۇ';
$t[271] = 'سەدىقىنى ئاشكارا بەرسەڭلار، بۇ ياخشىدۇر، ئەگەر ئۇنى مەخپى بەرسەڭلار ۋە يوقسۇللارغا بەرسەڭلار، تېخىمۇ ياخشىدۇر. بۇ، سىلەرنىڭ بەزى گۇناھلىرىڭلارغا كەففارەت بولىدۇ، اﷲ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر';
$t[272] = '(ئى مۇھەممەد!) ئۇلارنى ھىدايەت قىلىش سېنىڭ مەسئۇلىيىتىڭ ئەمەس (ساڭا تاپشۇرۇلغان ۋەزىپە پەقەت كىشىلەرگە اﷲ نىڭ ۋەھىسىنى يەتكۈزۈشتۇر). لېكىن اﷲ خالىغان بەندىسىنى ھىدايەت قىلىدۇ. مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، ئۇ ئۆزۈڭلارنىڭ پايدىسى ئۈچۈندۇر، سىلەر پەقەت اﷲ نىڭ رازىلىقى ئۈچۈنلا خەير - ئېھسان قىلىسىلەر (يەنى خەير - ئېھسانىڭلار ئۈچۈن اﷲ نىڭ رازىلىقىدىن باشقىنى كۆزدە تۇتماڭلار)، مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى قىلغان ياخشى ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)';
$t[273] = 'ئۆزىنى اﷲ يولىغا ئاتىغان، (تىرىكچىلىك قىلىش ئۈچۈن) يىراق جايلارغا بارالمايدىغان يوقسۇللارغا خەير - ئېھسان قىلىنىشى كېرەك. ئەھۋالنى ئۇقمىغانلار، ئۇلارنىڭ نەرسە تىلىمىگەنلىكلىرىگە قاراپ، ئۇلارنى باي دەپ گۇمان قىلىدۇ، ئۇلارنىڭ چىرايىدىن تونۇيسەن، ئۇلار كىشىلەرگە چاپلىشىۋېلىپ تىلەمچىلىك قىلمايدۇ، مېلىڭلاردىن نېمىنى خەير - ئېھسان قىلماڭلار، اﷲ ھەقىقەتەن ئۇنى بىلگۈچىدۇر';
$t[274] = 'ماللىرىنى كېچە - كۈندۈز (يەنى ھەممە ۋاقىت)، يوشۇرۇن ۋە ئاشكارا يوسۇندا خەير - ئېھسان قىلىدىغانلار پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ';
$t[275] = 'جازانە، ئۆسۈم يېگەن ئادەملەر (قىيامەت كۈنى گۆرلىرىدىن) جىن چېپىلىپ قالغان ساراڭ ئادەملەردەك قوپىدۇ. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار اﷲ ھارام قىلغان ئىشنى ھالال بىلىپ: &laquo;سودا - سېتىق، جازانىگە ئوخشاش (جازانە نېمىشقا ھارام بولىدۇ؟)&raquo; دېدى. اﷲ سودا - سېتىقنى ھالال قىلدى، جازانىنى، (بەدەلسىز بولغانلىقى، شەخسكە ۋە جەمئىيەتكە زىيانلىق بولغانلىقى ئۈچۈن) ھارام قىلدى. كىمكى پەرۋەردىگارى تەرىپىدىن ۋەز - نەسىھەت كەلگەندىن كېيىن (يەنى جازانە مەنئى قىلىنغاندىن كېيىن ئۇنىڭدىن) يانسا، بۇرۇن ئالغىنى ئۆزىنىڭ بولىدۇ، ئۇنىڭ ئىشى اﷲ قا تاپشۇرۇلىدۇ (يەنى اﷲ خالىسا ئۈنى كەچۈرىدۇ، خالىسا جازالايدۇ). قايتا جازانە قىلغانلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالىدۇ';
$t[276] = 'اﷲ جازانىنىڭ (بەرىكىتىنى) ئۆچۈرۈۋېتىدۇ، سەدىقىنىڭ (بەرىكىتىنى) زىيادە قىلىدۇ. اﷲ ھەربىر ناشۈكۈر (يەنى جازانىنى ھالال سانىغۇچى) گۇناھكارنى دوست تۇتمايدۇ';
$t[277] = 'ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ناماز ئوقۇغان زاكات بەرگەن كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا (چوڭ) ساۋابقا ئېرىشىدۇ، ئۇلاردا (ئاخىرەتتە) قورقۇنچ ۋە غەم - قايغۇ بولمايدۇ';
$t[278] = 'ئى ئىمان ئېيتقان كىشىلەر! (ھەقىقي) مۆمىن بولساڭلار، اﷲ (نىڭ ئەمرىگە مۇخالىپەتچىلىك) قىلىشتىن ساقلىنىڭلار، (كىشىلەرنىڭ زىممىسىدە) قېلىپ قالغان جازانىنى (يەنى ئۆسۈمنى) ئالماڭلار';
$t[279] = 'ئەگەر ئۇنداق قىلساڭلار، بىلىڭلاركى، اﷲ ۋە ئۇنىڭ رەسۇلى سىلەرگە ئۇرۇش ئېلان قىلىدۇ، ئەگەر (جازانە قىلىشتىن) تەۋبە قىلساڭلار، (قەرز بەرگەن) سەرمايەڭلار ئۆزۈڭلارغا قايتىدۇ، باشقىلارنى زىيان تارتقۇزمايسىلەر، ئۆزۈڭلارمۇ زىيان تارتمايسىلەر';
$t[280] = 'ئەگەر قەرزدارنىڭ قولى قىسقا بولسا، ئۇنىڭ ھالى ياخشىلانغۇچە كۈتۈڭلار، ئەگەر (خەيرلىك ئىش ئىكەنلىكىنى بىلسەڭلار، قىيىنچىلىقتا قالغان قەرزداردىن ئالىدىغان قەرزنى ئۇنىڭغا) سەدىقە قىلىپ بېرىۋەتكىنىڭلار سىلەر ئۈچۈن تېخىمۇ ياخشىدۇر';
$t[281] = 'سىلەر شۇنداق بىر كۈندىن (يەنى قىيامەت كۈنىدىن) قورقۇڭلاركى، ئۇ كۈندە سىلەر اﷲ نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاندىن ھەر كىشى قىلغان (ياخشى - يامان) ئەمەلىنىڭ نەتىجىسىنى تولۇق كۆرىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ، (يەنى ياخشى ئەمەلى كېمەيتىلىپ، يامان ئەمەلى كۆپەيتىلمەيدۇ)';
$t[282] = 'ئى مۆمىنلەر! مۇددەت بەلگىلەپ ئۆزئارا قەرز بېرىشسەڭلار، ئۇنى (ھۆججەت قىلىپ) يېزىپ قويۇڭلار، ئاراڭلاردىكى خەت بىلىدىغان كىشى ئۇنى (كەم - زىيادە قىلماي) ئادىللىق بىلەن يازسۇن، خەت بىلىدىغان كىشى (اﷲ ئۇنىڭغا بىلدۈرگەندەك توغرىلىق بىلەن يېزىشىنى) رەت قىلمىسۇن، ئۇ يازسۇن، زىممىسىدە باشقىلارنىڭ ھەققى بولغان ئادەم (يازىدىغان كىشىگە) ئېيتىپ بەرسۇن، (قەرزدار) پەرۋەردىگارى اﷲ تىن قورقسۇن، قەرز ئالغان نەرسىدىن ھېچنېمىنى كېمەيتىۋەتمىسۇن، ئەگەر قەرز ئالغۇچى ئادەم ئەخمەق (يەنى خۇدبىن، ئەقىلسىز، ئىسراپخور) ياكى ئاجىز (يەنى كىچىك بالا ياكى بەكمۇ قېرى) بولسا، ياكى (گەپ قىلالماسلىقى، گاچىلىقى، كېكەچلىكى تۈپەيلىدىن) ئۆزى ئېيتىپ بېرەلمىسە، ئۇنىڭ ئىشلىرىنى باشقۇرغۇچى ئادەم ئادىللىق بىلەن ئېيتىپ بەرسۇن، سىلەر ئەر كىشىڭلاردىن ئىككى كىشىنى گۇۋاھلىققا تەكلىپ قىلىڭلار؛ ئەگەر ئىككى ئەر كىشى يوق بولسا، سىلەر (ئادالىتىگە، دىيانىتىگە) رازى بولىدىغان كىشىلەردىن بىر ئەر، ئىككى ئايالنى گۇۋاھلىققا تەكلىپ قىلىڭلار، بۇ ئىككى ئايالنىڭ بىرسى ئۇنتۇپ قالسا، ئككىنچىسى ئېسىگە سالىدۇ. گۇۋاھچىلار (گۇۋاھلىققا) چاقىرىلغان ۋاقتىدا (گۇۋاھ بولۇشتىن) باش تارتمىسۇن. قەرز مەيلى ئاز بولسۇن، مەيلى كۆپ بولسۇن، ئۇنى قايتۇرۇش ۋاقتى بىلەن قوشۇپ يېزىشتىن ئېرىنمەڭلار. اﷲ نىڭ نەزىرىدە (يەنى ھۆكمىدە)، بۇ ئەڭ ئادىللىق ۋە ئەڭ ئىسپاتلىقتۇر (يەنى گۇۋاھلىقنىڭ ئۇنتۇلماسلىقىنى ئەڭ ئىسپاتلىغۇچىدۇر)، گۇمانلانماسلىقىڭلارغا ئەڭ يېقىندۇر. لېكىن ئاراڭلاردا قىلىشقان قولمۇقول سودىدا ھۆججەت يازمىساڭلارمۇ ھېچ گۇناھ بولمايدۇ. ئۆزئارا سودا قىلىشقان ۋاقتىڭلاردا گۇۋاھچى تەكلىپ قىلىڭلار، پۈتۈكچىگىمۇ، گۇۋاھچىغىمۇ زىيان يەتكۈزۈلمىسۇن؛ ئەگەر زىيان يەتكۈزسەڭلار گۇناھ ئۆتكۈزگەن بولىسىلەر، اﷲ نىڭ (ئەمرىگە ۋە نەھيىسىگە مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار، اﷲ سىلەرگە ئۆگىتىدۇ (يەنى سىلەرگە ئىككىلا دۇنيادا پايدىلىق بولغان ئىلىمنى بېرىدۇ)، اﷲ ھەممە نەرسىنى بىلگۈچىدۇر';
$t[283] = 'ئەگەر سىلەر سەپەر ئۈستىدە بولۇپ، خەت يازالايدىغان ئادەمنى تاپالمىساڭلار، بۇ چاغدا (قەرز ھۆججىتى يېزىپ بېرىشنىڭ ئورنىغا) گۆرۈ قويىدىغان نەرسىنى تاپشۇرساڭلار بولىدۇ؛ ئاراڭلاردا بىرىڭلار بىرىڭلارغا ئىشىنىپ ئامانەت قويسا، ئۇ چاغدا ئامانەتنى ئالغۇچى ئادەم ئامانەتنى تاپشۇرسۇن (يەنى قەرز بەرگۈچى قەرز ئالغۇچىغا ئىشىنىپ ئۇنىڭدىن بىر نەرسىنى گۆرۈ ئالمىسا، قەرزدار قەرزنى دېيىشكەن ۋاقتىدا قايتۇرسۇن)، (قەرزدار ئامانەتكە رىئايە قىلىشتا) پەرۋەردىگارىدىن قورقسۇن. گۇۋاھلىقنى يوشۇرماڭلار، كىمكى ئۇنى يوشۇرىدىكەن، ھەقىقەتەن ئۇنىڭ دىلى گۇناھكار بولىدۇ. اﷲ قىلمىشىڭلارنى تامامەن بىلگۈچىدۇر';
$t[284] = 'ئاسمانلاردىكى، زېمىندىكى شەيئىلەر (ئىگىدارچىلىق قىلىش جەھەتتىن بولسۇن، تەسەررۇپ قىلىش جەھەتتىن بولسۇن) اﷲ نىڭ ئىلكىدىدۇر. دىلىڭلاردىكى (يامانلىقنى) مەيلى ئاشكارا قىلىڭلار، مەيلى يوشۇرۇڭلار، ئۇنىڭ ئۈچۈن اﷲ سىلەردىن ھېساب ئالىدۇ. اﷲ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ. اﷲ ھەممە نەرسىگە قادىردۇر';
$t[285] = 'پەيغەمبەر پەرۋەردىگارى تەرىپىدىن ئۇنىڭغا نازىل قىلىنغان كىتابغا ئىمان كەلتۈردى، مۆمىنلەرمۇ ئىمان كەلتۈردى، ئۇلارنىڭ ھەممىسى اﷲ قا ۋە اﷲ نىڭ پەرىشتىلىرىگە، كىتابلىرىغا ۋە پەيغەمبەرلىرىگە ئىمان كەلتۈردى. (ئۇلار) &laquo;اﷲ نىڭ پەيغەمبەرلىرىنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىگە ئىمان ئېيتماي قالمايمىز)&raquo; دەيدۇ. ئۇلار: &laquo;بىز (دەۋىتىڭنى) ئاڭلىدۇق ۋە (ئەمرىڭگە) ئىتائەت قىلدۇق، پەرۋەردىگارىمىز، مەغپىرىتىڭنى تىلەيمىز، ئاخىر يانىدىغان جايىمىز سېنىڭ دەرگاھىڭدۇر&raquo; دەيدۇ';
$t[286] = 'اﷲ ھېچكىمنى تاقىتى يەتمەيدىغان ئىشقا تەكلىپ قىلمايدۇ. كىشىنىڭ قىلغان ياخشىلىقى (نىڭ ساۋابى) ئۆزىگىدۇر، يامانلىقى (نىڭ جازاسى) مۇ ئۆزىگىدۇر. (ئۇلار) &laquo;پەرۋەردىگارىمىز! ئەگەر بىز ئۇنتۇساق ياكى خاتالاشساق (يەنى بىز ئۇنتۇش ياكى سەۋەنلىك سەۋەبىدىن ئەمرىڭنى تولۇق ئورۇنلىيالمىساق)، بىزنى جازاغا تارتمىغىن. پەرۋەردىگارىمىز! بىزدىن ئىلگىرىكىلەرگە يۈكلىگىنىڭگە ئوخشاش، بىزگە ئېغىر يۈك يۈكلىمىگىن (يەنى بىزنى قىيىن ئىشلارغا تەكلىپ قىلمىغىن)، پەرۋەردىگارىمىز! كۈچىمىز يەتمەيدىغان نەرسىنى بىزگە ئارتمىغىن، بىزنى كەچۈرگىن، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بىزنىڭ ئىگىمىزسەن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن&raquo; دەيدۇ';
?>