$t[64] = 'ئۇلارنىڭ بايلىرىنى ئازاب بىلەن جازالىغان ۋاقتىمىزدا ناگاھ ئۇلار پەرياد قىلىدۇ';
$t[65] = '(ئۇلارغا ئېيتىلىدۇ) &laquo;بۈگۈن پەرياد قىلماڭلار، ھەقىقەتەن سىلەر بىزنىڭ ياردىمىمىزگە ئېرىشەلمەيسىلەر&raquo;';
$t[66] = 'مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلەتتى، (ئۇنى ئاڭلاشتىن يۈز ئۆرۈپ) ئارقاڭلارغا چېكىنىۋالاتتىڭلار';
$t[67] = 'ئۇلار ھەرەم ئەھلى بولغانلىقى بىلەن پەخىرلىنىپ ئىماندىن يۈز ئۆرۈيدۇ، كەچلىك پاراڭلىرىدا يامان سۆزلەرنى قىلىدۇ، (يەنى قۇرئانغا تەنە قىلىدۇ، پەيغەمبەر ئەلەيھىسسالامنى سۆكىدۇ)';
$t[68] = 'ئۇلار (ھەق) كالام (يەنى ئۇلۇغ قۇرئان) ئۈستىدە چوڭقۇر پىكىر قىلمىدىمۇ؟ ياكى ئۇلارغا (اﷲ تەرىپىدىن) ئاتا - بوۋىلىرىغا كەلمىگەن (يېڭى) نەرسە كەلدىمۇ؟';
$t[69] = 'ياكى ئۇلار ئۆزلىرىگە كەلگەن ئەلچىنى تونۇماي ئۇنى ئىنكار قىلىۋاتامدۇ';
$t[70] = 'ياكى ئۇلار ئۇنى مەجنۇن دېيىشەمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلارغا ئۇ ھەقىقەتنى ئېلىپ كەلدى. ئۇلارنىڭ تولىسى ھەقىقەتنى يامان كۆرىدۇ';
$t[71] = 'ئەگەر ھەقىقەت (يەنى قۇرئان) ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنىدىغان بولسا، ئەلۋەتتە ئاسمانلار، زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار خاراب بولغان بولاتتى، ئۇنداق بولغىنى يوق، ئۇلارغا (ۋەز - نەسىھەتنى ئۆز ئىچىگە ئالغان) قۇرئاننى ئاتا قىلدۇق، ئۇلار ئۆزلىرىگە (ۋەز - نەسىھەت بولغان) قۇرئاندىن يۈز ئۆرۈيدۇ';
$t[72] = 'ياكى سەن ئۇلاردىن (ئەلچىلىكنى يەتكۈزگەنلىكىڭگە) ھەق تەلەپ قىلامسەن؟ (يەنى ئۇلار شۇنداق گۇمان قىلامدۇ؟) پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) ئەجرى ياخشىدۇر، اﷲ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر';
$t[73] = 'سەن ئەلۋەتتە ئۇلارنى توغرا يولغا دەۋەت قىلىسەن';
$t[74] = 'ئاخىرەتكە ئىشەنمەيدىغانلار ھەقىقەتەن توغرا يولدىن چەتنىگۈچىلەردۇر';
$t[75] = 'ئەگەر ئۇلار (يەنى مۇشرىكلار) غا رەھىم قىلساق، ئۇلارغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتسەك، چوقۇم گۇمراھلىقلىرىدا داۋاملىق تېڭىرقاپ يۈرۈشەتتى';
$t[76] = 'ئۇلارنى بىز ھەقىقەتەن ئازاب (يەنى قەھەتچىلىك) بىلەن جازالىدۇق، ئۇلار پەرۋەردىگارىغا بويسۇنمىدى، ئىلتىجا قىلىپ دۇئامۇ قىلمىدى';
$t[77] = 'تاكى ئۇلارغا قاتتىق ئازابنىڭ دەرۋازىسىنى ئاچقان چېغىمىزدا ناگاھان ئۇلار ئۈمىدسىزلىنىپ كېتىدۇ';
$t[78] = 'اﷲ سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە دىللارنى ياراتتى، سىلەر ناھايىتى ئاز شۈكۈر قىلىسىلەر';
$t[79] = 'اﷲ سىلەرنى زېمىندا (نەسىللەندۈرۈش يولى بىلەن) ياراتتى، (ھېساب بېرىش ئۈچۈن) ئۇنىڭ دەرگاھىغا يىغىلىسىلەر';
$t[80] = 'اﷲ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنىڭ) جېنىنى ئالىدۇ، كېچە - كۈندۈزنىڭ ئۆزگىرىپ تۇرۇشى اﷲ نىڭ باشقۇرۇشىدىدۇر، چۈشەنمەمسىلەر؟';
$t[81] = '(ئۇلار اﷲ نىڭ قۇدرىتىنى ئويلاپ كۆرمىدى) بەلكى ئۇلار بۇرۇنقىلار نېمىدېگەن بولسا شۇنى دېدى';
$t[82] = 'ئۇلار ئېيتتى: &laquo;بىز ئۆلۈپ توپىغا ۋە (ئۇۋۇلۇپ كەتكەن) سۆڭەككە ئايلانغاندىن كېيىن، چوقۇم قايتا تىرىلەمدۇق؟';
$t[83] = 'ھەقىقەتەن بىز ۋە بىزنىڭ ئاتا - بوۋىلىرىمىز ئىلگىرى مۇشۇنداق ئاگاھلاندۇرۇلغان ئىدۇق، بۇ پەقەت قەدىمكىلەردىن قالغان ئەپسانىلاردۇر&raquo;';
$t[84] = 'ئېيىتقىنكى، &laquo;زېمىن ۋە ئۇنىڭدىكى مەخلۇقاتلار كىمنىڭ؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)&raquo;';
$t[85] = 'ئۇلار: &laquo;اﷲ نىڭ&raquo; دەيدۇ. (بۇنىڭدىن) پەند - نەسىھەت ئالمامسىلەر؟';
$t[86] = 'ئېيىتقىنكى، &laquo;يەتتە ئاسماننىڭ ۋە ئۇلۇغ ئەرشنىڭ پەرۋەردىگارى كىم؟&raquo;';
$t[87] = 'ئۇلار &laquo;اﷲ&raquo; دەيدۇ. ئېيىتقىنكى، &laquo;ئۇنىڭ (ئازابىدىن) قورقمامسىلەر؟&raquo;';
$t[88] = 'ئېيىتقىنكى، &laquo;ھەممە شەيئىنىڭ پادىشاھلىقى كىمنىڭ قولىدا؟ (ئىلتىجا قىلغانلارغا) پاناھ بولالايدىغان ۋە ئۇنىڭغا قارشى ھېچ نەرسە پاناھ بولالمايدىغان كىم؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)&raquo;';
$t[89] = 'ئۇلار: &laquo;اﷲ&raquo; دەيدۇ. ئېيىتقىنكى، &laquo;سىلەر قانداقمۇ قايمۇقتۇرۇلىسىلەر؟&raquo;';
$t[90] = 'ئۇنداق ئەمەس، ئۇلارغا بىز ھەق سۆزنى ئېلىپ كەلدۇق، ئۇلار چوقۇم يالغانچىلاردۇر';
$t[91] = 'اﷲ نىڭ بالىسى يوق، ئۇنىڭغا باراۋەر باشقا بىر ئىلاھمۇ يوق، ئەگەر بۇنداق بولسا ئىدى، ئۇ چاغدا ھەر ئىلاھ ئۆزى ياراتقان مەخلۇقنى يالغۇز ئىگىلەيتتى، بەزىسى بەزىسىدىن غالىب كېلەتتى، اﷲ ئۇلارنىڭ اﷲ قا نىسبەت بەرگەن نەرسىلىرىدىن پاكتۇر';
$t[92] = 'اﷲ غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، مۇشرىكلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئۈستۈندۇر';
$t[93] = 'ئېيتقىنكى، &laquo;پەرۋەردىگارىم! ئەگەر ماڭا ئۇلارغا (دۇنيادا) ۋەدە قىلغان ئازابنى كۆرسىتىدىغان بولساڭ';
$t[94] = 'ئېيتقىنكى: &laquo;پەرۋەردىگارىم! مېنى زالىم قەۋملەر ئىچىدە قىلمىغىن&raquo;';
$t[95] = 'بىز ئۇلارغا ۋەدە قىلغان ئازابنى ساڭا كۆرسىتىشكە ئەلۋەتتە قادىرمىز';
$t[96] = '(ئۇلارنىڭ قىلغان) يامانلىقىغا (ئەپۇ قىلىش بىلەن) ياخشىلىق قىلغىن، ئۇلارنىڭ (سېنى) سۈپەتلەشلىرىنى (يەنى ئۇلارنىڭ ساڭا قىلغان مەسخىرىسىنى) بىز ئوبدان بىلىمىز';
$t[97] = 'ئېيتقىنكى، &laquo;پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ۋەسۋەسىلىرىدىن پاناھ تىلەيمەن';
$t[98] = 'پەرۋەردىگارىم! ساڭا سېغىنىپ شەيتانلارنىڭ ماڭا ھەمراھ بولۇشىدىن پاناھ تىلەيمەن&raquo;';
$t[99] = 'ئۇلارنىڭ (يەنى كاپىرلارنىڭ) بىرىگە ئۆلۈم كەلسە ئېيتىدۇركى، &laquo;پەرۋەردىگارىم! مېنى (دۇنياغا) قايتۇرغىن';
$t[100] = 'مەن قويۇپ كەلگەن مال - مۈلكۈم بىلەن ياخشىلىق قىلىشىم مۇمكىن&raquo;. (ئۇنىڭ دۇنياغا قايتىشى) مۇمكىن ئەمەس، ئۇ چوقۇم شۇ سۆزنى قىلغۇچىدۇر، ئۇلار قەبرىلىرىدىن تۇرغۇزۇلغان كۈنگە قەدەر ئۇلارنىڭ ئالدىدا (ئۇلارنى دۇنياغا قايتىشتىن توسىدىغان) بىر توسما بولىدۇ';
$t[101] = 'سۇر چېلىنغان كۈندە ئۇلارنىڭ ئارىسىدىكى تۇغقانچىلىق مەۋجۇت بولماي قالىدۇ. بىر - بىرىنىڭ (ئەھۋالىنىمۇ) سوراشمايدۇ';
$t[102] = 'تارازىسى ئېغىر كەلگەنلەر (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ كەتكەنلەر) بەختكە ئېرىشكۈچىلەردۇر';
$t[103] = 'تارازىسى يېنىك كەلگەنلەر (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەنلەر) زىيان تارتقۇچىلاردۇر، جەھەننەمدە مەڭگۈ قالغۇچىلاردۇر';
$t[104] = 'ئۇلارنىڭ يۈزلىرىنى ئوت كۆيدۈرىدۇ، كالپۇكلىرى قورۇلۇپ، چىشلىرى ئېچىلىپ (بەت چىراي) بولۇپ قالىدۇ';
$t[105] = '(ئۇلارغا) &laquo;سىلەرگە (دۇنيادا) مېنىڭ ئايەتلىرىم ئوقۇپ بېرىلگەن، سىلەر ئۇنى (شۇنچە روشەن تۇرۇقلۇق) ئىنكار قىلغان ئەمەسمىدىڭلار&raquo; دېيىلىدۇ';
$t[106] = 'ئۇلار ئېيتىدۇ: &laquo;پەرۋەردىگارىمىز! بەتبەختلىكىمىز ئۈستىمىزدىن غالىب كېلىپ گۇمراھ قەۋم بولۇپ قالغان ئىدۇق';
$t[107] = 'پەرۋەردىگارىمىز! بىزنى دوزاختىن چىقارغىن (ئاندىن دۇنياغا قايتۇرغىن)، ئەگەر بىز (شۇنىڭدىن كېيىن) يەنە گۇناھ قىلساق، بىز ھەقىقەتەن زالىم بولۇپ قالىمىز&raquo;';
$t[108] = 'اﷲ ئېيتىدۇ: &laquo;جەھەننەمدە خار ھالدا قىلىڭلار، (ئۈستۈڭلاردىن ئازابنىڭ كۆتۈرۈلۈپ كېتىشى ھەققىدە) ماڭا سۆز ئاچماڭلار&raquo;';
$t[109] = 'شۈبھىسىزكى، بەندىلىرىمدىن بىر تۈركۈم كىشىلەر: &laquo;پەرۋەردىگارىمىز! بىز ئىمان ئېيتتۇق، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بولساڭ ئەڭ رەھىم قىلغۇچىسەن&raquo; دەيتتى';
$t[110] = 'ئۇلارنى مەسخىرە قىلدىڭلار، ھەتتا ئۇلار سىلەرگە مېنىڭ زىكرىمنى ئۇنتۇلدۇردى (يەنى ئۇلارنى مەسخىرە قىلىش بىلەن بولۇپ كېتىپ ماڭا ئىتائەت قىلىشنى ئۇنتۇدۇڭلار)، سىلەر ئۇلاردىن كۈلەتتىڭلار';
$t[111] = '(سىلەردىن يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغانلىقلىرى ئۈچۈن، مەن ئۇلارنى بۈگۈن مۇكاپاتلىدىم، ئۇلار ھەقىقەتەن مەقسەتكە ئېرىشكۇچىلەردۇر';
$t[112] = 'اﷲ (ئۇلارغا): &laquo;زېمىندا قانچىلىك تۇردۇڭلار؟&raquo; دەيدۇ';
$t[113] = 'ئۇلار: &laquo;بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق، (ئاي - كۈنلەرنى) ھېسابلىغۇچى (پەرىشتە) دىن سورىغىن&raquo; دەيدۇ (ئۇلار دوزاختا چېكىۋاتقان ئازاب تۈپەيلىدىن، زېمىندا قانچىلىك تۇرغانلىقىنى ئۇنتۇيدۇ)';
$t[114] = 'اﷲ: &laquo;ئەگەر سىلەر بىلسەڭلار (دۇنيادا) پەقەت ئازغىنا تۇردۇڭلار&raquo; دەيدۇ';
$t[115] = 'ئى ئىنسانلار! بىزنى سىلەر ئۆزۈڭلارنى بىكار ياراتتى (يەنى سىلەر بىزنى ھايۋاناتلارنى ياراتقىنىمىزدەك ھېچقانداق ساۋابمۇ بەرمەيدىغان، جازامۇ بەرمەيدىغان قىلىپ ياراتتى). ئۆزۈڭلارنى بىزنىڭ دەرگاھىمىزغا قايتۇرۇلمايدۇ دەپ ئويلامسىلەر؟';
$t[116] = 'ھەق - پادىشاھ اﷲ ئۈستۈندۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، اﷲ ئۇلۇغ ئەرشنىڭ پەرۋەردىگارىدۇر';
$t[117] = 'كىمكى ھېچقانداق دەلىلى بولمىغان ھالدا اﷲ قا قوشۇپ يەنە بىر مەبۇدقا ئىبادەت قىلىدىكەن، پەرۋەردىگارىنىڭ دەرگاھىدا ھېساب بېرىدۇ، كاپىرلار ھەقىقەتەن نىجات تاپمايدۇ';
$t[118] = '&laquo;پەرۋەردىگارىم! مەغپىرەت قىلغىن، رەھىم قىلغىن، سەن رەھىم قىلغۇچىلارنىڭ ياخشىسىسەن&raquo; دېگىن';
?>