$t[39] = 'سىلەر كىشىلەرنىڭ پۇل - مېلى ئىچىدە ئۆستۈرۈش ئۈچۈن بىرەر پۇل - مال بەرسەڭلار، اﷲ نىڭ دەرگاھىدا ئۇ ئۆسمەيدۇ (يەنى كىشىلەر بەرگىنىمدىن جىقراق قايتۇرسۇن دېگەن نىيەت بىلەن بېرىلگەن سوغىنىڭ اﷲ نىڭ دەرگاھىدا ساۋابى بولمايدۇ)، اﷲ نىڭ رازىلىقىنى كۆزلەپ بەرگەن سەدىقەڭلار (ياكى ئېھسانىڭلار) غا ھەسسىلەپ ساۋاب بېرىلىدۇ';
$t[40] = 'اﷲ سىلەرنى خەلق ئەتتى، ئاندىن سىلەرگە رىزىق بەردى. ئاندىن سىلەرنى قەبزى روھ قىلىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، شېرىك كەلتۈرگەنلىرىڭلاردىن قايسىسى بۇ ئىشلارنىڭ قايسىبىرىنى قىلالايدۇ، اﷲ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئالىيدۇر';
$t[41] = 'ئىنسانلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن، قۇرۇقلۇقتا ۋە دېڭىزدا ئاپەت يۈزبەردى، اﷲ ئۇلارنى تەۋبە قىلسۇن دەپ قىلمىشلىرىنىڭ بىر قىسمىنىڭ (جازاسىنى) ئۇلارغا تېتىتتى';
$t[42] = '(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، يەر يۈزىدە سەير قىلىڭلار، ئۆتكەنكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار (ئۆزلىرىگە زۇلۇم قىلغانلارنىڭ جايلىرىغا قاراڭلاركى، ئۇلارنىڭ پەيغەمبەرلەرنى ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتى قانداق بولغان، اﷲ تائالا ئۇلارنىڭ دىيارلىرىنى خاراپ قىلىپ، ئۇلارنى ئىبرەت ئالغۇچىلار ئۈچۈن ئىبرەت قىلدى)، ئۇلارنىڭ تولىسى مۇشرىك ئىدى';
$t[43] = 'اﷲ تەرىپىدىن كېلىدىغان، قارشى تۇرغىلى بولمايدىغان كۈن (يەنى قىيامەت) كېلىشتىن ئىلگىرى توغرا دىنغا يۈزلەنگىن، بۇ كۈندە ئۇلار (نىڭ بىر پىرقىسى جەننەتكە، بىر پىرقىسى جەھەننەمگە) ئايرىلىدۇ';
$t[44] = 'كىمكى كاپىر بولسا (دوزاختا ئەبەدىي قېلىپ) كۇفرىنىڭ جازاسىنى ئۆزى تارتىدۇ، كىملەركى ياخشى ئەمەل قىلسا، ئۆزلىرى ئۈچۈن (ئاخىرەتتە) جاي راستلايدۇ';
$t[45] = 'اﷲ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مەرھەمىتىدىن مۇكاپاتلايدۇ، اﷲ ھەقىقەتەن كاپىرلارنى دوست تۇتمايدۇ';
$t[46] = 'اﷲ نىڭ سىلەرگە ئۆزىنىڭ رەھمىتىنى تېتىتىش ئۈچۈن، ئەمرى بويىچە كېمىلەرنى دېڭىزلاردا ماڭدۇرۇش ئۈچۈن، ئۆزىنىڭ مەرھەمىتىدىن تىلىشىڭلار ئۈچۈن، شۈكۈر قىلىشىڭلار ئۈچۈن، شاماللارنى يامغۇر بىلەن خۇش خەۋەر بەرگۈچى قىلىپ ئەۋەتىشى اﷲ نىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر';
$t[47] = '(ئى مۇھەممەد!) شۈبھىسىزكى، سەندىن ئىلگىرى نۇرغۇن پەيغەمبەرلەرنى قەۋمىگە (پەيغەمبەر قىلپ) ئەۋەتتۇق، ئۇ پەيغەمبەرلەر ئۇلارغا (ئۆزلىرىنىڭ راست پەيغەمبەرلىكلىرىنى ئىسپاتلايدىغان) نۇرغۇن روشەن مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلىپ ئاسىيلىق قىلدى)، ئاسىيلىق قىلغانلارنى جازالىدۇق، مۆمىنلەرگە ياردەم قىلىش بىزگە تېگىشلىك بولدى';
$t[48] = 'اﷲ شاماللارنى ئەۋەتىپ بۇلۇتنى قوزغايدۇ، ئاندىن ئۇنى ئاسماندا خالىغىنى بويىچە تارقىتىدۇ، (گاھى) ئۇنى تارقاق پارچىلارغا بۆلىدۇ، شۇنىڭ بىلەن يامغۇرنىڭ بۇلۇتنىڭ ئارىسىدىن چۈشۈۋاتقانلىقىنى كۆرىسەن، اﷲ يامغۇرنى خالىغان بەندىلىرىنىڭ ئۈستىگە ياغدۇرغان چاغدا، ئۇلار دەرھال خۇشال بولۇپ كېتىدۇ';
$t[49] = 'ھالبۇكى، ئۇلار يامغۇر يېغىشتىن بۇرۇن ھەقىقەتەن ئۈمىدسىزلىنىپ كەتكەن ئىدى';
$t[50] = 'اﷲ نىڭ رەھمىتىنىڭ نەتىجىلىرىگە قارىغىنكى، ئۇ زېمىننى ئۆلگەندىن كېيىن قانداق تىرىلدۈرىدۇ، شۈبھىسىزكى، اﷲ ھەقىقەتەن ئۆلۈكلەرنى تىرىلدۈرگۈچىدۇر، اﷲ ھەممە نەرسىگە قادىردۇر';
$t[51] = 'ئەگەر بىز (زىرائەتكە زىيانلىق) شامالنى ئەۋەتسەك، ئۇلار (شامالنىڭ تەسىرىدىن) زىرائەتنىڭ سارغىيىپ كەتكەنلىكىنى كۆرسە، شۇنىڭدىن كېيىن ئۇلار (اﷲ نىڭ ئىلگىرىكى نېمەتلىرىگە) كۇفرىلىق قىلىدۇ';
$t[52] = 'شۈبھىسىزكى، سەن ئۆلۈكلەرگە (سۆزۈڭنى) ئاڭلىتالمايسەن، ئارقىسىغا قاراپ يۈز ئۆرۈگەن گاسلارغىمۇ چاقىرىقىڭنى ئاڭلىتالمايسەن';
$t[53] = 'كورلارنىمۇ گۇمراھلىقتىن قۇتقۇزۇپ توغرا يولغا سالالمايسەن، سەن پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىشىنىدىغانلارغىلا (سۆزۈڭنى) ئاڭلىتالايسەن، ئۇلار مۇسۇلمانلاردۇر';
$t[54] = 'اﷲ سىلەرنى ئاجىز ياراتتى، ئاندىن سىلەرنى ئاجىزلىقتىن كۈچلۈك قىلدى، ئاندىن (سىلەرنى) كۈچلۈكلۈكتىن ئاجىز قىلدى ۋە (سىلەرگە) قېرىلىقنى يەتكۈزدى، اﷲ نېمىنى خالىسا شۇنى يارىتىدۇ، ئۇ ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر';
$t[55] = 'قىيامەت قايىم بولغان كۈندە مۇشرىكلار (دۇنيادا) پەقەت ئازغىنا ۋاقىت تۇرغانلىقىغا قەسەم قىلىدۇ، ئۇلار دۇنيادىكى چاغلىرىدىمۇ مۇشۇنداق يالغانچىلىق قىلاتتى';
$t[56] = 'ئىلمى بار، ئىمانى بار ئادەملەر ئېيتتى: &laquo;سىلەر اﷲ نىڭ تەقدىرى بويىچە (دۇنيادا ۋە قەبرىلەردە) قىيامەت كۈنىگىچە قالدىڭلار، مانا بۇ قىيامەت كۈنىدۇر، لېكىن سىلەر بىلمەيتتىڭلار (يەنى قىيامەتنىڭ ھەقلىقىغا ئىشەنمەيتتىڭلار)&raquo;';
$t[57] = 'بۇ كۈندە زالىملارنىڭ (ئېيتقان) ئۆزرىسىنىڭ پايدىسى بولمايدۇ، ئۇلاردىن ئۇلارنىڭ (تەۋبە قىلىشى يا ئىتائەت قىلىشى بىلەن) اﷲ نى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ';
$t[58] = 'شەك - شۈبھىسىزكى، بۇ قۇرئاندا كىشىلەرگە تۈرلۈك مىساللارنى بايان قىلدۇق، ئەگەر سەن ئۇلارغا (قۇرئان ئايەتلىرىدىن) بىرەر ئايەتنى كەلتۈرسەڭ، كاپىرلار (دىللىرى قەساۋەتلىشىپ كەتكەنلىكتىن) چوقۇم ئېيتىدۇكى، &laquo;سىلەر پەقەت يالغانچىلار سىلەر&raquo;';
$t[59] = '(اﷲ نىڭ بىرلىكىنى) بىلمەيدىغان (كۇففار) لارنىڭ دىللىرىنى مۇشۇنداق پېچەتلەيمىز';
$t[60] = '(ئى مۇھەممەد!) (مۇشرىكلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن، اﷲ نىڭ (ساڭا نۇسرەت ئاتا قىلىش ۋە دىنىڭنى ئۈستۈنلۈككە ئىگە قىلىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، (ئاخىرەتكە) ئىشەنمەيدىغانلار سېنى دىققەت قىلىپ قويمىسۇن';
?>