$t[58] = 'مېھرىبان پەرۋەردىگارى تەرىپىدىن (ئۇلارغا) سالام دېيىلىدۇ';
$t[59] = '(گۇناھكارلارغا) &laquo;ئى گۇناھكارلار! بۈگۈن (بىر تەرەپكە) ئايرىلىڭلار&raquo; (دېيىلىدۇ)';
$t[60] = 'سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): &laquo;ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر&raquo; دەپ تەۋسىيە قىلمىدىممۇ؟';
$t[61] = 'سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): &laquo;ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر&raquo; دەپ تەۋسىيە قىلمىدىممۇ؟';
$t[62] = 'شۈبھىسىزكى، شەيتان سىلەردىن نۇرغۇن كىشىلەرنى ئازدۇردى، (بۇنى) چۈشەنمەمسىلەر؟';
$t[63] = 'سىلەرگە ۋەدە قىلىنغان جەھەننەم مانا شۇ';
$t[64] = 'سىلەر (دۇنيادىكى چېغىڭلاردا) كاپىر بولغانلىقىڭلار ئۈچۈن جەھەننەمگە كىرىڭلار';
$t[65] = 'شۇ كۈندە ئۇلارنىڭ ئېغىزلىرىنى پېچەتلىۋېتىمىز، ئۇلارنىڭ قىلمىشلىرىنى قوللىرى بىزگە سۆزلەپ بېرىدۇ، پۇتلىرى گۇۋاھلىق بېرىدۇ (يەنى ھەر ئەزا ئۇنىڭدىن سادىر بولغان ئىشنى سۆزلەپ بېرىدۇ)';
$t[66] = 'ئەگەر بىز خالىساق، ئۇلارنىڭ كۆزلىرىنى ئەلۋەتتە يوق قىلىۋېتەتتۇق، ئاندىن ئۇلار يول مېڭىشقا ئالدىرايتتى، لېكىن ئۇلار قانداقمۇ كۆرەلىسۇن؟';
$t[67] = 'ئەگەر بىز خالىساق، ئۇلارنى ئۆيلىرىدىلا سۈرىتىنى مۇبەددەل (يەنى مايمۇن، چوشقا ياكى تاش) قىلىۋېتەتتۇق، شۇنىڭ بىلەن ئۇلار ئالدىغىمۇ ماڭالمايتتى، ئارقىغىمۇ قايتالمايتتى';
$t[68] = 'بىز ئۆمرىنى ئۇزۇن قىلغان ئادەمنىڭ تېنىنى ئاجىزلىتىۋېتىمىز (يەنى ياشلىق، قىرانلىق ۋاقتىدىكىدىن ئاجىزلايدۇ، قېرىيدۇ)، ئۇلار (بۇنى) چۈشەنمەمدۇ؟';
$t[69] = 'ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز - نەسىھەتتۇر ۋە روشەن قۇرئاندۇر';
$t[70] = 'ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز - نەسىھەتتۇر ۋە روشەن قۇرئاندۇر';
$t[71] = 'ئۇلار بىلمەمدۇكى، ئۇلار ئۈچۈن ھايۋانلارنى (قۇدرەت) قولىمىز بىلەن خەلق ئەتتۇق، ئۇلار ھايۋانلارنى باشقۇرۇپ تۇرغۇچىلاردۇر';
$t[72] = 'ھايۋانلارنى ئۇلارغا بويسۇندۇرۇپ بەردۇق، ئۇلار ئۇ ھايۋانلارنىڭ بەزىسىنى مىنىدۇ، بەزىسىنى يەيدۇ';
$t[73] = 'ئۇلار شۇ ھايۋانلاردىن پايدىلىنىدۇ ۋە سۈتلىرىنى ئىچىدۇ، ئۇلار (پەرۋەردىگارىنىڭ بۇ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟';
$t[74] = 'ئۇلار ياردەمگە ئېرىشىشنى ئۈمىد قىلىپ، اﷲ نى قويۇپ باشقا مەبۇدلارنى ئىلاھ قىلىۋالدى';
$t[75] = 'ئۇ مەبۇدلار ئۇلارغا ياردەم بېرەلمەيدۇ. بۇلار شۇ مەبۇدلار ئۈچۈن (يەنى ئۇلارنىڭ خىزمىتى ئۈچۈن) ھازىرلانغان قوشۇندۇر';
$t[76] = '(ئى، مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) نىڭ سۆزى (يەنى سېنى ئىنكار قىلغانلىقى) سېنى غەمكىن قىلمىسۇن. بىز ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى ۋە ئاشكارا قىلغان (سۆز ۋە ھەرىكەتلىرى) نى بىلىپ تۇرىمىز';
$t[77] = 'ئىنسان بىلمەمدۇكى، بىز ھەقىقەتەن ئۇنى ئابىمەنىيدىن ياراتتۇق. ئەمدى ئۇ (پەرۋەردىگارىغا) ئاشكارا خۇسۇمەتچى بولۇپ قالدى';
$t[78] = 'ئۇ بىزگە (ئىنساننىڭ قايتا تىرىلىشىنى يىراق ساناپ، چىرىگەن سۆڭەكلەرنى) مىسال قىلىپ كۆرسەتتى. ئۆزىنىڭ يارىتىلغانلىقىنى بولسا ئۇنتۇدى، ئۇ: &laquo;چىرىپ كەتكەن سۆڭەكلەرنى كىم تىرىلدۈرەلەيدۇ؟&raquo; دېدى';
$t[79] = 'ئېيتقىنكى، &laquo;ئۇنى ئەڭ دەسلەپتە ياراتقان زات تىرىلدۈرىدۇ، ئۇ ھەر بىر مەخلۇقنى بىلگۈچىدۇر&raquo;';
$t[80] = 'اﷲ سىلەر ئۈچۈن يېشىل دەرەختىن ئوت پەيدا قىلدى. سىلەر ئۇنىڭ بىلەن ئوت ياقىسىلەر';
$t[81] = 'ئاسمانلارنى ۋە زېمىننى ياراتقان زات ئۇلارنىڭ ئوخشىشىنى يارىتىشقا قادىر ئەمەسمۇ؟ ئۇ بۇنىڭغا قادىر، اﷲ ماھىر ياراتقۇچىدۇر، ھەممىنى بىلگۈچىدۇر';
$t[82] = 'اﷲ بىرەر شەيئىنى (يارىتىشنى) ئىرادە قىلسا، ئۇنىڭغا &laquo;ۋۇجۇتقا كەل&raquo; دەيدۇ - دە، ئۇ ۋۇجۇتقا كېلىدۇ';
$t[83] = 'اﷲ پاكتۇركى، ھەر بىر نەرسىنىڭ ئىگىلىك ھوقوقى ئۇنىڭ قولىدىدۇر، (ئۆلگەندىن كېيىن) ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر';
?>